INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('"Podej mi ruku, já Tě podržím" - podpořte boj s rakovinou', 'AMA - společnost onkologických pacientů, jejich rodinných příslušníků a přátel, o.s.', 'AMAONKO', 'Cílem projektu je celkové zkvalitnění péče onkologického pacienta, ozdravení jak jeho psychického stavu, tak fyzické kondice, pomoc při návratu do stavu přibližně podobající se stavu před nemocí, pomoc při hledání svého místa v životě, znovunavrácení radosti a plnohodnotného rodinného, pracovního i společenského života, preventivní činností být prospěšní zdravé populaci ale i sobě, časným nálezem případného výskytu nemoci snížit náklady na léčbu a její doléčení.  ', 'Posláním organizace je poskytování sociální pomoci, poradenství a péče klientům s (post)onkologickým onemocněním a preventivní programy nádorových onemocnění pro studenty i širokou veřejnost. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('ACORUS: Společně proti domácímu násilí', 'Acorus, z.ú.', 'ACORUS', 'Podporou iniciativy „ACORUS: Společně proti domácímu násilí!" přispějete k zajištění víceúrovňové komplexní pomoci ženám a dětem, které zažívaly domácí násilí. Tato pomoc zahrnuje ambulantní služby v odborné poradně, nonstop linku, možnost využít krizová lůžka a na krizových lůžkách, utajené azylové ubytování a následné bydlení.Finanční prostředky ze sbírky budou použity na posílení víceúrovňové pomoci osobám ohroženým domácím násilím především formou podpory ambulantní služby. Ze sbírky bude uhrazen nájem v ambulantní poradně, kde je osobám ohroženým domácím násilím poskytováno odborné poradenství (sociálně-právní, psychologické, pedagogické, včetně psychoterapie a právních informací obětem trestných činů).ACORUS, o. s. poskytuje osobám ohroženým domácím násilím komplexní odbornou pomoc a všestrannou podporu směřující k překonání jejich nepříznivé sociální situace a k začlenění do běžného života bez přítomnosti násilí.Výjimečnost ACORUSu tkví především v poskytování přímé odborné komplexní pomoci osobám ohroženým domácím násilím, zejména ženám a dětem, přičemž utajené bydlení je výše zmíněné klientele poskytováno nonstop (24 hod. denně). V praxi se jedná o víceúrovňový systém psychosociální pomoci, který nabízí uvedené klientele: 1. okamžitý bezpečný úkryt a řešení akutní situace v rámci krátkodobého krizového pobytu (služba krizová pomoc) 2. azylové ubytování až na jeden rok s intenzivní sociálně právní, psychologickou a pedagogickou pomocí (služba azylový dům) 3. poradenské služby sociálně právní, právní, pedagogické a psychologické (služba odborné sociální poradenství) 4. specifické právní informace určené pro oběti trestných činů - oběti domácího násilí (nová služba poskytování právních informací obětem trestných činů je akreditovaná u Ministerstva spravedlnosti) Uvedené aktivity tvoří ucelený systém ochrany a pomoci a kontinuálně na sebe navazují.', 'ACORUS, o. s. poskytuje osobám ohroženým domácím násilím komplexní odbornou pomoc a všestrannou podporu směřující k překonání jejich nepříznivé sociální situace a k začlenění do běžného života bez přítomnosti násilí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Adopce na dálku v rámci ČR', 'DEJME DĚTEM ŠANCI o.p.s.', 'DDS', 'Projekt je zaměřen na komplexní podporu konkrétních dětí z dětských domovů na území ČR, které jsou a budou součástí naší společnosti. Podpora spočívá ve spoření pro děti po dobu jejich pobytu v dětském domově s tím, že naspořená částka bude dítěti k dispozici až po opuštění dětského domova, finanční podpoře při studiu vybraného oboru, splnění opodstatněného přání dítěte, materiální pomoci po opuštění dětského domova, osobním kontaktu s dítětem. Každé dítě má přiděleno svůj v.s., pod kterým jsou evidovány příspěvky, zaslané na podporu tohoto konkrétního dítěte. DEJME DĚTEM ŠANCI o.p.s. je garantem účelného využití peněz.', 'Naše obecně prospěšná společnost poskytuje komplexní pomoc dětem a mladým lidem, vyrůstajícím v dětských domovech na území celé ČR a to zejména na jejich cestě do samostatného života. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Adopce nablízko', 'Salesiánská asociace Dona Boska, o.s.', 'ADOPCENABLIZKO', 'Tento projekt působí v rámci rozvojové pomoci. Jeho účelem je pomoci ohroženým a znevýhodněným dětem a mládeži ve světě. Tento cíl je naplňován podporou dobrovolníků, kteří mají zájem poskytnout část svého života těm nejpotřebnějším. Prostřednictvím tohoto projektu je možné si dobrovolníka "adoptovat" a tím zajistit jeho účinnou pomoc na místě jeho působení.', 'SADBA vznikla za účelem pomáhat znevýhodněným dětem ve světě. Její hlavní náplní je pak školení dobrovolníků, kteří jsou vysíláni do salesiánských středisek v Africe, Evropě, ale i v Mexiku a Indii, kde se těmto ohroženým dětem věnují.Posláním organizace je: - výchova a vzdělávání dobrovolníků a dalších pracovníků, - podpora salesiánských misií a rozvojové spolupráce, - propojování salesiánských aktivit pro děti a mládé v rámci ČR, - národní a mezinárodní akce. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Adventní koncerty 2016', '', 'ADVENT', 'Čtyři Adventní koncerty jsou věnovány 4 neziskovým organizacím, mezi které je rovnoměrně rozdělen celý výtěžek sbírky. Adventní koncerty jsou nejstarším charitativním projektem České televize.1. Adventní koncert - 27. 11. 2016, Břevnovský klášter ve prospěch MIKASA (Ostrava) Od roku 2009 pomáhá organizace Mikasa dospělým a dětem postiženým autismem a Aspergerovým syndromem. Mikasa provozuje denní stacionář pro děti a mládež s tímto onemocněním. Výtěžek z AK podpoří nově zakládanou sociální službu Sociální rehabilitace, prostoru pro nácvik běžných schopností a dovedností, které vlivem poruchy autistického spektra nemocní neovládají. Posláním rehabilitace je v co největší míře přispět k integraci postižené osoby do společnosti - pomoci získat zaměstnání či umožnit samostatné bydlení.2. Adventní koncert - 4. 12. 2016, Loreta ve prospěch Sdružení Tulipán (Liberec) Sdružení Tulipán se od roku 2004 věnuje zaměstnávání lidí s duševním zdravotním postižením. Tulipán provozuje chráněnou dílnu, která zaměstnává přes 50 osob znevýhodněných poruchou duševního zdraví. Služby poskytované zaměstnanci chráněné dílny jsou velice různorodé, v nabídce jsou úklidové a zahradnické práce, šití, žehlení, kompletovací práce či vazby dokumentů. Zaměstnanci také vyrábí keramické výrobky, šperky, textilní a bytové doplňky. Finance získané skrze AK budou použity na koupi stěhovací dodávky a automobilu, díky kterým bude možné rozšířit zahradnické a stěhovací práce, což souvisí i s možností zaměstnat vyšší počet postižených osob. Dále bude výtěžek z koncertu použit na vybavení tří chráněných bytů, jejichž fungování plánuje Tulipán realizovat od roku 2017 pro 10 postižených osob.3. Adventní koncert - 11. 12. 2016, Basilika sv. Petra a Pavla na Vyšehradě ve prospěch Klubíčka Beroun Od roku 2011 pečuje Klubíčko Beroun o děti i dospělé s těžkým tělesným zdravotním postižením. Organizace provozuje Penzion Klubíčko Vráž, který celoročně poskytuje terapie, sociální a integrované služby. Klubíčko Beroun funguje jako sociální firma, která přednostně zaměstnává postižené osoby a osoby dlouhodobě pečující o blízké se zdravotním postižením. Finance získané z výtěžku AK chce organizace použít ke dvěma účelům. K vybudování multifunkční dílny Klubák sloužící k provozování integrovaných aktivit pro klienty a případně jejich zdravé děti. V druhé řadě k rekonstrukci a pořízení speciálního vybavení penzionu, díky kterým bude možné využívat plnou kapacitu lůžek. Kapacita se tak navýší z nynějších 6 míst na 17.4. Adventní koncert - 18. 12. 2016, Chrám sv. Mikuláše na Malé Straně ve prospěch Zdravotního klauna (Praha) Organizace Zdravotní klaun věnující se od roku 2001 zdravotním klauniádám má hlavní záměr - léčit smíchem. 86 profesionálních klaunů pravidelně navštěvuje 64 nemocnic a 3 léčebny v celé ČR. Organizace také provozuje Cirkus Paciento, týdenní program pro dlouhodobě nemocné děti při kterém klauni učí jak kouzlit nebo žonglovat, vše vrcholí slavnostním vystoupením. Svoje aktivity hradí organizace čistě z darovaných příjmů, pro pacienty je tato služba totiž zcela zdarma. Výtěžek z AK organizace využije na provoz zahrnující náklady na mzdy, cestovné, dárky pro pacienty a rekvizity. Díky finanční pomoci bude umožněno navštěvovat nemocnice v pravidelných intervalech a zprostředkovat dětským pacientům pestrobarevný zážitek, který je alespoň na chvíli oprostí od nehostinného prostředí nemocnice a pomůže jim se rychleji uzdravit. ', '', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('AGAPO - podpora osob se znevýhodněním', 'AGAPO, o.p.s.', 'AGAPO', 'Získané prostředky budou vynaloženy na přímou práci s klienty služby a na náklady souvisejícími s jejich chodem.Služby jsou klientům poskytovány v těchto programech. - Podporované zaměstnávání - Tranzitní program „ze školy do práce" - Job klub „motivačně-vzdělávací kurz" - Právo pro celý život „kurz základního právního povědomí" - Základní sociální poradenství - Nácvik sociálních dovedností', 'Naším posláním je podporovat osoby se zdravotním nebo sociálním znevýhodněním při hledání uplatnění na otevřeném trhu práce. Cílem je aby i lidé se znevýhodněním mohli žít běžným životem. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('AIDS pomoc - Červená stužka', 'Česká společnost AIDS pomoc, z.s.', 'AIDSPOMOC', 'Cílem projektu je financování aktivit České společnosti AIDS pomoc a Domu světla, zejména zajištění provozu Domu světla a preventivních činností v oblasti HI a AIDS a také na případné stavební práce spojené s rekonstrukcí Domu světla.', 'Česká společnost AIDS pomoc (ČSAP) vznikla koncem roku 1989 jako iniciativa ze strany blízkých, známých a rodin HIV pozitivních občanů, s cílem jim prakticky pomáhat při řešení každodenních komplikací, které infekce HIV přinášela. Práce sdružení se postupně rozšířila i o prevenci a osvětu a do její činnosti se zapojil i větší počet samotných HIV pozitivních osob. Významným milníkem v činnosti bylo vybudování sociálně-azylového centra Dům světla koncem 90. let, od kdy ČSAP nabízí rovněž bezplatné anonymní testování na HIV a azylové a ubytovací služby pro HIV+ osoby.Organizace realizuje AIDS poradny, bezplatné anonymní testování na protilátky HIV v Praze a Ostravě, nonstop telefonní poradenskou linku pro oblast prevence HIV/AIDS, besedy pro žáky základních a středních škol a další aktivity. V nedávné době spolek rozšířil svou činnost i na oblast právní pomoci v případech diskriminace HIV pozitivních osob a na snižování společenského stigmatu, který s sebou HIV infekce může přinášet. Financování organizace je z dotací, darů a příspěvků. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Akce Cihla - Sbírka na podporu lidí s mentálním postižením', 'Portus Praha z.ú.', 'CIHLA', 'Za peníze získané z pražské sbírky opraví pořadatelé kampaně ze společnosti Portus Praha střechu chráněného bydlení Fara.Hlavní je být v suchu!Dvanáct lidí s mentálním postižením našlo přístav v chráněném bydlení Fara v obci Slapy, kde mohou důstojně bydlet, pracovat, rozvíjet se a svobodně žít spolu s ostatními lidmi. Hrozí jim však, že již brzy v suchu nebudou! Střecha, která se jim rozpadá nad hlavou, vyžaduje rychlou rekonstrukci. Nezisková organizace Portus Praha proto shání prostředky na novou střechu a každý příspěvek může pomoci.Chráněné bydlení Fara Chráněné bydlení se nachází v bývalém objektu Fary a v roce 2000 bylo první ubytovací sociální službou pro lidi s mentálním postižením v obci Slapy. V současné době zde žije dvanáct lidí s těžkým a středním mentálním postižením, kteří potřebují celodenní podporu asistentů.Posláním chráněného bydlení Slapy je podat pomocnou ruku dospělý lidem, kteří nejsou dostatečně samostatní při naplňování svých potřeb v důsledku mentálního postižení. Poskytujeme celoroční pobytové a asistenční služby v obci Slapy s důrazem na podporu základních lidských práv, individuálního rozvoje a důstojného zařazování do běžného života.', '„Loď v přístavu je v bezpečí, ale proto se lodě nestaví" William SheddPortus Praha je přístavem pro lidi s mentálním postižením, aby mohli důstojně bydlet, pracovat, rozvíjet se a svobodně žít spolu s ostatními život člověka.První ročník AKCE CIHLA se konal v roce 1999 v Praze. Za peníze získané z jednotlivých ročníků benefiční sbírky se podařilo zrekonstruovat objekt bývalé Fary, postavit nové chráněné bydlení Na Vyhlídce, vybudovat sociálně terapeutickou dílnu v objektu Fary a postavit Sociální podnik v Davli DOBROTY S PŘÍBĚHEM. V tomto sociálním podniku pracují lidé s mentálním postižením - vyrábí zde nakládané sýry a utopence. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Aktivační centrum - Jsme tady', 'Jsme tady, o.p.s.', 'JSMETADY', 'Aktivační centrum - Jsme tady je určeno mladým lidem s kombinovaným postižením, převážně zrakovým + tělesným a mentálním. Zaměřuje se na jejich rozvoj, výchovu a vzdělávání, aby se mohli v co největší míře zapojit do běžného života ve společnosti. V centru uživatelé služeb mohou realizovat různé činnosti, od nácviku sebeobsluhy, rehabilitace, až po hodiny angličtiny, výuky na PC, hudební výchovy, keramiky a nechybí různé terapeutické činnosti jako canisterapie, muzikoterapie, arteterapie apod.', 'Obecně prospěšná společnost (původně Občanské sdružení) vznikla z iniciativy a potřeb rodičů těžce zdravotně postižených dětí, které ukončily povinnou školní docházku a nemají se kde uplatnit. Za tímto účelem bylo zřízeno aktivační centrum, kde budou moci klienti i nadále získávat vědomosti, schopnosti a návyky, které jim umožní zlepšit kvalitu jejich života. Získané finanční prostředky budou využity na personální a materiálně technické zajištění provozu a rozvoje Aktivačního centra - Jsme tady.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Altán Gloriet v Poděbradech', 'Farní sbor Českobratrské církve evangelické v Poděbradech', 'GLORIET', 'Altán Gloriet je vyhlídkový altán na terasách u Labe v Poděbradech. Má poškozenou statiku, propadlou klenbu a hrozí mu vážnější poškození. Proto jej chceme opravit. A zároveň tak zase vrátit hezkou tvář jednomu hezkému zákoutí u labské cyklostezky v Poděbradech.', 'Farní sbor ČCE sdružuje lidi hlásící se ke křesťanské víře evangelického vyznání v městě Poděbrady a okolí. Hlavním cílem je umožnit jim praktikování jejich víry, rozvíjet hodnoty vycházející z křesťanství, vychovávat a vzdělávat, sociálně a kulturně působit (nejen mezi členy církve). Za tímto účelem vyvíjí pravidelné - zpravidla týdenní aktivity, ke kterým patří nedělní bohoslužby pro všechny generace, kavárnička pro seniory, setkávání mládeže, setkávání maminek s malými dětmi, setkávání dětí a další nepravidelné akce, některé s regionálním dosahem ("Polabská evangelická pouť") jiné vícedenní (víkendovky pro děti, mládež, letní tábor). Všechny aktivity jsou otevřené pro veřejnost, nejen pro členy církve. K dalším aktivitám pro veřejnost patří pořádání koncertů, vánočního divadla, večerů s hostem („Ekklésia"), vzdělavatelných přednášek a dalších aktivit. Sbor je také vlastníkem poděbradské kulturní památky Altán Gloriet. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Alzheimer Stop!', 'Nadační fond Podepsáno Srdcem', 'ALZHEIMERSTOP', 'Projekt Alzheimer Stop! se zaměřuje na pomoc nemocným Alzheimerovou chorobou prostřednictvím shromažďování finančních prostředků, které jsou dále poskytovány zařízením pečujícím o nemocné Alzheimerem. Prostředky jsou určeny na vybavení, zdravotnické či rehabilitační přístroje a pomůcky.', 'Nadační fond Podepsáno srdcem se zaměřuje na podporu seniorů s Alzheimerovou chorobou, zajištění hmotné i nehmotné pomoci, iniciativ a aktivit v oblasti zdravotnictví.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Amelie pomáhá žít život s rakovinou', 'Amelie, o.s.', 'AMELIE', 'Amelie je tu pro každého, koho zasáhla onkologická nemoc, osobně nebo v blízkém okolí. Cílem projektu je rozvíjet Centra Amelie (Praha, Olomouc, Rakovník), která nabízejí bezplatnou psychosociální pomoc onkologicky nemocným a jejich blízkým (např. krizovou intervenci, individuální konzultace, sociální poradenství, skupinové aktivizační programy či jednorázové společenské a kulturní aktivity). Dílčím cílem projektu je postupně odtabuizovat téma rakoviny a přispívat tak k prevenci onkologických onemocnění.', 'Amelie, o.s. vznikla v r. 2006 a jejím hlavním cílem je poskytování odborné psychosociální pomoci dospělým okologicky nemocným a jejich blízkým. Za šest let své existence se rozrostla do tří krajů ČR (hl.m. Praha, Středočeský kraj a Olomoucký kraj), kde prostřednictvím Center Amelie (Praha, Olomouc, Rakovník) nabízí krizovou intervenci, konzultace s odborníky na zdravotně-sociální a psycho-sociální aspekty onkologického onemocnění, odborné sociální poradenství, terapeutické skupinové aktivity a další. Nedílnou součástí psychosiciální pomoci onkologicky nemocným a jejich blízkým je také činnost dobrovolníků v nemocnicích u dospělých onkologicky nemocných a v Centrech Amelie. Amelie, o.s. má akreditovaný dobrovolnický program, který dlouhodobě dodává proškolené a odborně zajištěné dobrovolníky na onkologická pracoviště nemocnic. Dobrovolníci také nacházejí uplatnění v Centrech Amelie, kde se věnují klientům a spolupracují na přípravě některých programů. Amelie, o.s. se také snaží rozvíjet povědomí o psycho-onkologické oblasti u odborné i laické veřejnosti. V současné době nabízí akreditované vzdělávání pro zdravotníky, sociální pracovníky a pracovníky v sociálních službách. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('ANO PRO ANABELL - pomoc a podpora osobám s poruchami příjmu potravy ... a nejen jim!', 'Centrum Anabell, z.s.', 'ANABELL', 'Projekt ANO PRO ANABELL je určen k podpoře klientům s poruchami příjmu potravy při hledání motivace k léčbě. Podpora je poskytována formou osobní nebo skupinové terapie a nutričního poradenství v Kontaktních centrech Anabell (blíže viz www.anabell.cz).', 'Posláním Centra Anabell, z.s. je pomoc a podpora osobám, postiženým i ohroženým poruchami příjmu potravy, hledat a nalézat východiska k řešení problémů, spojených s neplnohodnotným nebo nevhodným stravováním, zejména realizací primární i sekundární prevence a zvyšováním povědomí o nebezpečí těchto poruch ve společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('ARPIDA - Pod jednou střechou', 'ARPIDA, centrum pro rehabilitaci osob se zdravotním postižením, z.ú.', 'ARPIDA', 'Centrum ARPIDA od svého otevření v roce 1993 představuje unikátní spojení navzájem prostupných a na sebe navazujících léčebných, vzdělávacích, sociálních a pracovních prostředků ucelené rehabilitace osob s kombinovaným zdravotním postižením od narození až do dospělosti. Cílená podpora pod jednou střechou umožňuje dosahovat důležitých terapeutických pokroků a přizpůsobovat vzdělávání a sociální služby rozdílným potřebám každého příchozího člověka s postižením. Taková péče však po 22 letech zároveň znamená nutnost neustále opravovat a rekonstruovat rozsáhlé zázemí včetně zastaralého technického vybavení. Je potřebné obnovovat a doplňovat nákladné terapeutické pomůcky a zařízení. Centrum tak chce reagovat na poptávku svých klientů a přizpůsobovat se jejich měnícím se potřebám a moderním léčebným i vzdělávacím přístupům. To vše vyžaduje značné množství finančních prostředků, které v rámci běžného provozního rozpočtu organizace, nezbývají. Projekt ARPIDA - Pod jednou střechou umožňuje oslovit veřejnost, zapojit ji do zkvalitnění a rozvoje služeb centra ARPIDA pro další období.', 'Nabízíme pomoc rodinám pečujícím o děti, mládež a dospělé osoby s tělesným (motorickým) a kombinovaným postižením, formou denní komplexní koordinované péče v duchu ucelené rehabilitace. Tato péče je zajištěna prostřednictvím: - Střediska sociálních služeb - Nestátního zdravotnického zařízení - Škol a školských zařízení ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Asistenční pes - aby život nebyl pod psa', 'Pestrá společnost, o.p.s.', 'PESTRA', 'Pestrá společnost, o.p.s. je nezisková organizace, která cvičí asistenční a vodicí psi pro lidi s různým postižením. Od štěňátek do konce výcviku žijí psi v domácnosti trenérů, tedy výcvik probíhá přirozenou formou. Vodicí pes nahrazuje oči, asistenční pes nahrazuje nohy a ruce, signální pes nahrazuje sluch. Výcvik je akreditovaný mezinárodní organizací Assistance Dogs Europe a naše organizace také získala Značku spolehlivost od Asociace veřejně prospěšných organizací.', 'Podporujeme nezávislost lidí s postižením a otevíráme jim možnosti žít pestrý život. Motto: Aby život nebyl pod psa - výcvik asistenčních psů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('BabyKlokánci - podpora pro rodiny předčasně narozených a nemocných dětí', 'ZRNKA, z.s.', 'BABYKLOKANCI', 'Narození miminka je vždy radostně očekáváno celou rodinou. Všichni se na ně těší, představují si, jaké to bude, až všichni budou společně doma, jak ho přijmou starší sourozenci... Bohužel, některá miminka se rozhodnou podívat se na svět o něco dříve - některá o měsíc, jiná ještě mnohem dříve. Předčasně narozené nebo nemocné miminko je na dlouhou dobu odkázáno na péči zdravotnického personálu a pobyt v inkubátoru a rodiče najednou stojí s prázdnou náručí, s hlavou plnou obav z budoucnosti a pocitem, že pro něj vlastně ani nic udělat nemohou, že jsou „přebyteční". Opak je ale pravdou - nic není pro miminko víc potřeba než jeho rodiče! Pokud to je jen trochu možné, mohou „klokánkovat", povídat si s děťátkem, u maminek je také důležité podporovat a udržovat laktaci, protože pro nedonošené miminko není vhodnější výživa než mateřské mléko plné ochranných látek. Ne všechna neonatologická a novorozenecká oddělení jsou na tyto varianty připravená. Péče o nedonošená a nemocná miminka je velmi finančně náročná, zejména pokud se jedná o nákup specializovaných přístrojů a dalšího zdravotnického vybavení, a je naprosto logické, že jejich finance míří právě tímto směrem. My jsme tady proto, abychom pomohli tam, kde možnosti nemocnic končí. Pro oddělení s nedonošenými a nemocnými dětmi nakupujeme podle dohody to, co právě nejvíce potřebují pro podporu kontaktu miminek s rodiči. Nejčastěji se jedná o dvoufázové nemocniční elektrické odsávačky mateřského mléka Medela Symphony Standard, sady k těmto odsávačkám, ruce „The Zaky", které umožňují polohování miminek, pomáhají s termoregulací a zároveň děťátku přináší vůni rodičů. Dalšími položkami na seznamu bývají křesla k inkubátorům, deky, zavinovačky, povlečení, malé ponožky a čepičky - vše, co je aktuálně potřeba. Zároveň podporujeme setkávání rodičů, kteří mají stejné zkušenosti. Umožňuje jim to vyměňovat si informace a sdílet své životní příběhy, což neuvěřitelně pomáhá hlavně psychicky.', 'Posláním organizace je získávání finančních prostředků pro zdravotně znevýhodněné děti, pro tuzemská perinatologická centra a novorozenecká oddělení tuzemských nemocnic včetně dětských oddělení JIP v rámci projektu Babyklokánci; podpora dostupnosti péče zdravotních služeb porodních asistentek. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Běh s Kapkou', 'Nadační fond Kapka naděje', 'BEHSKAPKOU', 'Spolu s naším parterem připravujeme sérii dětských běhů, kde pořadatel část příspěvku věnuje na NF Kapka naděje. Rádi bychom vytvořili i speciální DMS, kterou by účastníci běhu mohli zasílat na naší organizaci. Součástí programu je vždy i běh pro rodiny a hlavní závod.', 'Nadační fond Kapka naděje byl založen s cílem trvale a kontinuálně podporovat Kliniku dětské hematologie a onkologie FN Motol v následujících oblastech: 1) Pomoc při zlepšení diagnostických postupů a léčebných metod 2) Pomoc v oblasti psychosociální péče 3) Pomoc při financování výstavby a přístrojového vybavení nové transplantační jednotky 4) Podpora vědeckých aktivit 5) Od roku 2002 NF Kapka naděje podporujeme dětská oddělení nemocnic po celé České republice. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Betlém dětem', 'Nadační fond Betlém nenarozeným', 'BETLEM', 'Posláním Azylového domu pro těhotné ženy v tísni je podpora těhotných žen a matek v překonávání jejich nepříznivé situace prostřednictvím nabídky ubytování, poradenství a zázemí. V průběhu pobytu je klientkám nabízena možnost rozvíjet své rodičovské kompetence skrze metodu videotrénink interakcí, setkání s psychologem (individuální i skupinové), skrze přednášky a diskuze na téma mateřství a rodičovství, a také v oblasti finančního hospodaření. Klientky se mohou zapojit do projektu Začít znovu, který jim nabízí formou zaměstnávání možnost získání pracovních návyků i drobného přivýdělku. Azylový dům provozuje Obecně prospěšná společnost Dlaň životu, kterou nadační fond za tímto účelem založil a nadále podporuje.', 'Nadační fond byl založen primárně za účelem šíření úcty a ochrany lidského života od jeho početí až k porodu formou konkrétních kroků vůči jednotlivému člověku. Prakticky podporuje provoz zařízení a zajišťuje rekonstrukci objektů, které mají sloužit pro ubytování matek, jejichž sociální a nezralé duchovní zázemí komplikuje přijetí počatého dítěte.V letech 2003 až 2007 zrekonstruoval budovu azylového domu, která od roku 2008 až dosud slouží jako Azylový dům pro těhotné ženy v tísni. Nadační fond jej finančně podporuje prostřednictvím své veřejné sbírky a od roku 2010 také prostřednictvím projektu DMS Betlém dětem. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('BEZPEČNĚ U VODY - POMOZTE NÁM POMÁHAT', 'Vodní záchranná služba ČČK, z.s.', 'VZS', 'Podpořte svým darem rychlou a účinnou pomoc osobám ohroženým na životě a zdraví při koupání a vodních sportech na vodních plochách po celé České republice! Podpořte Vodní záchrannou službu ČČK!', 'Posláním VZS ČČK je organizování a provádění preventivní a záchranné činnosti a poskytování první pomoci na bazénech, koupalištích, vodních plochách a tocích a v jejich bezprostřední blízkosti při sportovní a rekreační činnosti, v případech živelných katastrof a obecného ohrožení s cílem zabránit vážným úrazům, utonutím a ztrátám na materiálních hodnotách. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Bezpečný prostor pro děti', 'Bonanza Vendolí, z.ú.', 'BONANZA', 'Občanské sdružení Bonanza dlouhodobě provozuje nízkoprahový klub Prevence s Bonanzou ve Vendolí. Do klubu denně dochází více než 15 dětí, které zde smysluplně využívají svůj volný čas a společně s našimi zaměstnanci řeší své osobní problémy. Zajišťujeme pro ně bezpečný prostor, který mohou využívat pro svůj osobnostní rozvoj. Prostředkem k tomu jsou nám činnosti a práce s koňmi.Klub je umístěn na starém statku, který nutně potřebuje rekonstrukci, aby byla zajištěna dostatečná bezpečnost pohybu dětí v jeho prostorách. Výtěžek z DMS tak bude určen na dofinancování rekonstrukce prostor, které využívá Nízkoprahový klub Prevence s Bonanzou.', 'Jsme nestátní neziskovou organizací, jejímž posláním je pomoc ohroženým dětem a jejich rodinám. Provozujeme tři nízkoprahové kluby, do kterých mohou chodit děti a mládež bezplatně i anonymně. Mohou zde řešit své osobní problémy, dozvědět se něco nového o aktuálních tématech, udělat si úkoly, nebo si jen popovídat u stolního fotbálku a jiných her. Vedle těchto klubů provozujeme ještě sociální služby pro rodiny s dětmi - tedy pomáháme rodinám a dětem, které se ocitly v krizové situaci, nalézt možné způsoby řešení. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Bílá pastelka', 'Sjednocená organizace nevidomých a slabozrakých České republiky', 'PASTELKA', 'Veřejná sbírka Bílá pastelka se koná každým rokem v polovině října u příležitosti Mezinárodního dne bílé hole, kdy v jeden den do ulic českých a moravských měst vyjde přibližně 2 500 dobrovolníků s kasičkami a prodejním artiklem v podobě bílých pastelek v hodnotě 20 Kč. Výtěžek sbírky pomáhá financovat klíčové speciální služby pro zrakově postižené v ČR.', 'Posláním SONS je sdružovat a hájit zájmy nevidomých a jinak zrakově postižených občanů a poskytovat konkrétní služby vedoucí k integraci takto postižených do společnosti. Naše priority jsou: vyhledávání a kontaktování těžce zrakově postižených včetně osob v akutním stádiu po ztrátě zraku, podpora zaměstnanosti, socioterapeutická činnost realizovaná ve svépomocných skupinách a aktivizačních klubech, odstraňování architektonických a informačních bariér, ověřování nových forem pomoci těžce zrakově postiženým občanům včetně popularizace a osvětlování problematiky zrakového postižení, navigační centrum pro nevidomé. Podrobnější informace najdete na www.sons.cz a www.bilapastelka.cz . ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Bílá stopa Šumava', 'Regionální rozvojová agentura Šumava o.p.s.', 'BILASTOPASUMAVA', 'Na Šumavě se nachází nejrozsáhlejší síť udržovaných značených tras pro lyžaře u nás. Většina z více než 600 km udržovaných tras se nachází na území Národního parku Šumava. Regionální rozvojová agentura Šumava, o.p.s. koordinuje údržbu lyžařských běžeckých tras ve spolupráci s dalšími institucemi (Regionální sdružení Šumava, NP Šumava, Jihočeský a Plzeňský kraj, cca 30 šumavských obcí). Veškeré informace jsou k dispozici na www.bilastopa.cz.Obce financují 90% nákladů na úpravu lyžařských běžeckých tras vlastních rozpočtů (celkové náklady cca 3 mil. Kč/sezónu). Proto bychom chtěli využít dobrovolné formy "zpoplatnění" lyžařských tras prostřednictvím dárcovské SMS. Tak budou mít sami lyžaři možnost jednoduše přispět.', 'Posláním organizace je součinnost subjektů v regionu a propagace regionu. Za cca 10 let se podařilo vybudovat síť lyžařských běžeckých tras na Šumavě, což je výsledkem spolupráce šumavských obcí, Správy Národního parku Šumava a dalších dotčených organizací. RRAŠ a jeden z jejích zřizovatelů (Regionální sdružení Šumava) získali prostředky (z EU a krajských prostředků) na údržbu lyž. stop, na pořízení 3 rolb, pořízení a provoz 10 GPS (zřízení webu www.bilestopy.cz ). Dále provozujeme www.bilastopa.cz, každoročně vydáváme mapky a administrujeme krajské příspěvky. V plánu je sjednocení značení lyž. tras v terénu a zajištění dotace z EU na realizaci. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('CARE: humanitární pomoc a rozvojová spolupráce', 'CARE Česká republika', 'CARE', 'Cílem projektu je zajištění rozvojové spolupráce a humanitární pomoci při katastrofách a při nouzových situacích v zahraničí i v ČR podle aktuálních potřeb - realizací humanitárních, rozvojových a vzdělávacích aktivit. Zaměření projektů na pomoc v nouzi, boj proti chudobě a rozvojové programy zcela odpovídají poslání a smyslu činnosti CARE. Důležitost projektu s využitím DMS spočívá v možnosti reagovat bezprostředně na vzniklé krizové a nouzové situace v programových zemích, zajistit základní potraviny, prostředky pro přežití a zajistit plán dalších kroků k obnově, stejně jako v možnosti zajistit prostředky na programy rozvoje nejchudších regionů. Organizace CARE vyhodnocuje pravidelně dopad projektů a činností v jednotlivých zemích, zajišťuje zprávy od místních spolupracovníků k výstupům - od počtu podpořených osob, přes vytvořené instituce, realizované programy až po účinnost opatření v posilování, rozhodování a aktivního zapojení občanské společnosti, nebo posuzování dopadů na životní prostředí. Hlavním zdrojem reportingu jsou zprávy k projektům a výroční zprávy.', 'CARE Česká republika je spolek pro humanitární pomoc a rozvojovou spolupráci. Je součástí mezinárodní organizace CARE International. Organizace CARE je politicky a světonázorově nezávislá organizace, která prosazuje uskutečnění vize světa plného naděje, tolerance a sociální spravedlnosti, ve kterém musí být chudoba a nespravedlnost poraženy a lidé mohou žít důstojně v míru a bezpečí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Centrum Elpida', 'Elpida, o.p.s.', 'ELPIDA', 'Pomáháme seniorům k aktivnímu a samostatnému životu Elpida, o.p.s. má širokou nabídku navzájem provázaných programů a služeb pro seniory, které je podporují v samostatném, sebevědomém a aktivním způsobu života.Centrum Elpida je moderní vzdělávací a kulturní centrum s celopražskou působností. Seniorům nabízíme počítačové, jazykové, fotografické i pohybové kurzy, přednášky, poradny, kurzy finanční gramotnosti i tréninky paměti. Vytváříme pro ně program, který je podporuje v samostatnosti, nezávislosti a dodává jim sebevědomí z vlastní aktivity a tvůrčího přístupu k životu. I seniorský věk totiž může být stejně hezkou a naplněnou životní etapou jako mládí a střední věk.Časopis Vital plus je nejčtenějším časopisem pro seniory v ČR. Věnuje se společenským tématům, zdraví, přírodě, módě a stylu, ale i aktuálním tématům z oblasti práva či financí. Seniorům je distribuován zdarma domů, do pečovatelských zařízení, městských knihoven atd.OLD''S COOL mezigenerační projekty Elpida, o.p.s. dlouhodobě rozvíjí řadu projektů, které se zaměřují na zlepšování vnímání stáří a seniorů většinovou společností a podporují mezigenerační spolupráci. OLD''s COOL ReFestival, který propojuje maldé a straé, pořádá od roku 2013. Během festivalového týdne se konají výstavy, koncerty, divadelní představení, diskuze, workshopy, kurzy a setkávání napříč generacemi. Ponožky od babičky plete více než stovka seniorek z celé republiky. Prodejem ponožek Elpida, o.p.s. spolufinancuje programy pro seniory v Centru Elpida a zajišťuje přivýdělek pletařkám. Další výrazné projekty jsou tyto: Antikvariát Elpida, Kup Book!, výstavní cyklus Knihy se těžko vystavují.„Stáří nemusí být jen smutné, prázdné a osamělé. Čas se dá využít k tomu, aby si člověk užil a věnoval se svým zájmům, zálibám, koníčkům a mohl dělat, co ho baví, rozšiřovat a obohacovat se o nové znalosti, dojmy a zážitky. To vše umožňuje seniorům Elpida v plné míře a každý si může vybrat to své." říká absolventka mnoha kurzů v Centru Elpida a zpěvačka pěveckého sboru, paní Věra.Prostředky z DMS budou využity na zajištění výše uvedených programů Elpidy.', 'Organizace pomáhá seniorům stát se samozřejmou, sebevědomou a respektovanou součástí společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Centrum J. J. Pestalozziho pomáhá všem, kteří to aktuálně potřebují', 'Centrum J. J. Pestalozziho, o.p.s.', 'PESTALOZZI', 'Centrum J. J. Pestalozziho, o.p.s., je nevládní nezisková organizace, která poskytuje psychosociální, poradenské a vzdělávací služby ohroženým a znevýhodněným skupinám obyvatel v České republice. Organizace usiluje o znovuzařazení klientů do společnosti a zlepšení kvality jejich života.', 'Centrum J. J. Pestalozziho je nestátní nezisková organizace, která již 20 let velmi úspěšně poskytuje poradenské, vzdělávací a sociální služby ohroženým a znevýhodněným skupinám obyvatel v České republice s důrazem na kraje Pardubický a Vysočinu. Organizace usiluje o znovuzařazení klientů do společnosti a zlepšení jejich kvality života. Cílem všech poskytovaných služeb je řešení akutní krizové situace obyvatel v regionu, integrace dlouhodobě institucionalizovaných klientů zpět do společnosti, vytvoření záchytné sítě s nabídkou víceúrovňového systému sociální pomoci pro oběti domácího násilí, bezplatná odborná poradenská a právní pomoc, týkající se nejen dluhů, exekucí a nezaměstnanosti. Dílčím cílem je dosažení samostatnosti, nezávislosti a soběstačnosti osob a prevence sociálně-patologických jevů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Centrum Paraple', 'Centrum Paraple, o.p.s.', 'PARAPLE', 'Obecně prospěšná společnost Centrum Paraple pomáhá lidem na vozíku s poškozením míchy.Autonehoda, skok do vody, zánětlivý proces nebo nádor v míšním kanálu - co mají společného? Jejich následkem může dojít k poškození míchy. Většina z nás si nedovede představit, co všechno se tím změní. S běžnými denními činnostmi, které před úrazem nebo onemocněním byly naprostou samozřejmostí, nastanou problémy. Vstát ráno z postele, obléct se, připravit si jídlo, to vše se je obtížné. Pohyb je možný pouze pomocí vozíku, zhorší se další funkce organismu. Je to velký zásah do života člověka i jeho blízkých. V Centru Paraple však věříme, že zdravotním postižením kvalitní a aktivní život nekončí. Musí se však začít znovu a jinak - třeba na vozíku. S podporou rodiny i společnosti, nás všech. V Centru Paraple pomáháme lidem, které tato těžká životní situace potkala a podporujeme je ve snaze vrátit se zpět do aktivního života. Každý klient tu má k dispozici fyzioterapeuta, ergoterapeuta, sportovního terapeuta, sociálního pracovníka, psychologa a další, kteří mu pomáhají rozvíjet jeho schopnosti tak, aby dosáhl co největší samostatnosti - žil plnohodnotný rodinný život, věnoval se svým zájmům, vrátil se do zaměstnání. SLUŽBY CENTRA PARAPLE: - sociálně rehabilitační pobyty - ambulantní program - poradenství - sportovní programy - tematické kurzy - Zdravý životní styl, Máma-táta na vozíku, vzdělávací a kreativní kurzy Každý rok se na vozíku ocitne přibližně 250 nových lidí, téměř třetina z nich se stane klienty Centra Paraple. Každý rok naše služby opakovaně využívá více než 1 400 klientů.Jen s Vaší pomocí jim můžeme poskytovat své služby. Děkujeme, že nám pomáháte. ', 'Centrum Paraple pomáhá lidem ochrnutým po poškození míchy následkem úrazu nebo onemocnění v průběhu života a jejich rodinám překonat těžkou životní situaci a najít cestu dál. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Centrum pomoci Mathilda', 'Nadační fond Mathilda', 'OKO', 'Cílem projektu je pomoci nevidomým lidem integrovat se do společnosti. K tomu slouží zejména projekt „Šťastný vodicí pes" a projekt podpory uživatelů speciálních elektronických pomůcek.Šťastný vodicí pes přináší radost do života nevidomého člověka. Vodicí pes je živý tvor, který bude nevidomému plně oddaný po celý svůj život, ale potřebuje náležitou péči o zdraví, životní pohodu a vzájemný vztah. Vodicí pes usnadní pohyb nevidomému, bude tak rychlejší a bezpečnější, ale i nevidomý se musí spoustu nového naučit, aby si s novým psím pomocníkem rozuměli. Cílem projektu je:- Pes, který je ve své roli psa průvodce spokojen, jeho povaha a temperament jsou v souladu s prací vodicího psa. - Pes , který je na své budoucí poslání připraven s co nejmenší stresovou zátěží. - Pes, jehož osobnost je součástí jeho přípravy. - Pes,který je milující a také milován. - Pes, kterému je věnována veškerá péče, kterou si zaslouží. - Pes, který je vychováván lidmi, kteří jsou plně informováni o práci vodicího psa a znají důvody svého podílu na výchově vodicího psa. - Pes, který je ve výcviku veden odborně, fundovaným a kvalifikovaným cvičitelem, profesionálem, který rozumí jak práci se psy, tak i práci s nevidomými lidmi. Odbornost cvičitele umožní pejskovi, aby prošel přípravou co nejpříjemněji a zároveň velmi efektivně. - Pes, který je šetrně předán správnému člověku a do prostředí, které odpovídá jeho povahovým vlastnostem a temperamentu. - Pes, který je náležitě zacvičen se svým budoucím majitelem.Podpora uživatelů elektronických kompenzačních pomůcek Projekt pomáhá zrakově postiženým lidem, kteří vlastní elektronickou kompenzační pomůcku (PC s hlasovým výstupem, PC s braillským výstupem, mobilní telefon s hlasovým výstupem atd.) Centrum pomoci Mathilda nabízí těmto lidem školení v screeanreaderech HAL, Just VOIP a NVDA, školení v programech k mobilním telefonům a dále poradenství v oblasti screeanreaderů a v programech k mobilním telefonům, digitalizace černotiskových a zvukových knih. Centrum dále poskytuje poradenství jednak telefonické, přes skype, email či osobní. Těmito komunikačními způsoby pomáháme nevidomým znovu nastavit operační systém či speciální SW anebo jim pomáháme v orientaci s těmito speciálními SW. Ve zvlášť komplikovaných případech jede odborný pracovník ke klientovi domů.', 'Posláním Nadačního fondu Mathilda je vyhledávání a podpora smysluplných projektů, které pomáhají lidem s těžkým zrakovým postižením. Mathilda se programově zaměřuje na podporu projektů, které výrazně napomáhají zlepšení kvality života nevidomých a slabozrakých. Hlavně na projekty, které se týkají vzdělávání takto postižených osob nebo navazují na stávající sociální služby, případně je rozvíjejí a v konečném důsledku výrazně pomáhají v pracovním uplatnění a integraci do běžné společnosti. Jedná se zejména o projekty, které nedosahují na úplné financování ze strany státu. Mathilda činí veškerá potřebná opatření, včetně získávání prostředků, pro realizaci uvedených cílů. Dlouhodobě budou podporovány projekty, které se přímo dotýkají péče a podpory zrakově postižených, konkrétně to jsou tyto projekty:Podpora chovu štěňat Výcvik vodicích psů Sociální rehabilitace nevidomých Centrum pomoci Mathilda Digitální knihovna pro zrakově postižené - DKZP Podpora zrakově postižených hudebníků ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Centrum pro sirotky Mahango', 'Občanské sdružení Bez mámy', 'BEZMAMY', 'Projekt Centra pro sirotky Mahango si klade za cíl vytvořit domov pro zhruba 80 dětí bez rodičů z oblasti jižní Tanzanie. V Tanzanii je udáván celkový počet sirotků okolo 3,1 milionů. Asi polovina těchto dětí přišla o své rodiče vlivem viru HIV. Další příčiny jsou nedokonalá péče při porodu a po něm, běžné infekční choroby apod. Děti po smrti svých rodičů hledají pomoc u svých nejbližších příbuzných, ale ne vždy ji skutečně najdou. Ne, že by je příbuzní odmítli, ale většinou se nemohou o ně dobře postarat, sami stěží živí vlastní rodinu. Nedůstojné životní podmínky, nízká hygiena, podvýživa, negramotnost to jsou hlavní jasně viditelné problémy, se kterými se děti v Africe potýkají. Občanské sdružení Bez mámy se stará o sirotky tím, že poskytuje to, co je k přežití a lepší budoucnosti nejnutnější: domov s rodinnou atmosférou, strava, ošacení a potřebné vzdělání. Centrum pro sirotky Mahango má mít ve svém základu minimálně tři budovy. Dvě ubytovny (pro chlapce a dívky) a servisní budovu (kuchyň, jídelna a sklad). Dvě budovy jsou již plně dokončeny a poskytují domov asi 20 dětem. V roce 2013 jsme zahájili stavbu třetí budovy - ubytovny pro dívky. V roce 2014 již byla kompletně hotová hrubá stavba vč. střechy. Po úplné dostavbě ubytovny pro dívky bude základ centra pro sirotky kompletní. Děti budou mít zajištěny nejnutnější životní potřeby a zároveň budou vedeny k samostatnosti a vychovávány k odpovědnosti.', 'V Tanzanii žije 3,1 milionu sirotků, což je téměř tak velká skupina jako třetina obyvatelstva v České republice. Většina těchto dětí přichází o své rodiče kvůli AIDS a jiných onemocnění jako je malárie nebo infekční choroby. Státní podpora těmto dětem je malá, stát nezřizuje sirotčince. Deklaruje sirotkům sice bezplatné vzdělání a zdravotní péči, otázkou však zůstává, kdo bude dětem platit uniformu, a nebo, jak se ke zdravotní péči dopraví. Starost o dítě automaticky připadne příbuzným, avšak mnohdy se jedná o velmi vzdálenou přízeň. Děti přežívají ve velmi nuzných materiálních a mnohdy i duševních podmínkách. Nejsou motivovány k individuálnímu růstu a občas jim nebývá umožňováno navštěvovat ani školu. Úroveň tanzanských státních škol není sice nikterak valná, avšak stále by mělo platit, že dítě má být přes den ve škole, ne na poli. Občanské sdružení Bez mámy působí na jihu Tanzánie od roku 2006. Hlavním posláním je pomoc a podpora dětem, zvláště sirotkům, kterým zajišťujeme domov, stravu, ošacení a potřebné vzdělání zejména prostřednictvím projektů Centrum pro sirotky Mahango, Adopce sirotků a projekt Škola škole.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Cesta domů', 'Cesta domů, z.ú.', 'CESTADOMU', 'V Praze provozujeme Domácí hospic Cesta domů. Za pomoci prostředků moderní paliativní péče umožňujeme těm, kteří si to přejí, strávit závěrečné chvíle svého života důstojně, bez zbytečné bolesti a dalšího utrpení a v přítomnosti jejich nejbližších doma.Navzdory některým pozitivním změnám z posledních let je v České republice konec lidského života často doprovázen utrpením, osamělostí a ztrátou lidské důstojnosti. Sociologické průzkumy na téma Umírání a péče o nevyléčitelně nemocné přinesly následující důležitá zjištění: lidé chtějí umírat doma (78 %), jsou ochotni se o své blízké starat až do konce (88 %) a zdravotníci rovněž považují domácí prostředí za nejlepší místo pro umírající pacienty (88 %). Ačkoliv tedy většina populace i zdravotníků zvedá ruku pro domácí péči o lidi na konci života, přibližně 75 % úmrtí se odehrává ve zdravotnických či sociálních zařízeních.Cesta domů se snaží situaci umírajících zlepšit a nabídnout jim a jejich rodinám komplexní pomoc (zdravotnické, sociální, psychoterapeutické služby), která jim umožní prožít své poslední společné dny bez bolesti a v domácím prostředí. Tuto ambici se nám daří již od roku 2001 úspěšně naplňovat i přes komplikovanou situaci ve zdravotnictví a zcela nevyhovující institucionální rámec.Domácí hospic Cesta domů nemocným a jejich rodinám nabízí: - odbornou domácí paliativní péči v domácnosti klienta nepřetržitě 24 hodin denně, 7 dní v týdnu - poradenství v oblasti péče o nevyléčitelně nemocné a umírající - půjčování specifických zdravotních pomůcek - všestrannou podporu pečujícím rodinám nemocných (zaučení, konzultace, edukace) - doprovázení pozůstalých', 'Cesta domů je zapsaným ústavem, které se již od roku 2001 zabývá komplexní pomocí lidem na konci života a jejich rodinám. Cílem organizace je zlepšit v České republice podmínky péče o umírající, jejichž poslední chvíle jsou často doprovázeny utrpením, osamělostí a ztrátou lidské důstojnosti. O dosažení tohoto cíle dlouhodobě usilujeme jak prostřednictvím poskytování nepřetržité přímé pomoci umírajícím, kterou zajišťuje multidisciplinární tým domácího hospice složený z lékařů, zdravotních a sociálních pracovníků, psychoterapeuta a dobrovolníků, tak snahou o zlepšení postoje společnosti ve vztahu k lidem na konci života skrze edukační a osvětové aktivity.Kromě služeb domácího hospice poskytovaných 24 hodin denně 7 dní v týdnu, poskytujeme odlehčovací služby; odborné poradenství pro celou ČR; provozujeme portál www.umirani.cz; provozujeme veřejnou knihovnu specializovanou na paliativní péči; vydáváme knihy, brožury a informační materiály; podílíme se na jednáních o změně systému paliativní péče v ČR. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Cimrman pomáhá', 'Nadační fond Českého rozhlasu', 'CIMRMAN', 'Veřejnou sbírku Cimrman pomáhá vyhlašuje Nadační fond Českého rozhlasu ve spolupráci s ČRo Radiožurnál a organizacemi Centrum Paraple, o.p.s. a Nadační fond Slunce pro všechny u příležitosti 50té sezony Divadla Járy Cimrmana. Sbírka je určena na pomoc lidem s poškozením míchy, s těžkým zrakovým postižením a lidem se speciálními potřebami.', 'Účelem fondu je podpora rozvoje kultury a všech forem umění, zvláště mladé talentované umělce ze sociálně slabých rodin či handicapované, podpora rozvoje a rozšiřování vzdělání, podpora v oblasti sociální a zdravotní, poskytování charitativní a humanitární pomoci. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Česká alzheimerovská společnost - na pomoc občanům postiženým demencí', 'Česká alzheimerovská společnost, o.p.s.', 'PROTIDEMENCI', 'Projekt České alzheimerovské společnosti pomáhá lidem, trpícím poruchami paměti, ohroženým či trpícím Alzheimerovou chorobou či jiným onemocněním způsobujícím demenci a jejich rodinám zvládnout péči co nejdéle v domácím prostředí. Pomáhá zařízením, která poskytují lidem s demencí služby a mají zájem o jejich zlepšení. Poskytuje laické i odborné veřejnosti aktuální informace o možnostech co nejdelšího udržení kvality života lidí s demencí. V rámci projektu je poskytováno odborné poradenství, odlehčovací služby v domácím prostředí, informační materiály, vzdělávací kurzy a další služby. Velkou výhodou pro činnost společnosti je skutečnost, že v 25 zařízeních po celém území ČR má svá kontaktní místa. Cílem ČALS je zlepšení života lidí s demencí a jejich rodin.', 'ČALS od roku 1997 zlepšuje život lidí s demencí a těch, kteří o ně pečují tím, že: - usiluje o včasné stanovení správné diagnostiky a vhodné terapie zahrnující psychosociální intervence - podporuje co nejdelší setrvání člověka s demencí v domácím prostředí - podporuje rodinné pečujících - zvyšuje kvalitu institucionální péče - spravuje síť kontaktních míst - věnuje se advokačním službám - zvyšuje povědomí veřejnosti o demenci ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Česká asociace paraplegiků - CZEPA', 'Česká asociace paraplegiků - CZEPA', 'CZEPA', 'Hájíme zájmy vozíčkářů po poranění míchy, usilujeme o vytvoření podmínek pro jejich plnohodnotný život a pro integraci do většinové společnosti. S tímto cílem realizujeme řadu projektů:  - Tréninkové zaměstnání pro vozíčkáře (charitativní obchod Hvězdný bazar) - Startovací bydlení pro vozíčkáře, kteří jsou krátce po úraze - Podporované bydlení pro tetraplegiky  - Preventivní kampaň proti úrazům páteře a míchy Banal Fatal - Osvětové kampaně  - Komunitní portál Vozejkov.cz - Mobilní aplikace VozejkMap (mapa bezbariérových míst s navigací, data zadávají a ověřují sami vozíčkáři) - Informační a poradenský program pro vozíčkáře - Edukační program pro odbornou veřejnost - Spolupráce s lékaři a legislativci na zkvalitnění sociální a zdravotní péče 8 ze 14 zaměstnanců jsou vozíčkáři. Vozíčkářům s poraněním míchy pomáháme od roku 1990. Jsme zakladatelem Centra Paraple. ', 'Česká asociace paraplegiků - CZEPA je nezisková organizace, která sdružuje vozíčkáře po poranění míchy z celé ČR. Vychází z přesvědčení, že člověk na vozíku může a má právo žít kvalitním a aktivním životem - v rodině, v zaměstnání, s volným časem vyplněným zájmy a koníčky, stejně jako všichni ostatní lidé bez handicapu. CZEPA hájí zájmy vozíčkářů po poranění míchy, usiluje o vytvoření podmínek pro jejich plnohodnotný život a pro integraci do většinové společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daňové přiznání pomáhá', 'Česká společnost AIDS pomoc, z.s.', 'AIDSPOMOCOP', 'Daňové přiznání pomáhá je unikátní způsob podpory charitativních organizací. Tento společný projekt serveru OnlinePriznani.cz a Fóra dárců kombinuje využití snadného online zpracování daňového přiznání a možnosti přispět na dobrou věc.Uživatel si v aplikaci vyhotoví své daňové přiznání, které je mu zdarma zasláno na email. Jelikož ušetří nemalé prostředky za služby účetní, je mu nabídnuta možnost, aby jako formu poděkování za využité služby zaslal roční dárcovskou SMS. Po dobu jednoho roku tak může podporovat konkrétní projekt částkou 30 Kč měsíčně. Česká společnost AIDS pomoc, z.s. z této částky měsíčně obdrží 28,50 Kč. Projektem na podporu bezplatného testování na HIV pro širokou veřejnost a na realizaci preventivních besed o HIV/AIDS na základních a středních školách si České společnosti AIDS pomoc klade za cíl zvýšit dostupnost těchto služeb. Získané prostředky budou využity na dofinancování bezplatného anonymního testování a na realizaci besed pro děti a mládež mimo Prahu v dalších městech Čech a Moravy. ', 'Česká společnost AIDS pomoc (ČSAP) vznikla koncem roku 1989 jako iniciativa ze strany blízkých, známých a rodin HIV pozitivních občanů, s cílem jim prakticky pomáhat při řešení každodenních komplikací, které infekce HIV přinášela. Práce sdružení se postupně rozšířila i o prevenci a osvětu a do její činnosti se zapojil i větší počet samotných HIV pozitivních osob. Významným milníkem v činnosti bylo vybudování sociálně-azylového centra Dům světla koncem 90. let, od kdy ČSAP nabízí rovněž bezplatné anonymní testování na HIV a azylové a ubytovací služby pro HIV+ osoby.Organizace realizuje AIDS poradny, bezplatné anonymní testování na protilátky HIV v Praze a Ostravě, nonstop telefonní poradenskou linku pro oblast prevence HIV/AIDS, besedy pro žáky základních a středních škol a další aktivity. V nedávné době spolek rozšířil svou činnost i na oblast právní pomoci v případech diskriminace HIV pozitivních osob a na snižování společenského stigmatu, který s sebou HIV infekce může přinášet. Financování organizace je z dotací, darů a příspěvků. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daňové přiznání pomáhá', 'Zdravotní klaun o.p.s.', 'KLAUNOP', 'Daňové přiznání pomáhá je unikátní způsob podpory charitativních organizací. Tento společný projekt serveru OnlinePriznani.cz a Fóra dárců kombinuje využití snadného online zpracování daňového přiznání a možnosti přispět na dobrou věc. Uživatel si v aplikaci vyhotoví své daňové přiznání, které je mu zdarma zasláno na email. Jelikož ušetří nemalé prostředky za služby účetní, je mu nabídnuta možnost, aby jako formu poděkování za využité služby zaslal roční dárcovskou SMS. Po dobu jednoho roku tak může podporovat konkrétní projekt částkou 30 Kč měsíčně. Zdravotní klaun o.p.s. z této částky měsíčně obdrží 28,50 Kč.Cílem projektu je získat finanční prostředky na zajištění pravidelných návštěv zdravotních klaunů na  dětských odděleních nemocnic po celé České republice. Zároveň na rozšíření počtu navštěvovaných zařízení a nezbytné průběžné vzdělávání zdravotních klaunů. Návštěvy zdravotních klaunů, tzv. klauniády, budou v rámci tohoto projektu probíhat na dětských odděleních 64 nemocnic ve všech krajích České republiky - tedy i v tom vašem! V roce 2015 jsme uskutečnili 3 284 pravidelných klauniád pro děti!Pomozte nám roznášet smích a radost těm, kteří to potřebují! ', 'Zdravotní klaun je nezisková organizace s celostátní působností, která přináší humor a radost hospitalizovaným dětem, geriatrickým pacientům a dalším potřebným v oblasti zdravotnictví, čímž přispívá ke zlepšení jejich psychického a tím i celkového zdravotního stavu, a to prostřednictvím speciálně vyškolených zdravotních klaunů a speciálních projektů. V současné době 86 zdravotních klaunů navštěvuje pravidelně 64 nemocnic a 7 domovů pro seniory. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daňové přiznání pomáhá', 'Nadace Charty 77', 'KONTOBARIERYOP', 'Daňové přiznání pomáhá je unikátní způsob podpory charitativních organizací. Tento společný projekt serveru OnlinePriznani.cz a Fóra dárců kombinuje využití snadného online zpracování daňového přiznání a možnosti přispět na dobrou věc.Uživatel si v aplikaci vyhotoví své daňové přiznání, které je mu zdarma zasláno na email. Jelikož ušetří nemalé prostředky za služby účetní, je mu nabídnuta možnost, aby jako formu poděkování za využité služby zaslal roční dárcovskou SMS. Po dobu jednoho roku tak může podporovat konkrétní projekt částkou 30 Kč měsíčně. Konto BARIÉRY z této částky měsíčně obdrží 28,50 Kč.Nadace Charty 77 se zaměřuje na pomoc občanům s handicapem, a to odstraňováním fyzických bariér a vytvářením možností pro jejich návrat do aktivního života. Nadace dále zviditelňuje významné české osobnosti ze světa literatury, ekologie a boje za lidská práva.Konto BARIÉRY je stěžejním projektem nadace, který byl založen s cílem přispět ke zlepšení života handicapovaných spoluobčanů a k jejich plnohodnotnému zapojení do života. Pomoc je určena organizacím i jednotlivcům. Konto BARIÉRY přispívá na:- rehabilitační a kompenzační pomůcky (mechanické a elektrické vozíky, rehabilitační kočárky, chodítka, zdravotní lůžka apod.)  - bezbariérové úpravy bytů, škol a dalších veřejných budov, stipendia pro středoškoláky a vysokoškoláky z řad těžce zdravotně handicapovaných studentů  - dopravu handicapovaných (nákup osobních automobilů pro organizace, speciální autosedačky, úhrada svozu dětí do škol nebo denních stacionářů, speciální úpravy automobilů) osobní asistenci  pomůcky pro sluchově postižené  - podporu chráněného bydlení a chráněných pracovišť, Domy na půli cesty  - příspěvky na speciální pomůcky pro sportovní a kulturní aktivity, rekondiční, ozdravné pobyty a studijní pobyty a rekvalifikační kurzy.Více na www.kontobariery.cz.', 'Konto Bariéry, od roku 1992 stěžejní projekt Nadace Charty 77, je první a dosud jedinou kontinuální sbírkou, které se více než dvacet let účastní desetitisíce dárců - svým pravidelným měsíčním příspěvkem pomáhají naplňovat a financovat program Konta Bariéry. V začátcích jsme hodně pracovali s podtitulkem Konta Bariéry „Patří mezi nás". Chtěli jsme veřejnost naučit vnímat lidi s rozdílnými potřebami. Podařilo se nám to a jsme na to, že Konto Bariéry splnilo tento velký úkol, docela pyšní. Za více než dvacet let činnosti rozdělilo Konto Bariéry stovky miliónů korun, podpořilo tisíce projektů na zlepšení života lidí s handicapem a jejich plnohodnotné zapojení do společnosti. Z aktuálních potřeb lidí s postižením vychází Konto Bariéry i při formování nových projektů. Doplňuje stát a samosprávu tam, kde jejich pomoc nestačí. Stále více se zaměření jeho pomoci posouvá od standardní pomoci, tedy rehabilitačních a kompenzačních pomůcek, bezbariérových úprav bytů, škol a dalších veřejných budov, k projektům, které souvisejí se vzděláním a pracovním uplatněním lidí s handicapem.Více na www.kontobariery.cz. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daňové přiznání pomáhá', 'Z.s. TAP', 'TAPTAPOP', 'Daňové přiznání pomáhá je unikátní způsob podpory charitativních organizací. Tento společný projekt serveru OnlinePriznani.cz a Fóra dárců kombinuje využití snadného online zpracování daňového přiznání a možnosti přispět na dobrou věc.Uživatel si v aplikaci vyhotoví své daňové přiznání, které je mu zdarma zasláno na email. Jelikož ušetří nemalé prostředky za služby účetní, je mu nabídnuta možnost, aby jako formu poděkování za využité služby zaslal roční dárcovskou SMS. Po dobu jednoho roku tak může podporovat konkrétní projekt částkou 30 Kč měsíčně. Z.s. TAP z této částky měsíčně obdrží 28,50 Kč.Cílem veřejné sbírky je získávat finanční prostředky na zajištění realizovaných projektů a kulturních akcí, na provoz bezbariérových automobilů, na podporu turné černého humoru, grafické práce a úhradu mezd lektorů projektu STUDEO. ', 'Posláním z.s. TAP je péče o rozvoj dovedností a schopností osob se zdravotním postižením a podpora jejich přípravy na budoucí uplatnění na trhu práce. Podpora a organizace společenských a kulturních akcí studentů škol Jedličkova ústavu v Praze. Ochrana přírody a krajiny. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daňové přiznání pomáhá', 'Linka bezpečí, z.s.', 'LINKABEZPECIOP', 'Daňové přiznání pomáhá je unikátní způsob podpory charitativních organizací. Tento společný projekt serveru OnlinePriznani.cz a Fóra dárců kombinuje využití snadného online zpracování daňového přiznání a možnosti přispět na dobrou věc.Uživatel si v aplikaci vyhotoví své daňové přiznání, které je mu zdarma zasláno na email. Jelikož ušetří nemalé prostředky za služby účetní, je mu nabídnuta možnost, aby jako formu poděkování za využité služby zaslal roční dárcovskou SMS. Po dobu jednoho roku tak může podporovat konkrétní projekt částkou 30 Kč měsíčně. Linka bezpečí, z.s. z této částky měsíčně obdrží 28,50 Kč.Linka bezpečí, z.s. byla založena v roce 1994 za účelem pomoci dětem a mladým lidem nejen v jejich obtížných životních situacích, ale i při jejich každodenních starostech a problémech. Slouží především těm, kteří si nevědí rady se svými problémy, cítí se ohroženi, osamělí, zrazení, zmatení a z nejrůznějších důvodů se nechtějí nebo nemohou svěřit někomu ze svého okolí. Sdružení je akreditováno v rámci systému sociálně-právní ochrany dětí v České republice a je také členem celosvětové asociace dětských linek důvěry Child Helpline International (CHI). Stěžejní službou je Linka bezpečí 116 111, která funguje anonymně, zdarma 24 hodin denně, včetně nedělí a svátků, a to z pevného i mobilního telefonu. Děti mohou pro komunikaci využít také E-mailovou poradnu Linky bezpečí: pomoc@linkabezpeci.cz (odpověď do 3 pracovních dnů) nebo online Chat Linky bezpečí http://chat.linkabezpeci.cz, který je otevřen každý den (od pondělí do neděle) vždy od 15 do 19 hodin a o víkendech také v čase od 9 do 13 hodin.Problémy rodičů a dospělých s dětmi řeší odborníci v rámci služby Rodičovská linka na tel. 840 111 234 nebo 606 021 021. Sdružení Linka bezpečí je partnerem kombinovaného evropského projektu Safer Internet Cz, v rámci kterého provozuje asistenční linku pomoci www.pomoconline.cz.Na Lince bezpečí v současné době pracuje 100 konzultantů a 12 intervizorů v třísměnném provozu. Provoz zajišťuje kromě vedoucí Linky bezpečí rovněž sociální pracovník, psycholog a manažer následného vzdělávání konzultantů. Služby Linky bezpečí jsou poskytovány na vysoké profesionální úrovni. Všichni odborní pracovníci před vstupem na Linku bezpečí absolvují všeobecný výcvik v telefonické krizové intervenci akreditovaný Ministerstvem práce a sociálních věcí ČR v rozsahu 150 hodin. Každý rok přijímá Linka bezpečí okolo 250 000 dovolání. Nejčastějším tématem volajících dětí byly problémy s láskou a partnerstvím. Na druhém místě pak rodinné vztahy zahrnující obavy z reakce rodičů, přehnaná přísnost rodičů a problémy v komunikaci. Smutnou statistku doplňuje narůstající počet hovorů na téma sebevražedné tendence a kyberšikana. Finanční prostředky získané od dárců budou použity na projekty Linky bezpečí, z.s.Více informací na www.linkabezpeci.cz.', 'Linka bezpečí, z.ú. byla založena v roce 1994 za účelem pomoci dětem a mladým lidem nejen v jejich obtížných životních situacích, ale i při jejich každodenních starostech a problémech. Slouží především těm, kteří si nevědí rady se svými problémy, cítí se ohroženi, osamělí, zrazení, zmatení a z nejrůznějších důvodů se nechtějí nebo nemohou svěřit někomu ze svého okolí. Linka důvěry je akreditována v rámci systému sociálně-právní ochrany dětí v České republice a je také členem celosvětové asociace dětských linek důvěry Child Helpline International (CHI). ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daňové přiznání pomáhá', 'SOS dětské vesničky, z.s.', 'VESNICKYOP', 'Daňové přiznání pomáhá je unikátní způsob podpory charitativních organizací. Tento společný projekt serveru OnlinePriznani.cz a Fóra dárců kombinuje využití snadného online zpracování daňového přiznání a možnosti přispět na dobrou věc.Uživatel si v aplikaci vyhotoví své daňové přiznání, které je mu zdarma zasláno na email. Protože ušetří nemalé prostředky za služby účetní, má možnost poděkovat za využité služby zasláním roční dárcovské SMS SOS dětským vesničkám. Po dobu jednoho roku tak může podporovat konkrétní projekt částkou 30 Kč měsíčně. SOS dětské vesničky z této částky měsíčně obdrží 28,50 Kč.', 'SOS dětské vesničky, z.s. zajišťují náhradní rodinnou péči (v podobě skupinové pěstounské péče) dětem, jejichž vlastní rodina selhala. Organizace vyhledává a školí SOS pěstouny/ky, kteří pak v SOS dětské vesničce vychovávají svěřené děti v rodinném prostředí. Každá pěstounská rodina sestává z 3-7 dětí.Činnost SOS dětských vesniček, z.s. byla po předchozím období pod pravomocí státu obnovena po roce 1989. V 90. letech převzalo Sdružení SOS dětských vesniček zpět zařízení/vesničky v Karlových Varech - Doubí a v Chvalčově a úspěšně realizovalo výstavbu vesničky v Brně - Medlánkách.V roce 2003 vznikl také program následné péče pro děti z SOS dětských vesniček, tzv. Komunita mládeže. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daňové přiznání pomáhá', 'ZRNKA, z.s.', 'BABYKLOKANCIOP', 'Daňové přiznání pomáhá je unikátní způsob podpory charitativních organizací. Tento společný projekt serveru OnlinePriznani.cz a Fóra dárců kombinuje využití snadného online zpracování daňového přiznání a možnosti přispět na dobrou věc.Uživatel si v aplikaci vyhotoví své daňové přiznání, které je mu zdarma zasláno na email. Jelikož ušetří nemalé prostředky za služby účetní, je mu nabídnuta možnost, aby jako formu poděkování za využité služby zaslal roční dárcovskou SMS. Po dobu jednoho roku tak může podporovat konkrétní projekt částkou 30 Kč měsíčně. BABYKLOKÁNCI z této částky měsíčně obdrží 28,50 Kč.Narození miminka je vždy radostně očekáváno celou rodinou. Všichni se na ně těší, představují si, jaké to bude, až všichni budou společně doma, jak ho přijmou starší sourozenci... Bohužel, některá miminka se rozhodnou podívat se na svět o něco dříve - některá o měsíc, jiná ještě mnohem dříve. Předčasně narozené nebo nemocné miminko je na dlouhou dobu odkázáno na péči zdravotnického personálu a pobyt v inkubátoru a rodiče najednou stojí s prázdnou náručí, s hlavou plnou obav z budoucnosti a pocitem, že pro něj vlastně ani nic udělat nemohou, že jsou „přebyteční". Opak je ale pravdou - nic není pro miminko víc potřeba než jeho rodiče! Pokud to je jen trochu možné, mohou „klokánkovat", povídat si s děťátkem, u maminek je také důležité podporovat a udržovat laktaci, protože pro nedonošené miminko není vhodnější výživa než mateřské mléko plné ochranných látek. Ne všechna neonatologická a novorozenecká oddělení jsou na tyto varianty připravená. Péče o nedonošená a nemocná miminka je velmi finančně náročná, zejména pokud se jedná o nákup specializovaných přístrojů a dalšího zdravotnického vybavení, a je naprosto logické, že jejich finance míří právě tímto směrem. My jsme tady proto, abychom pomohli tam, kde možnosti nemocnic končí. Pro oddělení s nedonošenými a nemocnými dětmi nakupujeme podle dohody to, co právě nejvíce potřebují pro podporu kontaktu miminek s rodiči. Nejčastěji se jedná o dvoufázové nemocniční elektrické odsávačky mateřského mléka Medela Symphony Standard, sady k těmto odsávačkám, ruce „The Zaky", které umožňují polohování miminek, pomáhají s termoregulací a zároveň děťátku přináší vůni rodičů. Dalšími položkami na seznamu bývají křesla k inkubátorům, deky, zavinovačky, povlečení, malé ponožky a čepičky - vše, co je aktuálně potřeba. Zároveň podporujeme setkávání rodičů, kteří mají stejné zkušenosti. Umožňuje jim to vyměňovat si informace a sdílet své životní příběhy, což neuvěřitelně pomáhá hlavně psychicky.', 'Posláním organizace je získávání finančních prostředků pro zdravotně znevýhodněné děti, pro tuzemská perinatologická centra a novorozenecká oddělení tuzemských nemocnic včetně dětských oddělení JIP v rámci projektu Babyklokánci; podpora dostupnosti péče zdravotních služeb porodních asistentek. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dar s velkým D', 'Diakonie ČCE', 'DIAKONIECCE', 'Diakonie ČCE je druhá největší nestátní organizace zabývající se sociálními službami a občanskou pomocí. Denně pomáháme tisícům klientů v nepříznivých životních situacích - od rané péče, pomoci rodinám s dětmi s postižením, přes provoz nízkoprahových zařízení, azylové domy, občanské poradny, chráněné dílny a pracoviště apod. až po četná seniorská zařízení či hospic pro umírající. Dále zajišťujeme humanitární pomoc v ČR i v zahraničí.Připojte se k naší sbírce, je to unikátní příležitost, jak podporovat naše klienty - od malých dětí s postižením třeba až po seniory v požehnaném věku. Každý měsíc vybíráme na jiný smysluplný účel a každý měsíc tak můžete podpořit naše klienty v různých zařízeních a službách Diakonie, a učinit tak jejich život kvalitnější.Pomoc má mnoho tváří, může mít i tu Vaši. ', 'Posláním DČCE je poskytování sociální, zdravotní, pedagogické a pastorační péče lidem, kteří se v důsledku věku, zdravotního postižení, nemoci, osamocení či ohrožení dostali do nepříznivé životní situace. Jedná se o druhou největší nestátní neziskovou organizaci poskytující sociální služby v ČR. Je součástí široké sítě partnerských diakonických organizací v Evropě. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Daruj den doma', 'Hospic sv. Štěpána, občanské sdružení', 'DENDOMA', 'Přibližně 80 % lidí si přeje zemřít doma, největší zdravotní pojišťovna VZP však ani v letošním roce neproplácí poskytovatelům této služby náklady na domácí hospicovou paliativní péči. Hospic sv. Štěpána v Litoměřicích je jediným zařízením Ústeckého kraje, které tuto službu v okruhu 25 km poskytuje, jen v loňském roce jsme doprovodili 35 pacientů, od počátku roku 2015 jsme v domácí péči doprovodili 10 pacientů, další žádosti evidujeme, je tedy předpoklad, že loňský údaj bude v letošním roce výrazně překročen. Bez dostatku finančních prostředků to však nebude možné.', 'Hospic sv. Štěpána je specializované nestátní zdravotnické zařízení poskytující již 14 let pacientům a jejich rodinám paliativní léčbu a péči v terminální fázích nevyléčitelného onemocnění. Cílem je zajistit nemocnému i jeho rodině maximální kvalitu života, a to buď v domácím prostředí, nebo v lůžkovém zařízení. Hospic proto také provozuje poradnu s půjčovnu pomůcek, ambulanci paliativní medicíny a léčby bolesti. Od ledna 2014 hospic poskytuje novou službu - paliativní a hospicovou péči v domácím prostředí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Darujte domov', 'Přátelé domova', 'POMOCDOMOVU', 'Domov svaté Rodiny je nestátní nezisková organizace, která od roku 1991 poskytuje celoroční pobytové služby dospělým lidem s mentálním a kombinovaným postižením. V současné době pečuje Domov o 76 lidí, z toho někteří nedosáhnou svým příjmem na plnou úhradu za službu. Spolek Přátelé domova, který se skládá převážně z rodičů klientů, se rozhodl formou DMS pomoci pěti lidem, kteří v Domově žijí a nemají nikoho, kdo by jim s úhradou za službu finančně pomohl.', 'Spolek Přátelé domova spolupracuje s vedením Domova a podílí se na jeho koncepčních záměrech. Pomocí získaných financí pomáhá zvýšit kvalitu života klientů Domova. Každý rok plně financuje týdenní rekreaci pro klienty, podílí se platbě za canisterapii. Také nakupuje drobné vybavení, rehabilitační pomůcky a finančně podporuje osobní asistenci klientů. Další oblastí činnosti je koordinace vzájemné pomoci mezi rodiči. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dejme šanci dětem', 'Spolu dětem o.p.s.', 'SPOLUDETEM', 'Projekt Dejme šanci dětem klade důraz na podporu zejména vzdělávacích aktivit a talentu dětí a mládeže vyrůstající v dětských domovech a pěstounské péči, které zvyšují možnost uplatnit se v budoucím samostatném životě. Projekt je realizován na vlastních webových stránkách www.sance.cz. Dárci mohou přispět libovolnou finanční částkou na konkrétní dítě a jeho přání.', 'Spolu dětem o.p.s. od roku 2006 podporuje a pomáhá dětem žijícím mimo vlastní rodinu.Posláním organizace je prostřednictvím široké škály vzdělávacích projektů připravit děti a mladé lidi z dětských domovů na samostatný život, podporovat vzdělání a talent dětí, propojovat dětské domovy s místní komunitou, šířit informace o náhradní rodinné péči. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dejte s námi informacím smysl', 'z.s. InternetPoradna.cz', 'IP', 'InternetPoradna.cz je nezisková organizace, která již 15 let pomáhá lidem se zdravotním postižením, osobám pečujícím a seniorům. Poskytuje jim odborné sociální poradenství v oblastech dluhové problematiky, potíží s dávkami, důchody a psychologické či právní poradenství. Vedle poradenství nabízí také službu sociální rehabilitace, která pomáhá lidem se zdravotním postižením získat znalosti a dovednosti potřebné pro plnohodnotné začlenění se do života společnosti. Lidem v krizových situacích poskytuje telefonickou pomoc na Olomoucké lince důvěry a na chatu.', 'Posláním organizace je za pomoci moderních informačních technologií přispívat k sociálnímu začleňování, vzdělávání a zaměstnávání osob v nepříznivé sociální situaci, včetně vzdělávání odborníků v sociální oblasti. Organizace má tři registrované sociální služby - službu sociální rehabilitace Restart, službu odborné sociální poradenství a službu telefonickou krizovou pomoc.Posláním služby sociální rehabilitace Restart je podporovat dospělé osoby se zdravotním postižením, které se nacházejí v nepříznivé sociální situaci. Tyto lidi vedeme k samostatnému a zodpovědnému zvládání běžných životních situací, a to formou ambulantní i terénní. Posláním služby Poradna je poskytovat odborné sociální poradenství osobám starším 18-ti let se zdravotním znevýhodněním, seniorům a osobám o ně pečujícím. Podporujeme je při řešení jejich nepříznivé sociální situace tak, aby ji co nejsamostatněji zvládly vyřešit a mohly se ve svém životě co nejvíce společensky uplatnit. Služba je poskytována formou ambulantní i terénní. Prostřednictvím telefonické krizové pomoci poskytujeme podporu lidem v krizových situacích. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Děti do rodin', 'Dobrá rodina o.p.s.', 'DOBRARODINA', 'Jedná se o kampaň, která má primárně rozšířit povědomí o pěstounské péči a problematice náhradních rodin v ČR. Dalším cílem je získat prostředky, které jsou nad rámec státního příspěvku a mohou pěstounským a náhradním rodinám sloužit na úhradu zájmových kroužků, psychologa, občasné výpomoci s hlídáním atd..', 'V České republice v současné době vyrůstá téměř 10 000 dětí v různých typech ústavů. Naším cílem je vytvořit takové podmínky, aby i tyto děti mohly vyrůstat (byť v náhradním) rodinném prostředí. Jsme si vědomi skutečnosti, že žádný způsob náhradní péče nemůže plně nahradit vlastní rodinu. Podporujeme vazby dítěte na rodiče, příbuzné a další blízké osoby a vazby na přirozené sociální prostředí Klademe důraz na kvalitu péče, profesionalitu, spolehlivost a vysokou odbornost poskytovaných služeb. Naši činnost vykonáváme s respektem k právům dětí a všem jejich potřebám. Podporujeme a odborně doprovázíme pěstounské rodiny, které o tyto děti pečují. Poskytujeme odborné poradenství, odlehčovací služby, asistenci při kontaktu dítěte s rodinou a další služby, zajišťujeme odbornou podporu a vzdělávání pěstounů i sociálních pracovníků. Působíme ve všech krajích České republiky. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Děti potřebují rodinu', 'Amalthea z.s.', 'AMALKA', 'Vycházíme z přesvědčení, že nejlepším místem pro život dítěte je rodina - vlastní, adoptivní nebo pěstounská. Pouze v rodině může dítě najít bezpečí, které mu pomůže získat sebedůvěru, důvěru v okolní svět a schopnost navazovat vztahy.V programu Podpora pro rodinu a dítě podporujeme rodiny, které se ocitly v náročné životní situaci a hrozí odebrání dítěte z péče rodičů.Ivaně bylo 17 let, když otěhotněla. Sama vyrostla v ústavu a partner dítě nechtěl. Ona se o miminko chtěla starat, ale nevěděla, kde bude bydlet, z čeho bude žít a jak to vše zvládne. Po zapojení do služby se podařilo nalézt bydlení, vyřídit dávky a po narození syna jí pracovnice podporovala v péči o dítě. Dnes je Lukáškovi jeden rok, je z něj zdravé a spokojené dítě. Ivana se zkontaktovala po dlouhé době s tetou a ta jí také občas pomůže.Centrum náhradní rodinné péče podporuje náhradní (pěstounské a adoptivní) rodiny a děti v jejich péči, které krátkodobě či dlouhodobě nemohou vyrůstat ve své vlastní rodině.Veronika, 10 let, už dnes ví, jaké to je mít nablízku mámu, která ji má ráda a která jí pomůže i tehdy, když je jí smutno po její vlastní mámě. Ale nebylo to tak vždy - část svého života prožila v ústavním zařízení, protože máma péči o ni nezvládala, nechtěla se starat. Sama také vyrostla v dětském domově a od Veroniky nakonec utekla. Veronika po příchodu do náhradní rodiny nekomunikovala, nedokázala říct, co se jí děje, často se vztekala, ničila věci, ubližovala i sama sobě. Teď je ale nablízku pěstounka a pomáhá jí zvládnout její pocity strachu, samoty a opuštění. Veronika se učí, že problémy se dají řešit a nemusí od nich utíkat.', 'Posláním z.s. Amalthea je podpora rodiny jako jedinečného prostředí pro naplňování potřeb dítěte prostřednictvím poskytování sociálních služeb a dalších odborných a společenských aktivit. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Děti s DMO', 'Hašle z.s.', 'DETISDMO', 'Projekt je určen na pomoc rodinám s dětmi s DMO. Rodiče (většinou matky samoživitelky) si bohužel nemohou dovolit z finančních důvodů nákup zdravotních pomůcek pro své děti. Cílem projektu je získat finance na nákup zdravotních a kompenzačních pmůcek (např. kočárků, stolů na cvičení, polohovacích systémů, aj.)Jednotlivé projekty máme uveřejněny na www.hasle.cz/nase deti', 'Jsme nezisková organizace , které není lhostejný osud jiných lidí, kteří jsou odkázáni na pomoc druhých. Naše sdružení se zabývá pomoci dětem se zdravotním a tělesným postižením po celé republice. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dětský domov Korkyně', 'Soukromý dětský domov SOS 92, o.p.s.', 'KORKYNE', 'Dětský domov Korkyně je školské zařízení, které zahájilo svůj provoz v roce 1992. Bylo prvním nestátním zařízením tohoto typu v České republice. Žije u nás 20 dětí od 3do 18 let (v případě studia do 26let), které nemají to štěstí, aby mohly bezpečně vyrůstat ve své rodině. Jsou rozděleny do tří výchovných skupin, v budově v Korkyni je 16 dětí, o které se starají vždy dva vychovatelé, třetí skupina je externí v samostatných bytech. Cílem projektu je dát dětem více péče a pozornosti, kterou vzhledem ke svým zkušenostem se světem potřebují a kterou si zaslouží. Prostředky budou použity na zlepšení individuální péče o děti posílením týmu vychovatelů o dalšího člena. V současné době pečuje o osm dětí jeden vychovatel, myslíme si, že je to málo. Naše děti si zaslouží více pozornosti, abychom mohli častěji vyslechnout všechna jejich trápení, starosti, ale i radosti, častěji je obejmout a věnovat jim více času. Děti s vychovateli sdílejí den i noc, společně pečují o naši velkou domácnost bez pomoci jakýchkoli dalších zaměstnanců. Tímto chceme pomoci dětem lépe překonávat traumata, která jim umístění do dětského domova přináší.', 'je dětský domov, kde potřeby dítěte jsou na prvním místě, který chce být bezpečným zázemím pro děti, které nemohou žít se svými rodiči, chce být partnerem rodičů, jež se nedokáží či nemohou starat o svoje děti, který si stojí za tím, že dítě svou rodinu potřebuje a má na ni právo. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dětský hospicový stacionář Ondrášek', 'Mobilní hospic Ondrášek, o.p.s.', 'HOSPICONDRASEK', 'Péče o dětské pacienty s různými formami nevyléčitelných nemocí je nesmírně psychicky i fyzicky náročná. Pečující rodiče jsou vystaveni velké zátěži, což má samozřejmě vliv na psychickou pohodu a stabilitu v rodině. Děti vyžadují celodenní dohled a péči, včetně noční obsluhy. Například rodiny s dětmi na umělé plicní ventilaci zůstávají v péči o své děti osamoceny. Prakticky nemají žádnou či jen velmi omezenou možnost svěřit své dítě do péče služby zajištěné kvalifikovaným zdravotním personálem. Cílem projektu je zkvalitnění a rozšíření služeb Mobilního hospice Ondrášek zvýšením jeho kapacity a zřízením Dětského hospicového stacionáře. Chceme tak vyjít vstříc potřebám rodičů, kteří se dostávají do svízelné situace v souvislosti s péčí o nevyléčitelně nemocné dítě. Záměrem je vybudovat a zřídit Dětský hospicový stacionář Ondrášek, který bude poskytovat komplexní odlehčovací služby pro rodiče a blízké osoby našich pacientů. Chceme tímto poskytovat podpůrné služby pro rodiče a předcházet tak u nich vyčerpání a rozvoji syndromu vyhoření. Neméně důležitá pro rozvoj identity a samostatnosti takto nemocných dětí je zkušenost i s jiným pečovatelem než je jeho rodič. Dětem velmi prospěje pobyt v přátelském prostředí s ostatními dětmi při zajištění odborné zdravotní péče. Rodiče budou mít možnost odpočinout si a načerpat síly, zařídit i nezbytné záležitosti u lékaře, na úřadech apod. Zároveň budou moci sdílet s jinými rodiči své příběhy a osobní zkušenosti s nemocí svých dětí, což jim může usnadnit proces vyrovnání se s blížící se smrtí.', 'Posláním Mobilního hospice Ondrášek je poskytování odborné paliativní péče pro dospělé a děti v domácím prostředí, včetně sociálního poradenství v oblasti domácí hospicové péče a poradenství pro pozůstalé. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dětští onkologičtí pacienti', 'Nadační fond dětské onkologie KRTEK', 'KRTEK', 'Zlepšování kvality života dětských onkologických pacientů a jejich rodin v průběhu léčby dítěte a následně po ní včetně pomoci s návratem do běžného života. Podpora Kliniky dětské onkologie FN Brno a podpora výzkumu zaměřeného na onkologická onemocnění dětí.', 'Nadační fond dětské onkologie KRTEK podporuje od roku 1999 práci lékařů a zdravotníků Kliniky dětské onkologie Fakultní nemocnice Brno a je její neoddělitelnou součástí. Naší hlavní prioritou je zkvalitnění a zpříjemnění pobytu dětí v nemocnici, péče v průběhu trvání celé léčby a pomoc při vyrovnávání se s jejími následky. Zajišťujeme dovybavení pracovišť Kliniky dětské onkologie, přispíváme na výzkum, vzdělávání lékařů a zdravotnického personálu. Průběžně, podle aktuálních potřeb, vytváříme projekty zaměřené na cílenou pomoc jednotlivým hospitalizovaným dětem, i dětem, které již aktivní léčbu ukončily a vyrovnávají se s jejími následky.Nadační fond dětské onkologie KRTEK podporuje od roku 1999 práci Kliniky dětské onkologie FN Brno. Od začátku fungování NFDO KRTEK je jeho hlavní prioritou zkvalitnění a zpříjemnění pobytu dětí v nemocnici, péče v průběhu trvání celé léčby a pomoc při vyrovnávání se s jejími následky. Realizujeme programy na zvyšování kvality života malých pacientů a jejich blízkých. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Diabetes 24/7', 'Dia Help, o.s.', 'DIAHELP', 'Cílem projektu je celkové zkvalitnění péče o dětské diabetické pacienty a to hlavně formou osvěty a vzdělávání s důrazem na léčbu diabetu za pomocí moderních technologií a sociální začleňování dětských pacientů zpět do přirozeného kolektivu a krizové poradenství', 'Posláním organizace je: - Všeobecná pomoc pacientům trpícím DM I. a II. stupně a jejich rodinám. - Zprostředkování kvalitní odborné lékařské pomoci. - Zkvalitňování lékařské péče v regionech ČR. - Organizace odborných seminářů ke vztahu k DM. - Prosazovat zkvalitnění sociální a ekonomické péče pro pacienty s diabetem. - Spolupracovat se sdruženími pečujícími o stejně postižené pacienty. - Spolupracovat s farmaceutickými a vědeckými ústavy. - Přispívat na zdravotní pomůcky. - Poskytovat informace a přispět tak k vyšší informovanosti o DM. - Vydávat publikace o DM pro členy spolku i pro širokou veřejnost. - Podpora vlastních členů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Díky Vám dosáhnou', 'Domov pro mne, z.s.', 'PRODOMOV', 'Cílem projektu Díky Vám dosáhnou je získání finančních prostředků, které budou využity na rozšíření služby osobní asistence. Těmito prostředky bychom mohli poskytnout podporu ještě většímu počtu osob se zdravotním postižením či jejich blízkých v cestě za nezávislostí. Z tohoto důvodu umožňujeme náruživým čtenářům si v městě Brně nejen dobrou knihu najít, ale také ji věnovat někomu jinému a tím nás podpořit. Právě díky Vám totiž naši klienti, za pomoci osobních asistentů z Domova pro mne, dosáhnou téměř čehokoliv, například i na nejvyšší poličku knihovny.', 'Domov pro mne je brněnská nezisková organizace, která již 10 let pomáhá lidem se zdravotním postižením žít běžným způsobem života za pomoci sociální služby osobní asistence. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('DMS pro Zoo Ostrava - pro kvalitnější život', 'Zoologická zahrada Ostrava, příspěvková organizace', 'ZOOOV', 'Finanční prostředky získané prostřednictvím projektu budou použity ke zlepšení kvality života zvířat, tzn. budou využity k rekonstrukci stávajících či přípravě a výstavbě nových expozic.Výstavba nových moderních expozic samozřejmě závisí na dostatku finančních prostředků. Náklady na výstavbu kvalitních chovatelských zařízení např. pro šimpanze nebo tygry se pohybují v řádech několika desítek miliónů Kč. I přes nedostatek finančních prostředků je však možné zlepšit životní podmínky zvířat alespoň částečně, např. zvětšením prostoru, omezením či snížením počtu chovaných zvířat nebo alespoň částečnou rekonstrukcí expozic.Sami návštěvníci mohou Zoo Ostrava podpořit prostřednictvím symbolické adopce zvířat (tento systém úspěšně funguje již řadu let). Finanční prostředky takto získané putují do rezervního fondu a jsou využívány pro rozvoj zoologické zahrady, např. pro výstavbu nových expozic. Adopce však znamená poskytnutí částky o výši minimálně 500,- Kč (takto stanovená minimální částka vychází ze systému odměňování dárců, je také nutné se zaregistrovat) a nutná administrace spojená s adopcí může řadu návštěvníků odradit. Projekt dárcovských SMS přináší jednodušší způsob, jak podpořit danou organizaci prostřednictvím malé částky a zcela nezávisle. V případě zoologické zahrady mohou být podnětem pro zaslání DMS nejen sympatie k určitému druhu zvířete, ale také nevyhovující stav některých expozic, který samotní návštěvníci často kritizují. Nejen u těchto expozic bychom instalovali názorné cedulky, jak jednoduše lze naši zoo podpořit pomocí DMS.', 'byla zřízena v roce 1951. Její činnost vyplývá z poslání zoologických zahrad, tj. přispět k zachování biologické rozmanitosti volně žijících živočichů, jakož i výchova veřejnosti k ochraně přírody. Zoo Ostrava se aktivně podílí na záchraně ohrožených druhů v rámci Evropských záchranných programů, Evropských plemenných knih a Mezinárodních plemenných knih. Je členem Unie českých a slovenských zoologických zahrad (UCSZ, od r. 1991), Evropské asociace zoologických zahrad a akvárií (EAZA, od r. 1996), IZE (International Association of Zoo Educators, od r. 1998) a nejprestižnější světové organizace Světové asociace zoologických zahrad a akvárií (WAZA, od r. 2005) sdružující více než 200 nejlepších zoo a akvárií z celého světa. Od r. 2004 je licencovanou zoologickou zahradou v souladu s požadavky zákona o zoologických zahradách. Zoo Ostrava je v Moravskoslezském kraji jedinou zoo a patří v něm k nejvyhledávanějším turistickým cílům. Pro návštěvníky je otevřena po celý rok. V r. 2013 zoo navštívilo přes 430 tis. návštěvníků. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dny pro Izrael', 'Sion - Nová generace', 'SION', 'Projekt Dny pro Izrael je kulturně-společenský projekt. Touto iniciativou chceme přispět k dobrým a přátelským vztahům mezi naší zemí a Izraelem na poli kulturním, společenském, politickém, obchodním. Zároveň chceme i oslovit širokou veřejnost, předat jí jednak reálné informace o historii židovského národa a o současném dění v Izraeli a spolu s tím i přiblížit židovskou kulturu. Tím vším chceme preventivně působit vůči případným antisemitistickým tendencím a také veřejně vyjádřit naši podporu státu Izrael v souvislosti s násilím a terorismem, jemuž je stále vystavován. Projekt je realizován vždy v jednom týdnu v říjnu. V rámci projektu probíhají nejrůznější kulturní a vzdělávací aktivity (přednášky na školách o holocaustu, židovské kultuře, výstavy, přednášky o izraelské kultuře pro veřejnost, Klezmerové odpoledne - koncert pod širým nebem, Veřejné shromáždění na podporu Izraele, ochutnávka izraelských vín, apod.) Vrcholem celého projektu je Galavečer Dnů pro Izrael, který je vždy velkou společenskou událostí. Během Galavečera Dnů pro Izrael vystupují známí umělci, kteří chtějí veřejně vyslovit svou podporu Židům a Izraeli, dále vystupují umělecké soubory a skupiny, které přímo interpretují židovskou kulturu (tanec, hudba, poezie, zpěv), a tím seznamují návštěvníky se samotnou duší židovského národa. Osobnostmi, které již tradičně projekt Dny pro Izrael podporují jsou například: prezident Miloš Zeman, předseda Senátu Milan Štěch, premiér Bohuslav Sobotka nebo ministr kultury Daniel Herman.', 'Spolek Sion - Nová generace se zabývá obecně prospěšnými činnostmi vedoucími k naplněnému životu. Jde o aktivity se širokým záběrem, mezi jeho činnosti patří sociální, charitativní, přednášková, výchovná, vzdělávací, osvětová, výchovná, kulturní, sportovní, společenská, zájmová a případně jiná činnost směřující k pozitivním životním hodnotám. Konkrétně se jedná o organizaci akcí pro rodiny s dětmi např.:Město plné lampionů, Hradecká drakiáda, Dětský svět Sion, atd. Mezi další hlavní činnost patří organizace kulturně-společenského projektu Dny pro Izrael. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dobrý skutek', 'Dobrý skutek, z.s.', 'DOBRYSKUTEK', 'Sdružení Dobrý skutek svou pomoc zaměřuje na několik oblastí, které pracovně dělíme do následujících kategorií: - děti (postižené, nemocné, opuštěné...) a zařízení, která o děti pečují (např. dětské domovy, ústavy sociální péče atd.) - tělesně postižení - zvířata (např. asistenční psi výhradně pro dospělé klienty, záchrana ohrožených druhů ve volné přírodě i v zajetí atd.) - pěstounské rodiny Další kategorie mohou být dle potřeb přidány, popř. stávající kategorie rozšířeny.Každá žádost o finanční dar je pečlivě překontrolována (lékařské zprávy, kalkulace stavebních úprav...), doložena nezbytnost finanční podpory, žadatel je navštíven spolupracovníkem sdružení či spolupracujících organizací, zváženy všechny možnosti a postupy řešení daného případu. Průběh pomoci je fotograficky dokumentován, stejně jako předání daru a jeho využití. Dokumenty jsou zveřejňovány na webových stránkách u jednotlivých příjemců.Vždy podporujeme konkrétní osobu či konkrétní zařízení, aby bylo využití daru stoprocentně kontrolovatelné, jak ze strany DS, tak ze strany dárců. Náš klient má tvář, příběh, adresu, kontaktní telefon. Má zkrátka konkrétní podobu.Dobrý skutek o.s. beze zbytku ctí princip přerozdělování všech 100% finančních darů potřebným. Výtěžek z DMS bude rozdělen na aktuální případy: http://www.dobryskutek.cz/komu-pomahame/V období do konce roku 2010 jsme využívali službu DMS zejména na akcích pořádaných naším sdružením a podařilo se rozdělit částku 21 060 Kč.V dalším období do konce roku 2013 jsme využívali službu DMS zejména na akcích pořádaných naším sdružením a podařilo se rozdělit částku 28 263 Kč.Roční bilancování 2013 bylo obzvláště potěšující, neb jsme zaznamenali několik rekordních výsledků, z nichž nejdůležitější jsou: - podařilo se nám nashromáždit dary v celkové výši 5 908 256 Kč - obdarovali jsme celkem 124 klientů, 121 finančně, 3 věcným darem - uspořádali jsme Golf pro Dobrý skutek, setkání dárců na Zámečku a byli přizváni nadalších dvacet sedm akcí, které přinesly 2 098 999 Kč - rozšířili jsme dárcovskou základnu, navázali cenná partnerství a spolupráci sespolečností SC&C, s projektem Čtení pomáhá či se slevovým portálem Pepa.cz - svou činnost zahájil a prvním žádostem vyhověl Dobrý skutok SK', 'Hlavním poslání sdružení je společné provozování obecně prospěšných činností v oblasti zdravotnictví, školství a tělovýchovy, provozování internetového serveru „Dobrý skutek" za účelem shromažďování darů od organizací a občanů a jejich následné poskytování na konkrétní charitativní účely v oblasti zdravotnictví, školství a tělovýchovy, které se řídí příslušnými právními předpisy, rozhodnutími a pokyny příslušných správních úřadů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Doma je doma', 'PROSAZ, z.ú.', 'DOMAJEDOMA', 'Projekt si klade za cíl poskytnout kvalitní služby osobní asistence, které zajistí právo na plnohodnotný život v přirozeném prostředí domova bez nutnosti umístění do ústavu. Klienti využívající služeb osobní asistence, jsou především hendikepování spoluobčané, ale i senioři, kteří by bez každodenní pomoci osobní asistence byli odkázání na ústavní péči. Ta ale mnohdy hodnotu člověka degraduje. Osobní asistence vykonávána přímo v bytě klienta, tedy v jeho přirozeném prostředí, ale i v běžném veřejném prostředí, umožňuje i nadále žít kvalitní život se snahou o sociální začlenění do plnohodnotného života ve společnosti. Jde především o pomoc se základními úkony, které „zdravého" provází na každém jeho kroku. Ať už se jedná o pomoc při ranní hygieně, oblékání, pomoc při přesunu na vozík, doprovod do zaměstnání, do školy, k lékaři, pomoc při nákupech, pomoc při přípravě jídla, doprovod za kulturou, večerní hygienu, až po ukládání na lůžko apod. Osobní asistence je klientům k dispozici 7 dní v týdnu 365 dní v roce. Osobní asistent tedy nahrazuje např. klientovy nohy, ruce, hmat. Pro mnoho klientů je také zárukou kvalitní péče osoba předsedkyně sdružení Ing. Ivety Peškové. I přes své těžké tělesné postižení dokázala vybudovat stabilní neziskovou organizaci, která funguje již mnoho let. Paní předsedkyně Ing. Iveta Pešková je nositelkou ceny Olgy Havlové za rok 1997.', 'Posláním organizace PROSAZ je pomáhat zdravotně postiženým žít normálně. Zapojujeme je do běžného života zajištěním přímé péče, zaměstnáváním a nabídkou volnočasových a rekreačních aktivit. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Domácí a rehabilitační péče o nemocné děti', 'Velvet Smile, o.p.s.', 'SMILE', 'Projekt je zaměřen na pomoc a podpora rodinám, jež mají své nemocné či postižené dítě v domácí péči. Jedná se o náročnou a nepřetržitou péči, která je náročná jak psychicky, tak samozřejmě i ekonomicky. Finanční pomoc rodinám je poskytována například na úhradu zdravotnického jednorázového materiálu, hygienických pomůcek, doplňků stravy apod. S uvedenou péči je v mnoha případech spojena rovněž rehabilitace. Jedná se o formu pravidelných cvičení, dlouhodobých pobytů v rehabilitačním zařízení i užívání kompenzačních pomůcek.', 'Velvet Smile, obecně prospěšná společnost cílí svoji činnost na:- pomoc dětem a mladým s různým fyzickým, duševním onemocněním nebo postižením. - podporu rodin, které doma pečují o děti a mladé s různým typem onemocnění či postižení, - pomoc obětem domácího násilí - podporu zdravotnických a sociálních zařízení. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('DOMEČEK PLNÝ KOLEČEK - komunitní bydlení', 'Sportem proti bariérám, z. s.', 'DOMECEK', 'V rámci projektu DOMEČEK PLNÝ KOLEČEK (DPK) budujeme bezbariérové komunitní bydlení s asistencí pro minimálně 12 těžce postižených vozíčkářů. Nyní dokončujeme v budově bývalé tiskárny 1. komunitní bydlení pro 6 handicapovaných lidí (tři vozíčkáře). 2. komunitní bydlení pro další tři vozíčkáře s těžkým fyzickým handicapem a tři lidi s lehčím (např. i mentálním) postižením začínáme stavět v rodinném domku v areálu DPK. Všichni, kteří budou v DPK bydlet, zde budou mít díky pracovnímu uplatnění oprávněný pocit užitečnosti a díky vzájemné pomoci dostatek asistence pro aktivní život, což je často pravý opak toho v jaké situaci se nacházejí nyní. Společně bude tato komunita méně závislá na pomoci zvenčí (těžce postižení lidé pomáhají organizačně a jako koordinátoři lidem s mentálním postižením a lidé s lehčím postižením fyzicky pomáhají s asistencí vozíčkářům při méně náročných úkonech - při přípravě jídla, úklidu domácnosti, pracovních, sportovních a volnočasových aktivitách, atd.). Naše organizace komunitě zajišťuje spíše jen odbornou asistenci (např. při hygieně), supervizi, pracovní uplatnění, sportovní a volnočasové aktivity.V DOMEČKU PLNÉM KOLEČEK dále budujeme zázemí pro: - sociální služby: terénní osobní asistenci, odlehčovací služby pro rodiny starají se o nesoběstačného člena (formou krátkodobých pobytů s nepřetržitou asistencí), týdenní stacionář a terapeutické dílny - sportovní aktivity: tréninky, turnaje a školení - chráněné dílny: keramiku, výrobu marionet, sportovních pomůcek pro handicapované, proutěného zboží, atd. - farmu: pěstování plodin, chov zvířat a péče o krajinu - rehabilitaci: ergoterapii, fyzioterapii a fitness - vzdělávací akce: kurzy a besedyDíky sportovním a volnočasovým aktivitám, farmě, dílnám (kde budou probíhat kurzy keramiky, pletení z proutí, atd.), přednáškám, besedám a školením, je prostor DPK otevřený veřejnosti, kterou do našich aktivit zapojujeme přirozeně, neboť jsou pro ně tyto aktivity určeny také.Bezbariérové bydlení, odlehčovací služby a týdenní stacionář je poslední krok k tomu, abychom poskytli komplexní služby lidem, kteří nedokážou žít samostatně (ať z důvodů mentálních, nebo fyzických). Lidem s těžkým postižením se věnujeme již 10 let a víme v jak zoufalé situaci je mnoho rodin, kde pečující rodiče již péči o své dospělé „dítě" nezvládají. Jejich jediná blízká perspektiva je ústav, kde se jim často život „scvrkne" do pouhého zajištění základních potřeb. Těmto lidem, které tato neradostná budoucnost čeká, nebo už je potkala, nyní chceme pomoci.', 'Spolek byl založen ve veřejném zájmu a veřejně prospěšném poslání, kterým je zlepšování života a sociálního postavení postižených lidí a odstraňování nepříznivých důsledků jejich postižení v osobním i společenském životě. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Domov pro všechny generace a podpora znevýhodněným lidem', 'Každý koš pomáhá, z.s.', 'POMOCKOSEM', 'Vizionářský projekt - pojďte ho s námi postavit. Klade si za cíl vybudovat první domov, který bude integrovat několik generací. V domově bude 12 - 15 bytů, které budou sloužit jako pobytová sociální služba pro několikagenerační cílovou skupinu. Služby budou zahrnovat podporu při dalším vzdělávání, zdravotní a sociální podporu a výpomoc, duchovní péči. KKP chce tímto projektem rozvíjet koncentrovanou pomoc pro několik generací a provázat vzájemnou mezigenerační sounáležitost, a tak zdůraznit vzájemnou úctu i respekt mezi generacemi, neboť všechny mají svou důležitost pro život společnosti.', 'Posláním organizace je podpora a pomoc sociálně potřebným a slabým občanům, podpora společensky znevýhodněných nebo na okraj společenského zájmu odsunutých jednotlivců, skupin nebo organizací, vyvíjení ostatních činností vedoucích k respektování křesťanských a všelidských hodnot jako jsou občanská a lidská sounáležitost a úcta ke stáří. Podporujeme mladé lidi odcházející z ústavní péče, kteří žijí v domově na půl cesty Maják, popř. i po jejich odchodu. Podporujeme ve sportování osiřelé děti a mládež, pomáháme sociální integraci různě handicapovaným mladým lidem a dětem. Podpořili jsme také mobilní hospic. Nejvíce se nyní chceme soustředit na výstavbu Domova pro všechny generace - modelu vícegeneračního soužití. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Domov Řepy', 'Domov sv. Karla Boromejského', 'DOMOVREPY', 'Domov sv. Karla Boromejského poskytuje zdravotní a sociální služby nemocným nesoběstačným seniorům. Provozuje lůžkovou část a denní stacionář. Zajišťuje také rekvalifikaci odsouzeným ženám a zaměstnává je ve svém provozu.', 'Poslání Domova vychází z poslání jeho zřizovatele - Kongregace Milosrdných sester sv. Karla Boromejského a tím je služba potřebným a  bezmocným lidem. V současnosti Domov zajišťuje zdravotní a sociální pobyty pro nemocné seniory a provozuje denní stacionář. V objektu Domova také sídlí Oddělení výkonu trestu pro ženy. Těmto ženám Domov zajišťuje rekvalifikační sanitářský kurz sv. Zdislavy a  zaměstnává je v pomocných provozech (kuchyně, prádelna, úklid apod.). Absolventky sanitářského kurzu pak pracují přímo u lůžek pacientů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dům pro Charitu', 'Oblastní charita Polička', 'TRETIDUM', 'Dům pro Charitu je projekt, kterým chceme získat část prostředků na rozvoj a rekonstrukci nově zakoupeného domu čp. 10 ve Vrchlického ulici v Poličce. V domě bude provozována významná část stále se rozrůstajících sociálních služeb Oblastní charity Polička. Své zázemí zde najdou služby pro rodiny s nezletilými dětmi Šance pro rodinu a Centrum náhradní rodinné péče, dále Osobní asistence pro osoby se zdravotním postižením a v regionu naprosto ojedinělá služba Otevřené dveře pro lidi s duševním onemocněním. V přízemí vznikne cvičná kuchyň pro osoby s mentálním postižením.', 'Posláním Charity je pomáhat lidem v hmotné, sociální, duševní a duchovní nouzi bez ohledu na jejich příslušnost k rase, národnosti nebo náboženskému vyznání. Naším posláním je pomáhat lidem v nouzi tak, aby vedli život odpovídající jejich důstojnosti, podporovat je v samostatnosti a aktivaci vlastních sil a možností, a doprovázet je v obtížné životní situaci. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Dům služeb pro nevidomé Josefa Chaloupky', 'TyfloCentrum Brno, o.p.s.', 'DRAK', 'Chceme vybudovat vhodné prostory pro poskytování rozsáhlého komplexu služeb zrakově postiženým v Jihomoravském kraji. Finanční podpora, kterou nám poskytnete, bude přinášet dlouhodobý užitek zrakově postiženým lidem.Symbolem projektu je figurka vzpřímeného draka a s bílou holí (obdoba symbolu města Brna, kde drak je vlastně krokodýl): - dát rozvahu a klid - nad situací, v níž ses ocitnul, - dojít k rozhodnutí a koncepci - jak řešit svou životní situaci, - dělat rozumně a po krocích - vše, co ti pomůže.Pomozte vybudovat vhodné prostory pro poskytování rozsáhlého komplexu služeb zrakově postiženým v Jihomoravském kraji.Děkujeme.', 'TyfloCentrum Brno, o.p.s. již 14 let poskytuje sociální služby, aktivizační a motivační programy, i vzdělávací kurzy zrakově postiženým občanům především z Brna a Jihomoravského kraje. Pomáhá nevidomým a slabozrakým lidem překonávat důsledky těžkého zrakového postižení a zvyšovat kvalitu jejich života. Činí tak prostřednictvím komplexu poradenských, asistenčních, sociálně rehabilitačních, sociálně terapeutických a sociálně aktivizačních služeb. Společným cílem činností služeb je vést klienta k samostatnosti a nezávislosti, ke zkvalitnění jeho života, zlepšování životních podmínek, k podpoře společenského a kulturního uplatnění, k dosáhnutí integrace do společnosti na zásadě rovnosti, k rozšiřování možností pracovního uplatnění a uplatnění všestranných zájmů.   ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Etela pro děti', 'ETELA o.p.s.', 'ETELA', 'Cílem projektu je soustřeďování a poskytování finančních prostředků za účelem podpory a ochrany osob mladších 18ti let s přihlédnutím k jejich sociálnímu zázemí a zdravotnímu stavu. Poskytování zdravotní techniky např. polohovací lehátka, chodítka, invalidní vozíky, didaktické předměty, asistenční psy, Handle terpie, EEG terapie, hypoterapie, příspěvky na rehabilitační a ozdravné pobyty, speciální školky, soustředění pro zrakově postižené.', 'Posláním společnosti je podpora a ochrana osob mladších 18ti let s tělesným handicapem nebo v tíživé sociální situaci. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Fandíme rodině', 'Centrum pro rodinu a sociální péči z.s.', 'PRORODINY', 'FANDÍME RODINĚ je motto více jak dvacetileté činnosti Centra pro rodinu a sociální péči z. s., jehož posláním je podporovat manželství, rodinu a mezilidské vztahy na základě křesťanských hodnot, což naplňuje pomocí tří středisek.Středisko RODINA vytváří aktivity pro podporu mezilidských vztahů jako např. preventivní či vzdělávací programy pro rodiny, manžele, seniory, komplex poradenských služeb. Projekt VÝZVA nabízí sociální služby pro děti, mladé lidi s postižením a jejich rodiny v oblasti osobní asistence, poradenských služeb, úlevové péče a volnočasových aktivit.Klub ÁMOS realizuje interaktivní výstavy a projekty pro děti a mládež. Ročně naše projekty navštíví cca 43 000 lidí. Mnohé z těchto aktivit jsou zdarma, aby byly dosažitelné i pro sociálně slabší rodiny.', 'Posláním Centra pro rodinu a sociální péči z. s. je podporovat manželství, rodinu a mezilidské vztahy na základě křesťanských hodnot. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Fond seniorů', 'Nadace Taťány Kuchařové - Krása pomoci', 'FONDSENIORU', 'Fond seniorů podporuje prověřené projekty, které poskytují moderní sociální služby pro seniory, respektující jejich potřeby, zamezující jejich vyloučení ze společnosti a umožňující jim žít plnohodnotný život v přirozeném prostředí. Podporované projekty jsou zaměřeny zejména na služby osobní asistence a pečovatelské služby, kvalitní aktivizační programy, dobrovolnictví v domácnostech seniorů a sociálních zařízeních pro seniory a sociální poradenství. Nadace také pomáhá při získávání kompenzačních pomůcek a podílí se na zabezpečování vybavení zařízení poskytující sociální služby pro seniory.Nadace na základě Žádostí o poskytnutí nadačního příspěvku poskytuje příspěvky na konkrétní projekty organizací. Získané finanční prostředky z DMS jsou využívány na podporu jednotlivých prověřených projektů. O podpořených projektech pravidelně informujeme na webových stránkách Nadace: www.krasapomoci.cz/pomahame.', 'Posláním Nadace je usilovat o kontinuální a systematické řešení problematické situace seniorů v České republice a zlepšení kvality jejich života. Nadace svým působením usiluje o rozvoj a uplatňování základních humanitárních a právních principů života společnosti, a tak deklaruje svoji snahu o všestrannou podporu a pomoc vedoucí ke zlepšování a rozvoji kvality života seniorů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Hiporehabilitace ohrožených dětí z dětských domovů', 'o.s. Barry & spol.', 'HIPO', 'Projekt se zaměřuje na léčebnou jízdu na koni, která je součástí léčebných metod založených na kontaktu člověka s koňmi. Tato metoda se zaměřuje především na děti a mládež v ústavní výchově. Jízda na koni má za cíl příznivě působit na psychické i fyzické poruchy dětí. Projekt má za cíl vybudování a zajištění hipocentra v Dětském domově Býchory, který jako jediný domov v ČR zajišťuje komplexní péči pomocí hiporehabilitace a zajistit její služby dětem dětských domovů středočeského kraje a speciálních škol kolínského regionu.', 'Pořádání a podpora kulturních a společenských aktivit a programů v kolínském regionu. V rámci těchto aktivit je zahrnuta práce se sociálně ohroženými dětmi a mládeží, dětmi a mládeží s nařízenou ústavní a ochrannou výchovou, práce s tělesně a smyslově postiženými dětmi a mládeží. Snažíme se především o integraci těchto dětí do společnosti a o jejich snazší zapojení do běžného života. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Hospic Anežky České - Rozvoj hospicové péče', 'Oblastní charita Červený Kostelec', 'HOSPICCK', 'Hospic Anežky České, registrované nestátní zdravotně sociální zařízení - první hospic v ČR, slouží nemocným a jejich blízkým již 20. rokem. Usiluje o pozitivní a důstojný závěr lidského života v součinnosti s profesionální léčbou bolesti, tlumením dalších příznaků onemocnění se zajištěním lidské podpory. Usiluje o uspokojení všechny čtyřech okruhů potřeb těžce nemocného pacienta: biologických, psychologických, sociálních a spirituálních. Darované finanční prostředky jsou určeny na zdravotnický materiál, léky, na opravy a údržbu zařízení potřebného při péči o těžce nemocné a vybudování nového zázemí pro Mobilní hospic Anežky České a ambulanci hospicové a paliativní léčby. ', 'Obecným předmětem činnosti je zřizování a provozování charitativních služeb, domovů, zdravotnických, sociálních, školských a jiných zařízení a služeb, jejich metodické vedení a poradenský servis, provozování půjčoven zdravotnických pomůcek, zajišťování a poskytování duchovní a pastorační péče, pořádání sbírek a humanitárních a kulturních akcí za účelem pomoci potřebným v České republice a v zahraničí. Organizace poskytuje zdravotní a sociální služby (má registrováno 6 sociálních služeb), dále vzdělávání (vlastní akreditaci vzdělávacího střediska i akreditaci jednoho vzdělávacího projektu), volnočasové aktivity pro děti a mládež a další. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Hospic Dobrého Pastýře v Čerčanech', 'TŘI, o.p.s.', 'HOSPICCERCANY', 'Cílem realizace projektu Hospic Dobrého Pastýře v Čerčanech je umožnit lidem v terminální fázi většinou nádorového onemocnění důstojný a klidný závěr života, prožít ho bez bolesti a pocitu osamění, naplnit ho tím, co považují za důležité, učinit kroky, se kterými otáleli, rozloučit se, žít naplno tak, jak jim jejich onemocnění dovolí. Naší snahou je poskytovat kvalitní paliativní péči 24 hodin denně všem nemocným lidem, kteří se rozhodnou strávit poslední etapu svého života s námi. Prostředky vybrané prostřednictvím DMS budou využity na materiální zabezpečení Hospice Dobrého Pastýře v Čerčanech, opravy a udržování zařízení a částečnou úhradu energií.', 'Společnost TŘI, o.p.s. vznikla transformací Občanského sdružení TŘI, registrovaného v roce 1992. Dlouhodobým posláním organizace je poskytovat pomoc a péči zejména nemocným lidem se zvláštní orientací na pacienty v terminálním stadiu nevyléčitelného onemocnění. Snažíme se o návrat tradic rodinného doprovázení a poskytování odborné péče tam, kde již rodinná péče nestačí. Centrem našeho zájmu je člověk se všemi jeho specifickými potřebami. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Hospic sobě', 'Hospicová péče sv. Zdislavy, o.p.s.', 'HOSPICZDISLAVY', '"Když to doma nejde" Od roku 2009 doprovázíme v libereckém kraji umírající klienty se svými rodinami v jejich domácím prostředí. Ne vždy je tento model péče možný, ať již z důvodů na straně rodiny nebo pro limitující kapacitu domácího hospice. Potřebujeme dát šanci těm,kteří nemají to štěstí, aby mohli zemřít ve své posteli. Prostředí lůžkového hospice se snaží rodinnému prostředí co nejvíce přiblížit. Projekt lůžkového hospice přímo navazuje na domácí hospicovou péči a věnujeme se jeho uskutečnění od vzniku naší organizace. Přizvali jsme ke spolupráci Město Liberec, které darovalo Libereckému kraji budovu bývalého sirotčince s přilehlými pozemky. Od srpna 2014 zde probíhá celková rekonstrukce. Příjem prvních klientů předpokládáme na přelomu roku 2015/2016.Na podporu projektu jsme vyhlásili veřejnou sbírku "Dnes ty, zítra já" s podtitulem "Hospic sobě" na nákup vybavení lůžkového hospice, nákup pomůcek, zařízení a zdravotnického materiálu k poskytování hospicové a paliativní péče, vzdělávání pracovníků v oblasti paliativních a ošetřovatelských služeb pro terminálně nemocné pacienty a na obnovu technického zázemí organizace.Na provozování Hospice sv. Zdislavy se pilně chystáme, vzděláváme pracovníky v přímé péči i administrativě, oslovujeme dárce. V tuto chvíli již máme zakoupena všechna polohovací lůžka.Když na svět přicházíme, jsme obklopeni láskou a péčí. Stejně tak by tomu mělo být, když z tohoto světa odcházíme.', 'Naším posláním je doprovázení těžce a nevyléčitelně nemocných klientů a jejich rodin prostřednictvím zdravotní, sociální, psychologické a duchovní podpory. Respektujeme jedinečnost člověka a dbáme na to, aby byl vždy zachován individuální přístup. Přání a nároky pacientů a jejich rodin jsou pro nás na prvním místě. Vždy se snažíme o to, aby byla zachována kvalita života v co největší možné míře. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Hospic sv. Alžběty', 'Hospic sv. Alžběty o.p.s.', 'HOSPICBRNO', 'Hospicová péče poskytuje komplexní zdravotní, psychologickou, duchovní a sociální péči nevyléčitelně nemocným v závěrečné fázi choroby. Důraz je kladen na kvalitu života nemocných a jejich lidskou důstojnost. Zdravotní péčí jsou zvládány symptomy pokročilé choroby, jako je bolest, dušnost, zažívací obtíže a deprese.', 'Posláním organizace je poskytování komplexní pomoci a podpory nemocným v pokročilé a konečné fázi nevyléčitelných onemocnění a jejich blízkým. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Hospicová péče sv. Kleofáše Třeboň, o.p.s.', 'Hospicová péče sv. Kleofáše Třeboň, o.p.s.', 'HOSPICTREBON', 'Projekt si klade za cíl zajistit provoz terénních zdravotních služeb v domácím hospici v Třeboni. Zejm. tedy financovat provoz automobilů, nákup léků a pomůcek nezbytných pro domácí hospicovou paliativní péči a dalších předmětů nezbytných pro práci zdravotních sester v rodinách klientů.', 'Posláním našeho mobilního hospice je umožnit rodině pečovat o těžce nemocného nebo umírajícího člověka v jeho přirozeném domácím prostředí. Rodinu především doprovázíme - a to nejen v odborné zdravotní péči, bez které by umírající nemohl být doma, ale i v oblasti sociální a duchovní - vše v souladu s přáním a možnostmi každé rodiny individuálně. Naše organizace nabízí základní i odborné sociální poradenství a v rámci něho také půjčovnu kompenzačních pomůcek pro širokou veřejnost. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Hudba dětem - Darujme dětem Sistemu', 'Nadační fond Harmonie', 'HARMONIE', 'V rámci projektu Harmonie jsou na základních školách v sociálně vyloučených lokalitách budovány dětské orchestry, ve kterých se dětem dostává bezplatného hudebního vzdělání. Hudba se stává prostředkem sociálního začleňování, rozvíjí nejenom hudební ale i sociální dovednosti dětí. Děti se učí zodpovědnosti a práci v týmu, zároveň se zvyšuje jejich sebedůvěra a studijní výsledky.', 'Nadační fond Harmonie využívá intenzivního hudebního vzdělávání a práce v dětském orchestru jako nástroje k překonání sociálního vyloučení, získání sebedůvěry a zlepšení studijních výsledků dětí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Humanitární aktivity Armády spásy na území České republiky', 'Armáda spásy v České republice, z.s.', 'ARMADASPASY', 'Projekt se zaměřuje na získání finančních prostředků na podporu humanitárních aktivit Armády spásy na území České republiky. Především jde o péči o klienty bez domova, kteří se ocitli v extremní životní situaci. Může jit o důsledek domácího násilí, ztrátu bydlení atp. Projekt také nabízí řešení pro skupiny obyvatelstva ohrožené sociální izolací. Jako jsou například ženy na mateřské dovolené nebo senioři. Další cílovou skupinou jsou děti ohrožené sociálním vyloučením. V případě živelných katastrov na území ČR, je projekt určen rovněž na pomoc v postižených oblastech.', 'Armáda spásy poskytuje sociální služby na základě křesťanských zásad pro osoby, které jsou v nepříznivé sociální situaci. Snažíme se pomáhat i těm, kteří z lidského hlediska již nemají šanci nového návratu. Své služby Armáda spásy aktuálně poskytuje v 10 městech České republiky (Praha, Brno, Ostrava, Havířov, Opava, Karlovy Vary, Přerov, Šumperk, Krnov, Jirkov). V první řadě jde o služby pro osoby bez přístřeší (azylové domy, noclehárny, nízkoprahová denní centra) a osoby ohrožené sociálním vyloučením (azylové domy pro matky s dětmi, nízkoprahová zařízení pro děti a mládež). Armáda spásy je součástí všeobecné křesťanské církve. Všude na světě nabízí potřebným jak sociální tak i duchovní pomoc. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Humanitární pomoc Magna Děti v tísni ve světě', 'MAGNA, Děti v tísni, o.p.s.', 'MAGNA', 'Cílem projektu je dlouhodobé získávání peněžitých příspěvků na realizaci zahraničních misí organizace MAGNA, Děti v tísni/ Magna Children at Risk - poskytování akutní a dlouhodobé zdravotní a materiální pomoci lidem postiženým epidemií, přírodními katastrofami, hladomorem či lidem vyloučeným ze systému zdravotní péče jako i lidem v nouzi, kteří žijí v ohrožených částech země. Zabezpečení lékařského zázemí a to i prostřednictvím přímé účasti odborných pracovníků a lékařů, zajištění a distribuci potravin a dalších hmotných prostředků a léků včetně distribuce a výstavby zdravotnických zařízení či nouzových přístřeší.MAGNA pracuje přímo v terénu, kde má v současnosti přes 300 pracovníků - lékaře, logistiky, koordinátory a místní pracovníky schopné okamžitě reagovat na náhle vzniklé situace. Projekty jsou zaměřené na ošetření a péči o lidi postižené nemocemi jako HIV/AIDS, akutní podvýživa, malárie a jiné.V současnosti má zřízené svoje stálé mise na třech kontinentech: v Kambodži, Keni, DR Kongo, Jižním Súdánu, Haiti, Nikarague, Vietnamu a Myanmaru. Po tsunami jsme působili v Indii (2005-2010).Posláním organizace Magna Děti v tísni je pomáhat lidem v krizových oblastech světa, realizovat humanitární a rozvojové projekty ve světě zaměřené na pomoc lidem v nouzi, ať již způsobené chudobou, chorobami, válkou nebo ekologickou devastací, a to bez ohledu na etnickou, náboženskou či rasovou příslušnost.Od roku 2006 působíme i v České republice, přičemž mezinárodní síť Magna Deti v núdzi/Magna Children at Risk MAGNA) byla založena na Slovensku již v roce 2001.', 'Posláním organizace je pomáhat lidem v krizových oblastech světa, realizovat humanitární a rozvojové projekty zaměřené na pomoc lidem v nouzi, ať již způsobené chudobou, chorobami, válkou nebo ekologickou devastací, a to bez ohledu na etnickou, náboženskou či rasovou příslušnost. Pracujeme přímo v terénu, kde jsme schopni okamžitě reagovat na náhle vzniklé situace. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Humanitární projekty Lékařů bez hranic ve světě', 'Lékaři bez hranic - Médecins Sans Frontieres in Czech Republic, o.p.s.', 'MSF', 'Lékaři bez hranic poskytují zdravotnickou pomoc v téměř 70 zemích světa s více než 2 500 mezinárodních a 29 000 místních spolupracovníků. V zemích, ve kterých nefungují nebo zcela chybí jakékoli zdravotnické struktury, poskytují Lékaři bez hranic zdravotnickou péči, provozují kliniky, vykonávají chirurgické operace, bojují s epidemiemi, provádějí očkovací kampaně. Lékaři bez hranic rovněž pracují v odlehlých a těžce přístupných oblastech a také školí místní personál. Zabezpečují provoz výživových center pro podvyživené děti, provozují programy psychologické pomoci a ošetřují pacienty postižené infekčními nemocemi, např. tuberkulózou, spavou nemocí či HIV/AIDS. Hlavním cílem těchto aktivit je poskytnutí lékařské pomoci populacím, které nemají přístup ke zdravotnickým službám. Lékaři bez hranic využívají různých možností a prostředků k tomu, aby mohli informovat veřejnost o krizových situacích, pokud je to nezbytné ke zlepšení situace postižených. Cílem práce Lékařů bez hranic je zmírnění lidského utrpení, ochrana lidských životů a zdraví a znovunavrácení úcty k lidským bytostem a základním lidským právům.', 'Médicins Sans Frontières/Lékaři bez hranic jsou mezinárodní humanitární organizace, která poskytuje odbornou zdravotnickou pomoc lidem v ohrožení a v případě krizí. V zemích, ve kterých nefungují nebo zcela chybí jakékoli zdravotnické struktury, poskytují Lékaři bez hranic zdravotnickou péči, provozují kliniky, vykonávají chirurgické operace, bojují s epidemiemi, provádějí očkovací kampaně. Lékaři bez hranic rovněž pracují v odlehlých a těžce přístupných oblastech a také školí místní personál. Zabezpečují provoz výživových center pro podvyživené děti, provozují programy psychologické pomoci a ošetřují pacienty postižené infekčními nemocemi, např. tuberkulózou, spavou nemocí či HIV/AIDS. Hlavním cílem těchto aktivit je poskytnutí lékařské pomoci populacím, které nemají přístup ke zdravotnickým službám. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Humanitární sbírka NA PODPORU HAITI', 'Arcidiecézní charita Olomouc', 'HAITI', 'Hurikán Matthew zasáhl 4. října na Haiti kolem dvou milionu lidí, 750 000 z nich potřebuje akutně humanitární pomoc. Výtěžek ze sbírky Arcidiecézní charity Olomouc poputuje na zpřístupnění zdravotní péče venkovanům (zejména na severozápadě země), pro ty Haiťany, kteří přišli o střechu nad hlavou, pomůže zajistit novou - odolnější plechovou střechu. V těchto dnech působí na Haiti dva humanitární pracovníci. Podle jejich zjištění z terénu budou vyhodnoceny další způsoby pomoci. Arcidiecézní charita Olomouc pomáhá na Haiti již přes 10 let. Klíčovou roli sehrála i při zemětřesení v roce 2010.', 'Charita pomáhá všem, kdo pomoc potřebují bez ohledu na barvu pleti, národnost nebo náboženství. Vyhledává jakoukoliv nouzi a snaží se najít řešení jejího odstranění, což doprovází úctou k člověku a s Boží láskou. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Humanitární zahraniční sbírka', 'Charita Česká republika', 'CHARITASVET', 'Charita ČR zajišťuje humanitární pomoc v zemích, které postihne přírodní katastrofa nebo zasáhne válečný konflikt. Pomáhá uprchlíkům či jiným osobám, které musí opustit své domovy. V mnoha zemích světa poskytuje také pomoc místním obyvatelům v oblasti vzdělávání, zajištění živobytí či zdravotní a sociální péče. V rámci projektů rozvojové spolupráce působí v Moldavsku, Srbsku, Čečně, Gruzii, Indonésií, Kambodži, Myanmaru, Mongolsku, Pakistánu, na Haiti, v Etiopii a v dalších zemích. Pomoc poskytuje těm nejpotřebnějším bez ohledu na jejich příslušnost k rase, národnosti či náboženství. Díky členství v Caritas Internationalis má ve většině zemí místního partnera a dokáže tak minimalizovat náklady při provádění řady aktivit.', 'Charita Česká republika pomáhá potřebným bez ohledu na jejich vyznání, rasu nebo národnost. Je největším nestátním poskytovatelem zdravotních a sociálních služeb v České republice a věnuje se také humanitární pomoci a rozvojové spolupráci v zahraničí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Charitativní a stipendijní program Gendalos - Gendalos odrazí k úspěchu', 'IQ Roma servis, z.s.', 'GENDALOS', 'Program Gendalos pomáhá odrazit potřebné děti k úspěchu.Program nabízí adresnou motivující podporu usilovným a ctižádostivým mladým Romům při posilování a upevňování jejich individuálních vzdělávacích a profesních úspěchů.Pomozte i Vy.', 'Hlavním posláním IQ Roma servisu je být prostředníkem, který podporuje možnosti, příležitosti a odhodlání Romů na cestě jejich růstu a společenského uplatnění a chrání jejich práva a důstojnost v rámci společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Chocerady bez bariér', 'Nadace Charty 77', 'CHOCERADYBEZBARIER', 'Dětské centrum Strančice je zdravotnické zařízení (příspěvková organizace) pro děti do tří, maximálně do šesti let věku, které jsou odkázány na nepřetržitou a intenzivní pomoc druhých. V roce 2013 kvůli restitučnímu nároku přišlo Dětské centrum Strančice o zahradu/ pozemek, na kterém je budova umístěna. Soud přiznal restituentce nárok na všechny pozemky v okolí domova a bylo rozhodnuto o přemístění zařízení do nedalekých Chocerad, kde je budova bývalé léčebny vhodná pro dětské centrum. Přestěhováním tak vznikne Dětské centrum Chocerady. V nové lokalitě však zcela chybí hřiště vhodné pro děti umístěné ve zdravotnickém zařízení.S budováním hřiště souvisí vyhlášení DMS sbírky (sbírka prostřednictvím dárcovské SMS) na získání finančních prostředků na nákup herních prvků (houpačky, prolézačky, kryté pískoviště).', 'Konto Bariéry, od roku 1992 stěžejní projekt Nadace Charty 77, je první a dosud jedinou kontinuální sbírkou, které se více než dvacet let účastní desetitisíce dárců - svým pravidelným měsíčním příspěvkem pomáhají naplňovat a financovat program Konta Bariéry. V začátcích jsme hodně pracovali s podtitulkem Konta Bariéry „Patří mezi nás". Chtěli jsme veřejnost naučit vnímat lidi s rozdílnými potřebami. Podařilo se nám to a jsme na to, že Konto Bariéry splnilo tento velký úkol, docela pyšní. Za více než dvacet let činnosti rozdělilo Konto Bariéry stovky miliónů korun, podpořilo tisíce projektů na zlepšení života lidí s handicapem a jejich plnohodnotné zapojení do společnosti. Z aktuálních potřeb lidí s postižením vychází Konto Bariéry i při formování nových projektů. Doplňuje stát a samosprávu tam, kde jejich pomoc nestačí. Stále více se zaměření jeho pomoci posouvá od standardní pomoci, tedy rehabilitačních a kompenzačních pomůcek, bezbariérových úprav bytů, škol a dalších veřejných budov, k projektům, které souvisejí se vzděláním a pracovním uplatněním lidí s handicapem.Více na www.kontobariery.cz. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Chodíme s Asistencí', 'Asistence, o.p.s.', 'ASISTENCE', '"Chodíme s Asistencí" je 7,5 kilometru dlouhý charitativní pochod krásnými zákoutími Prahy s cílem zvýšit povědomí veřejnosti o potřebách lidí se zdravotním postižením, kteří potřebují asistenci. Pochod je součástí veřejné kampaně, ve které pomáhají společnosti i jednotlivci.Pochod je určen pro širokou veřejnou (od začátečníků po zkušené turisty). Je na účastnících zvolit si tempo pochodu i přestávky. Rodiny, skupiny přátel, výletníci jsou srdečně zváni nejen podpořit smysl akce, ale také poznat nové lidi a malebné scenérie Prahy.', 'Obecně prospěšná společnost Asistence rozpouští vnitřní a vnější bariéry v životě lidí s postižením. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('I slůňata stůňou', 'Zoologická zahrada Ostrava, příspěvková organizace', 'SLUNE', 'Jednou z vážných nemocí, které ohrožují přežití slonů je tzv. sloní herpes způsobený komplexem herpes virů. Proti této nemoci postihující nejvíce mláďata slonů indických doposud neexistuje účinná vakcína. Herpes virus má k dnešnímu dni na svědomí až 80 úhynů slonů indických v zoo a převážná většina slonů v lidské péči již tento virus (byť v neaktivní podobě) v sobě nosí. Nemoc existuje již 4 milióny let a nepostihuje jen mláďata slonů chovaných v zoologických zahradách, ale i ta, která žijí ve volné přírodě. Volně žijící slony ale nemoc prozatím ohrožuje podstatně méně. Je zde reálný předpoklad, že s ohledem na stále se zmenšující životní prostor ve volné přírodě, který mají sloni zatím k dispozici, se tato nemoc bude u posledních populací volně žijících zvířat vyskytovat čím dál častěji. Nejvíce jsou tímto onemocněním ohrožena právě ta mláďata, která nemohou pít mateřské mléko a jsou odchovávána na umělé výživě. Ohrožena je tedy i ostravská samička narozená 4. února 2014 v naší zoo. Samička měla od narození problémy s příjmem potravy a prakticky celou dobu probíhá nesmírně komplikovaný pokus o její odchov. V současné době probíhá intenzivní výzkum, jehož cílem je najít účinnou látku, která chrání slony indické v přírodě. Možným vysvětlením by mohly být některé přirozené složky potravy zastoupené v jejich původní domovině. Cílem projektu je podpořit tyto výzkumné aktivity tak, aby v budoucnu nedocházelo k tak častým úmrtím sloních mláďat. Příjemcem výtěžku z veřejné sbírky bude Leibnizův institut pro výzkum zvířat v Berlíně (Leibniz Institute for Zoo and Wildlife Research Berlin, IZW), oddělení reprodukčního managementu pod vedením Prof. Dr. Thomase Hildebrandta, který tento výzkum provádí.', 'byla zřízena v roce 1951. Její činnost vyplývá z poslání zoologických zahrad, tj. přispět k zachování biologické rozmanitosti volně žijících živočichů, jakož i výchova veřejnosti k ochraně přírody. Zoo Ostrava se aktivně podílí na záchraně ohrožených druhů v rámci Evropských záchranných programů, Evropských plemenných knih a Mezinárodních plemenných knih. Je členem Unie českých a slovenských zoologických zahrad (UCSZ, od r. 1991), Evropské asociace zoologických zahrad a akvárií (EAZA, od r. 1996), IZE (International Association of Zoo Educators, od r. 1998) a nejprestižnější světové organizace Světové asociace zoologických zahrad a akvárií (WAZA, od r. 2005) sdružující více než 200 nejlepších zoo a akvárií z celého světa. Od r. 2004 je licencovanou zoologickou zahradou v souladu s požadavky zákona o zoologických zahradách. Zoo Ostrava je v Moravskoslezském kraji jedinou zoo a patří v něm k nejvyhledávanějším turistickým cílům. Pro návštěvníky je otevřena po celý rok. V r. 2013 zoo navštívilo přes 430 tis. návštěvníků. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('IMPULS pro lidi s roztroušenou sklerózou', 'Impuls, nadační fond', 'IMPULS', 'Pomozte zlepšit život a budoucnost nevyléčitelně nemocných lidí s roztroušenou sklerózou! Nadační fond IMPULS směřuje příspěvky do těchto oblastí: - TERAPIE A REHABILITACE PACIENTŮ - jedná se zejména o příspěvky na fyzioterapii a psychoterapii ve specializovaných RS centrech, kde tato péče není hrazena z veřejných financí, - REHABILITAČNÍ POMŮCKY A PŘÍSTROJE - pro nezbytnou léčbu pacientů s RS, - MEDICÍNSKÝ VÝZKUM - NF IMPULS je zřizovatelem jediného celostátního Registru pacientů s RS (ReMuS), jeho financování zajišťuje z vlastních zdrojů, - VĚDA A VZDĚLÁVACÍ PROGRAMY pro pacienty, jejich rodiny i zdravotnický personál, - OSVĚTOVÁ PRÁCE o problematice RS - organizace kampaní ve prospěch pacientů a sportovního 24hodinového Maratonu s roztroušenou sklerózou (MaRS).Nadační fond IMPULS je jediným nadačním subjektem v ČR, který se specializuje výhradně na pomoc v oblasti roztroušené sklerózy. Jen za 14 let existence věnoval na boj s touto nemocí bezmála 19 miliónů korun.Darujte i Vy lidem s ereskou šanci na život. Děkujeme Vám. www.nfimpuls.cz', 'Nadační fond Impuls vznikl v roce 2000 za účelem podpory projektů zabývajících se zajištěním komplexní a kvalitní léčby, výzkumem a osvětovou činností demyelinizačních onemocnění, především roztroušené sklerózy mozkomíšní. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Jizerská magistrála', 'JIZERSKÁ, o.p.s.', 'MAGISTRALA', 'Jizerské hory a běžecké lyžování k sobě neodmyslitelně patří. Jizerská magistrála dnes zahrnuje přes 180 km upravovaných tratí a 23 nástupních míst, která jsou rozložena po celých Jizerských horách. Díky úzké spolupráci s dalšími provozovateli tratí, např. Jizerky pro vás v Kořenově nebo Bieg Piastów v polských Jakuszycích, mohou lyžaři přejet i na trasy v Krkonoších nebo na upravené tratě na polské straně. Do stop Jizerské lyžařské magistrály zavítá každoročně více jak půl milionu lyžařů.O úpravu stop se zde již od roku 1999 stará nezisková organizace založená obcí Bedřichov JIZERSKÁ, o.p.s. Bližší informace o magistrále a o společnosti naleznete na webových stránkách organizace www.jizerskaops.cz.  ', 'Jizerská, obecně prospěšná společnost vznikla v roce 1999 s hlavním cílem - aby úprava a údržba běžeckých tratí v Jizerkách dostala jasná pravidla a byla vedena profesionálně. Úkolem Jizerské o.p.s.je každoročně získat finační prostředky na úpravu tratí, na pořízení či opravy strojů a na všechny ostatní náklady s Jizerskou magistrálou spojené.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Justýna - pomoc obětem násilí z nenávisti', 'In IUSTITIA, o.p.s.', 'JUSTYNA', 'Nikdo nesmí být napadán pro to, kým je. Nikdo nesmí být napadán pro to, koho miluje. Nikdo nesmí být napadán pro to, že má jinou barvu kůže, než je bílá. Je pátek večer a Vy po celém týdnu v práci sedíte s přáteli v hospodě na malém městě, kde žijete už řadu let. Před půlnocí se všichni zvednete a jdete každý svou cestou domů do postele, cestu už znáte nazpaměť a trefil/a byste i poslepu, je to přece malé město, kde všechno a všechny znáte. Něco je však jinak, žena na rohu je verbálně a fyzicky napadána skupinou mužů. Vy z dálky přihlížíte, a  když ženu dobíjí, tak ji slyšíte tiše vzlykat na silnici. Jdete ji pomoci. Důvod útoku? Její národnost, žena je původem z východní Asie a proto byla zbita a  urážena. Žena utrpěla šok a od té doby se bojí večer vycházet. Co uděláte Vy? Půjdete dál? Chcete pomoci, ale nevíte, jak? Kontaktujte poradnu Justýna. In IUSTITIA klientku zastoupí v trestním řízení, provede ji obtížnou situací a  předá kontakt na psychologické poradenství. Poradna Justýna poskytuje právní a sociální služby osobám ohroženým nebo vystaveným násilí z nenávisti, a to ve třech regionálních pobočkách v Praze, Brně a Českých Budějovicích. S násilím z nenávisti se může setkat každý. Podpořte činnost poradny Justýna dárcovskou smskou a bojujte s násilím z nenávisti. Pomoc poskytujeme menšinám i většinové populaci.', 'Posláním In IUSTITIA je zajištění sociálních služeb, právních informací a zastupování osobám dotčeným násilím z nenávisti a prevence násilí z nenávisti a projevů nesnášenlivosti, zejména rasismu, antisemitismu, islamofobie, homofobie a transfobie, genderového násilí, násilí z důvodu věku, zdravotního stavu, sociálního statusu či příslušnosti k subkultuře.In IUSTITIA se jako první a jediná organizace v ČR věnuje problematice násilí z nenávisti komplexně. Poskytujeme právní a sociální poradenství osobám vystaveným násilí z nenávisti, a to nejen ve třech kamenných poradnách, ale také formou terénní pomoci. Na základě akreditace MŠMT a MPSV ČR vzděláváme Policii ČR, sociální pracovníky a pracovnice, metodiky a metodičky prevence násilí ad. Naším patronem je ekonom Tomáš Sedláček. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Kapka naděje', 'Nadační fond Kapka naděje', 'KAPKANADEJE', 'Cílem projektu je shromažďování prostředků určených zejména k zajištění léčebných, školních, kulturních a sociálních potřeb nemocných dětí a to zejména dětí postižených onkologickým onemocněním a jinak nemocných dětí.', 'Nadační fond Kapka naděje byl založen s cílem trvale a kontinuálně podporovat Kliniku dětské hematologie a onkologie FN Motol v následujících oblastech: 1) Pomoc při zlepšení diagnostických postupů a léčebných metod 2) Pomoc v oblasti psychosociální péče 3) Pomoc při financování výstavby a přístrojového vybavení nové transplantační jednotky 4) Podpora vědeckých aktivit 5) Od roku 2002 NF Kapka naděje podporujeme dětská oddělení nemocnic po celé České republice. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('KLIKA pro HANDICAP', 'Handicap Zlín, z.s.', 'KLIKA', 'Proč právě KLIKA? Protože KLIKA otevírá zdravotně postiženým členům sdružení HANDICAP Zlín pomyslné dveře k novým příležitostem a protože "mít KLIKU" přece znamená mít štěstí!Účelem projektu KLIKA pro HANDICAP je získání finančních prostředků k zajištění služeb, které sdružení poskytuje 70-ti osobám s tělesným a mentálním postižením:- nepřetržitá osobní asistence „...zůstáváme doma a jsme nezávislí!", - individuální doprava speciálně upravenými vozidly „... jsme tehdy a tam, kde potřebujeme!", - klub dětí a mládeže  „... máme si kde hrát a tvořit, s kým cestovat, poznávat svět i sami sebe".Pokud „vezmete za KLIKU", děkujeme za pomoc a přejeme, ať i Vy „máte KLIKU"!', 'Již dvacátým rokem nabízí naše občanské sdružení lidem se zdravotním postižením ze Zlína a okolí pomoc v jejich nepříznivé osobní situaci a poskytuje podporu při jejich začleňování do běžného životaa) poskytováním sociálních služeb (osobní asistence u vozíčkářů a seniorů, dopravní služby imobilním) b) organizováním aktivizačních programů pro osoby se zdravotním postižením (zejména volnočasových pro děti a mládež a sportovních pro dospělé) ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Klokánek - Fond ohrožených dětí', 'Fond ohrožených dětí', 'FODKLOKANEK', 'Klokánek je zařízení pro děti vyžadující okamžitou pomoc (ZDVOP) Fondu ohrožených dětí, jehož cílem je v co největší míře nabídnout týraným, zanedbávaným či zneužívaným dětem přechodnou individuální péči založenou na principech citově vřelé rodinné výchovy. V Klokáncích se maximálně o čtyři děti po celou dobu pobytu stará v samostatném bytě přechodná náhradní rodina, „teta a strejda", nebo střídavě po týdnu dvě „tety". Sociální pracovníci a psychologové Klokánků usilují o co nejrychlejší řešení situace dítěte a to cestou návratu dítěte domů, pokud se podařilo vyřešit nepříznivou rodinnou situaci, umístěním do širší rodiny nebo nalezením trvalé náhradní rodinné péče.Prostředky z veřejné sbírky FOD využívá na zajištění potřebné péče o umístěné děti.', 'Fond ohrožených dětí od roku 1990 pomáhá dětem opuštěným, zanedbávaným a týraným, je držitelem „pověření k výkonu sociálně právní ochrany dětí". FOD zřizuje a provozuje 17 zařízení Klokánek s celkovou kapacitou 354 míst. Dále FOD provozuje krizovou linku pro matky, které tají těhotenství. Zabývá se osvětou a usiluje o zlepšení legislativy i praxe v oblasti ochrany dětí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Klub mladých migrantů – podpora inkluzivního vzdělávání', 'META, o.p.s. - Společnost pro příležitosti mladých migrantů', 'META', 'Získané finanční prostředky budou použity na spolufinancování Klubu mladých migrantů. Na METU se obrací stále více klientů, kteří potřebují pomoc s orientací v českém vzdělávacím systému. Základní podpora těchto dětí a mladistvých, kteří se ocitli v novém prostředí bez znalosti jazyka a kulturních specifik, spočívá v poradenství a asistenci. Dětem je v rámci klubu nabízeno doučování, kurzy českého jazyka, volnočasové aktivity klubu Čekuj!, knihovna a studovna. Žákům po základní škole sociální pracovník pomáhá při výběru střední či vysoké školy či s vyplněním přihlášky. V roce 2015 jsme poskytli podporu 400 dětem s odlišným mateřským jazykem. Váš příspěvek nám pomůže tyto služby i nadále udržet bezplatné. Podpoříte sociálně znevýhodněné děti a urychlíte tak velmi komplikovaný proces jejich integrace do naší společnosti. I pro děti a mládež migrantů je vzdělání základ pro jejich další život.', 'META, o.p.s. je nezisková organizace, která od roku 2004 podporuje cizince v rovném přístupu ke vzdělávání a v pracovní integraci. Zároveň nabízí podporu pedagogům a školám, pokud se potýkají se specifickými potřebami dětí a žáků s odlišným mateřským jazykem (OMJ). Kromě přímé práce s cizinci a pedagogy se META svou činností snaží prosazovat a spoluvytvářet vzdělávací systém, který dokáže koncepčně a kontinuálně reagovat na vzdělávací potřeby všech žáků a rozvíjet jejich potenciál. Jen v roce 2015 využilo služeb organizace celkem 613 cizinců, 109 cizinců se tady učilo česky. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('KohoVolit.eu', 'KohoVolit.eu', 'KOHOVOLIT', 'KohoVolit.eu se snaží primárně využívat "tvrdá" data pro veřejnou kontrolu. Provozuje webové aplikace VolebníKalkulačka.cz, NapišteJim.cz nebo BudováníStátu.cz. Organizujeme konference a barcampy se zaměřením na eDemokracii a otevřená data.', 'Cílem organizace je podpora demokracie v ČR pomocí vzdělávání a tvorby webových nástrojů. Podporujeme otevřená data ve veřejné správě. Naši Volební kalkulačku použilo před prezidentskými volbami 2013 více jak 1 000 000 občanů. Díky jednání s Poslaneckou sněmovnou Parlamentu ČR se nám podařilo prosadit, že prakticky všechna data ze Sněmovny jsou dnes zveřejňována také ve strojově čitelné podobě, která umožňuje snadné další použití těchto dat. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Komplexní psychoterapeutická a podpůrná péče pro onkologicky a jinak chronicky nemocné a jejich blízké', 'PRO Gaudia, z. ú.', 'GAUDIA', 'Psychologická pomoc pro nemocné a jejich blízké: - přinášíme úlevu formou terapeutických rozhovorů - pomáháme zvládnout sitaci příbuzným a blízkým - přispíváme k přívětivé atmosféře ve zdravotnických zařízeních - pracujeme v nemocnici i v terénu - služby v nemocnicích zdarma - jsme odborníci', 'PRO Gaudia, z. ú. je sdružení odborníků poskytujících psychosociální služby onkologicky a jinak vážně nemocným pacientům a jejich blízkým. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Koně pomáhají', 'Občanské sdružení CHEWAL', 'CHEWAL', 'Občanské sdružení Chewal se od roku 2001 věnuje hiporehabilitaci, tedy terapeutickému oboru, v němž se setkávají lidé se specifickými potřebami a koně. Zaměřuje se na dvě formy hiporehabilitace:HIPOTERAPIE: „Fyzioterapie na koni" určená dětem od nejútlejšího věku i dospělým s nejrůznějšími tělesnými a/nebo kombinovanými obtížemi. Provádí se pod dohledem zkušeného terapeuta a s pomocí asistentů. Úžasné je, že nebolí, je příjemná a jako léčba ani nevypadá - žádné nemocniční prostředí a bílé pláště, ale zelená louka a koník, který zve na projížďku.AKTIVITY S VYUŽITÍM KONÍ: „Hry s koňmi" pro všechny - děti i dospělé všech generací. S koňmi se dá provádět spousta zajímavých činností a všechny se dají vymyslet a uzpůsobit tak, aby rozvíjely fyzické, mentální i sociální dovednosti, posilovaly kladné vlastnosti a tlumily ty nežádoucí. Do aktivit s využitím koní se mohou zapojit i klienti na vozíčku, se smyslovou či jinou vadou. Pro děti, které se hodně pohybují v nemocnicích, rehabilitačních zařízeních, léčebnách a lázních, jsou aktivity s koňmi kontaktem se živou přírodou.Sdružení navštíví ročně kolem 500 klientů, z toho je 100 klientů stálých, kteří docházejí pravidelně. Většinou jsou to malé děti.', 'Hlavním cílem Občanského sdružení Chewal je dosažení nejlepšího možného zdravotního a sociálního stavu klientů s ohledem na jejich konkrétní zdravotní potíže. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Kontaktní sestra', 'Nadační fond POROZUMĚNÍ', 'POROZUMENI', 'Kontaktní sestra - váš člověk v každé nemocniciHlavní snahou NF Porozumění je zlepšení komunikace ve zdravotnictví a zařazení role Kontaktní sestry do systému zdravotní péče v ČR.Kontaktní sestra by měla ulehčit a zpříjemnit pobyt v nemocnici. Pomoci pacientům/rodičům hospitalizovaných dětí s organizačními problémy, zajistit dostatečnou informovanost, zprostředkovat kontakt s lékaři a jinými odborníky, vysvětlit léčebné a ošetřovatelské postupy, poskytnout pacientům/rodičům možnost vypovídat se. Být jim praktickým průvodcem a  oporou po celou dobu hospitalizace.Podpořte naší snahu začlenit Kontaktní sestru mezi zdravotníky každé nemocnice.', 'Posláním Nadačního fondu je shromažďování finančních prostředků k zajištění hmotné i nehmotné pomoci při podpoře iniciativ a aktivit přispívajících k vytváření, ochraně a rozvoji duchovních hodnot, ochraně lidských práv a sociálních hodnot, ochraně životního prostředí a kulturních památek, rozvoji vědy, vzdělání, tělovýchovy, kultury, zdraví a sportu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Konto BARIÉRY', 'Nadace Charty 77', 'KONTOBARIERY', 'Nadace Charty 77 se zaměřuje na pomoc handicapovaným občanům, a to nejen odstraňováním fyzických bariér, ale i vytvářením možností pro jejich návrat do aktivního života. Nadace dále zviditelňuje významné české osobnosti ze světa literatury, ekologie a boje za lidská práva..Konto BARIÉRY je stěžejním projektem nadace, který byl založen s cílem přispět ke zlepšení života handicapovaných spoluobčanů a k jejich plnohodnotnému zapojení do života. Pomoc je určena organizacím i jednotlivcům.Konto BARIÉRY přispívá na:- rehabilitační a kompenzační pomůcky (mechanické a elektrické vozíky, rehabilitační kočárky, chodítka, zdravotní lůžka apod.)  - bezbariérové úpravy bytů, škol a dalších veřejných budov, stipendia pro středoškoláky a vysokoškoláky z řad těžce zdravotně handicapovaných studentů- dopravu handicapovaných (nákup osobních automobilů pro organizace, speciální autosedačky,   úhrada svozu dětí do škol nebo denních stacionářů, speciální úpravy automobilů)- osobní asistenci- pomůcky pro sluchově postižené- podporu chráněného bydlení a chráněných pracovišť, Domy na půli cesty- příspěvky na speciální pomůcky pro sportovní a kulturní aktivity, rekondiční, ozdravné pobyty a studijní pobyty a rekvalifikační kurzy.Více na www.kontobariery.cz.', 'Konto Bariéry, od roku 1992 stěžejní projekt Nadace Charty 77, je první a dosud jedinou kontinuální sbírkou, které se více než dvacet let účastní desetitisíce dárců - svým pravidelným měsíčním příspěvkem pomáhají naplňovat a financovat program Konta Bariéry. V začátcích jsme hodně pracovali s podtitulkem Konta Bariéry „Patří mezi nás". Chtěli jsme veřejnost naučit vnímat lidi s rozdílnými potřebami. Podařilo se nám to a jsme na to, že Konto Bariéry splnilo tento velký úkol, docela pyšní. Za více než dvacet let činnosti rozdělilo Konto Bariéry stovky miliónů korun, podpořilo tisíce projektů na zlepšení života lidí s handicapem a jejich plnohodnotné zapojení do společnosti. Z aktuálních potřeb lidí s postižením vychází Konto Bariéry i při formování nových projektů. Doplňuje stát a samosprávu tam, kde jejich pomoc nestačí. Stále více se zaměření jeho pomoci posouvá od standardní pomoci, tedy rehabilitačních a kompenzačních pomůcek, bezbariérových úprav bytů, škol a dalších veřejných budov, k projektům, které souvisejí se vzděláním a pracovním uplatněním lidí s handicapem.Více na www.kontobariery.cz. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Konto lidí dobré vůle', 'Arcibiskupství olomoucké', 'VELEHRAD', 'Již sedmnáctým rokem se konají na Velehradě oslavy státního svátku u příležitosti Dne slovanských věrozvěstů pod názvem Dny lidí dobré vůle, jejichž cílem je postavit most mezi všemi lidmi dobré vůle (bez rozdílu národnosti, politického přesvědčení nebo náboženského vyznání). Projekt, jehož vlajkovou lodí je Večer lidí dobré vůle, poukazuje jak na hodnoty židokřesťanské kultury, tak na působení svatých Cyrila a Metoděje v našem geografickém prostoru, ba i na vznik české vzdělanosti a státnosti. Každoročně celou akci doprovází dárcovská kampaň, v níž jsou shromažďovány finanční prostředky na Konto lidí dobré vůle.', 'Náboženská činnost. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Konto Naše dítě', 'Nadace Naše dítě', 'NASEDITE', 'Konto Naše dítě pomáhá dětem, které to mají v životě mnohem těžší než my. Trápí je handicap nebo těžký životní osud. Ale chuť do života neztrácejí. A v tom je chce nadace maximálně podpořit. Konto má transparentní účet a každý si proto může ověřit, že 100 % prostředků jde na pomoc dětem.Do sbírky na pomoc konkrétním dětem se může zapojit široká veřejnost a dlouhodobě tak přispívat k ochraně dětí.Prostředky jdou na přímou podporu handicapovaných, týraných, zneužívaných, opuštěných a zanedbávaných dětí.', ' ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Koruna denně pro Charitu Zábřeh', 'Charita Zábřeh', 'CHARITAZABREH', 'Pojďme se zapojit do pomoci lidem, kteří jsou buď vážně nemocní, nebo na sklonku svého života a chtějí prožít tento okamžik důstojně a v okruhu svých blízkých. Charita Zábřeh poskytuje rodinám, které se takto rozhodnou postarat o své členy až do konce, podporu formou zdravotně-sociálních služeb. Často je ale potřeba více než jen kvalifikovaná pomoc - je potřeba domácnost přizpůsobit a dovybavit pomůckami, které péči ulehčí a učiní možnou. Z výtěžku Vámi zaslaných DMS chceme pořídit polohovací postel, antidekubitní matrace s kompresorem, polohovací prvky, nafukovací vanu pro koupel těla, invalidní vozík, WC židli.', 'Posláním Charity Zábřeh je projevovat milosrdnou lásku skutky, a tak naplňovat křesťanské učení o lásce k člověku. Charita vyhledává potřebné, poskytuje praktickou pomoc lidem (bez rozdílu vyznání, rasy, politické příslušnosti) v nepříznivé životní situaci a podporuje jejich začlenění do běžného života. Své služby poskytuje především na území děkanátu Zábřeh a pomoc pak podle možností kdekoliv u nás i v zahraničí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Koruna pro Kláru', 'Klára pomáhá z.s.', 'KLARAPOMAHA', 'Motto: "Koruna ke korunce, kapka ke kapce, velká louže, velké moře, naše činy, menší hoře."Projekt "Koruna pro Kláru" je zaměřen na aktivní zapojování našich spoluobčanů a firem do veřejně prospěšných aktivit a dále sděluje nápaditou formou téma rodinné péče.Cílem projektu je získat finanční prostředky na nákup vozidla zajišťujícího mobilitu našich pracovníků při jejich práci pomoci pečujícím, pozůstalým, seniorům a znevýhodněným osobám při řešení jejich krajních životních situací.V rámci projektu chceme nastudovat stejnojmenné divadelní představení, kdy příběhem celé hry bude pomoc Kláře získat zpět svou korunu, kterou ztratila prodejem na získání prostředků na svou léčbu (vychází z příběhu vzniku spolku).Dále chceme realizovat dětské malování, kdy na webových stránkách bude ke stažení černobílý obrázek Kláry, který děti vymalují a pošlou na naši adresu. Obrázky pak použijeme jako osobní poděkování za pomoc.', 'Klára pomáhá z.s. má za cíl zlepšovat kvalitu života pečujících osob. Pomáháme rodinám a lidem dlouhodobě pečujícím o své nejbližší. Máme nejen osobní zkušenost s dlouhodobou péčí, ale i odbornou kvalifikaci, kterou dále rozvíjíme.Poskytujeme: - poradenství v oblasti vyřizování sociálních dávek - informace a podporu ve využití dalších možných návazných sociálních služeb - pomoc při uplatňování práv a oprávněných zájmů pečujících osob - podporu a provázení v průběhu pečování - podporu v návratu do zaměstnání po dlouhodobém opatrování člena rodiny - podporu pozůstalým a jejich rodinám ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Kryštůfek záchranář dětem 2016', 'Nadační fond Kryštůfek', 'KRYSTUFEK', 'Prvním pilířem projektu je pomoc dětem, které se ocitnou v péči záchranářů. Pro děti bývá ošetřování a transport na palubě sanitního vozu mnohdy velmi stresující záležitostí. Stejně tak okamžiky, kdy se ocitnou na oddělení urgentního příjmu v nemocnici. Přinést malým pacientům alespoň trochu radosti, zabavit je a zmenšit jejich strach, to jsou hlavní myšlenky projektu. Pomáhá při tom plyšová hračka, mající podobu malého záchranáře, která je k dispozici doktorům, sestrám a dalším sanitárním pracovníkům. Hračku dávají zejména dětem, jež jsou vážně nemocné či zraněné, nebo bez doprovodu svých rodičů. Plyšová hračka pak dítě provází i během jeho další léčby v nemocnici. Druhým pilířem je podpora zdravotnického vzdělávání dětí.', 'Posláním nadačního fondu je podpora zdravotnického vzdělání dětí a dále pomoc dětem, které se ocitnou v péči záchranářů. Jde například o znalosti první pomoci a resuscitace, o schopnosti jak se zachovat v krizových situacích a o další základní zdravotnické dovednosti. Účelem nadačního fondu je rovněž podporovat organizace, které jsou do zdravotnické záchranné pomoci zapojeny. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Kura Kura Želvy v ohrožení', 'Zoo Brno a stanice zájmových činností, příspěvková organizace', 'ZACHRANZELVU', 'Krutý osud želv oslovil i Unii českých a slovenských zoo. Ta společně se Zoo Brno a s indonéskou neziskovou organizací vytvořila projekt, jehož cílem je zřídit vzdělávací centrum pro domorodce i turisty a návštěvníky ostrova Nusa Penida, ležícího poblíž ostrova Bali v Indonésii. Centrum Kura Kura (indonésky „Zachraň želvu!") bude upozorňovat na nebezpečí, které hrozí sladkovodním i mořským želvám, jedné z nejohroženějších skupin indonéské fauny. Při centru vznikne i záchranná stanice pro želvy zabavené nelegálním obchodníkům a Kura Kura se postupně promění ve středisko integrace ochranářských aktivit ve prospěch želv, jejich životního prostředí a indonéské přírody vůbec.', 'Organizace se zaměřuje na činnost chovatelskou, ochranářskou, vědeckou a vzdělávací v oblasti fauny a flory. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Lata - Ve dvou se to lépe táhne', 'LATA - Programy pro ohroženou mládež', 'LATA', 'Projekt Ve dvou se to lépe táhne pomáhá dětem a mladým lidem (ve věku 13-16 let), kteří řeší situace, jež nemohou nebo neumí zvládnout vlastními silami, a potýkají se s potížemi nejrůznějšího rázu - s vrstevníky, v rodině, ve škole, s trávením volného času, nejrůznějšími psychickými obtížemi spojenými s obdobím dospívání a stavěním se na vlastní nohy apod. Lata jim nabízí podporu v podobě mladého člověka - dobrovolníka (18-30let), který pro ně bude mít každý týden 2-3 hodiny, návrhy k trávení společného času a prostor k naslouchání, jenž může vnést nové světlo do obtížné situace a pomoci klientovi rozšířit jeho možnosti.', 'LATA, o.s. je nezisková organizace fungující 15. rokem, pomáhá ohroženým dětem a mladým lidem.Finanční prostředky získané formou DMS budou použity v rámci našeho projektu "Ve dvou se to lépe táhne", který pomáhá ohroženým dětem a mládeži. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('LEGIE 100', 'Nadační fond Legie 100', 'LEGIE', 'Projekt „LEGIE 100" realizujeme v souvislosti se 100. výročím vzniku čs. legií a samostatného Československa. Za touto významnou událostí stojí většinovým podílem zásluhy československých legionářů, které by se, i pro budoucnost našeho národa, rozhodně neměly opomíjet.', 'Posláním Nadačního fondu Legie 100 je podpora pietních, kulturních a osvětových projektů a dokumentační, vzdělávací, ediční a archivační činnost související s historií Československa se zaměřením na národněosvobozenecké hnutí, válečné konflikty a historii vojenství, činnost legií a odbojové aktivity, zejména s ohledem na výročí 100 let založení samostatného Československa. Připomenutí zásadních historických zásluh legionářů a odbojářů. Podpora idejí demokratické společnosti vycházející z tradic Československé republiky a jejích duchovních hodnot. Získávání prostředků na splnění účelů nadačního fondu převážně za trvání projektu LEGIE 100 u příležitosti oslav 100. výročí založení samostatného Československa. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Leontinka', 'Nadace Leontinka', 'LEONTINKA', 'Mnohostranná pomoc dětem 0-25 let se zrakovým postiženým; umožnit integraci zrakově postižených dětí do společnosti, především do škol a zájmových činností, podílet se na zlepšování prevence zrakových onemocnění dětí a na výzkumu nových léčebných metod; podílet se na vzdělávání odborného personálu těchto zařízení + zvyšovat dostupnost speciálních pomůcek a léčebných programů pro zrakově postižené děti; přispívat ke zvyšování úrovně vybavení zdravotnických a sociálních institucí, které pečují o těžce zrakově postižené děti.', 'Posláním organizace je mnohostranná pomoc dětem 0-25 let se zrakovým postiženým - přispívat ke zvyšování úrovně vybavení zdravotnických a  sociálních institucí, které pečují o těžce zrakově postižené děti - podílet se na vzdělávání odborného personálu těchto zařízení - zvyšovat dostupnost speciálních pomůcek a léčebných programů pro zrakově postižené děti - šířit informace o potřebách zrakově postižených dětí - podílet se na zlepšování prevence zrakových onemocnění dětí a výzkumu nových léčebných metod - umožnit integraci zrakově postižených dětí do škol a zájmových organizací ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Lepší škola pro všechny', 'Člověk v tísni o.p.s.', 'LEPSISKOLA', 'Cílem projektu je podpora vzdělávacího systému v České republice. Pomáháme žákům i učitelům na 3 300 školách. Učíme učitele pracovat s nadanými i hendikepovanými dětmi. Chceme, aby si žáci nejen něco zapamatovali, ale hlavně rozvíjeli svoje schopnosti a nadání. Přibližujeme studentům moderní historii. Doučujeme děti ze sociálně slabších rodin, aby neskončily na zvláštní škole a později bez práce. Inspirujeme studenty k řešení místních problémů. Učíme mladé lidi respektu, otevřenosti a odpovědnosti.', 'Jsme nevládní nezisková organizace vycházející z myšlenek humanismu, svobody, rovnosti a solidarity. Lidskou důstojnost a svobodu považujeme za základní hodnoty. Věříme, že lidé kdekoli na světě mají mít právo rozhodovat o svých životech a společně sdílet práva vyjádřená Všeobecnou deklarací lidských práv. Usilujeme o otevřenou, informovanou, angažovanou a zodpovědnou společnost k problémům doma i za hranicemi naší země. Chceme se aktivně podílet na vytváření společnosti, ve které jsou kulturní, etnické, rasové a jiné odlišnosti zdrojem obohacení, a nikoli konfliktů. Jsme součástí občanské společnosti. Naše činnost vychází z osobní iniciativy a aktivity stovek našich zaměstnanců a dobrovolníků, jak v České republice, tak v desítkách zemí, kde pracujeme a z důvěry a podpory, kterou pro svoji práci získáváme od jednotlivců, firem, vlád i mezinárodních institucí. Oblasti a priority činnosti si určujeme sami, podle naléhavosti situace a podle našich reálných možností něco změnit. Nemůžeme pracovat všude, za podstatný považujeme skutečný dopad. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Letní dům pro ohrožené děti', 'Letní dům, z.ú.', 'LETNIDUM', 'Posláním Letního domu je pomoci dětem a mladým lidem, vyrůstajícím v náhradní výchovné péči či bez fungujícího rodinného zázemí, aby prožívali šťastné dětství a dospěli k samostatnému a plnohodnotnému životu. Letní dům pracuje dlouhodobě a cíleně, tvořivě rozvíjí osobnost, pomáhá v krizovém období a podporuje vztahové vazby v prostředí, kde děti a mladí lidé vyrůstají a žijí. Prostředky budou využity na dlouhodobé programy na podporu cílové skupiny.', 'Posláním ústavu je pomoci dětem a mladým lidem, vyrůstajícím v náhradní výchovné péči či bez fungujícího rodinného zázemí, aby prožívali šťastné dětství a dospěli k samostatnému a plnohodnotnému životu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Lety Divokých hus', 'Nadace Divoké husy', 'HUSY', 'S vaší pomocí podpoříme neziskové projekty v sociální a zdravotní oblasti v celé ČR zcela unikátním způsobem - zdvojnásobíme výtěžky benefičních akcí až do výše 60 000 Kč.V rámci projektu Lety Divokých hus vyhlašuje Nadace každých 14 dní veřejnou sbírku ve prospěch konkrétní organizace a projektu. Prostředky z DMS budou rozděleny v otevřeném grantovém řízení v rámci programu Benefice s Divokými husami.', 'Podáváme pomocné křídlo těm, kteří chtějí sami létat. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Linka bezpečí - krizová pomoc pro děti a mladistvé', 'Linka bezpečí, z.s.', 'LINKABEZPECI', 'Linka bezpečí je největší a nejdéle fungující celostátní krizovou linkou pro děti a mladé lidi do 26 let. Denně se na Linku bezpečí obrací více než 450 dětí a mladých lidí z celé ČR se žádostí o pomoc či radu. Na bezplatné číslo 116 111 volají se svými starostmi jako je šikana ve škole, hádky s kamarády, rozvod rodičů, ale i s tématy jako osamělost, týrání, sebepoškozování. Často si nemají s kým promluvit, případně se za svůj problém stydí a bojí se svěřit někomu ve svém okolí. Kromě telefonu mohou děti Linku kontaktovat také prostřednictvím emailu a chatu. Odborně vyškolení konzultanti dokáží dětem ulevit, povzbudit je a pomoct jim najít východisko, případně zprostředkovat lokální pomoc ve všech místech České republiky. Linka bezpečí je tu pro děti zdarma, anonymně a nonstop, 365 dnů v roce, 24 hodin denně. O půlnoci, na Štědrý den, o prázdninách, vždy. Linka bezpečí pomáhá i rodičům, proto provozuje i Rodičovskou linku, na kterou se mohou obracet rodiče, prarodiče, pedagogové, zkrátka všichni dospělí, kteří mají starost o děti. Napomáhá porozumění mezi rodiči a dětmi a dokáže jim pomoci řešit jejich neshody.Víte o Lince bezpečí, že: Dar 500 Kč = náklad na provoz 15 minut Linky bezpečí = 6 šťastnějších dětských příběhů Dar 2000 Kč = náklad na provoz 1 hodiny Linky bezpečí = 25 šťastnějších dětských příběhů V nepřetržitém provozu se střídá na 80 konzultantů... - Pomoc dětem v krizi zajišťuje tým 80 vyškolených konzultantů, kteří se střídají v nepřetržitém provozu ve 4 hodinových směnách.Za 21 let jsme přijali více než 10 000 000 hovorů ...Volající může zůstat v anonymitě, vše je důvěrné... - Klademe velký důraz na důvěrnost, a proto žádné konkrétní příběhy dětí nezveřejňujeme v médiích, ani si hovory nenahráváme. Volající může zůstat v anonymitě. Konzultant je vyškolený profesionál... - Každý konzultant musí před přijetím na Linku bezpečí projít 150-hodinovým kurzem telefonické krizové intervence a v průběhu své práce na Lince bezpečí se povinně dále vzdělává. Online služby Linky bezpečí... Stále více dětí se na Linku bezpečí obrací prostřednictvím emailové a chatové komunikace, proto Linka bezpečí tyto služby stále více rozšiřuje.', 'Linka bezpečí, z.ú. byla založena v roce 1994 za účelem pomoci dětem a mladým lidem nejen v jejich obtížných životních situacích, ale i při jejich každodenních starostech a problémech. Slouží především těm, kteří si nevědí rady se svými problémy, cítí se ohroženi, osamělí, zrazení, zmatení a z nejrůznějších důvodů se nechtějí nebo nemohou svěřit někomu ze svého okolí. Linka důvěry je akreditována v rámci systému sociálně-právní ochrany dětí v České republice a je také členem celosvětové asociace dětských linek důvěry Child Helpline International (CHI). ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Linka seniorů', 'Elpida, o.p.s.', 'LINKASENIORU', 'Jeden hovor může zachránit lidský život.Každý den a již čtrnáctým rokem je k dispozici anonymní a bezplatná telefonická krizová pomoc pro seniory, pečující o seniory a osoby v krizi. Na Lince seniorů 800 200 007800 200 007 mají volající k dispozici důvěrný prostor pro sdílení svých pocitů, průvodce náročnou životní situací a nabídku smysluplných kontaktů na další služby a instituce. Linka seniorů podporuje aktivní, nezávislý a důstojný život svých klientů. Jediný hovor na lince dokáže změnit lidský život..."Ještě že existujete, to bych neměl už nikoho!" Cítili jste se někdy celým světem opuštění? Přesně takový je pocit mnoha starých lidí, kteří na Linku seniorů volají. Mluví o ztrátě svého milovaného partnera, o postupném "odcházení" svých přátel, o ubývání sil a také o ztrátě naděje. Na lince mohou o tom všem mluvit beze strachu. Jsou vždy respektováni a se zájmem jim naslouchají lidé s dlouholetými odbornými zkušenostmi. Společně pak hledají cestu z bludného kruhu samoty a jiných těžkých krizových situací. Na sluchátku Linky seniorů je tak často slyšet "hovor s vámi mi dal znovu naději, změnil mi život". Příběhy, které se Lince seniorů řeší, napsal sám život. Každý z nás či našich blízkých se může dostat do podobné situace. Je dobré vědět, kam se obrátit. Podpořte tuto smysluplnou a potřebnou službu pro seniory, jejíž kvalita je bezesporu prokázána nepřetržitou čtrnáctiletou existencí a 30 000 hovory ročně. Prostředky z DMS budou plně využity na provoz Linky seniorů.', 'Organizace pomáhá seniorům stát se samozřejmou, sebevědomou a respektovanou součástí společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Loutky v nemocnici', 'Loutky v nemocnici', 'LOUTKY', 'LOUTKY V NEMOCNICI se věnují práci na poli dramaterapie a rozvíjí spektrum aktivit v oblasti psychosociální péče. Prostřednictvím divadelních představení, her a písniček se snažíme pomoci v procesu uzdravování, snažíme se otevírat prostor ke hře a komunikaci o prožívané realitě. LOUTKY V NEMOCNICI to je divadlo, které usiluje o ulehčení pobytu dětí v nemocnicích. Přinášíme za dětmi do nemocnice spoustu loutek - malých i velkých, přinášíme pohádky a písničky, přinášíme možnost si hrát a povídat. Jevištěm se může stát třeba nemocniční postýlka. Není to jen obyčejné představení. Hrajeme s dětmi a pro děti. Nabízíme bezprostřední komunikaci a děti se zapojují do hry: napovídají, pomáhají, vymýšlejí příběhy, hrají s loutkami a často se svěří i se svým trápením. Tak to je ta naše medicína.', 'Z.s. LOUTKY V NEMOCNICI se věnuje práci na poli dramaterapie a rozvíjí spektrum aktivit v oblasti psychosociální péče. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Mezigenerační setkání Povídej', 'Mezi námi, o.p.s.', 'DARUJVZPOMINKU', 'Mezigenerační setkání v programu Povídej se konají pravidelně a jejich náplní jsou různorodé společné aktivity zaměřené na aktivizaci seniorů a poznávání dětí a vzájemné interakci a komunikaci. Naším cílem je přispět ke změně nahlížení generací na sebe navzájem. Chceme, aby se poznávali i ti nejstarší s těmi nejmladšími, aby si rozuměli a společně objevili, jak krásné a zajímavé jsou jejich světy. Při výběru partnerských zařízení (školské a seniorské) je naší prioritou přínos pro místní komunitu, tedy blízkost školy a domova pro seniory tak, aby se účastníci projektu mohli potkávat i v běžném životě.Na vytvoření mezigeneračních skupin čekají další babičky, dědečkové i děti, jak se dozvídáme od pracovníků školek a domovů seniorů, kteří nás kontaktují. Váš příspěvek bude použit na  vznik a realizaci setkání v nových mezigeneračních skupinách v různých regionech celé ČR. Velice Vám děkujeme za příspěvek, který přinese spoustu nových zážitků seniorům a dětem.', 'Obecně prospěšná společnost Mezi námi se zrodila z pocitu, že je škoda, když se vazby mezi generacemi vytrácejí. Proto jsme začali podporovat pravidelná setkávání těch nejmenších s těmi nejstaršími - dětí z mateřských či základních škol a babiček a dědečků z domovů či klubů seniorů. Mezigenerační setkání v projektu nazvaném „Povídej" se konají jednou měsíčně a jejich náplní jsou společné aktivity - výtvarné, hudební či pohybové, ale také čtení, vyprávění nebo oslavy různých svátků. Věříme, že je potřebné a správné, aby k sobě ti nejmenší s těmi nejstaršími nacházeli cestu, aby společně objevovali, jak zajímavé jsou jejich světy. Považujeme za důležité učit děti úctě ke stáří. Senioři se stávají jejich kamarády; děti se přirozeně učí, že ve společnosti žijí i osoby, které potřebují naši péči a ohleduplnost. Seniorům naopak nabízíme možnost čerpat energii z ryzí radosti, upřímnosti a úžasné vitality těch nejmenších. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Modré dveře pomáhají osobám v krizi, duševně nemocným, mentálně handicapovaným a jejich rodinám', 'Terapeutické centrum Modré dveře, z.ú.', 'MODREDVERE', 'Terapeutické centrum Modré dveře nabízí péči pro lidi s psychickými obtížemi, pro osoby v krizi, pro lidi s duševním onemocněním nebo mentálním handicapem. Sociální služby jsou v souladu se zákonem pro klienty zdarma. Díky centru mohou lidé z regionu Praha-východ nalézt odbornou pomoc ve své blízkosti a nejsou odkázáni na zařízení v Praze. Terénní forma poskytování sociálních služeb pak umožní nabízet pomoc i klientům, kteří nejsou z mnoha důvodů vůbec schopni opustit své bydliště.', 'Našim cílem je podpora všech forem zdraví s nejvyšším důrazem na zdraví duševní. Terapeutické centrum Modré dveře, z.ú. si klade za cíl podporovat destigmatizaci osob s handicapem různými formami, například propojováním aktivit pro osoby handicapované a pro osoby zdravé. Dalším cílem je pořádat různé kulturní, sportovní a společenské akce pro veřejnost, dále pak volnočasové aktivity pro veřejnost. Současně můžeme vykonávat prevenci sociálně patologických jevů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Modrý hroch proti úrazům', 'Nadační fond Modrý hroch', 'STOPURAZUM', 'Cílem projektu Nadačního fondu Modrý hroch je získání finančních prostředků na podporu dětské traumatologie a dětí po úrazech v rámci České republiky. Podpora dětské chirurgie, ortopedie a traumatologie v rámci oddělení, klinik a nemocnic na území České republiky. Podpora vedení preventivních kampaní proti dětským úrazům.', 'Nadační fond Modrý hroch je nezisková organizace založená v roce 2005 ve spolupráci s Klinikou dětské chirurgie, ortopedie a traumatologie FN Brno, která patří mezi osm největších traumatologických center v České republice a její spádové území zahrnuje celou jižní a střední Moravu. Nadační fond Modrý hroch pomáhá konkrétním dětem po těžkých úrazech, ale stará se také o dětské pacienty přímo v nemocnici, a to například v rámci zábavných programů a volnočasových aktivit. Prioritním cílem fondu je kromě materiální pomoci v podobě nových nadstandardních přístrojů, lůžek a vybavení pro volný čas, také rozšíření možnosti společné hospitalizace rodičů s dětmi. Další prostředky věnuje nadační fond do osvěty a prevence dětských úrazů mezi širokou veřejnost. Jedním z důležitých cílů je také podpora jednotlivých dětských pacientů po těžkých úrazech a pomoc při jejich návratu do běžného i sportovního života. V posledních letech rozšiřuje svou pomoc Modrý hroch do dalších měst a nemocnic v rámci celé České republiky. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Modrý klíč otevírá dveře lidem s mentálním postižením', 'Modrý klíč, o.p.s.', 'MODRYKLIC', 'Modrý klíč o.p.s. je nezisková nestátní organizace poskytující sociální služby dětem a dospělým lidem s mentálním a kombinovaným postižením. Je nástupnickou organizací navazující na více než dvacetiletou historii poskytovatele sociálních služeb Škola SPMP Modrý klíč. Modrý klíč v roce 2015 poskytuje zázemí komplexní péče pro 115 uživatelů. Sociální služby denního stacionáře, týdenního stacionáře a odlehčovací péče jsou uživatelům nabízeny v různých formách přizpůsobených druhu a hloubce jejich postižení i jejich věku. Rozvoj těchto forem je veden snahou k maximálně možnému přiblížení života lidí s postižením normálnímu životu jejich vrstevníků. Dětem je dále zpřístupněno vzdělávání na úrovni základní školy speciální a mateřské školy speciální. Pro starší uživatele služeb je denní program realizován formou předprofesní přípravy denního a týdenního stacionáře a v pracovních dílnách, kde se seznamují se zpracováním různého materiálu jako např. keramická hlína, textil, dřevo, kov nebo s činnostmi rozvíjejícími samostatnost a v budoucnu i možnost pracovního uplatnění jako např. úklid, pomocné práce v kuchyni, v zahradnictví apod. Modrý klíč se zaměřuje na pomoc uživatelům s mentálním a kombinovaným postižením a uživatelům s problémovým chováním, jež je způsobeno jejich omezenou sociální adaptabilitou pro specifické poruchy vnímání, rozumové analýzy situací, předvídavosti apod. Rozsah hloubky postižení uživatelů služeb Modrého klíče je v pásmu od hlubokého stupně mentálního postižení po střední formy v kombinaci s tělesným, smyslovým či duševním postižením, popřípadě autismem či podobným vývojovým postižením. Jedna třetina uživatelů služby je postižena epilepsií. Modrý klíč se zabývá hledáním a rozvíjením nových terapeutických, výchovně vzdělávacích a sociálně podpůrných metod pomoci těmto lidem žít obyčejný život. POMÁHAT MŮŽETE I VY!Cíle projektu 1. Dostupnost a rozvoj nabízených aktivit sociálních služeb pro cílovou skupinu dětí i dospělých osob s mentálním postižením (programy výchovné, vzdělávací, aktivizační, pracovní, zájmové a další aktivity podporující sociální začleňování osob cílové skupiny do společnosti). 2. Poskytnout rodinám šanci být s postiženým členem rodiny co možná nejdéle v kontaktu a být jim průvodcem v řešení jejich náročných situací. 3. Umožnit osobám s mentálním postižením plnohodnotný život a umožnit jim pozitivní vývoj. 4. Zabezpečit podporu pracovníkům v sociálních službách (systém celoživotního vzdělávání, supervize, psychohygiena, předcházení syndromu vyhoření).', 'Posláním organizace je podpora dětí i dospělých lidí s mentálním a kombinovaným postižením v rozvíjení jejich schopností, dovedností a samostatnosti při začleňování do společnosti. Organizace poskytuje registrované sociální služby denního stacionáře, týdenního stacionáře a odlehčovací služby pro výše uvedenou cílovou skupinu. V projektu je také řešeno vzdělávání na úrovni základní školy speciální a mateřské školy speciální a pracovní uplatnění pro pozice chráněných pracovních míst. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('MojeLiga', 'Liga vozíčkářů', 'MOJELIGA', 'MojeLiga je projekt komunitního dárcovství a podpory Ligy vozíčkářů. Smyslem je aktivizovat komunitu kolem Ligy vozíčkářů a nabídnout jim nástroje, kterými se mohou zapojit do činnosti organizace. Ať už jde o dobrovolnictví, pořádání benefic či individuálního darcovství.', 'Posláním Ligy vozíčkářů je podporovat lidi se zdravotním postižením v každodenním životě, v práci, volném čase i při řešení obtížných situací, aby mohli žít podle svých představ. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Moravskoslezský spolek na ochranu zvířat', 'Moravskoslezský spolek na ochranu zvířat', 'MSSOZ', 'Cílem projektu MSSOZ je poskytnout veterinární péči a vykastrovat co nejvíce zvířat v naší péči, aby se zabránilo dalšímu nekontrolovatelnému množení zvířat. Kastracemi chceme zamezit přemnožení psů a koček v problémových lokalitách Moravskoslezského kraje jako jsou Karvínsko a Bohumínsko. Kastrace spojíme se šířením osvěty pomocí propagačních materiálů. Cíl bude vyhodnocen vždy ve výroční zprávě za uplynulý rok, ve které bude předložen finanční přehled použitých peněz.', 'Moravskoslezský spolek na ochranu zvířat pomáhá v případech týrání zvířat či zanedbání jejich péče. Za pomoci svých členů a dobrovolníků pečujeme o opuštěná a týraná zvířata, zajišťujeme jim potřebnou léčbu a hledáme jim nové domovy. Řešíme kauzy množíren, podáváme podněty k šetření případů na příslušné úřady. Větší část činnosti zahrnuje práce v terénu, monitoring bezdomoveckých kolonií, podpora a spoluprace s útulky v Moravskoslezském kraji, šíření osvěty o ochraně zvířat. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Multidisciplinární péče o lidi postižené svalovou dystrofií.', 'Asociace muskulárních dystrofiků v ČR', 'AMD', 'Cílem naší organizace je poskytovat informace o svalové dystrofii jak stávajícím členům, tak i nově příchozím. Sledovat světový výzkum a nové postupy v péči a zveřejňovat tyto poznatky prostřednictvím časopisu Zpravodaj AMD, webových stránek, sociálních sítí, propagačních materiálů atd. Pomáhat orientovat se takto postiženým lidem v sociální problematice a začleňovat je do společnosti. Pořádat rekondiční pobyty, setkání členů, odborné přednášky. Chtěli bychom, aby v budoucnu vzniklo specializované centrum pro toto onemocnění.', 'Jsme organiazce s celorepublikovou působností, která sdružuje občany s progresivním nervosvalovým onemocněním. Toto onemocnění způsobuje celkové ochabování svalstva a to po celou dobu života. Důsledkem jsou pak zdravotní a sociální problémy. Cílem naší organizace je poskytovat informace o této nemoci všem, kterých se tato problematika dotýká. Sledujeme světový výzkum a uveřejňujeme jeho výsledky prostřednictvím časopisu, který vydáváme čtyřikrát do roka. Informujeme v něm též naše členy o sociální problematice, činnosti organizace a řešení problémů spojených s touto nemocí. Informace zveřejňujeme i na našich webových stránkách. Poskytujeme bezplatné sociálně právní poradenství, pořádáme ozdravné rekondiční pobyty tuzemské i zahraniční a také sportovní akce s ohledem na postižení našich členů. Naše činnost je zaměřena na sociální a zdravotní problematiku lidí především s muskulární dystrofií, snažíme pomáhat i lidem jinak tělesně postiženým, kteří jsou členy našeho sdružení a to především v začleňování do společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Muži v boji za zdraví', 'Nadační fond MUŽI PROTI RAKOVINĚ', 'PROSTATA', 'V rozvinutých zemích je karcinom prostaty (KP) nejčastějším nádorovým onemocněním mužů a druhou nejčastější příčinou úmrtí z onkologických příčin. Cílem projektu je spolu s partnerskou nadací Movember zvýšit informovanost české populace o problematice prevence, diagnostiky a léčby karcinomu prostaty a varlat a zkvalitnit možnosti diagnostiky a léčby uroonkologických onemocnění v českých nemocnicích. Toho bude docíleno prostřednictvím rozvoje informačního portálu www.rakovinaprostaty.cz, sdružujícího všechny zásadní informace o dané problematice, zabezpečením provozu internetové poradny, vydáním informačních materiálů a vyhlášením grantové soutěže na a nákup přístrojové techniky pro urologická oddělení. Cíle projektu budou monitorovány prostřednictvím analýzy návštěv a aktivit na stránce rakovinaprostaty.cz, množstvím distribuovaných informačních materiálů o léčbě rakoviny prostaty mezi veřejnost a počtem a typem přístrojů, které byly pro české nemocnice zakoupeny. Zásadním rozdílem mezi rakovinou prostaty a jinými nádorovými onemocněními je, že se objevuje v relativně pozdním věku a že jde často o ohraničené a velmi pomalu rostoucí nádory, které v horizontu dožití mnohé muže životně neohrozí. Proto se většina urologických společností staví k plošnému vyhledávání rakoviny prostaty rezervovaně a doporučuje tzv. včasnou detekci karcinomu prostaty. Problém je ale potřeba s veřejností komunikovat. Cílem projektu je spolu s partnerskou nadací Movember zvýšit informovanost české populace o problematice prevence, diagnostiky a léčby karcinomu prostaty a varlat a zkvalitnit možnosti diagnostiky a léčby uroonkologických onemocnění v českých nemocnicích. Toho bude docíleno prostřednictvím rozvoje informačního portálu www.rakovinaprostaty.cz, sdružujícího všechny zásadní informace o dané problematice, zabezpečením provozu internetové poradny, vydáním informačních materiálů a vyhlášením grantové soutěže na a nákup přístrojové techniky pro urologická oddělení.', 'Posláním organizace je - zkvalitňování a rozšiřování léčebných možností závažných onemocnění, zejména v oblasti uroonkologie a dalších urologických onemocnění, - podpora spolupráce při zabezpečování potřebného množství a kvality léků a léčebných přípravků, speciálního zdravotnického materiálu a technických pomůcek, - podpora rozvoje všech metod léčby závažných onemocnění, zejména uroonkologických a dalších urologických onemocnění, - poskytování příspěvků na pořízení přístrojové techniky, - podpora vzdělávání a výzkumu v oblasti léčby závažných onemocnění, zejména uroonkologických a dalších urologických onemocnění, - podpora informovanosti odborné a laické veřejnosti o různých aspektech prevence a léčby závažných onemocnění, zejména v urologii, - podpora vydávání specializovaných publikací a populárně naučných textů, kongresů a dalších odborných setkání, - podpora spolupráce s dalšími organizacemi obdobného zaměření v České republice, - poskytování stipendií na odborné stáže, postgraduální vzdělávání, konference a semináře pro lékaře a ostatní zdravotnické pracovníky, - zajištění psychosociální podpory pacientů a jejich rodin. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Na slovíčko Mluvit bez obav', 'Občanské sdružení LOGO', 'OSLOGO', 'Projekt je určen na pomoc dětem s autismem, výrazně opožděným vývojem řeči. Často nemohou sdělit, co je trápí, z čeho mají radost. Cílem je rozvoj jejich komunikačních dovedností, nastavení alternativní komunikace.', 'Posláním Občanského sdružení LOGO je pomoc dětem i dospělým postiženým vážnými poruchami komunikace a hybnosti. Umožnit jim komunikovat s okolím, sdělovat svoje myšlenky, názory a přání. Co nejvíce zkvalitnit jejich život. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Naberte sil - odlehčovací služba pro lidi s mentálním postižením', 'Diakonie ČCE - středisko Rolnička', 'ROLNICKA', 'Rolnička již více než 20 let poskytuje své služby dětem i dospělým s mentálním a kombinovaným postižením, a to i těm s nejtěžším stupněm postižení. Naše služba je založena na přesvědčení, že každý člověk by mělo mít právo a možnost vyrůstat a žít v těsném sepětí se svou rodinou. Péče o těžce postiženého člena rodina je však mnohdy velmi náročná a v zájmu zachování funkční rodiny je pak žádoucí nabídnout službu, která by blízkým postiženého člověka umožnila nabrat nových sil.Proto se Rolnička rozhodla rozšířit nabídku svých služeb o odlehčovací službu. Jedná se o časově omezenou pobytovou službu, která zatím bude otevřena pouze pro naše dospělé klienty, především se středním a těžším stupněm postižení (tedy pro ty, kteří nemohou využívat naší služby chráněného bydlení). Podnět na vznik této služby vzešel přímo od rodin našich klientů, zájemce o službu tedy máme. K dispozici máme i prostory na táborském Sídlišti nad Lužnicí, kde chceme zahájit provoz této služby s kapacitou 4 lůžek. Předpokládaný začátek služby je leden 2017.Peníze získané prostřednictvím DMS využijeme na pořízení vybavení potřebného pro provoz této služby a pro zahájení provozu.', 'Jsme různí, ale patříme k sobě, každý člověk je jedinečný a zasluhuje respekt. Rolnička je křesťanské centrum poskytující sociální, vzdělávací, terapeutické, pracovní a rehabilitační služby šité na míru dětem a dospělým s různými typy postižení a jejich rodinám z Táborska, především z okolí Soběslavi. Rolnička je tu proto, aby každý mohl s potřebnou pomocí dosáhnout osobního maxima a prožít celý život v sepětí s rodinou, přáteli a známými tam, kde se cítí doma. Potřebujeme k tomu Vaší podporu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nadace Terezy Maxové dětem', 'Nadace Terezy Maxové dětem', 'TEREZADETEM', 'Cílem projektu Nadace Terezy Maxové dětem je snížení počtu dětí vyrůstajících v ústavní výchově a také kompenzace nedostatků ústavní péče u dětí, které nemohou vyrůstat v rodině. Projekt je určen všem, kdo poskytují účinnou a efektivní podporu opuštěným a znevýhodněným dětem. Cílovými příjemci mohou být - neziskové organizace, kojenecké ústavy, dětské domovy, diagnostické ústavy, děti v ústavní výchově a v pěstounské péči.Prostředky z DMS budou rozděleny v otevřeném grantovém řízení, v rámci třech pilířů činnosti nadace.Každoročně dojde k evaluaci grantovaní a nastavení priorit pro daný kalendářní rok. ', 'Posláním nadace je umožnit každému dítěti zdravý a bezpečný život v rodinném prostředí. V České republice je však stále 24 000 dětí odkázáno na ústavní výchovu. Jednoznačným cílem nadace je proto snížení počtu dětí v ústavech, a to prostřednictvím tří směrů pomoci, jimiž jsou: ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nadační fond Fragilis', 'Nadační fond Fragilis', 'FRAGILIS', 'Nadační fond Fragilis má za cíl podporovat prostřednictvím pečlivě vybraných českých projektů neziskových organizací jakkoli znevýhodněné osoby a přispívat k jejich začleňování do běžné společnosti.', 'Posláním nadačního fondu Fragilis je vyhledávání a následná podpora projektů v Čechách i na celém světě, které řeší problematiku znevýhodněných osob a přispívají k jejich začleňování do běžné společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nadační fond Kolečko', 'NADAČNÍ FOND KOLEČKO', 'KOLECKO', 'V České republice se ročně zraní asi 300 000 dětí, až 3000 z nich má po úrazu trvalé následky. Snažíme se zlepšit podmínky jejich léčení tím, že finančně podporujeme všech osm center dětské traumatologie. Nakupujeme léčebné a diagnostické přístroje, zdravotní a rehabilitační pomůcky nebo zútulňujeme prostředí ambulancí a lůžkových oddělení.', 'V rámci našich preventivních programů učíme děti a mladé lidi bezpečnému chování v silničním provozu a při sportovních aktivitách. Samozřejmě nedokážeme zabránit všem úrazům, proto se snažíme zlepšit podmínky léčení a zpříjemnit pobyt v nemocnici tím, že finančně podporujeme tzv. centra dětské traumatologie, která léčí pacienty od 0 do 18 let věku po těžkých úrazech. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nadační fond Šestý smysl', 'Nadační fond Šestý smysl', 'SESTYSMYSL', 'Účelem projektu je získání peněžitých příspěvků ve prospěch podpory občanů z Prahy 6 a to především sociálně slabých spoluobčanů, rodin s handicapovanými dětmi, dětí z dětských domovů apod.', 'NF byl zřízen za účelem podpory dlouhodobého rozvoje Prahy 6 a jejích občanů, a to zejména podpory v sociální oblasti a v oblasti kultury, vědy, vzdělávání, zdravotnictví, tělovýchovy a sportu. Naším hlavním cílem je podporovat sociálně slabé spoluobčany, rodiny s handicapovanými dětmi, děti z dětských domovů, začínající sportovce, umělce atp. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nadační fond Umění doprovázet pomáhá lůžkových hospicům v ČR', 'Nadační fond na podporu rozvoje hospicového hnutí v ČR Umění doprovázet', 'PROHOSPICE', 'Cílem projektu je získat finanční prostředky na nákup chybějících zdravotnických pomůcek, které pomáhají zlepšit kvalitu života těžce nemocných pacientů v jeho závěrečné fázi. Jedná se např. o automatická polohovací lůžka, kontinuální dávkovače léků, oxygenerátory (přístroje k aplikaci kyslíku), antidekubitní matrace, invalidní vozíky, toaletní vozíky, chodítka, apod.', 'Poslání a cíle Nadačního fondu Umění doprovázet:* zlepšení informovanosti veřejnosti o hospicích a hospiovém hnutí v ČR* materiální podpora stávajících a nově vznikajících hospiců v ČR* podpora vzdělávání odborného personálu v oblasti hospicové a paliativní péče ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Naděje', 'NADĚJE', 'NADEJE', 'Pomáháme lidem bez domova při opětovném zapojení do života společnosti, i lidem, kteří jsou sociálním vyloučením bezprostředně ohroženi. Příčin, kvůli kterým se lidé dostanou do nepříznivé sociální situace, vedoucí k bezdomovectví, je mnoho. Mohou to být materiální, vztahové, osobní, nebo institucionální problémy. Mezi našimi klienty jsou lidé ve středním věku, ale i senioři a mladí dospělí, muži i ženy. Součástí našeho Integračního programu jsou terénní, ambulantní a pobytové služby, tedy poskytování stravy, ošacení, hygieny, péče praktického lékaře, ubytování, sociálního a pracovního poradenství a psychologické podpory.', 'Cílem činnosti NADĚJE je vybudování a provozování sítě služeb lidem v nouzi na křesťanských principech. Ve svých zařízeních i mimo ně poskytuje NADĚJE pomoc duchovní, morální, sociální, zdravotní, lékařskou, hygienickou, stravovací, ubytovací, hmotnou, právní, poradenskou, osvětovou, vzdělávací, výchovnou, apod., včetně doplňkových služeb. Své služby zaměřuje zejména na lidi osamělé, zdravotně postižené, společensky vyloučené nebo jinak znevýhodněné. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Naděje pro románský kostel sv. Šimona a Judy v Lenešicích', 'Lenešický okrašlovací spolek, o.s.', 'SIMONAJUDA', 'Jedná se o jedinečný projekt záchrany kulturní památky, na kterém se podílejí přední odborníci. V červenci roku 2008 došlo k pádu věže románského kostela sv. Šimona a Judy v Lenešicích na Lounsku. Jak dokládá nápisový kámen na věži, byl kostel postaven benediktiny z nedalekého kláštera Porta Apostolorum v Postoloprtech, který zanikl během husitských válek. Lenešický kostel je tak posledním dokladem vyspělosti tamní stavební huti.Od roku 2009 probíhá jeho záchrana. Zpevněny byly základy lodě kostela a budoucí věže. Obvodové zdivo téměř dosahuje výšky krovů.V roce 2012 byla stěnou oddělena loď kostela od věže. Na svátek sv. Šimona a Judy v říjnu 2012 se proběhl koncert, který po 4 letech tyto duchovní prostory otevřel.Souběžně se stavebními pracemi probíhá restaurování inventáře kostela. V současné době je zrestaurován oltářní obraz a v roce 2013 se uskuteční oprava oltáře.', 'Posláním je působit v obci Lenešice a okolí a svou činností přispívat k ochraně kulturního a historického dědictví a podpoře cestovního ruchu.Hlavním cílem našeho sdružení je obnova románského kostela sv. Šimona a Judy v Lenešicích. Kostel sv. Šimona a Judy patří patrně k posledním dochovaným stavbám klášterní stavební huti postoloprtského kláštera Porta Apostolorum. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nadějné vyhlídky pro osoby po mozkové příhodě', 'ERGO Aktiv, o.p.s.', 'CHUTKZIVOTU', 'Podporou projektu "Nadějné vyhlídky" přispějete k zajištění komplexní odborné pomoci osobám po mozkové příhodě a jiném získaném poškození mozku. Pomůžete těmto lidem, aby měli zase chuť žít. Cílem služby je vytvořit podmínky a rozvíjet dovednosti, které klientům pomohou v návratu do aktivního života, v nejlepším případě i zaměstnání. Při práci s klientem využíváme koncept založený na posilování motivace klienta, jeho aktivitě a převzetí odpovědnosti za výsledek terapie. Problematika osob se získaným poškozením mozku je komplexní a na řešení potíží s mobilitou, samostatností, kognitivními funkcemi, komunikací a získáním zaměstnání pracuje multidisciplinární tým odborníků z oblasti fyzioterapie, ergoterapie, sociálního poradenství, psychoterapie, logopedie a dalších. Celý proces a postup rehabilitace je systematický a koordinovaný.', 'Posláním naší organizace je poskytovat lidem po cévní mozkové příhodě či jiném získaném poškození mozku specifickou komplexní podporu a následnou péči, která jim pomůže při návratu do aktivního života a zaměstnání.Náš sen: Každý člověk se získaným poškozením mozku najde své smysluplné naplnění, místo a hodnotu.Náš cíl: Návrat osob se získaným poškozením mozku (ZMP) do aktivního pracovního i společenského života a převzetí odpovědnosti klientů za svůj vlastní aktivní život ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nákup kompenzačních pomůcek', 'Dotek, o.p.s.', 'DOTEK', 'Cílem projektu je získat prostředky na nákup kompenzačních pomůcek pro klienty odlehčovací služby Dotek, o.p.s. a také pro klienty, kteří si kompenzační pomůcky půjčují do domácnosti, kde o ně pečuje rodina.', 'Posláním naší obecně prospěšné společnosti je citlivě a individuálně pečovat o seniory, kterým zdravotní stav neumožňuje samostatnost, s ohledem na jejich potřeby v bio-psycho-socio-spirituální rovině a s ohledem na jejich životní příběhy v návaznosti na prostředí, kde žijí či žili. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Náprava -přeměna- smíření', 'Mezinárodní vězeňské společenství, z.s.', 'NAPRAVA', 'Projekt Náprava -přeměna- smíření zahrnuje všechny, kterých se zločin dotýká: oběti, pachatele, odsouzené, propuštěné, a rodiny všech. Pachatelé potřebují péči před výstupem z věznice, aby se už nevraceli ke stejným vzorcům chování. MVS vede ve věznicích kurzy pro odsouzené a mění jejich hodnotové žebříčky směrem k přijímání odpovědnosti za zločin a skutečnou nápravu. Propuštění se v prvních dnech a měsících znovu dostávají do ohrožení zopakování zločinu a potřebují doprovod a vytvoření nového kvalitního zázemí. MVS čeká nejpotřebnější u bran věznice a pečuje po ně do okamžiku, než se postaví na vlastní nohy. Sociálně slabí propuštění čekají 2 měsíce na dávky hmotné nouze a přitom je nebezpečí se opět někomu ublíží. MVS vede nízkoprahový klub pro propuštěné, jejich rodiny a přátele. Scházejí se tam i bezdomovci, jakkoliv správná prevence vede ke snížení jak rizik recidiv, tak bezdomovectví. O Vánocích MVS posílá dárky dětem, které mají rodiče ve VTOS. Tyto děti jsou neviditelnými oběťmi zločinu, zažívají nepřítomnost rodiče a velký sociální propad rodiny i vlastní stigmatizaci. Finanční podpora se týká všech oblastí projektu Náprava - přeměna-smíření. Je to podpora bydlení, hrazení cest pro doklady, úhrada léků ( pokud není jiná možnost), pomoc s doprovodem do místa bydliště, provoz Klubu 2 Ryby, kontakt s propuštěními po telefonu, pomoc dětem odsouzených.', 'Spolek pomáhá odsouzeným, propuštěným, obětem a rodinám všech, kterých se dotkl zločin, zejména dětem odsouzených. Snahou je prevence kriminality, prevence bezdomovectví a cesta k uzdravování obětí. MVS je křesťanské mezidenominační sociální hnutí. Spolek připravuje odsouzené na propuštění v jednoročním kurzu v několika věznicích. Po propuštění čeká sociálně slabé propuštěné a pomáhá při další resocializaci. V projektu Building Bridges MVS vede setkání skupin nesouvisejících pachatelů a obětí, kteří se setkávání k tzv restorativnímu dialogu uvnitř věznice . Projekt je zaměřen na pomoc obětem trestných činů a startuje jejich ozdravovací procesy, a u odsouzeným pomáhá v přijetí jejich odpovědnosti za minulé jednání a hledání cest ke kompenzaci činu a k pozitivní změně náhledu na zločin. . MVS čtvrtým rokem vede nízkoprahový klub pro propuštěné v Praze. MVS posílá o Vánocích vánoční dárky dětem odsouzených rodičů v projektu Andělský strom , spolu osobním dopisem rodiče z vězení. MVS zajišťuje službu dopisování, osobních návštěv ve věznicích, a zasílá nárokové balíčky sociálně slabým odsouzeným. MVS je stabilní a rychle rostoucí organizace, založená na dobrovolnické pomoci zejména křesťanů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Národní centrum biodiverzity', 'Česká krajina, o.p.s.', 'KRAJINA', 'Projekt "Národní centrum biodiverzity" patří mezi největší ochranářské projekty v České republice a vytvoří v Česku jakousi Noemovu archu ohrožených druhů živočichů a rostlin. Projekt vzniká ve spolupráci s experty Ústavu biologie obratlovců Akademie věd ČR, Jihočeské univerzity a Biologického centra Akademie věd ČR."Národní centrum biodiverzity" se zaměřuje na dvě oblasti. První je vysazování stromů a tím vytváření nových přírodních celků na pozemcích, kde se již zemědělcům nevyplácí hospodařit. Tyto pozemky jsou vykupovány a přeměňovány na přirozené lesy a přírodní louky. Druhým cílem "Národního centra biodiverzity" je vracet do přírody druhy, které byly v minulosti na území České republiky vyhubeny. V první fázi připravujeme projekty zaměřené na návrat zubra, divokých koní, pratura, kočky divoké, divokých včel, divokého osla a dalších druhů. Součástí projetku je rovněž obnova původních prostředí pro tyto druhy.', 'Obecně prospěšná společnost Česká krajina je celostátní nezisková organizace působící v oblasti ochrany přírody, krajiny a životního prostředí. Jejím hlavním cílem je ochrana biologické rozmanitosti (biodiverzity) a zmírňování dopadů klimatických změn na přírodu i člověka a adaptace krajiny na změny klimatu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Návrat divokých koní', 'Zoologická zahrada hl. m. Prahy', 'PREVALACIMONGOLSKO', 'Nedílnou součástí moderní zoo je osvětová činnost v souvislosti se záchranou ohrožených druhů ve volné přírodě Zoo Praha tímto projektem dává najevo, že nemyslí jen na vlastní potřeby a stává se v dobrém slova smyslu reklamní a produkční agenturou pro ohrožené druhy Prostřednictvím veřejné sbírky, kde jsou DMS jedním z možných platebních nástrojů. Návštěvníci Zoo Praha (1,3 mil./rok) i široká veřejnost Dlouhodobý projekt navazující na sbírku Pomáháme jim přežít Sbírkou nabyté prostředky hodlá Zoo Praha poskytnout na úhradu leteckého transportu koní Převalského a s ním souvisejících výdajů v rámci tohoto projektu. Projekt jedinečným způsobem vtahuje návštěvníky zoo, zejména děti a mládež, do problematiky ochrany fauny a flóry doma i ve světě. Jednoduchost a rychlost poskytnuté pomoci díky DMS (a dalším souvisejícím platebním metodám) má šanci pochopit 1 300 000 návštěvníků Zoo Praha ročně. V ČR se jedná o nejnavštěvovanější instituci svého druhu.', 'Zoologické zahrady dnes působí ve světě, v němž je stále více ohrožována druhová pestrost přirozených ekosystémů neboli biodiverzita. Jsou to právě tyto instituce, které mají možnost postihnout celý rejstřík ochranářských aktivit, od výzkumu přes ochranu ex-situ (včetně chovu ohrožených druhů a výchovné činnosti) po účast na projektech in-situ přímo v terénu.Výchova a vzdělávání Každým rokem přichází do zoologických zahrad na celém světě více než 600 milionů návštěvníků nejrůznějšího věku i zájmů. V pražské zoo se během posledních tří let počet návštěvníků zdvojnásobil na více než 1,2 milionu osob ročně. Je to velká výzva i závazek. Zoologická zahrada tak ovlivňuje svými výchovnými a vzdělávacími aktivitami širokou veřejnost i zájmové skupiny. Je schopna díky neocenitelnému potenciálu živých zvířat působit u návštěvníků na jejich názory, postoje, vytváření hodnot a chování.Chov zvířat Pražská zoo jako moderní instituce svého druhu vytváří pro chované živočichy co nejoptimálnější podmínky, a to jak koncepcí a ztvárněním výběhů i ubikací, tak sestavováním jedno- či vícedruhových skupin. Návštěvníci si tak mohou vytvořit představu o životě zvířat a jejich vazbách k prostředí, zvířata pak mají příležitost rozvinout co nejširší rejstřík přirozeného chování.Ochrana ex-situ Zoo Praha se dlouhodobě podílí na ochraně živočichů ex-situ. Vytváří pojistné chovné skupiny u druhů, které jsou v přírodě bezprostředně ohrožené, nicméně u kterých již probíhají snahy o zpětný návrat nebo je lze očekávat.Ochrana in-situ Stále výrazněji se pražská zoo podílí na projektech in-situ, jejichž cílem je ochrana živočichů přímo v místě jejich výskytu. Jedná se o zapojení do reintrodukcí ohrožených druhů do rezervací i volné přírody nebo o metodickou a materiální pomoc v těchto oblastech. Programy se týkají mimo jiné koně Převalského, gorily nížinné, adaxe, ibise skalního, zubra, čápa černého, orlosupa bradatého, supa hnědého, užovky podplamaté, sysla obecného, kvakoše nočního, puštíka bělavého, sovy pálené a sýčka obecného. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Návrat lososů', 'České Švýcarsko, o. p. s.', 'NAVRATLOSOSU', 'Není to dávno, co lososi běžně obývali české řeky. Člověk intenzivním lovem, stavbami vodních děl a znečišťováním tyto původní obyvatele vyhubil. Nyní lososi dostávají druhou šanci. Díky adoptivním rodičům.', 'Posláním obecně prospěšné společnosti České Švýcarsko je šetrný rozvoj regionu České Švýcarsko. Mezi základní cíle organizace patří posílit environmentální rozvoj regionu založený na ochraně přírodních a kulturních hodnot, posílit ekonomický rozvoj regionu založený na šetrné turistice a pozitivní prezentaci a posílit sociální rozvoj regionu založený na znalostech a vzdělání. Společnost realizuje tyto hlavní programové větve: ochrana přírodních a kulturních hodnot, odpora šetrného cestovního ruchu a přeshraniční spolupráce, tvorba a realizace regionálních projektů, propagace a informačně-publikační aktivity a vzdělávání a osvěta. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nebojte se volat o pomoc', 'Cesta z krize, z.ú.', 'VOLANIOPOMOC', 'Projekt je zaměřen na odbornou telefonickou krizovou pomoc (1) dětem, které jsou jakkoliv ohroženy (útěkem či únosem, šikanou, neshodami v rodině, výchovnými problémy atd.) Kontaktovat linku 116 000 může non stop a zdarma kdokoliv, kdo má chuť takto ohroženému dítěti pomoci. (2) všem lidem, kteří se nacházejí v krizi a potřebují pomoc a nasměrování, aby byli schopni krizové období zvládnout. Těmto lidem je určena linka 116 123, která je v provozu zdarma v omezeném rozsahu každý den.', 'Zabýváme se problematikou pohřešovaných a jinak ohrožených dětí, včetně prevence. Uvedli jsme do ČR evropskou krizovou linku 116 000 pro děti a jejich blízké pod názvem Linka Ztracené dítě. Chceme také oživit myšlenku jednotného telefonního čísla pro snadno dostupnou krizovou telefonickou pomoc pro dospělé. Volající, kteří nespadají do cílové skupiny rodin s dětmi, se mohou obrátit na Linku první psychické pomoci, kterou provozujeme na čísle 116 123. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nemocnice sester boromejek', 'Nemocnice Milosrdných sester sv. Karla Boromejského v Praze', 'BOROMEJKY', 'Nemocnice Milosrdných sester sv. Karla Boromejského v Praze trvale usiluje o vysokou kvalitu poskytovaných zdravotních služeb i o profesionální a zároveň hluboce lidský přístup k nemocným i jejich blízkým. Tento rozměr péče není ohodnocen v úhradách zdravotních pojišťoven, nejen proto hledáme další finanční zdroje, které nám pomohou obnovovat vybavení nemocnice a zušlechťovat prostředí našim pacientům.', 'Nemocnice Milosrdných sester sv. Karla Boromejského v Praze je nestátní zdravotnické zařízení, které je výjimečné nejen svojí polohou v blízkosti Pražského hradu, ale především historickou vazbou nemocnice a Kongregace Milosrdných sester sv. Karla Boromejského, jenž určuje duchovní rozměr a osobnější přístup v péči o nemocné. Nemocnice pečuje o pacienty nepřetržitě již 160 let, a v současné době je v České republice jedinou církevní nemocnicí s akutní péčí. Nemocnice poskytuje ambulantní a lůžkovou, základní a specializovanou diagnostickou a léčebnou péči v řadě oborů včetně lékárenství a doplňkových služeb. A to všem pacientům bez rozdílu vyznání, politické či rasové příslušnosti. V nemocnici se nachází mimo standardní oddělení: interní, chirurgické, gastroenterologické, rehabilitační, anesteziologicko-resuscitační, radiodiagnostické, oddělení následné péče, také Dětské a dorostové detoxikační centrum a oddělení paliativy, které pečuje o pacienty v terminálním stádiu jejich života. Pacienti odcházející po propuštění do svého domova mohou využít Domácí zdravotní péče naší nemocnice. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nemůžeš chodit,(ne)můžeš tančit!', 'Občanské sdružení Bílá holubice', 'HOLUBICE', 'Výběr DMS bude odstartován na  4. ročníku koncertu Advent na trůnech, který je společným vystoupením tanečního souboru občanského sdružení Bílá holubice a ostravského barda Jarka Nohavici. Koncert se bude konat 6.12.2015 v 19 hodin v Domě kultury města Ostravy. Předpokládaná účast 600 návštěvníků. Posláním koncertu je vyvolat v divákovi pocit, že handicapovaní umělci jsou standardní součástí uměleckého zážitku , kteří dokáží nejen dojímat, ale také bavit. Prostředky, které budou vybrány prostřednictvím dárcovských DMS použije občanské sdružení na úhradu nákladů, které jsou spojeny s účinkováním v tanečně-divadelních projektech a rozvoj PR aktivit v roce 2016. Naše představení průměru navštíví představení 200 - 250 diváků.', 'Sdružení usiluje o co nejvyšší kvalitu života dětí a mladých lidí s tělesným postižením, a to prostřednictvím spolupráce s profesionály z oblasti pohybového a divadelního umění. Sdružení funguje již devátým rokem jako zastřešující organizace tanečního souboru "Bílá holubice", který spojuje handicapované a zdravé tanečníky. V současné době má repertoáru šest celovečerních představení a mnoho kratších vystoupení. Dlouhodobým cílem a vizí sdružení je umělecká profesionalizace handicapovaných členů souboru. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('NEZAPOMENU - Podpora výzkumu Alzheimerovy choroby', 'Alzheimer nadační fond', 'NEZAPOMENU', 'Nedoufejte v zázračný lék, pomozte nám ho najít! Podpořte výzkum Alzheimerovy choroby a dalších neurodegenerativních nebo cévních onemocnění mozku, které vedou k poruchám kognitivních funkcí a demenci. Získané prostředky budou využity na podporu profesionálů pečujících o pacienty s Alzheimerovou chorobou.', 'Podporujeme odborníky pečující o nemocné Alzheimerovou chorobou. Naším cílem je, aby v České republice byl dostatek kvalitních profesionálů - lékařů, pečovatelů, vědců, kteří by se problematice Alzheimerovy choroby a dalším typům neurodegenerativních onemocnění věnovali. Podporujeme výzkum a vzdělávání, založili jsme stipendijní program pro mladé lékaře. Chceme tak zlepšit kvalitu péče o pacienty i podmínky, za kterých je poskytována. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Nový Prostor', 'Nový Prostor, z.ú.', 'NP', 'Cílem projektu je získat peněžité příspěvky na pomoc a podporu uživatelů sociální služby (prodejců časopisu Nový Prostor)- sociální rehabilitace (část úvazku sociálních pracovníků, úhrada za odborné služby pro uživatele - např. právník, psycholog apod., příspěvky na ubytování či dopravu pro uživatele, příspěvek na úhradu základních potřeb uživatelů, úhrada výchovných, vzdělávacích a ostatních akcí pro uživatele, příspěvek na materiální vybavení sociální služby). Dále potraviny, nápoje a motivační odměny pro uživatele, alkotester s příslušenstvím (včetně jeho provozu a údržby), příspěvky na sprchu, materiál pro identifikaci uživatelů.', 'Nový Prostor dává možnost pracovat všem, kteří pracovat chtějí a mohou, ale jinde příležitost nenašli. Pomáháme preventivně osobám, které se mohou dostat do tíživé životní situace. Realizujeme sociálně prospěšné projekty. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Oblastní charita Hradec Králové', 'Oblastní charita Hradec Králové', 'CHARITAHK', 'Provoz, investice a vybavení sociálních služeb: azylový dům, pečovatelská služba, noclehárna, sociální rehabilitace, nízkoprahová denní centra, odborné sociální poradenství; Podpora: hospicové péče, ošetřovatelské služby, sociálního šatníku, pořádání kulturních a společenských aktivit ve prospěch uživatelů služeb, konání akcí v rámci humanitární pomoci (nenadále katastrofy, krizové situace), individuální pomoc sociálně slabým.', 'Posláním Oblastní charity Hradec Králové /OCHHK/ je pomáhat lidem v hmotné, sociální, duševní a duchovní nouzi bez ohledu na jejich příslušnost k rase, národnosti nebo náboženskému vyznání, a to v Hradci Králové a okolí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Obnova Bezručovy chaty na Lysé hoře', 'KČT oblast Moravskoslezská', 'GIGULA', 'Obnova Bezručovy chaty je v rámci KČT v celé ČR akcí číslo jedna. Nová chata bude návštěvníkům Lysé hory poskytovat základní servisní služby, restaurační služby, rychlé občerstvení a  ubytování v turistické kategorii. Chata přispěje ke zlepšení infrastruktury vrcholové části Lysé hory a k zlepšení kultury prostředí. Stavba je projektovaná v kvalitě nízkoenergetického objektu, s odpovídající akumulační technikou a ekologicky šetrnými zdroji energií, tj. tepelné čerpadlo, solární panely, rekuperace a pod. Bezručova chata a značené turistické cesty budou vybaveny informačními a naučnými panely se zaměřením na poznání a ochranu přírody a krajiny.', 'Posláním KČT je vytváření všestranného turistického programu, včetně mezinárodních turistických aktivit, vytváření materiálních, organizačních a metodických podmínek pro bezpečný a volný pobyt v přírodě, značení a údržba sítě turistických tras, ochrana přírody a krajiny, péče o kulturní památky, vytváření podmínek pro činnost seniorů, rodin a mládeže, pomoc novým členům klubu a rozvoj jeho tradic. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Obnova kaple na poutní cestě do Hájku', 'Poutní cesta Hájek', 'KAPLE', 'Cílem projektu je postavení zřícené kaple č. 18 na poutní cestě do Hájku. V roce 2015 byly provedeny první stavební práce: byly dokončeny nutné terénní úpravy a základy ke kapli. V letošním roce bychom rádi zajistili dostatečné množství finančních prostředků, aby mohly být zahájeny práce na restaurování dochovaných kamenných prvků a výroba těch chybějících.', 'Spolek Poutní cesta Hájek usiluje o záchranu a propagaci historické poutní cesty, která vede z Břevnova v Praze k loretě do Hájku u Červeného Újezda. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Obnova zemětřesením poničených nepálských vesnic', 'Namasté Nepál, z.s.', 'NAMASTE', 'Zemětřesení, které Nepál zasáhlo na jaře 2015, připravilo velkou část Nepálců o střechu nad hlavou. Velké škody napáchalo i v oblastech, kde naše organizace dlouhodobě realizuje své programy rozvojové pomoci. Nejefektivnější způsob rozvojové pomoci je vzdělávání. Pomáháme tedy nepálským stavařům získat znalosti o seismicky odolném stavění. Členové našeho týmu prošli školením organizovaném National Society for Earthquake Technology, čímž získali oprávnění k předávání znalostí o seismicky odolném stavění dalším stavařům, zedníkům, ale i obyčejným lidem, kteří si chtějí svépomocí obnovit svůj zbořený domek. A to je náš cíl. Pomáháme obnovit nepálské domy tak, aby vydržely případná další zemětřesení.', 'Posláním organizace je rozvojová, humanitární, vzdělávací a kulturní podpora lidí v Nepálu a přibližování života lidí v Nepálu lidem v ČR. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Ochrana zvířat', 'Nadace na ochranu zvířat', 'OCHRANAZVIRAT', 'Cílem projektu Ochrana zvířat je trvalé zlepšení vztahu člověk-zvíře-životní prostředí. Nadace pomáhá zmírňovat utrpení a zajišťovat životní pohodu zvířat v lidské péči i zvířat volně žijících.Získané finanční prostředky z DMS budou přerozděleny organizacím zabývajícím se ochranou zvířat - spolkům na ochranu opuštěných a týraných zvířat, útulkům pro psy a kočky, útulkům pro přestárlá a handicapovaná zvířata, záchranným stanicím apod., a to zejména na nákup krmiva, vybavení kotců a také na veterinární péči a léčbu, sterilizaci a kastraci koček apod.', 'Posláním Nadace na ochranu zvířat je trvale zlepšovat vztah člověk-zvíře-životní prostředí, pomáhat zmírňovat utrpení a zajišťovat přijatelné životní podmínky zvířat v lidské péči, držených v zajetí i zvířat volně žijících. Toto poslání vychází z přesvědčení, že zvířata jsou cítící bytosti, které si zasluhují pozornost, péči a ochranu ze strany člověka. Nadace vznikla v roce 1994, její patronkou je paní Marta Kubišová, zpěvačka. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Otvírám tvé srdce bílým klíčem', 'Bílý klíč porozumění', 'VIDIME', 'Pomůžete nám pomáhat. Myslíte si, že je možné, aby i nevidomí byli samostatní? My se o to snažíme a s vaší pomocí se nevidomí a těžce zrakově postižení snáze začlení mezi intaktní veřejnost.', 'BKP vznikl ze snahy občanů zlepšit životní podmínky nevidomých a slabozrakých spoluobčanů. Cílem je usnadňovat život nevidomých a těžce zrakově postižených osob a prostřednictvím sbírek, sponzorských darů a dotací umožnit zrakově postiženým občanům vybavení kluboven (2) a účast na spolkovém životě, dalším vzdělání, možnost účasti na rekondičních pobytech apod.Sbírka je zřízena za účelem získání finančních prostředků na sociálně aktivizační služby pro osoby se zrakovým postižením, na rozšíření půjčovny kompenzačních pomůcek, poradenství a informační servis, vydávání tematicky zaměřených bulletinů, na osvětové aktivity.   ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Paměť národa', 'POST BELLUM o.p.s.', 'PAMET', 'Paměť národa je projektem společnosti Post Bellum, Českého rozhlasu a Ústavu pro studium totalitních režimů, v rámci kterého dokumentaristé zaznamenávají vzpomínky pamětníků významných událostí 20. století. Kolekce nahrávek, doplněná fotografiemi a archivními materiály, je zpřístupněna na internetové stránce www.pametnaroda.cz. V současnosti zde lze najít více než 3000 vzpomínek, jejichž počet se neustále rozrůstá. Jedná se tak o jednu z největších veřejně přístupných pamětnických sbírek v Evropě. Paměť národa slouží nejen badatelům, historikům či novinářům, ale i široké veřejnosti, která se tak prostřednictvím osobních příběhů seznamuje s fenomény moderních dějin.', 'Společnost Post Bellum mimo jiné vysílá rozhlasový pořad Příběhy 20. století, každoročně uděluje Ceny paměti národa lidem, kteří měli odvahu postavit se zlu, provozuje mobilní aplikaci Místa paměti národa a organizuje vzdělávací projekty na školách Příběhy našich sousedů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Parkinson - už vím ....', 'Parkinson-Help z.s.', 'PARKI', 'Projekt je zaměřen na vzdělávání pacientů, na šíření osvěty mezi laickou i odbornou veřejností. Chceme vydávat aktuální informace a zároveň natočit edukační materiál pro ošetřující personál a pro rodinní příslušníky. Plánujeme bořit stigmata o nemoci a to všemi dostupnými, a na trhu běžnými, metodami.', 'Posláním organizace je seznamovat širokou veřejnost s obtížemi onemocnění a získávat lidi vstřícné přispívat ke zkvalitnění života nemocných nevyléčitelnou Parkinsonovou nemocí a bořit stigmata ve společnosti o tomto onemocnění. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora lidí ohrožených existenční nouzí', 'Diecézní charita Brno', 'DCHB', 'Cílem projektu je podpora lidí v nouzi z důvodu postižení, dlouhodobé ztráty zaměstnání, rodičovské dovolené, stáří, příslušnosti k národnostní menšině nebo z důvodu nenadálé krizové události. Charita těmto lidem nabízí odborné sociální poradenství, které jim pomáhá v překonání krizových situací a k opětovnému začlenění do společnosti. Konkrétním příkladem pomoci lidem v mimořádné tísni jsou stravenky na základní potraviny nebo jízdenky k nutné dopravě domů či na úřady.', 'Od roku 1992 pomáhá lidem, kteří se o sebe sami nemohou postarat, ať již v důsledku stáří, zdravotního handicapu nebo nepříznivých sociálních podmínek. Pomáhá také obětem živelných pohrom a civilizačních neštěstí v ČR (povodně 1997, 2002, 2009) i v zahraničí (zemětřesení na Haiti 2010, pomoc lidem na Ukrajině nebo v Moldavsku). Diecézní charita Brno je součástí celostátní Charity ČR a působí na území brněnské diecéze (kraj Vysočina, Jihomoravský kraj). Poskytuje zde 130 zdravotních a sociálních služeb, které každoročně využije zhruba 50 000 uživatelů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora programů SOS dětských vesniček', 'SOS dětské vesničky, z.s.', 'VESNICKY', 'Získané finanční prostředky budou použity v programech SOS dětských vesniček zaměřených na pomoc ohroženým dětem a rodinám s dětmi a na podporu pěstounské péče v ČR. SOS dětské vesničky patří mezi nejvýznamnější neziskové organizace v České republice. Ročně pomáhají stovkám rodin ohrožených odebráním dítěte, stejně jako týraným, zneužívaným a zanedbávaným dětem, které ve své původní rodině nemohou vyrůstat. SOS dětské vesničky poskytují komplexní služby sahající od prevence odebírání dětí z rodin přes okamžitou pomoc dětem v akutní nouzi až po podporu pěstounských rodin či pomoc sociálně znevýhodněným mladistvým se vstupem do samostatného života. Programy organizace jsou v rámci lokálních zdrojů ze 60 procent financovány z příspěvků soukromých dárců a lze je podpořit ve veřejné sbírce na účtu: 211757409/0300.Programy SOS dětských vesniček: http://www.sos-vesnicky.cz/nase-cinnost/', 'SOS dětské vesničky, z.s. zajišťují náhradní rodinnou péči (v podobě skupinové pěstounské péče) dětem, jejichž vlastní rodina selhala. Organizace vyhledává a školí SOS pěstouny/ky, kteří pak v SOS dětské vesničce vychovávají svěřené děti v rodinném prostředí. Každá pěstounská rodina sestává z 3-7 dětí.Činnost SOS dětských vesniček, z.s. byla po předchozím období pod pravomocí státu obnovena po roce 1989. V 90. letech převzalo Sdružení SOS dětských vesniček zpět zařízení/vesničky v Karlových Varech - Doubí a v Chvalčově a úspěšně realizovalo výstavbu vesničky v Brně - Medlánkách.V roce 2003 vznikl také program následné péče pro děti z SOS dětských vesniček, tzv. Komunita mládeže. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora rodin dětí s postiženým od narození do 7 let věku', 'Společnost pro ranou péči o.s.', 'DOMOVJEDOMA', 'Cílem projektu je podpora rodin dětí s postiženým do 7 let věku, kterou zajišťuje Společnost pro ranou péči na celém území ČR, prostřednictvím středisek rané péče. Podpora je zaměřena na odbornou pomoc celé rodině (dítěti i rodičům) v domácím prostředí. Výsledkem je možnost vychovávat postižené dítě nebo dítě s ohroženým vývojem v domácím prostředí. Získané finanční prostředky budou využity pro zvýšení počtu podporovaných rodin na celém území ČR a k rozvoji poskytovaných služeb (např. logopedie, fyzioterapie, psychologie). Část prostředků bude věnována na nákup didaktických pomůcek pro rozvoj psychomotorických dovedností dětí.', 'Společnost pro ranou péči poskytuje odbornou pomoc a poradenství rodinám, které pečují o dítě s postižením nebo s ohroženým vývojem. Provázíme rodiny v obtížné životní situaci a usilujeme o to, aby mohly vychovávat dítě v jeho přirozeném domácím prostředí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora rozvoje programu transplantací kostní dřeně', 'Nadace pro transplantace kostní dřeně', 'KOSTNIDREN', 'Cílem projektu Nadace pro transplantace kostní dřeně je podpora rozvoje programu transplantací kostní dřeně a navazujících projektů (vyšetření potencionálních dárců a jejich zařazení do databáze, edukace, příspěvky na krytí zvýšených životních nákladů během a po alogenní transplantaci, výstavba nového transplantačního centra při FN v Plzni a ČNRDD)', 'Nadace pro transplantace kostní dřeně podporuje rozvoj transplantací kostní dřeně a krvetvorných buněk v ČR a programy ke zlepšení péče a léčebných výsledků u nemocných s těžkými chorobami krve a zhoubnými nádory v České republice. Grantové příspěvky směřuje v současné době do následujících oblastí: •    rozvoj dárcovství kostní dřeně v ČR •    programy na podporu nemocných po transplantaci a onkologické terapii •    vzdělávání v hematologii, onkologii a transplantační medicíně •    investice do přístrojové techniky a vybavení specializovaných pracovišť •    výzkum v oblasti hematologie, onkologie a transplantační medicíny ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora Sarkoma Centra v Motole', 'Vojenský a špitální Řád svatého Lazara Jeruzalémského - Bohemia', 'SARKOM', 'Projekt je zaměřen na finanční podporu Centra pro léčbu sarkomů měkkých tkání, které se zabývá léčbou maligních nádorů. Finanční prostředky budou poukazovány na odborné vzdělávání lékařů v této velmi úzce specializované oblasti. Dále budou finanční prostředky použity na nutné speciální zdravotnické vybavení centra, zejména nástroje a přístroje nad rámec zdravotnické techniky běžné pro léčbu ostatních druhů maligních nádorů.', 'Cílem OSLJ - Bohemia je bránit a ochraňovat křesťanskou víru, rozvíjet snahy o sjednocení křesťanů, aktivně konat humanitární a charitativní činnosti ve všech jejich podobách, zejména v souladu se slavnou tradicí Řádu vyvíjet a poskytovat ve všech formách pomoc, službu a péči nemocným a umírajícím a všeobecně všem, kteří pomoc, podporu a péči potřebují a sami si pomoci nemohou a za účelem realizace humanitárních a charitativních cílů shromažďovat finanční prostředky od svých členů, sympatizantů, nadací, grantů a dalších donátorů a vyvíjet sbírkovou činnost za tímto účelem.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora Tibeťanů', 'MOST, o.p.s.', 'PROTIBET', 'Cílem projektu je podpora vzdělanosti a zlepšování životních podmínek dětí, nomádů, tibetských uprchlíků i starých lidí. Zabýváme se rozvojovou a humanitární pomocí v indických Himálajích a Tibetu, podporujeme místní školy a řemeslné tradice. Od roku 2002 tyto oblasti pravidelně navštěvujeme, spolupracujeme s místními organizacemi, a proto dobře známe situaci a potřeby místních obyvatel.', 'Posláním naší společnosti je zlepšení vzdělanosti, sociálních podmínek a podpora místních ekonomik ve vysokohorských regionech severní Indie a Tibetské autonomní oblasti v Číně. Naše činnost není zaměřena pouze na rozvojovou spolupráci v cílových oblastech, naším cílem je také rozvíjet povědomí o podporovaných oblastech u mládeže, studentů a široké veřejnosti. Různými formami (Festival ProTibet, workshopy pro školy, spolupráce s UP Olomouc, atd...) se snažíme přibližovat nejen život a mizející kulturu himálajských regionů, zaměřujeme se také na obecnou problematiku rozvojové pomoci, chudoby a lidských práv ve světě. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora vzdělávání a zlepšení úrovně života dětí s kombinovaným postižením', 'Pink Crocodile, o.p.s.', 'PINK', 'Mezinárodní charitativní organizace Pink Crocodile podporuje v Čechách vzdělávání a služby pro děti s těžkým kombinovaným postižením. Zároveň se snaží podporovat jejich práva, adekvátní zapojení se do společnosti a zlepšit úroveň jejich života. Team Pink Crocodile věří, že děti s kombinovaným postižením jsou schopné dělat vše tak, jako ostatní děti v běžné populaci, jen je třeba jim k tomu více pomoci od ostatních. Proto pro ně organizace založila školu Pink Crocodile School s rozsáhlým integračním programem, který je inspirován podobnými projekty z Velké Británie a USA. Jedná se o děti tak těžce kombinovaně postižené, které je obtížně zařadit i do stávající sítě speciálních škol (kombinovaná senzorická postižení s mentálním, fyzickým a zdravotním např. srdeční vada, těžká epilepsie či obtížný příjem potravy). Děti se ve škole Pink Crocodile nejen vzdělávají a rehabilituji, ale také třeba účastní maratonu, hrají v kapele, jezdí na koni, učí se vařit, či lyžují. Každé jednotlivé dítě potřebuje ke svému vzdělávání speciální terapie a pomoc individuálního asistenta pedagoga. Program je registrován pod Magistrátem hl. města Prahy a MSMT a dostal velmi nadprůměrné hodnocení ČESKÉ ŠKOLNÍ INSPEKCE. Zároveň se Pink Crocodile snaží prosadit, aby se i ostatní děti s takovýmto těžkým kombinovaným postižením mohly vzdělávat tak, jak je jim to umožněno v rámci Pink Crocodile School. Státní podpora bohužel pokrývá jen část nákladů na vzdělávání a služby pro tyto děti a je v ČR až 15x nižší, než v jiných státech EU. Proto se Pink Crocodile snaží získat dotace pomoci fundraisingových aktivit, např. DMS Podpořte nás!', 'Mezinárodní charitativní organizace Pink Crocodile podporuje v Čechách vzdělávání a služby pro děti s těžkým kombinovaným postižením. Zároveň se snaží podporovat jejich práva, adekvátní zapojení se do společnosti a zlepšit úroveň jejich života. Team Pink Crocodile věří, že děti s kombinovaným postižením jsou schopné dělat vše tak, jako ostatní děti v běžné populaci, jen je třeba jim k tomu více pomoci od ostatních. Proto pro ně organizace založila školu Pink Crocodile School s integračním programem. Děti se tam nejen vzdělávají a rehabilitují, ale také třeba účastní maratonu, hrají v kapele, jezdí na koni, učí se vařit, či lyžují, apod.V zahraničí má Pink Crocodile projekty podporující neslyšící děti, žijící v chudobě, zejména v Indii, Srí Lance a Bangladéši. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpora zdravotnického střediska ADRA v keňském Itibu', 'ADRA o.p.s.', 'KENA', 'ADRA se dlouhodobě podílí na rozvoji zdravotní péče ve venkovské oblasti Itibo v západní Keni. Od roku 2006 zde podporuje místní zdravotnické středisko, které postupně rekonstruuje. Za tu dobu se podařilo se opravit a vybavit operační sálek, zubní ordinaci, porodnici a ambulanci. V roce 2011 byla dokončena výstavba nové budovy lůžkové části s jednotkou intenzivní péče. V současnosti je potřeba novou budovu dovybavit a zprovoznit, aby mohla sloužit pacientům, kteří potřebují dlouhodobější péči.Středisko každý den navštíví několik desítek nemocných a zraněných dětí i dospělých, kteří sem putují často i několik kilometrů pěšky.Od roku 2008 se ve středisku konají odborné stáže, kterých se účastní čeští studenti medicíny, lékaři a zdravotníci.', 'ADRA, o. p. s. Česká republika organizuje a uskutečňuje pomoc lidem v ohrožení života a majetku, který je způsobeno přírodními katastrofami nebo válečnými konflikty. Svými aktivitami rovněž podporuje rozvoj společnosti v oblasti sociální vzdělávací a osvětové při respektu základních lidských práv a svobod doma i ve světě. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpořte děti, kterým zemřel táta za volantem', '', 'TRUCKHELP', 'Řízení kamionu a autobusu patří k nejnebezpečnějším povoláním. Tito profesionální řidiči, kteří jsou neustále na cestách, bývají nejvíc vystaveni nebezpečí dopravních nehod. Nadace Truck HELP už více jak deset let pomáhá dětem, jejichž tátové přišli o život právě při výkonu řidičského povolání. ', '', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Podpořte výsadbu a ochranu stromů', 'Nadace Partnerství', 'STROM', 'Stromy přináší čistější vzduch a příjemný stín, chrání před větrem, snižují hluk i zkrášlují prostředí. Pomozte nám je vrátit do krajiny a obcí. Zaměřujeme se na původní druhy dřevin. Do výsadeb prostřednictvím grantů zapojujeme širokou veřejnost všech věkových skupin, protože když si lidé stromy sami vysadí, mají k nim lepší vztah, pečují o ně, váží si jich a je méně pravděpodobné jejich poškození. Od roku 2004 jsme pomohli vysadit více než 57 tisíc stromů a keřů. S vaším přispěním budeme v sázení stromů pro další generace pokračovat. Děkujeme za podporu!', 'Nadace Partnerství pomáhá lidem pečovat o životní prostředí. Poskytuje jim k tomu granty, odborné služby a inspiraci ze zahraničí. Podporuje kvalitní veřejná prostranství, zelené stavění, vzdělávání, zeleň ve městě i v krajině, zklidňování dopravy, šetrnou turistiku a ochranu přírody. Každoročně vyhlašuje oblíbenou celostátní anketu Strom roku, založila tradici Festivalu otevřených sklepů, v Brně postavila vzdělávací centrum Otevřená zahrada určené pro environmentální vzdělávání žáků škol, dospělých i studentů univerzit. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pojďme chránit zvířata', 'Svoboda zvířat Plzeň', 'SVOBODAZVIRAT', 'V rámci projektu se budeme zaměřovat na informování a vzdělávání veřejnosti v oblasti ochrany zvířat, a to v následujících oblastech: • poskytování obecných i praktických informací o ochraně zvířat (např. jak postupovat v případech týrání zvířat atd.); • šíření informací týkajících se ochrany tzv. kožešinových zvířat (kampaň "Proti srsti", www.protisrsti.cz, www.ukaztetovlade.cz); • šíření informací týkajících se ochrany pokusných zvířat (kampaň "Za nahrazení pokusů na zvířatech, www.pokusynazviratech.cz, www.netestovanonazviratech.cz ); • šíření informací týkající se ochrany zvířat v cirkusech (kampaň „Cirkusy bez zvířat", www.cirkusybezzvirat.cz); • šíření informací o ochraně (nejen) toulavých koček a přímá pomoc v terénu převážně v Plzni a okolí (Sekce na ochranu koček při Svobodě zvířat, www.kocici.cz);• poskytování ekovýchovných programů pro základní školy.', 'Svoboda zvířat je celostátní nezisková organizace pro ochranu zvířat. Vychází z myšlenky, že každý život má svou vlastní neopakovatelnou hodnotu, která není závislá na momentálním hodnocení člověkem. Svoji činnost zahájila v roce 1994.SZ se snaží změnit vztah lidí ke zvířatům. Nejde však jen o zlepšení životních podmínek zvířat, ačkoliv z krátkodobého hlediska s tímto přístupem SZ souhlasí. Hlavní snahou je změnit pohled na zvířata tak, aby byla vnímána jako tvorové, kteří mají hodnotu sami o sobě. Zacházení s nimi by nemělo být hodnoceno podle jejich možného úžitku pro člověka. Celá činonost SZ je založena na myšlence nenásilí a nepřekročení osobní svobody jednotlivce.Mezi úspěchy SZ patří:Největším úspěchem, který však v této obasti SZ vnímá, je posun postojů veřejnosti ve vnímání zvířat jako cítících bytostí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomáháme dětem s handicapem sportovat', 'Nadační fond Emil', 'EMIL', '• Hlavním posláním projektu je integrace dětí a mladých lidí se zdravotním postižením prostřednictvím sportovních aktivit do společnosti. Sportovní aktivity dětí s tělesným postižením umožňují snazší překonávání jejich handicapu. Akce je určena pro handicapované děti a mladé lidi, kteří již jsou výkonnostními sportovci, děti, které se sportem teprve začínají či by začít mohly a děti a mladé bez handicapu.• Děti bez tzv. zdravotní klasifikace nemají obvykle možnost účastnit se závodů a vidět své vzory z řad výkonnostních sportovců s handicapem a setkat se s nimi, proto účast na podobné mezinárodní akci má pro ně velký motivační potenciál.• Organizovaní sportovci mají možnost změřit své síly mezi sebou ve velké mezinárodní soutěži a zároveň se stát vzory pro své vrstevníky s postižením, ale i „zdravé" vrstevníky. Prostřednictvím aktivit určených pro smíšené týmy mladých lidí s postižením a bez handicapu dochází k propojení těchto dvou skupin a akce se tak stává výrazným činitelem sociálního začlenění osob s tělesným postižením.• Akce je unikátní nejen v tuzemsku, ale i v evropském měřítku. Její tradice v moravské metropoli začíná rokem 2009, kdy se uskutečnil první ročník sportovních her pro handicapovanou mládež, tehdy pod názvem Hry handicapovaných talentů - do Brna tehdy zavítali mladí sportovci z České a Slovenské republiky. V roce 2012 se akce účastnily tři stovky osob ze třinácti států Evropy a Hry byly vyhlášeny Mezinárodním paralympijským výborem v londýnské Trinity Hall jako největší a nejužitečnější evropský projekt pro handicapovanou mládež.', 'Účelem Nadačního fondu Emil je účinná podpora handicapovaných dětí a shromažďování finančních prostředků určencýh k zajištění sociálních, léčebných, sportovních a kulturních potřeb handicapovaných dětí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomáháme dětem s UNICEF', 'Český výbor pro UNICEF', 'UNICEF', 'Cílem projektu je získání finančních prostředků pro pomoc dětem v nejchudších zemích prostřednictvím programů UNICEF, zaměřených zejména na tyto oblasti: • krizová pomoc v situacích přírodních a válečných katastrof • péče o děti raného věku • nezávadná voda a hygiena • podvýživa • očkování • prevence a léčba HIV/AIDS, pomoc sirotkům AIDS • vzdělávání pro všechny děti • ochrana dětí před jakýmkoliv zneužíváním, vykořisťováním a krutostí', 'UNICEF (Dětský fond OSN) pracuje ve více než 150 zemích světa, kde dětem pomáhá přežít a podporuje jejich všestranný rozvoj. UNICEF zajišťuje zdravotní péči, výživu, pitnou vodu a hygienu, základní vzdělání a ochranu před násilím, zneužíváním a riziky nemoci AIDS. Práce UNICEF je financována výhradně z dobrovolných příspěvků a z prodeje zboží. Největší část pomoci směřuje do těch částí světa, kde je situace dětí nejtíživější - do rozvojových zemí a do oblastí zasažených přírodní nebo válečnou katastrofou. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomáháme jim přežít', 'Zoologická zahrada hl. m. Prahy', 'OHROZENEDRUHY', 'Pražská zoo není jen chovatelskou a vzdělávací institucí, ale také se intenzivně věnuje ochraně ohrožených druhů zvířat přímo v jejich domovině. Pomáhejte jim přežít spolu s námi! Podobně jako jiné moderní zoologické zahra¬dy pokládáme ochranu ohrožených druhů za svůj prvořadý úkol. Přispíváme k ní jak tím, že vzácné druhy úspěšně rozmnožujeme, tak i tím, že některé z nich chráníme v místě jejich přirozeného výskytu, a v neposlední řadě rovněž svou vzdělávací a osvětovou činností. V mnoha ochranářských projektech se přitom všechny přístupy kombinují a zvířata odchovaná v Zoo Praha se vracejí do volné přírody, kde se jim nadále snažíme pomáhat mimo jiné i tím, že na jejich stranu získáváme prostřednictvím osvěty místní obyvatele. Projekt Návrat divokých koní, jehož nejviditelnější součástí jsou každoroční převozy koní Převalského z pražské zoo zpět do mongolských stepí, kde byli v minulosti vyhubeni, stejně jako projekt Toulavý autobus, v jehož rámci se zasazujeme o ochranu goril, pralesních slonů a dalších zvířat ve střední Africe, vstoupily do mezinárodního povědomí. Tyto projekty však zdaleka nejsou jediné. Zoo Praha pomáhá přežít celé řadě dalších druhů, od kriticky ohrožených krokodýlů gaviálů v Indii, přes supy mrchožravé a orlosupy bradaté v evropských horách, až po kriticky ohrožené užovky podplamaté nebo sysly obecné v pražské Troji. Žádný z našich projektů by přitom nebyl myslitelný bez přízně, již pražské zoo projevuje její zřizovatel a nejširší veřejnost. Právě díky prostředkům získaným od lidí, kterým není osud ohrožených druhů zvířat lhostejný, můžeme alespoň malým dílem přispět k tomu, aby si naše planeta zachovala nesmírnou pestrost života. Přidejte se k nám!', 'Zoologické zahrady dnes působí ve světě, v němž je stále více ohrožována druhová pestrost přirozených ekosystémů neboli biodiverzita. Jsou to právě tyto instituce, které mají možnost postihnout celý rejstřík ochranářských aktivit, od výzkumu přes ochranu ex-situ (včetně chovu ohrožených druhů a výchovné činnosti) po účast na projektech in-situ přímo v terénu.Výchova a vzdělávání Každým rokem přichází do zoologických zahrad na celém světě více než 600 milionů návštěvníků nejrůznějšího věku i zájmů. V pražské zoo se během posledních tří let počet návštěvníků zdvojnásobil na více než 1,2 milionu osob ročně. Je to velká výzva i závazek. Zoologická zahrada tak ovlivňuje svými výchovnými a vzdělávacími aktivitami širokou veřejnost i zájmové skupiny. Je schopna díky neocenitelnému potenciálu živých zvířat působit u návštěvníků na jejich názory, postoje, vytváření hodnot a chování.Chov zvířat Pražská zoo jako moderní instituce svého druhu vytváří pro chované živočichy co nejoptimálnější podmínky, a to jak koncepcí a ztvárněním výběhů i ubikací, tak sestavováním jedno- či vícedruhových skupin. Návštěvníci si tak mohou vytvořit představu o životě zvířat a jejich vazbách k prostředí, zvířata pak mají příležitost rozvinout co nejširší rejstřík přirozeného chování.Ochrana ex-situ Zoo Praha se dlouhodobě podílí na ochraně živočichů ex-situ. Vytváří pojistné chovné skupiny u druhů, které jsou v přírodě bezprostředně ohrožené, nicméně u kterých již probíhají snahy o zpětný návrat nebo je lze očekávat.Ochrana in-situ Stále výrazněji se pražská zoo podílí na projektech in-situ, jejichž cílem je ochrana živočichů přímo v místě jejich výskytu. Jedná se o zapojení do reintrodukcí ohrožených druhů do rezervací i volné přírody nebo o metodickou a materiální pomoc v těchto oblastech. Programy se týkají mimo jiné koně Převalského, gorily nížinné, adaxe, ibise skalního, zubra, čápa černého, orlosupa bradatého, supa hnědého, užovky podplamaté, sysla obecného, kvakoše nočního, puštíka bělavého, sovy pálené a sýčka obecného. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomáháme tam, kde je třeba', 'Portimo, o.p.s.', 'PORTIMO', 'Projekt pomůže dětem i dospělým prostřednictvím sociálních služeb a programů Portimo, o.p.s. v Novém Městě na Moravě a okrese Žďár nad Sázavou.Vybrané finanční prostředky pomohou zajistit aktivity, vybavení a pomůcky sociálních služeb: Raná péče, Občanská poradna, Osobní asistence, EZOP - nízkoprahové zařízení pro děti a mládež, Sociálně aktivizační služby pro rodiny s dětmi a dále podpoří programy primární prevence a dobrovolnictví.', 'Posláním organizace je podpora dětí, mládeže i dospělých v nepříznivé životní situaci, a to prostřednictvím sociálních služeb, které respektují jedinečnost každého člověka, jsou profesionální a diskrétní. Nabízíme také preventivní a dobrovolnické programy. Poskytujeme otevřené informace, chceme být přirozenou součástí života místní komunity. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomáhat může každý (PMK)', 'Nadace ADRA - adventistická nadace pro pomoc a rozvoj', 'ADRAPOMOC', 'Získané prostředky budou použity na pomoc konkrétním dětem, dospělým, seniorům a rodinám, které se kvůli špatné zdravotní či sociální situaci ocitly v mimořádně těžké situaci, kterou nemohou zvládnout vlastními silami.', 'Nadace ADRA podporuje projekty v České republice i ve světě zaměřené na: a) Zvýšení úrovně života a zdraví lidí, kteří se ocitli v  tíživé životní situaci a potřebují pomoc. Podporuje děti i dospělé, jednotlivce i rodiny - handicapované, nemocné, v sociální tísni; lidi, kteří se vlivem např. požáru, autonehody aj. dostali do kritických problémů, jež nemohou zvládnout vlastními silami. b) Osvětovou činnost, výchovu a vzdělávání k sociálnímu cítění a ke zvyšování úrovně občanské zodpovědnosti a vzájemného soužití. c) Rozvoj dobrovolnictví. d) Podporu pomoci lidem při řešení krizových situací v důsledku živelních katastrof, rasových, náboženských nebo politických problémů bez ohledu na rasovou, etnickou, politickou nebo náboženskou příslušnost, věk, pohlaví nebo jiné rozdíly. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomáhejte s námi', 'Nadační fond Českého rozhlasu', 'ROZHLAS', 'Získání finančních prostředků na pomoc lidem zasažených povodněmi na území ČR.', 'Účelem fondu je podpora rozvoje kultury a všech forem umění, zvláště mladé talentované umělce ze sociálně slabých rodin či handicapované, podpora rozvoje a rozšiřování vzdělání, podpora v oblasti sociální a zdravotní, poskytování charitativní a humanitární pomoci. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomáhejte s námi ženám s rakovinou prsu', 'Mamma HELP, z. s.', 'MAMMAHELP', 'Rakovinou prsu ročně onemocní více jak 7 000 žen. Psychosociální podpora pacientek i jejich rodin je dlouhodobě nedostatečná. Sdružení Mamma HELP proto již od roku 2000 buduje a provozuje síť poraden, kde tuto službu poskytují vyléčené pacientky. Pomoc je bezplatná a může jí využít každý, bez ohledu na členství ve sdružení. Zájem o tuto podporu každoročně roste, proto je žádoucí rozšířit působnost kromě stávajících sedmi měst i do dalších regionů.', 'Mamma HELP je největší pacientskou organizací, která se stará o ženy s rakovinou prsu a jejich blízké. Působí celostátně a nabízí prioritně bezplatnou pomoc a poradenství v síti MAMMAHELP center - Praha, Brno, Olomouc,  Hradec Králové, Přerov, Zlín, Plzeň a České Budějovice. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc dětem a jejich rodinám společně najít cestu z krizové situace', 'Dům tří přání', 'DUMTRIPRANI', 'Projekt je zaměřen na odbornou pomoc dětem, které jsou ohroženy vztahovými a výchovnými problémy v rodině, dětem hospitalizovaným rodičů, rodičů s psychiatrickým onemocněním či závislostí na návykových látkách. Pomoc je určena i dětem s výchovnými problémy a poruchami chování, týraným, zneužívaným nebo zanedbávaným. Projektem bychom rádi upozornili na to, že dětí ohrožených krizovou situací v rodině je kolem nás více, než si myslíme, a že velkým fenoménem je v současné době narůstající počet dětí ohrožených rozvodovými spory rodičů. V rodinách kde jsou manželé rozvedeni nebo jsou v rozvodovém řízení (případně, kdy se partneři rozcházejí) a kde probíhají vleklé a vyčerpávající konflikty jsou děti touto situací velmi ohroženy.', 'Dům tří přání je nestátní nezisková organizace, jejímž posláním je všestranná pomoc dětem a jejich rodinám, které se ocitly v krizové situaci. Dům tří přání je zařízením pověřeným k výkonu sociálně-právní ochrany dětí a jeho služby: krizová pomoc, sociální rehabilitace a sociálně aktivizační služby pro rodiny s dětmi, jsou registrovány dle zákona o sociálních službách. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc dětem a mladistvým s handicapy', 'Zajíček na koni, o.p.s.', 'ZAJICEKDETEM', 'Cílem projektu je zlepšení zdravotní a psychické situace znevýhodněných klientů naší organizace. Tohoto cíle dlouhodobě dosahujeme díky letním integračním pobytům s jízdami na koních, zimním integračním pobytům na různých místech České republiky, začleněním handicapovaných osob do běžné společností výpomocí v kavárně.', 'Zajíček na koni, o.p.s. pomáhá pomocí kvalitních terapií jízdou na koni, provozem kavárny, integračních pobytů a sportovních aktivit trvale zlepšovat fyzický a duševní stav handicapovaných a znevýhodněných lidí a podporuje funkčnost rodiny. Chceme dosáhnout, aby se rodiče svým dětem intenzivně věnovali, a aby zdravé děti vnímaly děti handicapované jako sobě rovné... ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc dětem a mladým lidem s postižením', 'Nadace Jedličkova ústavu', 'PROJEDLICKU', 'Projekt DMS PROJEDLICKU je již od roku 2008 významnou součástí veřejné sbírky NJU, prostřednictvím které shromažďuje finanční prostředky určené žákům a klientům JÚŠ a organizacím poskytujícím služby dětem a mladým lidem s postižením na úžemí celé ČR.', 'Základním posláním Nadace Jedličkova ústavu je shromažďování finančních prostředků a jiných hodnot za účelem zlepšení komplexní rehabilitační péče o děti a mladé lidi s tělesným handicapem ve všech jejích aspektech - výchovně vzdělávacích , rehabilitačních a odborně terapeutických a dále všestranná pomoc v integraci mladých lidí s handicapem do společnosti s cílem zamezit sociální a společenské izolaci. Pomoc je směřována ke klientům a absolventům Jedličkova ústavu a  škol a také k neziskovým organizacím v celé České republice, které mají podobné poslání jako Nadace. Naši podporu pro děti a mladé lidi s tělesným postižením cíleně směřujeme k následujícím projektům: - Podpora současných i bývalých žáků a studentů škol Jedličkova ústavu - Sociální a pracovní rehabilitace - Bezbariérová doprava - Osobní asistence - Volnočasové aktivity - Léčebné terapie a kompenzační pomůckySvým příspěvkem pomůžete lidem s postižením věnovat se běžným každodenním aktivitám. Děkujeme. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc dětem s hematologickým, nebo onkologickým onemocněním', 'Haima CZ, o.s.', 'HAIMA', 'Podpora a pomoc dětem s onkologickým onemocněním a nemocemi krvetvorby od začátku do konce léčby, pomoc s jejich návratem do běžného života. Pomoc rodičům dlouhodobě nemocných dětí, psychologická a sociální podpora. Publikační činnost - informační materiály pro pacienty a rodiče, osvěta a popularizace dětské hematologie a onkologie, pořádání besed a přednášek.', 'Posláním organizace je pomoc dětem s hematologickým, nebo onkologickým onemocněním a jejich rodinám. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc dětem s nelehkým startem', 'Občanské sdružení Smíšek', 'SMISEK', 'Hlavním cílem projektu je vytvořit předškolním dětem (ve věku 3-6 let) z rodin ze znevýhodněného prostředí (azylové domy, ubytovny, chráněné byty) včasnou integraci do společnosti vrstevníků a následně lepší podmínky při vstupu do ZŠ. Děti ve věku 3-6 let se nemohou bránit, ani si dojít pro pomoc k učiteli či jiné osobě. A přitom je to období, kdy se tvoří osobnost, učí se ze vzorů svého okolí. Tyto děti jsou většinovou společností opojeny, dostávají se do zorného pole až v případě vyhrocené krizové situace v rodině a ohrožení života a zdraví dětí. I tyto děti jsou naše budoucnost. A je velmi krátkozraké argumentovat, že mají své rodiče, ať se starají oni. Tito rodiče své kompetence nezvládají, patrně proto, že je nezvládali jejich rodiče a oni to neumí. - Projekt je jeden z mála v kraji, který se předškolním dětem ze sociálně znevýhodněného prostředí věnuje komplexně 5 dní v týdnu, 8 hod/denně. - Jedinečnost a inovativnost propojení Integrační školičky Smíšek, Smíškovin, Klubu Smíšek a sociálně aktivizačních služeb pro rodiny s dětmi, - ucelená, intenzivní a dlouhodobá efektivní práce s rodinou jako s celkem. Aktivity navazují jedna na druhou - synergický efekt.Většina dětí docházející do školiček je pod dozorem státních sociálních pracovníků OSPOD, některé rodiny mají určen soudní dohled, vzhledem k nedostatečné péči o dítě a nedostatku rodičovských kompetencí. K dětem je přistupováno s individuální péčí, kterou potřebují díky stresujícím zážitkům (Acorus, Rosa a další azylové domy, domácí násilí).', 'Jsme nezisková organizace, která pomáhá dětem propojením jejich sociálních, vzdělávacích a volnočasových aktivit Všem dětem v Smíšku chceme individuálním přístupem garantovat zařazení do běžného vzdělávacího procesu nebo zařazení do speciální školy odpovídající jeho individuálním potřebám.Posláním organizace je podpora a pomoc předškolním dětem ve věku 3-6 let z nefunkčních rodin a znevýhodněného prostředí vymanit se z původní rodiny a poskytnout jim vhodné podněty pro dorovnání hendikepů a další zdravý rozvoj v důležitém období, kdy se tvoří osobnost. Hladší vstup do prvého ročníku ZŠ, včasnou integraci do společnosti jejich vrstevníků a následně lepší vzdělávací podmínky s pozitivnějším výhledem do budoucnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc dětské psychiatrii v Opařanech', 'Vojenský a špitální Řád svatého Lazara Jeruzalémského - Bohemia', 'LAZARUS', 'Z vybraných prostředků bude financována hippoterapie nebo ergoterapie pro pacienty Dětské psychiatrické nemocnice v Opařanech, vybavení pracovních dílen, nákup vánočních dárků nebo koncerty pro dětské pacienty.', 'Cílem OSLJ - Bohemia je bránit a ochraňovat křesťanskou víru, rozvíjet snahy o sjednocení křesťanů, aktivně konat humanitární a charitativní činnosti ve všech jejich podobách, zejména v souladu se slavnou tradicí Řádu vyvíjet a poskytovat ve všech formách pomoc, službu a péči nemocným a umírajícím a všeobecně všem, kteří pomoc, podporu a péči potřebují a sami si pomoci nemohou a za účelem realizace humanitárních a charitativních cílů shromažďovat finanční prostředky od svých členů, sympatizantů, nadací, grantů a dalších donátorů a vyvíjet sbírkovou činnost za tímto účelem.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc chovu a záchraně ohrožených druhů zvířat v ZOO Brno', 'Zoo Brno a stanice zájmových činností, příspěvková organizace', 'ZOOBR', 'Projekt je určen všem milovníkům zvířat všech věkových skupin a všem občanům, kteří mají zájem spolupodílet se na zlepšení chovu a záchraně ohrožených druhů zvířat. Specifickou skupinu představuje mládež, u které je žádoucí vytvořit pocit, že i malou částkou může přispět ke zlepšení podmínek chovu zvířat.Příspěvky z veřejné sbírky budou použity pro opravy expozic a k zajištění kvalitnějšího welfare zvířat.', 'Organizace se zaměřuje na činnost chovatelskou, ochranářskou, vědeckou a vzdělávací v oblasti fauny a flory. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc krajanům v rumunském Banátu', 'Člověk v tísni o.p.s.', 'BANAT', 'Cílem pomoci je především vytvořit v krajanských vesnicích pracovní příležitosti srovnatelné s těmi, jaké by rumunští Češi měli v České republice při maximálním zachování kulturních hodnot komunity v Banátu a jejích zvyklostí.', 'Jsme nevládní nezisková organizace vycházející z myšlenek humanismu, svobody, rovnosti a solidarity. Lidskou důstojnost a svobodu považujeme za základní hodnoty. Věříme, že lidé kdekoli na světě mají mít právo rozhodovat o svých životech a společně sdílet práva vyjádřená Všeobecnou deklarací lidských práv. Usilujeme o otevřenou, informovanou, angažovanou a zodpovědnou společnost k problémům doma i za hranicemi naší země. Chceme se aktivně podílet na vytváření společnosti, ve které jsou kulturní, etnické, rasové a jiné odlišnosti zdrojem obohacení, a nikoli konfliktů. Jsme součástí občanské společnosti. Naše činnost vychází z osobní iniciativy a aktivity stovek našich zaměstnanců a dobrovolníků, jak v České republice, tak v desítkách zemí, kde pracujeme a z důvěry a podpory, kterou pro svoji práci získáváme od jednotlivců, firem, vlád i mezinárodních institucí. Oblasti a priority činnosti si určujeme sami, podle naléhavosti situace a podle našich reálných možností něco změnit. Nemůžeme pracovat všude, za podstatný považujeme skutečný dopad. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc lidem s autismem a jejich rodinám', 'Asociace pomáhající lidem s autismem APLA-JM o.s.', 'APLAMORAVA', 'Asociace pomáhající lidem s autismem APLA-JM o.s. s působností pro Jihomoravský kraj vznikla, aby komplexně a dlouhodobě pomáhala lidem s poruchou autistického spektra (PAS) ve všech fázích jejich života. Nezaměřujeme se pouze na osoby s touto poruchou, ale také na celou rodinu, které nabízíme poradenství, asistenci, skupinové setkávání nebo také podporu sourozenců. Obecným cílem APLA Jižní Morava je zkvalitnění života osob s PAS. Tohoto cíle dosahujeme prostřednictvím registrovaných sociálních služeb, a to konkrétně rané péče, osobní asistence a sociální rehabilitace. Dále pro klienty pořádáme volnočasové aktivity i letní tábory. Odborné veřejnosti nabízíme akreditované kurzy pro pedagogy, lékařský i nelékařský personál, pracovníky v sociálních službách a sociální pracovníky. APLA Jižní Morava též provozuje centrum včasné prevence PASPOINT, kde nabízí podporu celé rodině v období stanování diagnózy. Výtěžek z DMS použijeme na podporu aktivit APLA Jižní Morava ve všech oblastech práce této organizace.', 'APLA Jižní Morava se zabývá komplexní a systematickou pomocí lidem s poruchami autistického spektra a jejich blízkým. Organizace nabízí v oblasti sociálních služeb osobní asistenci, ranou péči a sociální rehabilitace. Zájemce soustřeďuje v akreditovaném dobrovolnickém centru a vzdělávacím institucím poskytuje akreditované kurzy pro pedagogické pracovníky, pracovníky sociálních služeb, psychology, lékaře i zdravotnický personál. APLA-JM provozuje i poradenské centrum a pořádá letní tábory nebo volnočasové aktivity pro děti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc lidem s onemocněním roztroušenou sklerózou mozkomíšní', 'Unie Roska – postižených roztroušenou sklerózou v ČR', 'ROSKA', 'Na pomoc nemocným roztroušenou sklerózou mozkomíšní, na podporu činnosti Roska, pomáhající již 20 let lidem tímto onemocněním, dále na podporu ediční činnosti, tisku informačních tiskovin a brožur o problematice roztroušenou sklerózy, vydávání časopisu Roska, podporu sociálně právního poradenství a podporu činností regionálních organizací Unie Roska. ', 'Unie Roska, Česká MS společnost, je pacientská organizace, která pomáhá lidem s roztroušenou sklerózou mozkomíšní žít kvalitní a plnohodnotný život. Má celorepublikovou působnost ve všech 14 krajích ČR. Regionální organizace Roska organizují rehabilitační aktivity a slouží jako kontaktní místa poskytující kompletní sociální a pracovně právní poradenství včetně konkrétní pomoci. Unie Roska vydávání knižní publikace, osvětové a edukační materiály, je aktivním členem mezinárodních MS organizací. Ke Světovému dni roztroušené sklerózy organizuje benefiční a osvětové akce a pořádá každoroční Národní konferenci „Místo pro kvalitní život s roztroušenou sklerózou" ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc obětem humanitární krize', 'MAGNA, Děti v tísni, o.p.s.', 'MAGNAPOMOC', 'Magna Děti v tísni je nezisková organizace, jejímž posláním je pomáhat lidem v krizových oblastech světa, realizovat humanitární projekty zaměřené na poskytování akutní a dlouhodobé zdravotní, psychosociální, materiální pomoci lidem postiženým humanitárními krizemi, přírodními katastrofami, epidemiemi, hladomorem, lidem vyloučeným ze systému lékařské péče, lidem v nouzi a těm, kteří žijí v ohrožených částech země, a to bez ohledu na etnickou, náboženskou či rasovou příslušnost.MAGNA má dlouholeté zkušenosti s prací v humanitárních katastrofách. Její týmy působily ve většině velkých katastrof - tsunami v jihovýchodní Asii, zemětřesení na Haiti, cyklon v Barmě, hladomor na hranicích Keni a Somálska, cyklon na Filipínách a  zemětřesení v Nepále. MAGNA má také rozsáhlé zkušenosti s prací s uprchlíky, v současnosti zabezpečuje humanitární pomoc v rámci utečenecké krize v pohraničních oblastech EU. MAGNA je největší humanitární organizace, která zabezpečuje imunizaci dětí a žen v táboře pro vnitřně vysídlené lidi v Jižním Súdánu a také pomáhala uprchlíkům v somálských táborech a v komunitách v severovýchodní Keni.Magna Děti v tísni/ Magna Children at Risk působí na čtyřech kontinentech světa, spolu se svými více než 400 terénními pracovníky poskytujeme zdravotní pomoc dětem a jejich rodinám každý den.', 'Posláním organizace je pomáhat lidem v krizových oblastech světa, realizovat humanitární a rozvojové projekty zaměřené na pomoc lidem v nouzi, ať již způsobené chudobou, chorobami, válkou nebo ekologickou devastací, a to bez ohledu na etnickou, náboženskou či rasovou příslušnost. Pracujeme přímo v terénu, kde jsme schopni okamžitě reagovat na náhle vzniklé situace. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc obětem humanitárních katastrof ve světě.', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce', 'DIAKONIEPOMOC', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce poskytuje humanitární pomoc při mimořádných událostech v České republice a organizuje rozvojovou spolupráci a humanitární pomoc v zahraničí. Reagujeme na humanitární katastrofy okamžitou pomocí.Spolupracujeme s našimi partnery v zahranicí při zjišťování potřeb zasažených lidí, aby naše pomoc byla cílená a efektivní. Podporujeme lidi strádající v důsledku válečných konfliktů i humanitárních katastrof. Naše pomoc směřuje ke zmírnění jejich utrpení, ochraně jejich důstojnosti, zlepšení zdravotní a hygienické situace apod. Naším hlavním cílem je pomoci zasaženým co nejdříve se opět postavit na vlasní nohy.', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce poskytuje humanitární pomoc při mimořádných událostech v České republice a organizuje rozvojovou spolupráci a humanitární pomoc v zahraničí. V České republice pomáháme při povodních a věnujeme se také prevenci a připravenosti obcí na mimořádné události. Pracujeme s dobrovolníky, a to jak v České republice, tak také v zahraničí.Od roku 2014 také nabízíme školení pro učitele základních škol v oblasti Globálního rozvojového vzdělávání. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc obětem kriminality v ČR', 'Bílý kruh bezpečí, z.s.', 'BKB', 'Cílem projektu je zvýšit společenský respekt k obětem kriminality, zvýšit dostupnost komplexní služby pro oběti, svědky a pozůstalé, realizovat specifické služby pro zranitelné a zlomené oběti kriminality, podpořit rozvoj dalších produktů (služeb) reagujících na nové potřeby obětí kriminality, přispět k přijetí příslušné legislativy v ČR (zákon o obětech kriminality) a podílet se výrazně na její implementaci do praxe, podpořit vnitřní rozvoj BKB zejména v oblasti fundraisingu a profesionalizace managementu v regionech.Projekt je zaměřen na provoz a rozvoj pracovišť BKB v ČR, standardizaci služeb, vzdělávání pomáhajících profesionálů, osvětu a prosazování změn ve výkonu spravedlnosti, vymahatelnosti práva, apod. ', 'Cílem organizace je zlepšení situace obětí kriminality v ČR, zejména jejich postavení v trestním řízení (rovněž i svědků a pozůstalých po obětech). Cíl je naplňován prostřednictvím přímé pomoci, iniciativami v oblasti legislativy a výkonu spravedlnosti v praxi, osvětovou činností v oblasti prevence kriminality.Služby BKB jsou odborné, podávané srozumitelnou formou, bezplatné, nestranné a diskrétní. Do cílové skupiny patří oběti a svědci oznámené, ale i skryté/neoznámené kriminality. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc opuštěným a týraným psům', 'Dogpoint, o.p.s.', 'DOGPOINT', 'Projekt pomáhá zajistit dostatek financí na zajištění dočasného ubytování pro týrané a opuštěné psy, zajištění kvalitní péče (veterinární, zajištění welfare, zvýšení hygienického standardu) pro nemocná nebo zraněná zvířata, resocializace a práce s těmito zvířaty tak, aby mohla být začleněna do nových rodin a nekončila na ulici. S tímto souvisí i rozvoj organizace a zvýšení počtu zvířat, kterým lze takto efektivně pomoci.', 'Pomáháme opuštěným a týraným zvířatům - především pak psům, kteří jsou díky týrání a lidské lhostejnosti v akutním ohrožení života. Pomáháme s jejich resocializací a přípravou na začlenění do nových, trvalých domovů. Poskytujeme osvětu, vzdělávání a rozšiřování tohoto tématu do podvědomí veřejnosti. Snažíme se o snížení počtu bezprizorních zvířat osvětou. Zaměřujeme se především na pomoc psům z celého území ČR - pomáháme cíleně, efektivně, profesionálně. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc postiženým dioxiny ve Vietnamu', 'Občanské sdružení Development Worldwide', 'DETIVIETNAM', 'Cílem veřejné sbírky je podpořit výstavbu, rekonstrukci a provoz rehabilitačních center a chráněných dílen pro postižené (děti a mládež, ženy, váleční veteráni) v provinciích Thai Binh a Thua Thien Hue. Následky intoxikace svých rodičů dioxiny v průběhu vietnamské války trpí v obou provinciích tisíce dětí. Míra jejich postižení často vyžaduje nepřetržitou péči ze strany jejich rodin. Možnosti odborné zdravotní či rehabilitační pomoci jsou z velké části závislé na kapacitách provinčních středisek VAVA (Asociace obětí Agent Orange). Tato střediska navíc nabízejí odborná školení pro postižené, kteří jsou schopni se vyučit nějaké řemeslo. Tyto aktivity jsou součástí zdravotní rehabilitace a současně pomáhají k novému zapojení postižených a jejich rodin do společnosti. V chráněných dílnách probíhají například kursy šití a pletení, výroby uměleckých dřevěných obrazů nebo broušení ozdobných kamenů. Dílny nabízejí také dlouhodobější pracovní uplatnění. Na platy zdravotního personálu a školitelů (většinou dobrovolníci v důchodovém věku) ani na provoz center stát významněji nepřispívá a jejich činnost je tak závislá na sponzorech a na výtěžku z chráněných dílen. Veřejnou sbírku pořádá Občanské sdružení Development Worldwide ve spolupráci s občanským sdružením INFO-DRÁČEK a Svazem Vietnamců v České republice.', 'Posláním orgnizace je zvyšování kvality rozvojových projektů, efektivní spolupráce na národní a mezinárodní úrovni za účelem zlepšování situace našich partnerů z rozvojových zemí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc pro Parent Project - Pomoc dětem se svalovou dystrofií DMD/BMD', 'PARENT PROJECT, z.s.', 'SVAL', 'Posláním pacientské rodičovské organizace Parent Project je podpora výzkumu a  budoucí léčby svalových (muskulárních) dystrofií DMD/BMD, zlepšení základní péče a kvality života a podpora integrace svých pacientů.• Přispívá na rehabilitační, kompenzační a zdravotní pomůcky, osobní asistenci • Pořádá ozdravné pobyty a pravidelná setkávání rodin s postiženými dětmi • Pořádá odborné školení a semináře pro rodiče postižených dětí a lékaře • Pro své členy, laickou i odbornou lékařskou veřejnost překládá a vydává informační materiály a odborné publikace s poznatky o nemoci DMD/BMD • Zajišťuje spojení a sdílení poznatků s výzkumnými centry ve světě a podobnými organizacemi • Podporuje vytvoření center komplexní péče v ČR • Podporuje nadějné projekty výzkumu získání účinného léku a projekty klinického testováníDíky Vaší podpoře můžeme nadále plnit své poslání. Děkujeme, že jste vstoupili do života našich dětí.', 'Parent Project (Projekt rodičů) je sdružení rodičů a blízkých, jejichž děti se narodily se svalovou (muskulární) dystrofií Duchenne/Becker (DMD/BMD). Svalové dystrofie jsou genetická onemocnění, která se projevují jako postupné ochabování svalstva. Průběh jednotlivých forem dystrofií je různý. Nejčastější a nejkrutější formou je Duchennova svalová dystrofie (DMD).Posláním naší rodičovské organizace Parent Project je podpora výzkumu a budoucí léčby svalových dystrofií DMD/BMD, zlepšení základní péče a kvality života a podpora integrace.Parent Project byl založen v USA roku 1994. Naše organizace působí v ČR od roku 2001. Více na www.parentproject.cz.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc pro Pomocné tlapky - Od štěněte k hrdinovi', 'Pomocné tlapky, o.p.s.', 'POMOCNETLAPKY', 'Cílem projektu je výchova a výcvik asistenčních psů pro tělesně postižené spoluobčany. Ti tak získávají zdarma neocenitelného parťáka, který částečně nahradí lidského asistenta. Příprava začíná výchovou štěňat v adoptivních rodinách na jejich budoucí roli asistenčního psa, následuje speciální výcvik mladých psů a také zaškolení klienta, aby co nejlépe zvládali soužití s asistenčním psem. Asistenční psi mohou částečně nahradit službu osobních asistentů nebo rodinných příslušníků a snížit závislost tělesně postižených lidí na jejich péči, zvýšit samostatnost a sebevědomí a tím umožnit klientům aktivně a plnohodnotně se zapojit do běžného života a zlepšit navazování komunikace s okolím.Pes-asistent ovládá okolo 80 povelů, k těm základním a nejvíce využívaným úkonům patří podávání spadlých předmětů do ruky klienta, otvírání a zavírání dveří, rozsvěcení světel, otvírání pračky, vyndavání prádla, otvírání lednice a podání určeného předmětu (lahev s pitím, krabička s jídlem), dále je to pomoc při sebeobsluze - stahování ponožek, kalhot, rozepínání a stahování bundy,... zvedání ruky na ovladač el.vozíku apod. Každý pes je cvičený na míru klienta - dle jeho potřeb. Více informací na www.sundejmiponozku.cz. Neméně významnou součástí výcviku je příprava psa na pomoc formou canisterapie - speciální zooterapie, kdy se speciálním přikládáním psa k tělu postiženého uvolňují spazmy (křeče), dojde k celkovému uvolnění a prohřátí organizmu. Při cílených hrách se psem dítě i dospělý klient nenásilně, rád a dobrovolně cvičí - rehabilituje.. ', 'Posláním organizace je výcvik asistenčních psů pro vozíčkáře. Byli jsme první organizací v ČR, která v roce 2001 začala poskytovat své služby klientům z celé ČR komplexně (od cílené přípravy štěněte, přes výcvik na míru klientovi, po předání a následnou péči o asistenční tým po celou dobu aktivní služby). Jsme jednou z mála organizací, která úspěšně pracuje a plní své poslaní bez podpory státu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc pro slané děti', 'Klub nemocných cystickou fibrózou,z.s.', 'SLANEDETI', 'Na první pohled je nemusíte poznat. Vypadají zdravě a přeci nejsou. Cystická fibróza je nevyléčitelné, život zkracující genetické onemocnění. Nemocní potřebují komplexní lékařskou, psychosociální a hmotnou podporu, aby mohli co nejdéle plnohodnotně žít jako my, jejích zdraví vrstevníci.', 'Posláním Klubu je zlepšování kvality života nemocných cystickou fibrózou a dále zvyšování informovanosti veřejnosti o tomto onemocnění. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc předčasně narozeným dětem a jejich rodinám', 'Nadační fond pro předčasně narozené děti a jejich rodiny', 'PREDCASNEDETI', 'Cílem projektu je získávání finančních prostředků na podporu a pomoc konkrétním rodinám a jejich konkrétním potřebám spojeným s předčasně narozeným dítětem, jako je např. příspěvek na speciální výživu dítěte, hygienické potřeby dítěte, zdravotnické pomůcky, příspěvek na ubytování rodičům po dobu hospitalizace dítěte, cestovní výlohy rodičům po dobu hospitalizace dítěte, pomoc při zajištění psychologické péče pro rodinu předčasně narozeného dítěte, pomoc při zajištění rehabilitační péče předčasně narozeného dítěte, pomoc při zajištění následné péče např. lázeňský pobyt, apod.', 'Naším posláním je pomáhat rodinám předčasně narozených dětí, zmírnit finanční a psychickou zátěž spojenou s jejich odloučením. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc při povodních v ČR', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce', 'POMOCPOVODNE', 'Středisko humanitární a rozvojové spolupráce pomáhá lidem v České republice zasaženým povodněmi a jinými mimořádnými událostmi. Peníze získané z DMS zpráv použijeme na podporu naší činnosti při poskytování humanitární pomoci a také na prevenci před povodněmi.', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce poskytuje humanitární pomoc při mimořádných událostech v České republice a organizuje rozvojovou spolupráci a humanitární pomoc v zahraničí. V České republice pomáháme při povodních a věnujeme se také prevenci a připravenosti obcí na mimořádné události. Pracujeme s dobrovolníky, a to jak v České republice, tak také v zahraničí.Od roku 2014 také nabízíme školení pro učitele základních škol v oblasti Globálního rozvojového vzdělávání. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc přichází na čtyřech tlapkách', 'Helppes - Centrum výcviku psů pro postižené, o.p.s.', 'HELPPES', 'Cílem projektu Pomoc přichází na čtyřech tlapkách je zkvalitnění života osob s postižením pomocí speciálně vycvičených psů. Asistenční a signální psi dokážou částečně nahradit pomoc a službu osobních asistentů či rodinných příslušníků, čímž zvyšují soběstačnost a samostatnost lidí se závažným zdravotním postižením, možnosti při jejich zařazení do pracovního či vzdělávacího procesu, pomáhají jim při návratu do aktivního života, s běžnými denními úkony a sebeobsluhou, rozvojem jemné i hrubé motoriky, řečových a hmatových schopností, dokážou přivolat pomoc apod.Helppes vychovává a cvičí v projektu Pomoc přichází na čtyřech tlapkách pro osoby s postižením tyto typy služebních psů: Asistenční psy pro vozíčkáře a osoby s tělesným postižením, balanční psy Asistenční / průvodcovské psy pro osoby trpící autismem Asistenční / terapeutické psy pro dětské klienty Signální psy pro osoby s diabetes Signální psy pro osoby trpící stavy ztráty vědomí či záchvatovými onemocněními (epilepsie, kardio-záchvaty, narkolepsie atp.) Signální psy pro osoby se sluchovým postižením Psy se speciálním výcvikem pro osoby s kombinovanými handicapy Protože pouze vodicí psi pro osoby se zrakovým postižením jsou všem výcvikovým subjektům plně hrazeni státem, a to až do výše 350.000,-Kč, ale všichni ostatní žadatelé, bez ohledu na typ a závažnost jejich postižení a jejich potřebnost, nedostanou na své psí pomocníky příspěvek od státu žádný a jsou pro možnost získání psího pomocníka zcela plně závislí na grantových řízeních, nadačních příspěvcích, sponzorských darech, firemních a individuálních dárcích apod., budou vybrané finanční prostředky z tohoto projektu využity ve prospěch osob s postižením - žadatelů o státem nedotované psí pomocníky.', 'Posláním neziskové organizace Helppes je integrace osob se zdravotním postižením do společnosti a kolektivu, jejich návrat do aktivního života, zařazení do vzdělávacího či pracovního procesu, rozšíření možností při sebeobsluze, získání větší samostatnosti, nezávislosti, sebevědomí, svobody a bezpečí. To vše pomocí speciálně vycvičených psů v projektu Pomoc přichází na čtyřech tlapkách.Asistenční a signální psi dokážou částečně nahradit pomoc a službu osobních asistentů či rodinných příslušníků, čímž zvyšují soběstačnost lidí s postižením. Helppes je jediná profesionální nezisková organizace svého druhu v ČR, která je registrovaným poskytovatelem sociálních služeb. Helppes poskytuje zcela komplexní soubor souvisejících služeb - od zaškolení žadatele o speciálně vycvičeného psa, přes jeho výuku a proškolení, předání psa a secvičení se psem, až po následný servis po celou dobu aktivní služby psa. Helppes poskytuje všechny své služby pomocí profesionálního týmu nejen odborníků na výcvik psů, ale i pracovníků v sociálních službách s odborným vzděláním pro práci s osobami se zdravotním postižením. Helppes, jako první organizace ze zemí tzv. bývalého východního bloku, dokázal splnit podmínky nejvyšších mezinárodních standardů v poskytování služeb tohoto druhu a získal akreditaci mezinárodních odborných organizací Assistance Dogs Europe a Assistance Dogs International. Organizace Helppes je držitelem značky Spolehlivá veřejně prospěšná organizace, Ceny veřejnosti v ocenění "Neziskovka roku 2013 a 2014″, osvědčení NROS pro Zdravě řízenou NNO. Helppes, jako jediný v ČR, je také držitelem Certifikátu světové organizace Delta Society. Proč je pro klienty neziskové organizace důležitý výtěžek z tohoto projektu: Pouze vodicí psi pro osoby se zrakovým postižením jsou všem výcvikovým subjektům plně hrazeni státem, a to až do výše 350.000,-Kč. Ale všichni ostatní žadatelé, bez ohledu na typ a závažnost jejich postižení a jejich potřebnost, nedostanou na své psí pomocníky příspěvek od státu žádný a jsou proto pro možnost získání psího pomocníka zcela plně závislí na grantových řízeních, sponzorských darech, donátorech, výtěžku z tohoto projektu apod. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc rodinám s dětmi v nouzi', 'Oblastní charita Pardubice', 'POMOCRODINAM', 'Posláním Sociálně aktivizačních služeb pro rodiny s dětmi je poskytovat pomoc a podporu rodinám s dětmi z oblasti Pardubicka, jejichž vývoj je ohrožen v důsledku dopadů obtížné životní situace, kterou rodiče nedokážou sami bez pomoci překonat, a u kterých existují další rizika ohrožení jejich vývoje. Nabídkou odborných sociálních služeb směřujeme ke stabilizaci rodinné situace uživatelů a napomáháme tím k setrvávání dítěte ve své původní rodině. Služby jsou posktovány terénní formou. Snažíme se podporovat přirozenou kompetenci rodičů při péči o jejich děti.', 'Posláním organizace je pomáhat lidem v hmotné, sociální, zdravotní, duševní a duchovní nouzi. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc týraným psům začít nový, šťastný život', '"RAFAEL" První dočasný domov pro týrané psy - terapie, léčení a osvěta o.p.s.', 'RAFAEL', 'Cílem projektu je podpora neziskové organizace RAFAEL-ops, která zachraňuje a stará se o brutálně týrané psy. Po vyléčení jim hledá nové, milující rodiny. Peníze budou využity na veterinární péči, operace, léky a krmivo.', 'Posláním organizace je záchrana a ubytování týraných psů z celé ČR, jejich fyzické a psychické vyléčení a následně umístění do nových domovů. Osvěta pro školky a děti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomoc Ukrajině', 'Diecézní charita ostravsko - opavská', 'UKRAJINA', 'Humanitární pomoc obětem nepokojů na Ukrajině. Podpora zdravotnických zařízení a následná pomoc lidem, kteří se v důsledku nepokojů dostali do stavu nouze (léky, šatstvo, deky, pomoc zraněným, pozůstalým, apod.). Podpora rozvojových projektů místních partnerů.Diecézní charita ostravsko-opavská působí na Ukrajině od roku 2005. Za deset let činnosti se díky spolupráci s ukrajinskými partnery pomohlo tisícům potřebných lidí, předalo více jak 50 mil. korun a převezlo několik desítek tun materiální pomoci. Více informací na www.adoptujsi.cz ', 'Diecézní charita ostravsko-opavská (DCHOO) je nestátní neziskovou organizací, církevní právnickou osobou. DCHOO je samostatnou součástí Charity Česká republika. Územím jejího působení je ostravsko-opavská diecéze (oblast Moravskoslezského kraje a Jesenicka).Posláním Diecézní charity ostravsko-opavské je v souladu s jejími stanovami pomáhat lidem, kteří se ocitli v sociální, materiální, zdravotní, duševní, duchovní, nebo jiné nouzi, a to bez ohledu na vyznání, rasu nebo národnost, naplňovat přikázání křesťanské lásky k bližnímu, svědčit o rovnosti všech lidí a hodnotě každého jednotlivce. Více informací na www.dchoo.charita.cz. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomozte dětem!', 'Nadace rozvoje občanské společnosti', 'KURE', '• Dlouhodobý charitativní projekt organizovaný společně Českou televizí a Nadací rozvoje občanské společnosti spojený s celonárodní veřejnou sbírkou zaměřenou na přímou a účinnou pomoc ohroženým a znevýhodněným dětem do 18 let v celé ČR. • Sbírkové konto č.ú. 505333505/5500, které spravuje Nadace rozvoje občanské společnosti na základě osvědčení ke sbírce vydaného Magistrátem hl. m. Prahy, je otevřené pro individuální příspěvky i dárcovství, pro firemní dárcovství, pro výtěžky podpůrných sbírkových a benefičních akcí. • V průběhu celého roku jsou v rámci veřejné sbírky Pomozte dětem organizovány sbírkové a benefiční akce po celé ČR, které pořádají tzv." spojenci" sbírky. • Česká televize se významným způsobem podílí na organizování sbírkového projektu: Sbírka v přímém přenosu velkého zábavního večerního pořadu a pořadů z regionálních studií, celodenní vstupy - Studia Pomozte dětem, cyklus dokumentů Kde peníze pomáhají, který přináší divákům zpětnou vazbu o tom, jak byly využity nadační příspěvky. Česká televize realizuje velkou televizní i mimotelevizní kampaň v období vyvrcholení projektu. • Podle posledního průzkumu společnosti STEM si Pomozte dětem stále udržuje svoji vedoucí pozici mezi dobročinnými sbírkami. Pomozte dětem je nejtransparentnější a nejznámější českou sbírkou.', 'Nadace rozvoje občanské společnosti (NROS) existuje již dvacet tři let a patří mezi stabilní neziskové organizace v České republice. Jejím posláním je rozvíjet občanskou společnost a český neziskový sektor. Od svého založení úspěšně podpořila více než 6 000 projektů částkou přesahující 1,8 miliardy korun. Nadační příspěvky poskytuje z grantových programů pocházejících ze zdrojů Evropského hospodářského prostoru, ale i z individuálních či firemních zdrojů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomozte nám pomáhat', 'Ruka pro život o.p.s.', 'RUKAPROZIVOT', 'Podpořte činnost organizace, která již 6 let poskytuje sociální služby dospělým osobám s mentálním a kombinovaným postižením a seniorům. Vaše prostředky budou smysluplně využity na provoz denních stacionářů, nákup speciálních pomůcek a budovaní Domova pro osoby s poruchou autistického spektra.', 'Společnost poskytuje obecně prospěšné služby v oblasti poskytování všemožné podpory fyzickým osobám s mentálním, tělesným a kombinovaným postižením, fyzickým osobám s chronickým duševním onemocněním, fyzickým osobám ohroženým závislostí nebo závislým na návykových látkách, fyzickým osobám v nepříznivé sociální situaci či s jiným sociálním handicapem, dále seniorům, dětem a mládeži (dále společně jen „potřebné osoby"), a to zejména v oblasti sociální, ekonomické, kulturní a vzdělanostní. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomozte nám pomáhat', 'Český červený kříž', 'CCK', 'Cílem projektu je finanční zajištění pomoci poskytované Českým červeným křížem a dalšími složkami Mezinárodního hnutí Červeného kříže a Červeného půlměsíce při katastrofách, ozbrojených konfliktech a jiných mimořádných událostech v České republice i v zahraničí a podpora dalších činností Českého červeného kříže v souladu s jeho posláním a úkoly.Při své činnosti se Český červený kříž řídí 7 principy Mezinárodní hnutí Červeného kříže a Červeného půlměsíce, kterými jsou: HUMANITA NESTRANNOST NEUTRALITA NEZÁVISLOST DOBROVOLNOST JEDNOTA SVĚTOVOSTVíce informací o činnosti Českého červeného kříže naleznete na www.cervenykriz.eu.', 'Posláním Českého červeného kříže (ČČK) je zejména předcházet a zmírňovat utrpení, chránit zdraví, život a úctu k lidské bytosti, podporovat vzájemné porozumění, přátelství a mír mezi národy bez rozdílů národnostních, rasových, náboženských, řídních a politických a usilovat o naplňování základních principů hnutí Červeného kříže. Český červený kříž plní úkoly vyplývající z Ženevských úmluv, jejich dodatkových protokolů, rezolucí Mezinárodní konference Červeného kříže a Červeného půlměsíce a ze zákona č. 126/1992 Sb., o ochraně znaku a názvu Červeného kříže a o Československém červeném kříži, ve znění pozdějších předpisů. Činnost ČČK zahrnuje zejména přípravu v oblasti civilní obrany a ochrany obyvatelstva, výcvik první pomoci, poskytování humanitární pomoci v případě katastrof a jiných mimořádných událostí, poskytování zdravotnických, záchranných, sociálních a dalších humanitárních služeb, organizaci a podporu sociální a zdravotní výchovy dětí a mládeže. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomozte nám pomáhat psům a kočkám z útulku. PESOS', 'PESOS o.p.s.', 'PESOS', 'Cílem naší organizace je snaha o zlepšení podmínek v útulcích a záchranných stanicích pro opuštěná, týraná a handicapovaná zvířata. Získáváme finance na konkrétní projekty útulků, azylů ale i soukromých osob, která pečují o nechtěná zvířata. Zajištujeme finance na stavbu kotců, na nezbytné operační zákroky a jinou veterinární péči opuštěných zvířat a na zajištění krmiva pro všechna zvířata bez domova. Dále poskytujeme obecné i praktické informací o ochraně zvířat (např. jak postupovat v případech týrání zvířat atd.). Více o naší činnosti na www.pesos.cz.', 'Posláním organizace je pomoc opuštěným, týraným, handicapovaným a jinak postiženým psům. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomozte nám v Opavě pomáhat', 'Charita Opava', 'CHARITAOPAVA', 'Charita Opava pomáhá občanům Opavy již déle než 20 let a poskytuje ve městě více než polovinu sociálních služeb, díky nímž se zde lépe žije dětem i seniorům. Projekt POMOZTE NÁM V OPAVĚ POMÁHAT spočívá ve vzájemné solidaritě občanů Opavy - mnoho malých finančních darů zmůže dohromady hodně. Pokud nám pomůžete v Opavě pomáhat, pomůžeme ve chvíli, kdy to budete potřebovat, i my vám.', 'Posláním organizace je profesionálně poskytovat služby sociálně, zdravotně a společensky znevýhodněným lidem a zaměstnávat osoby s problémy na trhu práce. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pomozte nám vidět mámu a tátu každý den', 'EDA cz, z.ú.', 'EDA', 'Hlavně, aby bylo zdravé. To je přání, které doprovází nastávající rodiče celým těhotenstvím. Ale co když se vám narodí dítě nemocné? Je to situace, na kterou se nelze předem připravit. A právě proto tu jsou poradkyně rané péče, které přicházejí za rodinou domů, kde se dítě cítí nejlépe, a pomáhají rodičům zpracovat zcela novou situaci. Poradkyně je pro rodinu naprosto nepostradatelným pomocníkem. Učí je, jak s dítětem pracovat, jak mu pomoci se rozvíjet, půjčují stimulační hračky a celkově jsou podporou celé rodině až do 7 let věku dítěte. Součástí služeb EDY je posouzení zrakových funkcí a dovedností dítěte. Rodiče se například dozví, jak dítě opravdu vidí a jak jeho zrak trénovat.Cíle projektu: Rodina získá kvalitní péči a veškeré dostupné informace o metodách, pomůckách a postupech práce s dítětem, které má zrakové či kombinované postižení.Získané finanční prostředky jsou použity na péči a podporu rodin dětí se zrakových a kombinovaným postižením ve 4 krajích České republiky (Hl. město Praha, středočeský kraj, Ústecký kraj a Pardubický kraj).', 'EDA cz., z.ú. (do 31.12.2015 Raná péče EDA, o.p.s.) je držitelem značky SPOLEHLIVÁ veřejně prospěšná organizace. Současně jsme získali ocenění Neziskovka roku 2015.Službu rané péče poskytujeme rodinám s dítětem se zrakovým nebo kombinovaným postižením od jeho narození do 7 let věku již více než 25 let. Působíme v Praze, Středočeském, Pardubickém a Ústeckém kraji a naše služby jsou poskytovány především v domácím prostředí, kde se dítě cítí nejlépe. Provozujeme také jedinečnou celorepublikovou krizovou telefonickou a chatovou Linku EDA 800 40 50 60, která je určená rodinám dětí se závažnou diagnózou, po těžkém úraze, předčasně narozených nebo dětí s postižením. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Porucha autistického spektra - každé 100. dítě se rodí s touto diagnózou.', 'Národní ústav pro autismus, z.ú.', 'NAUTIS', 'Projekt je zaměřen na poskytování služeb lidem s poruchami autistického spektra (PAS). Cílem projektu je nabídnout uživatelům komplexní sociální služby - diagnostika poruch autistického spektra, základní a odborné sociální poradenství, raná péče, odlehčovací služby, služba osobní asistence, sociálně aktivizační služby, sociální rehabilitace, terapie aj.Projekt NAUTIS je unikátní v tom, že jako jediný v ČR, poskytuje nejkomplexnější nabídku služeb pro lidi s autismem a jejich rodiny.', 'V listopadu 2015 došlo ke změně právní formy a názvu našeho spolku Asociace pomáhající lidem s autismem - APLA Praha, Střední Čechy, o.s. na Národní ústav pro autismus, z.ú. K transformaci našeho spolku na novou právní formu - ústav, došlo v souladu s platnou legislativou dle nového občanského zákoníku. Hledali jsme novou právní formu i nový název, které by lépe vystihovaly naší činnost a pozici největšího poskytovatele služeb pro lidi s autismem v České republice.Po transformaci zůstáváme i nadále nestatní neziskovou veřejně prospěšnou organizací, s cílem zkvalitnit život lidí s autismem a jejich rodin. Poskytujeme služby psychologické, psychiatrické, sociální, vzdělávací a osvětové. Jsme registrovaným poskytovatelem terénních a pobytových sociálních služeb a realizujeme programy podporovaného zaměstnávání. Provozujeme ojedinělé celoroční pobytové zařízení pro osoby s autismem a těžkým problémovým chováním.Od června 2010 jsme stálým členem mezinárodní asociace neziskových organizací Autism-Europe se sídlem v Bruselu. Od té doby se aktivně podílíme na prosazování zájmů a práv lidí s autismem a jejich rodin v Evropě.Zkráceně si říkáme NAUTIS. NAUTIS je nejen zkratka složená z písmen Národního ústavu pro autismus, ale je to i námořní simulátor - pomůcka, která má pomoci lodím na moři najít správný směr na své cestě. Stejně jako si klademe my ve svém poslání nasměrovat lidi s autismem a všechny, kdo jim poskytují péči, správným směrem a podpořit je v bezpečné a klidné cestě směrem k spokojenému životu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Postavme školu v Africe', 'Člověk v tísni o.p.s.', 'AFRIKA', 'Ne každé dítě v Africe může chodit do školy. Často prostě proto, že v jeho okolí žádná škola není. Budováním škol a vzděláváním místních učitelů se Člověk v tísni snaží lidem pomoci dostat se ze začarovaného kruhu chudoby. Cílem projektu je stavět nové školy v Etiopii a dalších zemích Afriky.', 'Jsme nevládní nezisková organizace vycházející z myšlenek humanismu, svobody, rovnosti a solidarity. Lidskou důstojnost a svobodu považujeme za základní hodnoty. Věříme, že lidé kdekoli na světě mají mít právo rozhodovat o svých životech a společně sdílet práva vyjádřená Všeobecnou deklarací lidských práv. Usilujeme o otevřenou, informovanou, angažovanou a zodpovědnou společnost k problémům doma i za hranicemi naší země. Chceme se aktivně podílet na vytváření společnosti, ve které jsou kulturní, etnické, rasové a jiné odlišnosti zdrojem obohacení, a nikoli konfliktů. Jsme součástí občanské společnosti. Naše činnost vychází z osobní iniciativy a aktivity stovek našich zaměstnanců a dobrovolníků, jak v České republice, tak v desítkách zemí, kde pracujeme a z důvěry a podpory, kterou pro svoji práci získáváme od jednotlivců, firem, vlád i mezinárodních institucí. Oblasti a priority činnosti si určujeme sami, podle naléhavosti situace a podle našich reálných možností něco změnit. Nemůžeme pracovat všude, za podstatný považujeme skutečný dopad. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('POVODEŇ 2013 - POMÁHÁME ZOO PRAHA', 'Zoologická zahrada hl. m. Prahy', 'ZOOPRAHA', 'POVODNĚ Pomozte nám přestěhovat pražskou gorilí rodinu tam, kam na ni Vltava nemůže! Pražská zoo leží v malebném ohbí řeky Vltavy. Blízkost vody však ukrývá jedno riziko: povodně. Před menšími záplavami, do úrovně 20leté vody, chrání zoo protipovodňová hráz. Od roku 1931 však zahradu už třikrát zasáhly záplavy velké. Zatím poslední ničivé povodni čelila zoo v červnu 2013. Díky obdivuhodnému nasazení zaměstnanců zoo se z postižené oblasti podařilo odvézt do bezpečí téměř všechna zvířata a většinu majetku. Přesto se náklady na opravu poničených staveb vyšplhaly k několika desítkám milionů korun. Běžný provoz v zoo se nám podařilo obnovit i díky Vaší pomoci v rekordním čase. Ještě jednou Vám za vše velmi děkujeme! Hrozba další velké - stoleté či dokonce pětisetleté - vody, proti které nepomohou ani plánovaná opatření, však nad zoo visí neustále. A spolu s ní i nebezpečí, že povodeň opět zaplaví i stávající pavilon goril, který se nachází v jejím nejnižším místě. Přemisťování do protipovodňové věže pavilonu je pro lidoopy velmi stresující, jejich případná evakuace by pak byla spojena s obrovskými riziky. Další velká povodeň by tak pravděpodobně znamenala, že bychom museli od chovu našich nejoblíbenějších a nejpopulárnějších zvířat upustit. To pro nás samozřejmě nepřichází v úvahu! Richardovi a jeho rodině se proto chystáme stavět nový pavilon tam, kam se na ně velká voda nedostane. Architektonický návrh již máme. Jen začít stavět, řeklo by se... Stavba nového domova pro naše gorily však kromě velkého úsilí bude stát i nemalé peníze. Budeme Vám proto velmi vděčni, pokud se rozhodnete na stavbu nového pavilonu přispět. I malý příspěvek pomůže gorilám bydlet už napořád v suchu! Děkujeme Vám! Bez Vaší pomoci bychom se - ani tentokrát - neobešli.', 'Zoologické zahrady dnes působí ve světě, v němž je stále více ohrožována druhová pestrost přirozených ekosystémů neboli biodiverzita. Jsou to právě tyto instituce, které mají možnost postihnout celý rejstřík ochranářských aktivit, od výzkumu přes ochranu ex-situ (včetně chovu ohrožených druhů a výchovné činnosti) po účast na projektech in-situ přímo v terénu.Výchova a vzdělávání Každým rokem přichází do zoologických zahrad na celém světě více než 600 milionů návštěvníků nejrůznějšího věku i zájmů. V pražské zoo se během posledních tří let počet návštěvníků zdvojnásobil na více než 1,2 milionu osob ročně. Je to velká výzva i závazek. Zoologická zahrada tak ovlivňuje svými výchovnými a vzdělávacími aktivitami širokou veřejnost i zájmové skupiny. Je schopna díky neocenitelnému potenciálu živých zvířat působit u návštěvníků na jejich názory, postoje, vytváření hodnot a chování.Chov zvířat Pražská zoo jako moderní instituce svého druhu vytváří pro chované živočichy co nejoptimálnější podmínky, a to jak koncepcí a ztvárněním výběhů i ubikací, tak sestavováním jedno- či vícedruhových skupin. Návštěvníci si tak mohou vytvořit představu o životě zvířat a jejich vazbách k prostředí, zvířata pak mají příležitost rozvinout co nejširší rejstřík přirozeného chování.Ochrana ex-situ Zoo Praha se dlouhodobě podílí na ochraně živočichů ex-situ. Vytváří pojistné chovné skupiny u druhů, které jsou v přírodě bezprostředně ohrožené, nicméně u kterých již probíhají snahy o zpětný návrat nebo je lze očekávat.Ochrana in-situ Stále výrazněji se pražská zoo podílí na projektech in-situ, jejichž cílem je ochrana živočichů přímo v místě jejich výskytu. Jedná se o zapojení do reintrodukcí ohrožených druhů do rezervací i volné přírody nebo o metodickou a materiální pomoc v těchto oblastech. Programy se týkají mimo jiné koně Převalského, gorily nížinné, adaxe, ibise skalního, zubra, čápa černého, orlosupa bradatého, supa hnědého, užovky podplamaté, sysla obecného, kvakoše nočního, puštíka bělavého, sovy pálené a sýčka obecného. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pozvedněte slabé!', 'Nadace EURONISA', 'EURONISA', 'Sbírka je zaměřena na podporu neziskových organizací, které jejím prostřednictvím mohou získávat další prostředky na svou činnost. Veřejná sbírka bude prováděna na území české části Euroregionu NISA (Liberecký kraj a oblast Šluknovského výběžku). Spuštěna byla k 1. prosinci 2003.Účel konání veřejné sbírky: Shromažďování finančních prostředků za účelem podpory sociálních a charitativních aktivit v české části Euroregionu Nisa.', 'Základním cílem nadace, který vychází z tradičních křesťanských hodnot, je pomáhat lidem žít plnohodnotný život nezávisle na jejich věku, sociálním postavení a zdravotním stavu. Účelem nadace je podpora projektů a činností v oblasti sociální, kulturní a vzdělávací, především v české části Euroregionu Nisa (okresy Liberec, Jablonec nad Nisou, Semily, Česká Lípa a oblast Šluknovského výběžku). Za tímto účelem nadace shromažďuje a posuzuje jednotlivé projekty sociálních, kulturních a vzdělávacích aktivit. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Praga-Haiti', 'Fidcon', 'PRAGAHAITI', '. Výstavba studní s pitnou vodou v severozápadní oblasti na Haiti. Podpora misie v Baie-de-Henne. . Poskytujeme konkrétní, efektivní a dlouhodobou pomoc nejchudším obyvatelům Haiti. Zajišťujeme zdroje pitné vody v nejméně dostupných oblastech. Nabízíme práci místním lidem. Učíme je dovednostem potřebným pro dlouhodobý rozvoj.', 'Podstatou činnosti Fidcon je vzdělávání. Snažíme se, aby byli lidé odpovědní vůči sobě, druhým a prostředí kolem sebe. Motivujeme k hodnotám, kterých si společnost váží. Realizujeme manažerská školení, teambuildingy, zážitkové kurzy, humanitární projekt Praga-Haiti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Prague Pride, miluji Tě!', 'Prague Pride z.s.', 'PRAGUEPRIDE', 'Prague Pride už není jen nejteplejším festivalem v Česku! Letos jsme spustili poradnu sbarvouven.cz pro LGBT lidi a jejich příbuzné a přátele. Rozjíždíme queer akce i v dalších velkých městech země. Navíc slavíme páté výročí ohřívání srpnové Prahy. Letos se potkáme mezi 10. a 16. srpnem na více než 100 akcích na různých místech hlavního města. Průvod Prahou projde v sobotu 15. srpna. Jsme dobrovolnickou organizací a vaše DMS nám může pomoct udělat festival ještě větší, barevnější a veselejší. Více se o nás dozvíte na www.praguepride.cz.', 'Naším cílem je podpora a propagace tolerantní společnosti. Pracujeme na odstraňování předsudků, které vedou k diskriminaci z důvodu sexuální orientace. Pořádáme největší LGBT akci v Česku a jednu z největších ve střední Evropě! Takže v létě se uvidíme v Praze. Každý tolerantní a otevřený člověk je vítán. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Pravidelné klauniády pro děti', 'Zdravotní klaun o.p.s.', 'KLAUN', 'Cílem projektu je získat finanční prostředky na zajištění pravidelných návštěv zdravotních klaunů na lůžkových odděleních ve fakultních nemocnicích v Čechách a na Moravě a na průběžné udržování vysokého standardu a profesionality vystoupení zdravotních klaunů. Návštěvy klaunů, tzv. klauniády budou v rámci tohoto projektu probíhat na dětských lůžkových odděleních 64 nemocnic ve všech krajích České republiky ( celkový přehled nemocnic je uveden ve výroční zprávě z r. 2014). V roce 2014 jsme uskutečnili 3162 pravidelných klauniád pro děti celkem.', 'Zdravotní klaun je nezisková organizace s celostátní působností, která přináší humor a radost hospitalizovaným dětem, geriatrickým pacientům a dalším potřebným v oblasti zdravotnictví, čímž přispívá ke zlepšení jejich psychického a tím i celkového zdravotního stavu, a to prostřednictvím speciálně vyškolených zdravotních klaunů a speciálních projektů. V současné době 86 zdravotních klaunů navštěvuje pravidelně 64 nemocnic a 7 domovů pro seniory. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('PRO MATĚJE', 'PRO MATĚJE z.s.', 'MATEJ', 'Z opakující se angíny se po standardní operaci mandlí Matějova diagnóza proměnila na stav silného poškození mozku. Nejvíc bolí, že se to celé stát nemuselo .... Matěj to nevzdal a byl v tomto stavu předán rodičům do domácí péče, která je extrémně náročná, zejména tím, že je nepřetržitá. A tak tedy tam kde již síly maminky Matěje nestačí, rodiče se obrací na péči odborných asistentek. Po tom všem čím Matěj prošel a stále prochází, je ale domácí péče jediným dostupným řešením k tomu, aby mohl Matěj nadále žít alespoň trochu důstojně. Matýsek dokáže projevit radost i smutek z některých věcí a dokáže dávat najevo, že je rád, že stále žije a že je doma. I díky Vám může žít Matěj důstojněji v domácím prostředí s námi a může čekat, zdali věda nenalezne způsob, jak mu více pomoci. Pro Matějovy rodiče představuje příspěvek na Matýskovo speciální sbírkové konto tu největší pomoc. Díky příspěvkům od dárců jsou schopni poskytnout Matýskovi kvalitní domácí péči a nakoupit zdravotní pomůcky, které Matýsek životně nutně potřebuje. Ani jednu z těchto věcí nehradí zdravotní pojišťovna. Moře potřebné pomoci se však skládá i z malých kapek a tak jakýkoliv finanční příspěvek vrací Matýskovi naději a rodičům víru, že další měsíce budou mít jeho péči z čeho platit.Celý příběh Matěje a jeho rodiny si můžete přečíst na www.promateje.cz.', 'Posláním organizace je pomáhat rodinám s dětmi s těžkým poškozením zdraví, zejména rodinám s dětmi v komatu, aktuální cíl - pomoc pro Matěje Kociána. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Proč zrovna já?', 'KAFIRA o.p.s.', 'KAFIRA', 'Proč zrovna já? Tuhle otázku často slýcháváme a ještě častěji si ji kladou naši klienti - děti od 7 let, dospělí a senioři se zrakovým nebo kombinovaným postižením.Nevíme, proč zrovna Oni, ale můžeme a umíme jim pomoci získat nebo znovu nabýt soběstačnost a samostatnost, zvládnout péči o domácnost, o sebe, nakoupit, uvařit. Pomůžeme najít vhodnou práci, pomůcku, která usnadní běžné denní činnosti, řešit další otázky a problémy, s kterými si neví rady. A nezapomínáme také na vzdělávání, duševní pohodu a volný čas.Našimi stěžejními aktivitami jsou: - nácvik péče o domácnost, vč. nácviku nakupování potravin a přípravy stravy, - nácvik sebeobsluhy, - nácvik prostorové orientace a samostatného pohybu, vč. nácviku využívání dopravních prostředků - nácvik Braillova bodového písma, - nácvik práce s kompenzačními a dalšími pomůckami pomáhající zvládat zrakové omezení - půjčovna kompenzačních pomůcek, - doprovody a aktivizační činnosti, - nácviky využívání zbytku zraku a stimulace dalších smyslů prostřednictvím Snoezelen prostředí a dalších technik, - počítačové kurzy a kurzy angličtiny, - pomoc se získáním sociálních příspěvků, dávek a s řešením nepříznivé sociální situace, - podpora při pracovním začleňování, vč. podpory udržení práce - podpora rodičů postižených dětí i dalších rodinných příslušníků všech klientů.Naše působnost je v rámci Moravskoslezského kraje. Služby poskytujeme buď v regionálních pobočkách v Opavě, Ostravě, Novém Jičíně a Frýdku-Místku, nebo v terénu a domácnosti klienta. A to celoročně v pracovní dny.Chcete se dozvědět více? Víte o někom, kdo by naši pomoc potřeboval? Více na www.kafira.cz nebo www.facebook.com/kafiraopava.Všem dárcům děkujeme.', 'Posláním společnosti je podpora nezávislého a samostatného života dětí od 7 let a dospělých, které jejich poškození zraku ovlivňuje při každodenních činnostech běžných ve společnosti. Snažíme se vytvořit zázemí, které povzbuzuje, motivuje a dodává „chuť do života". Poskytujeme registrované sociální služby, které podporují soběstačnost a samostatnost a zvyšují šance nalézt vhodné pracovní uplatnění. Služby jsou poskytovány v terénu v rámci celého Moravskoslezského kraje, nebo ve střediscích v Opavě, Ostravě, Novém Jičíně a Frýdku-Místku.Naši snahou také je, oslovovat společnost tak, aby sama byla pro uživatelé našich služeb podobným zázemím s tolerancí k odlišnosti a s postojem „oni patří také k nám". ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Projekt adopce významných pražských hrobů', 'Správa pražských hřbitovů', 'SPH', 'Cílem projektu je restaurování a údržba hrobových zařízení významných hrobů na veřejných pohřebištích ve správě SPH, které byly zařazeny do Projektu adopce významných hrobů a od jejichž zařazení do tohoto projektu uplynulo více než 2 roky, aniž by v této době došlo k uzavření adopční nájemní smlouvy, nebo aniž by SPH přijala účelový dar, určený na opravu takového hrobového zařízení v takové výši, který by umožnil provést jeho celkovou opravu.', 'Provozování komplexních služeb souvisejících se správou a provozem pražských pohřebišť. Péče o válečné hroby, správa nemovitých kulturních památek, údržba zeleně. Vypravování pohřbů, poskytování souvisejících služeb. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Protajči se ke zdraví', 'Aliance žen s rakovinou prsu, o. p. s.', 'PROTAJCI', 'Speciální zdravotní cvičení Tai-chi, které má pomoci ženám po léčbě rakoviny prsu s regenerací a posílením jejich organizmu a s návratem k co nejvíce plnohodnotnému životu. Kromě toho je ale i propagací důležitosti zdravého pohybu a zdravého životního stylu pro každého člověka jako prevence vzniku rakoviny a civilizačních chorob obecně. Projekt zahrnuje pravidelná cvičení, ukázkové semináře, benefiční a propagační akce. Tento projekt Aliance žen s rakovinou prsu probíhá ve spolupráci s Českou obcí sokolskou a je přístupný i pro veřejnost.', 'Aliance žen s rakovinou prsu poskytuje informační a edukační servis pro onkologicky nemocné, koordinuje projekty a kampaně ve prospěch prevence a léčby rakoviny prsu, zajišťuje metodickou pomoc pacientským organizacím a sjednocuje jejich snahy při prosazování práv a rovných podmínek k léčení a rekonvalescenci nemocných, zastupuje české onkologické pacientky v mezinárodních organizacích. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Provoz Česko-ugandské nemocnice v Buikwe', 'Arcidiecézní charita Praha', 'UGANDA', 'Sbírka je určena na podporu Česko-ugandské nemocnice sv. Karla Lwangy, kterou v Ugandě vybudovala a provozuje Arcidiecézní charita Praha. Jde o unikátní projekt v rámci zahraniční rozvojové spolupráce ČR - největší stálou českou nemocnici, která je umístěna ve vesnické oblasti, ročně poskytuje kvalitní lékařskou péči až 30.000 pacientů z řad nejchudších obyvatel regionu Buikwe. Tým 100 pracovníků zajišťuje kvalitní lékařskou péči, osvětu i preventivní péči.Nemocnice zahrnuje čtyři ambulance, operační sál, lůžková oddělení, porodnici nebo ordinaci zubaře. K dispozici je laboratoř, rentgen a další přístroje pro stanovení diagnózy. Tým zdravotníků týdně vyjíždí do nepřístupných vesnických oblastí, aby poskytl prvotní ošetření a poradenství 250-300 pacientům, kteří nemají prostředky, aby se dopravili za lékařem. Zvláštní pozornost je věnována pacientům s HIV/AIDS, kterým je poskytována antiretrovirální léčba, a dětem trpícím podvýživou.', 'Arcidiecézní charita Praha (dále jen Charita Praha) poskytuje pomoc v nouzi těm, kteří se ocitli v tíživé životní situaci nebo na okraji společnosti, zejména pak rodičům (matkám) s dětmi v tísni, lidem se zdravotním postižením, seniorům, lidem bez domova a mládeži z dětských domovů. Významnou činností Charity Praha je také zahraniční rozvojová spolupráce. V oblasti vzdělávání, zdravotní péče a prevence, zemědělství, komunitního rozvoje a sociální práce jsou podporovány nejchudší děti, rodiny a celé komunity k dosažení soběstačnosti s trvale vyšší životní úrovní. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Prožít co nejvíce, trpět přitom co nejméně.', 'DEBRA ČR, z.ú.', 'DEBRA', 'Projekt Prožít co nejvíce, trpět přitom co nejméně umožní zkvalitnění života lidí s nemocí motýlích křídel.Posláním charitativní organizace DEBRA ČR je zvyšovat kvalitu života pacientům, kteří trpí vzácnou vrozenou vadou kůže epidermolysis bullosa congenita - zkráceně EB, známou také jako nemoc motýlích křídel. Snažíme se zapojovat lidi s touto nevyléčitelnou nemocí do plnohodnotného života.Zasláním DMS pomůžete i vy lidem s EB prožít co nejvíce a trpět přitom co nejméně! Veřejnou sbírku realizujeme na tyto účely: 1. přímou finanční podporu lidí s EB (úhrada věcí a služeb, které využívají lidé s EB) 2. nepřímou finanční podporu lidí s EB - finanční podporu projektů DEBRA ČR 3. nepřímou finanční podporu lidí s EB - finanční podpora Klinického EB Centra při Fakultní nemocnici Brno 4. nepřímou finanční podporu lidí s EB - podpora výzkumu léčby nemoci EB', 'Charitativní organizace DEBRA ČR, z.ú. byla založena v roce 2004. Jsme členem mezinárodního uskupení DEBRA INTERNATIONAL. Organizace DEBRA na celém světě podporují lidi se vzácným puchýřnatým onemocněním Epidermolysis bullosa congenita (EB) - v Česku nazývaným nemoc motýlích křídel. Slovo DEBRA znamená Dystrophic epidermolysis bullosa Research Association. Naše aktivity směřují k tomu, aby lidé a rodiny žijící s EB nebyli ve své obtížné sociální situaci na základě svého znevýhodnění izolováni a dokázali čelit tíživé ekonomické situaci, velkým psychickým a sociálním problémům. Cílem činnosti DEBRA ČR je pomoci prosazovat a hájit zájmy pacientů s EB, zabezpečovat jejich informovanost a spolupracovat s vládními a krajskými úřady. Našim klientům nabízíme: - sdružování lidí s EB a rodin, kde žije takto nemocný člen; - osobní a individuální přístup; - odborné sociální poradenství; - zdravotní a výživové poradenství; - psychologickou podporu; - přímou finanční podporu a pomoc nemocným s EB prostřednictvím veřejné sbírky; - realizaci projektů pro lidi s EB; - šíření osvěty o onemocnění EB. Služby a sociální poradenství jsou poskytovány zdarma.Co je onemocnění EB? EB je zkrácené označení vrozeného dědičného puchýřnatého onemocnění kůže Epideromolysis bullosa congenita. Tato choroba je geneticky podmíněná a zatím ji nelze zcela vyléčit. Díky vysoce specializované lékařské péči se však daří život nemocných s EB výrazně zkvalitňovat. Projevy EB jsou velmi rozmanité. Kůže lidí s EB je křehká jako křídla motýlů. Příčinou EB jsou „chyby" v genu, které naruší soudržnost kůže. I mírný tlak či tření kůže pak způsobuje tvorbu puchýřů a bolestivých, otevřených ran na kůži a sliznicích (např. v dutině ústní, v jícnu, trávicím nebo močovém ústrojí). Postižení nehtů, vlasů, zubů je v různém rozsahu. Onemocnění EB neovlivňuje intelekt. Na světě žije asi půl miliónů lidí s EB, v Evropě je to 30 000 a v České republice asi 250 pacientů s EB. Proč nemocní s EB potřebují pomoci? - Nemocní s EB neznají den bez bolesti. - Onemocnění EB je doposud nevyléčitelné. - Péče o nemocného s EB je náročná - časově, psychicky i finančně. - Nemoc motýlích křídel zasahuje celou rodinu, kde se narodí takto nemocný člen. - Lidé s EB bývají často sociálně znevýhodněni a vyloučeni. Mají problém získat příspěvky a výhody státního systému ČR. - EB je velmi vzácné onemocnění. Jeho léčbě se věnuje pouze jediné centrum v České republice, EB Centrum ČR v Brně. Proto může být nejen pro laiky, ale i pro řadu lékařů, sociálních pracovníků a dalších odborníků neznámé. - Malý počet nemocných je problémem např. při vyjednávání se zdravotními pojišťovnami nebo při zajišťování odborných studií, které hledají terapii pro pacienty s EB. - Lidé s nemocí motýlích křídel mají stejné schopnosti, nadání a přání jako všichni ostatní. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('První dny na svobodě', 'Centrum podpory podnikání Praha, o.p.s.', 'PRVNIDNY', 'Centrum podpory podnikání Praha pomáhá propuštěným osobám z Věznic z celé ČR najít zaměstnání a začlenit se do společnosti. Přispívá tak ke snižování kriminality a recidivy v ČR. Pro propuštěné čeká vždy garantovaná pracovní pozice. První dny na svobodě však bývají pro tyto osoby nejtěžší - z Věznice vyjdou bez peněz a bez zajištěného ubytování. Často tak během několika dnů sejdou ze správné cesty a vrátí se ke kriminální činnosti, jelikož jim mnohdy nic jiného nezbývá. Přispějte nám na jejich stravu a ubytování a usnadněte jim tak přechod do normálního života. Pomůžete tak nejen jim, ale celé společnosti.', 'Centrum podpory podnikání Praha o.p.s. vzniklo s cílem podporovat podnikatelskou gramotnost u těch skupin lidí, které se nemají šanci sami dostat zpět na trh práce po těžkém životním období vlastními silami. Ať už se jedná o rodiče po rodičovské dovolené, matky po mateřské dovolené, zdravotně indisponované, nebo bývalé vězně. Ti všichni potřebují pomoc při hledání zaměstnání nebo při startu vlastního podnikání. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Překročit RUBIKON', 'RUBIKON Centrum', 'RUBIKON', 'Cílem projektu je poskytování bezplatných komplexních služeb vedoucích k integraci osob s trestní minulostí zpět do společnosti: služby pro zvýšení zaměstnanosti, k řešení zadluženosti a efektivity alternativních trestů, vč. programů ke snížení recidivy mladistvých pachatelů.', 'Přes 20 let vytváříme programy ke snižování recidivy. Naší snahou je opětovně začlenit lidi s trestní minulostí do života, rozvíjet jejich konkurenceschopnost na pracovním trhu a posílit jejich ekonomickou situaci.Podpořit sociální stabilizaci a vstup na trh práce lidí s trestní minulostí se vyplácí nejen pro ně samotné, ale i pro celou společnost. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rekonstrukce útulků v České republice', 'Nadační fond PES V NOUZI', 'PESVNOUZI', 'Cílem projektu je získání financí na rekonstrukce, opravy a zateplovací práce v psích útulcích, azylech a karanténních stanicích v České republice.', 'Naše pomoc se soustředí především na návrat ztracených pejsků zpět do rodin, podporu psí adopce, zajištění chybějící veterinární péče v útulcích, rekonstrukce útulků a zajištění chybějícího krmiva a potřeb pro útulky a depozita. Nadační fond poskytuje materiální a finanční příspěvky psím útulkům, karanténním stanicím, depozitům a azylům, fyzickým a právnickým osobám, neziskovým organizacím, pokud jejich projekty souvisí s posláním a cíli nadačního fondu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rekonstrukce zastřešení a stavební obnova hradu Vízmburk', 'Sdružení pro Vízmburk, o.s.', 'VIZMBURK', 'Některé obvodové zdi budou opraveny. Do zdi a někde do terénu budou zabudovány kovové patky, na kterých budou stát kovové sloupy. Na sloupy budou upevněny kovové vaznice. Tam budou osazeny dřevěné trámy, které budou pobity prkny. Na nich bude izolace a krytina. Okapy budou z mědi.', 'Cílem organizace je záchrana hradu Vízmburk a jeho postupné zpřístupnění veřejnosti, záchrana a prezentace archeologického fondu z hradu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Restaurování rychlíkového vozu Calm z roku 1936', 'NADACE OKŘÍDLENÉ KOLO', 'CALM', 'Naším hlavním cílem je získávat finanční prostředky pro zachování kulturního technického dědictví železnice. Obsahově se jedná o značné množství staveb, zařízení a vybavení železnice. Nejpopulárnější jsou jak železničářské, tak i laické veřejnosti železniční kolejová vozidla (ŽKV). Opravit, resp. restarurovat osobní vůz Calm do původního stavu, tak, aby při tom splňoval nynější technické podmínky pro jeho provozování po železnici, je malý zázrak. Každé zprovoznění železničního kolejového vozidla je samo o sobě unikátní a svou odbornou, ale i finanční náročností se liší od ostatních projektů.', 'NADACE OKŘÍDLENÉ KOLO je nepolitická nezávislá nezisková právnická osoba sloužící výhradně obecně prospěšným cílům. Posláním nadace je získávání finančních i nefinančních prostředků pro opravy a údržbu historických železničních kolejových vozidel, dražních staveb, zařízení a  vybavení. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('ROSA: Stop násilí', 'ROSA - centrum pro týrané a osamělé ženy', 'ROSA', 'Domácí násilí a násilí ve vztahu může ohrozit kohokoliv z nás. Oběti potřebují nejen odvahu, ale i pomoc, aby našly sílu řešit násilí v partnerském vztahu. Cílem projektu je podpora sociálních služeb poskytovaných občanským sdružením ROSA obětem domácího násilí • azylového ubytování pro ženy oběti domácího násilí a jejich děti s utajenou adresou • odborného a opakovaného sociálního a sociálně právního poradenství v Informačním a poradenském centru ROSA • telefonické krizové linky pro všechny oběti domácího násilí a jejich příbuzné', 'ROSA - centrum pro týrané a osamělé ženy je nestátní nezisková organizace, která se zaměřuje na přímou komplexní pomoc ženám - obětem domácího násilí a jejich dětem a na prevenci tohoto jevu. ROSA usiluje o zlepšení systému pomoci ženám - obětem domácího násilí a jejich dětem. Jeho součástí je i odborné specializované poradenství a  komplexní pomoc pro ženu - oběť domácího násilí, mezi níž patří krizová intervence, sociálně-terapeutické poradenství, vytvoření bezpečnostního plánu a poskytování utajeného azylového bydlení pro ženy - oběti domácího násilí . ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Roz(h)led', 'Nadání a dovednosti o.p.s.', 'ROZHLED', 'V rámci individuálního kariérového poradenství hledají děti pod vedením profesionálního kariérového kouče svoje silné a slabé stránky, zjišťují, jak nejlépe využít své dovednosti a zkušenosti. Každý účastník/účastnice následně absolvuje tři exkurze, které odpovídají jeho/její profilaci. Následuje porada s kariérním koučem zaměřená na reflexi zážitků z exkurzí. Workshop Nakopni svou kariéru nabízí účastníkům/účastnicím tipy na zlepšení sebeprezentace a komunikace a přípravu na pracovní pohovory včetně psaní CV. Během stínování má účastník/účastnice možnost poznávat detailnější pracovní náplň své budoucí profese. Závěrem účastník/účastnice zhodnotí stínování i přínos celého projektu nejprve s kariérním koučem a potom s ostatními kolegy a kolegyněmi, zakomponuje své nově nabyté zkušenosti do CV a spolu s koučem zformuluje další možnosti svého rozvoje po skončení projektu.', 'Naším posláním je pomáhat znevýhodněným žákům, studentům a absolventům z dětských domovů a z pěstounské péče se správným výběrem studia a se vstupem na trh práce. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rozmazaný svět', 'Nadace prof. Vejdovského', 'KUK', 'Získané prostředky budou pomáhat zlepšovat nezávislost, samostatnost, soběstačnost a životní úroveň osob se zrakovým či kombinovaným postižením a zvyšovat kvalitu mnohdy již, ve srovnání s jinými zeměmi, zastaralých postupů, přístupů při vzdělávání a práci s osobami se zrakovým postižením, ale také při léčbě závažných očních chorob, jejich diagnostice a předcházení těmto chorobám.', 'Posláním organizace je vzdělávání a péče o zrakově postižené. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rozsviťte s námi Prahu, aneb Každý má svou žárovku!', 'Nadační fond "N"', 'ZAROVKA', 'Projekt připravuje Radio City každý rok pro vybranou neziskovou organizaci. Každý člověk může prostřednictvím DMS sponzorovat žárovku na pražském vánočním stromě na Staroměstském náměstí, virtuálně se pak žárovka rozsvítí na webových stránkách projektu. V letošním roce bude výtěžek věnován Nadačnímu fondu "N" - nemocným a předčasně narozeným dětem na zakoupení přístroje, který by zamezil zbytečným odběrům krve novorozenců. ', 'Posláním organizace je rozvíjení a zlepšování neonatologické péče ve FN Motol, podpora preventivní léčebné a zdravotně sociální péče o novorozené děti, snižování novorozenecké úmrtnosti, zlepšení přístrojové vybavenosti na úroveň špičkových světových pracovišť, růst odborné úrovně podporováním studijních pobytů lékařů i sester. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rozšíření výběhu pro Konopišťského medvěda', 'Konopišťský medvěd o.s.', 'KONOPISTE', 'Sbírka občanského sdružení Konopišťský medvěd je spuštěna za účelem shromáždění finančních prostředků na tyto účely:1. peníze na rozšíření výběhu pro medvěda 2. peníze na každoroční údržbu výběhu, plat medvědáře a potravu pro medvěda 3. peníze na pořízení nové medvědice - partnerky současného medvěda Jiřího za účelem spuštění chovu ', 'Cílem sdružení je udržení a rozvoj chovu medvědů na Konopišti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rozvoj dobrovolnických programů ADRA v ČR', 'ADRA o.p.s.', 'ADRADOBROVOLNIK', 'Hlavním cílem projektu je organizace a podpora dobrovolnických programů u seniorů, dětí a mládeže, sociálně potřebných či osob se zdravotním postižením. Cílem tohoto projektu je spolupráce s organizacemi, které projevily zájem o začlenění dobrovolnictví do systému své práce. Dobrovolnická centra ADRA pomáhají tyto dobrovolníky nalézt, vyškolit, koordinovat jejich službu včetně péče o samotné dobrovolníky. Dobrovolníci pak přinášejí uživatelům sociálních a zdravotnických zařízení něco navíc, pomáhají jim radovat se ze života nebo vyplnit jejich volný čas.', 'ADRA, o. p. s. Česká republika organizuje a uskutečňuje pomoc lidem v ohrožení života a majetku, který je způsobeno přírodními katastrofami nebo válečnými konflikty. Svými aktivitami rovněž podporuje rozvoj společnosti v oblasti sociální vzdělávací a osvětové při respektu základních lidských práv a svobod doma i ve světě. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rozvoj Historicko-řemeslně vzdělávacího centra Šelmberk', 'Danar z.s.', 'DANAR', 'Cílem projektu je vybudování Historicko-řemeslně vzdělávacího centra Šelmberk v areálu stejnojmenného hradu, které bude zaměřené na vzdělávání široké veřejnosti (dětí i dospělých) v oblasti historie, tradičních řemesel, experimentální archeologie a environmentální výchovy.Koncepce projektu zahrnuje vybudování středověkého řemeslného podhradí (celkem 7 řemeslných dílen a hospodářství s informačním systémem) pro účely prezentace života ve středověku široké veřejnosti, stálou expozici o vzniku, stavbě a funkcích středověkých hradů v hradních prostorách, zřízení informačního centra a celkovou rekonstrukci stávající budovy ubytovny s cílem vytvořit vybavené prostory pro pravidelné výukové programy a zlepšit ubytovací podmínky pro účastníky těchto programů. Finanční prostředky získané prostřednictvím sbírky budou použity zejména na vybudování venkovních řemeslných dílen středověkého charakteru evokujících řemeslné a hospodářské zázemí hradu, na rekonstrukci budovy ubytovny - opravu střechy, zateplení, vybudování vnitřních řemeslných dílen a vzdělávacích prostor v půdních prostorách, vybudování ČOV a dalších prvků technického zázemí a také na běžnou údržbu areálu a stávajících prvků centra. ', 'Danar z.s. se zabývá historicko řemeslně vzdělávacími aktivitami s cílem pozitivně ovlivnit zejména mladou generaci. V současné době nabízí mnoho originálních aktivit a programů s důrazem na vzdělávací charakter a odlišnost od konzumní zábavy. Získání dlouhodobého pronájmu hájovny Šelmberk spojené s pověřením provozování a údržby hradu Šelmberk bylo popudem k vytvoření ucelené koncepce rozvoje lokality jako Historicko řemeslně vzdělávacího centra Šelmberk. V rámci této koncepce zde sdružení kromě postupné renovace areálu pořádá ekologické, historické řemeslné vzdělávací pobytové programy, kulturní akce pro veřejnost, mezinárodní setkání a soutěže mládeže. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rozvoj zdravotní, sociální a rehabilitační péče pro lidi nemocné roztroušenou sklerózou v Domově sv. Josefa', 'Oblastní charita Červený Kostelec', 'DOMOVJOSEF', 'V Domově sv. Josefa - dosud jediném nestátním zdravotnickém a sociálním zařízení pro nemocné roztroušenou sklerózou (RS) - pečujeme již od roku 2001 o lidi s RS v těžkém stádiu nemoci. Základem a cílem naší činnosti je pochopení jedinečnosti každého člověka a úcta k jeho osobnosti a potřebám s ohledem na toto závažné onemocnění. Při komplexní péči o takto nevyléčitelně nemocné se snažíme naplnit celou šíři potřeb pro jejich důstojný a plnohodnotný život. Usilujeme o poskytování péče a služeb, které pomohou nemocným RS posílit nebo vrátit sebeúctu a radost ze života, možnost sociálních kontaktů, sebeobsluhy a zvýšení sebevědomí pocitem, že i když je život s RS velmi těžký, je to dar a je dobré ho ŽÍT.', 'Obecným předmětem činnosti je zřizování a provozování charitativních služeb, domovů, zdravotnických, sociálních, školských a jiných zařízení a služeb, jejich metodické vedení a poradenský servis, provozování půjčoven zdravotnických pomůcek, zajišťování a poskytování duchovní a pastorační péče, pořádání sbírek a humanitárních a kulturních akcí za účelem pomoci potřebným v České republice a v zahraničí. Organizace poskytuje zdravotní a sociální služby (má registrováno 6 sociálních služeb), dále vzdělávání (vlastní akreditaci vzdělávacího střediska i akreditaci jednoho vzdělávacího projektu), volnočasové aktivity pro děti a mládež a další. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Rozvojová spolupráce ve světě.', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce', 'DIAKONIESVET', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce se v oblasti rozvojové spolupráce specializuje zejména na zemědělství, podporu zdrojů obživy a sociální služby.V Moldavsku se např. věnuje poskytování domácí péče seniorům v severní části země. V Gruzii zase podporuje místní organizaci, která se věnuje dospělým lidem s mentálním postižením. V Etiopii již několik let díky postní sbírce podporuje ženy-samoživitelky, aby byly schopny dlouhodobě zabezpečit své rodiny. Kromě toho v jižní Etiopii pracuje s chudými zemědělskými komunitami.', 'Diakonie ČCE - Středisko humanitární a rozvojové spolupráce poskytuje humanitární pomoc při mimořádných událostech v České republice a organizuje rozvojovou spolupráci a humanitární pomoc v zahraničí. V České republice pomáháme při povodních a věnujeme se také prevenci a připravenosti obcí na mimořádné události. Pracujeme s dobrovolníky, a to jak v České republice, tak také v zahraničí.Od roku 2014 také nabízíme školení pro učitele základních škol v oblasti Globálního rozvojového vzdělávání. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('S Rytmusem o krok dál', 'Rytmus Liberec, o.p.s.', 'RYTMUSLBC', 'Prostřednictvím DMS můžete přispět na vzdělávání a přípravu lidí se zdravotním postižením pro úspěšný vstup na trh práce a  pro snadnější vstup do běžného života. Také nás finančně podpoříte při realizování nejrůznějších akcí, kde se budete moci seznámit s příběhy lidí, kteří chtějí pracovat nebo již pracují, a to i přes svá zdravotní znevýhodnění.', 'Posláním organizace je umožnit lidem se znevýhodněním aktivní zapojení a seberealizaci zejména při vzdělávání a pracovním uplatnění v běžném prostředí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('S Tamtamem na cestách', 'Centrum pro dětský sluch Tamtam, o.p.s.', 'TAMTAM', 'Cílem organizace je poskytovat komplex kvalitních služeb pro děti i dospělé se sluchovým postižením. Jedná se o služby rané péče, služby sociálně aktivizačních a služby sociálního a dalšího odborného poradenství. Naším cílem je zajistit tento komplex našim stávajícím uživatelům, ale také zabezpečit nové zájemce o naše služby - zejména rodiny s dětmi, kterým právě byla sdělena diagnóza sluchového postižení jejich malého dítěte. Cílem projektu je získat potřebné finance, které by dorovnávaly částku, kterou potřebujeme pro plynulý a bezproblémový chod všech našich služeb a která by nám zároveň umožnila pořídit takové vybavení, které by zvyšovalo kvalitu našich služeb a umožňovalo harmonický rozvoj dětí. Každoročně se potýkáme s nedostatkem financí, které nám alokuje Ministerstvo práce a sociálních věcí a krajské úřady. Tento nedostatek financí se odráží na jistotě, že služba může celoročně plynule, bez omezení fungovat. Rodiny v některých krajích tak mají i tu zkušenost, že služba byla na nějakou dobu pozastavena a služba jim nemohla být poskytnuta. Pozastavení služby vždy negativně ovlivňuje podporu rodiny a tím přímo ohrožuje plynulý rozvoj dítěte. Těmto rodinám není možné zajistit náhradní službu, jelikož jsou naše služby jedinečné a nezastupitelné.', 'Centrum pro dětský sluch je organizace, která vyjadřuje a hájí zájmy sluchově postižených dětí a jejich rodičů a dospělých se sluchovým postižením. Snahou naší organizace je přispívat k rozvoji péče o sluchově postižené děti a dospělé a pomáhat jim a jejich rodinám překonávat následky sluchového postižení. Usilujeme o vytváření předpokladů pro co nejdokonalejší integraci sluchově postižených do společnosti.Poskytujeme komplex služeb pro rodiny se sluchově postiženými dětmi a dospělé se sluchovým postižením. Nedílnou součástí podpory dětí i dospělých se sluchovým postižením je vydávání publikací, které se rodičům stávají průvodcem v problematice výchovy a vzdělání jejich sluchově postiženého dítěte a kterých je v ČR velký nedostatek. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Sbírka pro ČR', 'Charita Česká republika', 'CHARITAPOMOC', 'Sbírka je určena na pomoc při přírodních katastrofách a živelných pohromách v České republice (např. povodně) a také na podporu lidí v nouzi, zejména na podporu cílových skupin, umístěných v zařízeních Charity ČR (Domovy pokojného staří, azylové domy pro matky s dětmi v tísni, centra pro zdravotně postižené, hospice...). Dále je sbírka určena na podporu lidí s hendikepem, lidí s drogovou závislostí, nevyléčitelně nemocným, seniorům, lidem ve výkonu trestu, lidem v krizové situaci.Charita ČR poskytuje pro výše zmíněné cílové skupiny stovky služeb a desítky zařízení na území celé republiky.', 'Charita Česká republika pomáhá potřebným bez ohledu na jejich vyznání, rasu nebo národnost. Je největším nestátním poskytovatelem zdravotních a sociálních služeb v České republice a věnuje se také humanitární pomoci a rozvojové spolupráci v zahraničí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Sbírka pro hluchoslepé Červenobílé dny', 'LORM - Společnost pro hluchoslepé z.s.', 'LORM', 'Sbírka Červenobílé dny je dlouhodobý projekt sdružení LORM - Společnosti pro hluchoslepé z.s. Výtěžek sbírky je určen na pomoc lidem se souběžným postižením zraku a sluchu různého stupně - tzv. hluchoslepotou.Díky sbírce Červenobílé dny mohou lidé s hluchoslepotou: - využívat terénních sociálních služeb sdružení LORM - průvodcovské a tlumočnické služby na úřady a k lékařům - učit se prostorové orientaci a samostatnému pohybu s červenobílou holí - učit se komunikovat ve specifických systémech, např. v Lormově abecedě, taktilním znakovém  jazyce, Braillově písmu atd. - soutěžit ve sportovních disciplínách na Lormolympiádě - umělecky tvořit a svými díly se pochlubit v umělecké soutěži o Cenu Hieronyma Lorma - jezdit s traséry a průvodci LORMu na týdenní pobyty na běžkách, na tandemových kolech, se zaměřením na sociální rehabilitaci nebo komunikaci v taktilním znakovém jazyce - odebírat časopis Doteky, který propojuje hluchoslepé z celé republiky - a mnohé další, více napoví Výroční zprávy a např. Fotogalerie na www.lorm.cz.Sdružení LORM je jediným registrovaným poskytovatelem sociálních služeb pro hluchoslepé v ČR, více o sbírce na www.cervenobiledny.cz.', 'LORM z.s. sdružuje od roku 1991 hluchoslepé spoluobčany, jejich rodinné příslušníky, odborníky i všechny ostatní zájemce zabývající se problematikou hluchoslepých osob. Cílem služeb je připravit klienta vést samostatný, plnohodnotný a důstojný život ve svém přirozeném prostředí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('SENIOR TELEFON (telefonická krizová pomoc seniorům)', 'ŽIVOT 90', 'ZIVOT90', 'Senior Telefon (800 157 157) je služba bezplatné telefonické krizové pomoci (TKP) - linka důvěry zaměřená na pomoc a podporu klientů seniorů a osob o ně pečujících. Je jedinou bezplatnou linkou krizové pomoci a poradenství v ČR pro seniory s nonstop provozem. Na Senior Telefon se obracejí klienti z celé České republiky. Sociální služba TKP - Senior Telefon je kontinuální od roku 1991.  ', 'Jsme tu proto, abychom prohlubovali kvalitu života celé společnosti tím,že přispějeme svou humanitární činností k řešení specifických problémů seniorů, umožnili jim aktivně a smysluplně žít v jejich vlastním domově tak dlouho, jak je to jen možné, abychom provázeli seniory podzimem života tak, aby i jejich stáří plynulo důstojně.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Shromáždění finančních prostředků na podporu psů a koček v útulku při Zoo Hodonín, podpora chovu zvířat v zoo.', 'Zoologická zahrada Hodonín, příspěvková organizace', 'ZOOHOD', 'Shromáždění finančních prostředků na podporu psů a koček v útulku při Zoo Hodonín a na podporu chovu zvířat v zoo.', 'Všestranná odborná podpora ochrany živočichů a rostlin, vytváření vhodného prostředí pro odpočinek a vzdělávání návštěvníků zoologické zahrady. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Skutečná pomoc', 'Člověk v tísni o.p.s.', 'SKUTEK', 'Cílem projektu je získávání a shromažďování peněžitých příspěvků pro podporu a realizaci projektů rozvojové spolupráce v souladu s miléniovými cíli tisíciletí OSN zejména v sektorech vzdělání, zdroje obživy, voda a hygiena, zdraví a boj s chudobou v zemích odsouhlasených MZV ČR, dále na nezbytný rozvoj organizace, zejména vytvoření dostatečně silného a stabilního zázemí pro realizaci výše uvedených projektů a s tím spojených nákladů.', 'Jsme nevládní nezisková organizace vycházející z myšlenek humanismu, svobody, rovnosti a solidarity. Lidskou důstojnost a svobodu považujeme za základní hodnoty. Věříme, že lidé kdekoli na světě mají mít právo rozhodovat o svých životech a společně sdílet práva vyjádřená Všeobecnou deklarací lidských práv. Usilujeme o otevřenou, informovanou, angažovanou a zodpovědnou společnost k problémům doma i za hranicemi naší země. Chceme se aktivně podílet na vytváření společnosti, ve které jsou kulturní, etnické, rasové a jiné odlišnosti zdrojem obohacení, a nikoli konfliktů. Jsme součástí občanské společnosti. Naše činnost vychází z osobní iniciativy a aktivity stovek našich zaměstnanců a dobrovolníků, jak v České republice, tak v desítkách zemí, kde pracujeme a z důvěry a podpory, kterou pro svoji práci získáváme od jednotlivců, firem, vlád i mezinárodních institucí. Oblasti a priority činnosti si určujeme sami, podle naléhavosti situace a podle našich reálných možností něco změnit. Nemůžeme pracovat všude, za podstatný považujeme skutečný dopad. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Sluníčko na pomoc opuštěným dětem a náhradním rodinám', '', 'ROZUMACIT', 'Dne 1.4.2015 pořádá Nadační fond Rozum a Cit již 15. ročník veřejné sbírky Sluníčkový den pro opuštěné děti a pěstounské rodiny. Studenti budou široké veřejnosti nabízet ke koupi magnetku, placku s motivem sluníčka a žeton do nákupního vozíku za 30 Kč. Výtěžek bude určen dětem v náhradní rodinné péči především na vzdělávání, výukové pomůcky, asistenční službu k handicapovaným dětem, rehabilitační a kompenzační pomůcky a pomoc sociálně slabým rodinám, především prarodičům - pěstounům a podporu na zázemí pro náhradní rodinnou péči.POJĎTE S NÁMI POMÁHAT! Kupte si sbírkový předmět a uděláte dobrý skutek. Nebo zašlete dárcovskou SMS ve tvaru: DMS ROZUMACIT na číslo 87 777. ', '', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('SOS Nepál', 'Člověk v tísni o.p.s.', 'NEPAL', 'Pomozte s námi obětem zemětřesení v Nepálu!Na místo jsme den po zemětřesení vyslali tříčlenný humanitární tým, který bude distribuovat pomoc nejohroženějším lidem. Na okamžitou pomoc jsme z humanitárního fondu Klubu přátel Člověka v tísni uvolnili 1 000 000 korun.Nejničivější zemětřesení v Nepálu za posledních 80 let dosáhlo síly 7,8 stupně a epicentrum mělo v centrální části země. Silné otřesy si zatím vyžádaly tisíce mrtvých a zraněných, zcela zničily stovky domů. Záchranáři se ale zatím nedostali na nejpostiženější místa a nepálské úřady tvrdí, že pod troskami je uvězněno daleko více obětí."Mluvil jsem s příbuznými v Gorce a říkali mi, že nejméně ve třech okolních vesnicích je většina domů srovnaných se zemí," říká programový manažer Člověka v tísni v Afghánistánu Sudip Joshi, který pochází z Nepálu. "Počet obětí ale nikdo nezná, protože celé vesnice v horských oblastech jsou odříznuté," dodává Sudip, který je součástí tříčlenného humanitárního týmu. Pomoc je možné posílat přímo na konto sbírky SOS NEPÁL 54333345/0300 Svůj dar si můžete odečíst z daní: http://www.clovekvtisni.cz/cs/chci-pomoci/odecteni-daru-z-dani. Pro platby ze zahraničí: IBAN: CZ8803000000000054333345Teď je důležitá zdravotnická pomoc, jídlo a voda Ten bude přímo v terénu zkoumat rozsah škod, aktuální potřeby lidí a okamžitě pomáhat nejohroženějším lidem. "Zaměříme se na oblast Gorka v epicentru zemětřesení. Zásadní teď bude dostat na místo co nejrychleji urgentní zdravotnickou pomoc a dostatek jídla a vody. Poté se začneme zaměřovat na provizorní opravy zničených domů," říká vedoucí humanitárních programů Člověka v tísni Marek Štys."Pomoc budeme zároveň koordinovat s partnerskými organizacemi Welthungerhilfe a Concern Worldwide z platformy Alliance2015, které na místě budou také mít svoje týmy," dodává Marek Štys. "Za pár týdnu přijdou do Nepálu silné monzuny a to situaci lidí, kteří právě přišli o domov, značně zkomplikuje. Je proto potřeba lidem co nejrychleji nabídnout pomoc," říká ředitel Člověka v tísni Šimon Pánek. "Prosíme tedy všechny, aby ve chvíli, kdy to země nejvíce potřebuje, vyjádřili s postiženým Nepálem svoji solidaritu," dodává Šimon Pánek.', 'Jsme nevládní nezisková organizace vycházející z myšlenek humanismu, svobody, rovnosti a solidarity. Lidskou důstojnost a svobodu považujeme za základní hodnoty. Věříme, že lidé kdekoli na světě mají mít právo rozhodovat o svých životech a společně sdílet práva vyjádřená Všeobecnou deklarací lidských práv. Usilujeme o otevřenou, informovanou, angažovanou a zodpovědnou společnost k problémům doma i za hranicemi naší země. Chceme se aktivně podílet na vytváření společnosti, ve které jsou kulturní, etnické, rasové a jiné odlišnosti zdrojem obohacení, a nikoli konfliktů. Jsme součástí občanské společnosti. Naše činnost vychází z osobní iniciativy a aktivity stovek našich zaměstnanců a dobrovolníků, jak v České republice, tak v desítkách zemí, kde pracujeme a z důvěry a podpory, kterou pro svoji práci získáváme od jednotlivců, firem, vlád i mezinárodních institucí. Oblasti a priority činnosti si určujeme sami, podle naléhavosti situace a podle našich reálných možností něco změnit. Nemůžeme pracovat všude, za podstatný považujeme skutečný dopad. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('SOS netopýr - na záchranu a péči o nalezené, zraněné a hendikepované netopýry včetně netopýřích kolonií', 'ZO ČSOP Nyctalus', 'SOSNETOPYR', '• Cílem projektu je zkvalitnění záchrany a péče o hendikepované jednotlivé netopýry a netopýří kolonie nalezené převážně na území Prahy a okolí v bytech, kancelářích a dalších místech obývaných či navštěvovaných lidmi, a jejich navrácení zpět do přírody. • Všichni netopýři v ČR jsou zákonem chráněni v kategorii silně nebo kriticky ohrožený druh, počet přijímaných zvířat neustále stoupá zejména v souvislosti se zateplováním paneláků a kácením doupných stromů. • Každoročně vyhodnocujeme příjmy netopýrů, podrobná nálezová data slouží vědeckým a ochranářským účelům. On-line databáze přijatých zvířat na webu. • Soustavně provádíme osvětovou činnost, roční návštěvnost více než 4000 osob.', 'Posláním organizace je ochrana a obnova přírody, krajiny a životního prostředí, ekologická výchova a podpora trvale udržitelného života. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('SOS Sýrie a Irák', 'Člověk v tísni o.p.s.', 'SYRIE', 'Člověk v tísni pomáhá syrským uprchlíkům a obětem válečného konfliktu od roku 2012. Zajišťujeme potravinovou a materiální pomoc, svoz komunálního odpadu, aby se předešlo šíření epidemií. Podporujeme základní školy ve městě Aleppu a na venkově v provincii Idlib.', 'Jsme nevládní nezisková organizace vycházející z myšlenek humanismu, svobody, rovnosti a solidarity. Lidskou důstojnost a svobodu považujeme za základní hodnoty. Věříme, že lidé kdekoli na světě mají mít právo rozhodovat o svých životech a společně sdílet práva vyjádřená Všeobecnou deklarací lidských práv. Usilujeme o otevřenou, informovanou, angažovanou a zodpovědnou společnost k problémům doma i za hranicemi naší země. Chceme se aktivně podílet na vytváření společnosti, ve které jsou kulturní, etnické, rasové a jiné odlišnosti zdrojem obohacení, a nikoli konfliktů. Jsme součástí občanské společnosti. Naše činnost vychází z osobní iniciativy a aktivity stovek našich zaměstnanců a dobrovolníků, jak v České republice, tak v desítkách zemí, kde pracujeme a z důvěry a podpory, kterou pro svoji práci získáváme od jednotlivců, firem, vlád i mezinárodních institucí. Oblasti a priority činnosti si určujeme sami, podle naléhavosti situace a podle našich reálných možností něco změnit. Nemůžeme pracovat všude, za podstatný považujeme skutečný dopad. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('SOS Ukrajina', 'Člověk v tísni o.p.s.', 'SOSUKRAJINA', 'Pomoc obětem násilí na Ukrajině.', 'Jsme nevládní nezisková organizace vycházející z myšlenek humanismu, svobody, rovnosti a solidarity. Lidskou důstojnost a svobodu považujeme za základní hodnoty. Věříme, že lidé kdekoli na světě mají mít právo rozhodovat o svých životech a společně sdílet práva vyjádřená Všeobecnou deklarací lidských práv. Usilujeme o otevřenou, informovanou, angažovanou a zodpovědnou společnost k problémům doma i za hranicemi naší země. Chceme se aktivně podílet na vytváření společnosti, ve které jsou kulturní, etnické, rasové a jiné odlišnosti zdrojem obohacení, a nikoli konfliktů. Jsme součástí občanské společnosti. Naše činnost vychází z osobní iniciativy a aktivity stovek našich zaměstnanců a dobrovolníků, jak v České republice, tak v desítkách zemí, kde pracujeme a z důvěry a podpory, kterou pro svoji práci získáváme od jednotlivců, firem, vlád i mezinárodních institucí. Oblasti a priority činnosti si určujeme sami, podle naléhavosti situace a podle našich reálných možností něco změnit. Nemůžeme pracovat všude, za podstatný považujeme skutečný dopad. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Společně proti bezmoci', 'Alsa, z.s.', 'ALS', 'ALS, amyotrofická laterální skleróza, je onemocnění, které za plného vědomí zbavuje člověka možnosti ovládat své tělo. Nejdříve Vám vypadne z ruky hrnek, přestáváte mluvit, usedáte na vozík, přichází konec. Nemoc může potkat kohokoli, neexistuje léčba. Pomozte nám bojovat proti BEZMOCI.Amyotrofickou laterální sklerózou u nás trpí zhruba 800 lidí, ročně se objeví téměř 200-300 nových pacientů a stejný počet jich každý rok zemře. Jedná se o nemoc, při které člověk během dvou až pěti let postupně ztrácí schopnost ovládat svaly. Onemocnění si nevybírá slabé jedince s podlomeným zdravím, často sahá po lidech na vrcholu sil a tvůrčího potenciálu - z nejznámějších případů jmenujme tlumočnici Danu Gálovou, fotbalistu Mariána Čišovského nebo politika Stanislava Grosse.V České republice v současné době neexistuje systematická péče o tyto nemocné. Nemoc postupuje rychle, a když nemocný standardním způsobem požádá pojišťovnu o pomůcku, stává se, že je mu přiznána, až když ji nepotřebuje. Pacienti s ALS potřebují pravidelnou speciální fyzioterapii, speciální pomůcky na odkašlávání, elektricky ovládané vozíky... a především drahé komunikační prostředky, které jim po ztrátě hlasu i schopnosti ovládat končetiny pomohou sdělit svým bližním, co je trápí. A potřebují to rychle, ne po zdlouhavém procesu schvalování. Mentální schopnosti nemocných zůstávají nezměněné a o to ostřeji si svou bezmoc uvědomují. Chtějí přeci zůstat v kontaktu se svým okolím.CO S TÍM? S Vaší pomocí budujeme platformu, na které budou dostávat nemocní ALS rychlou a odpovídající komplexní péči. Chceme nakoupit pomůcky, které budeme schopni rychle zapůjčit potřebným, než je dostanou od pojišťovny, pakliže jim je vůbec přizná. S týmem odborníků (lékař, psychiatr, fyzioterapeut, logoped) už navštěvujeme, ale chceme navštěvovat v daleko větší míře, všechny, kteří naši pomoc potřebují. Chceme vyškolit lékaře, fyzioterapeuty, logopedy a další odborný personál všude po republice, aby uměli účinně pomáhat ALS nemocným. A abychom ulevili jejich příbuzným, chceme zajišťovat odlehčovací pobyty s péčí o nemocné. Plánujeme vybudovat sociálně zdravotnické centrum, které všechny tyto aktivity zastřeší - nabídne služby fyzioterapeuta, psychologa a dalších, vytvoří prostor pro krátkodobé pobyty, ale i stacionář pro případy, kdy už se příbuzní nebudou moci o své blízké postarat. Co uděláme s Vaším příspěvkem? Nakoupíme nejpotřebnější vybavení do naší půjčovny.Nejnutněji potřebujeme pomůcky a vybavení pro pacienty, jako jsou snímače očních pohybů, odsávačky, dlahy na protahování spastických končetin, motomed, přístroje Coughassist a komunikační tablety. Kromě pomůcek peníze použijeme na zajištění rekondičních pobytů pro pacienty a školení odborné péče pro jejich blízké.', 'Spolek ALSA je organizace sdružující pacienty a profesionály se zájmem o amyotrofickou laterální sklerózu, ALS. Našim prvořadým cílem je pomoc pacientům s ALS - poradenstvím, konzultací vhodných terapií, pomoci s vyřizováním pomůcek a příspěvků a dalšími. Život s ALS není jednoduchý a komplexní péče o pacienta s ALS je v ČR stále v plenkách. Všechno to pídění se po informacích, výzkumu, věrohodnosti terapií... Víme, jaké to je a vyvíjíme spoustu aktivit, aby se situace postupne měnila. Chceme, aby lidé s ALS věděli, ze navzdory nesnadné životní situaci kterou onemocnění ALS přináší, na to vsechno nejsou sami, že je má kdo vyslechnout a poradit.Kromě zázemí pro pacienty spolek ALSA sdružuje odborníky - lékaře a zdravotní personál s dlouholetými zkušenostmi s ALS . Našim cílem je rozšiřovat tyto zkušenosti a předávát je dál. Nas tým má bohaté zkušenosti z oblasti fyzioterapie, logopedie - komunikace a komunikačních pomůcek, poruch polykání, umělé plicní ventilace, zajištění výživy a dalšími, vše se zaměřením na unikátní potřeby lidí s ALS. Jedním z probíhajících projektů je edukace a osvěta zdravotnického personálu nemocnic a hospicových zařízení tak, aby odborné instituce byly schopny poskytovat pacientům plnohodnotnou odbornou péči. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Sport bez bariér', 'Nadace Charty 77', 'SPORTBEZBARIER', 'Sport bez bariér je projekt, jehož cílem je finanční podpora sportovních aktivit lidí s postižením. Adresáty podpory mohou být organizace i jednotlivci všech generací. Projekt je zaměřen na oblasti ohrožené nefinancováním ze státních zdrojů. O podpoře jednotlivých žadatelů rozhoduje rada projektu. Poslední zasedání se uskutečnilo 27. dubna 2016. Mezi 16 žádostí bylo rozděleno přes 420 tisíc korun.Nejbližším cílem je podpořit sbírku Konta Bariéry při příležitosti exhibičních turnajů All star game, organizovaných prostřednictvím basketbalové federace. Předem oznámený příjemce pomoci bude na konci sbírky prostřednictvím Konta Bariéry podpořen výnosem, na kterém se budou podílet sportovní fandové i širší veřejnost, která se se sbírkou seznámí prostřednictvím mediální podpory partnerů ČT, radio F1, onlinové komunikace facebook a web Konta Bariéry.', 'Konto Bariéry, od roku 1992 stěžejní projekt Nadace Charty 77, je první a dosud jedinou kontinuální sbírkou, které se více než dvacet let účastní desetitisíce dárců - svým pravidelným měsíčním příspěvkem pomáhají naplňovat a financovat program Konta Bariéry. V začátcích jsme hodně pracovali s podtitulkem Konta Bariéry „Patří mezi nás". Chtěli jsme veřejnost naučit vnímat lidi s rozdílnými potřebami. Podařilo se nám to a jsme na to, že Konto Bariéry splnilo tento velký úkol, docela pyšní. Za více než dvacet let činnosti rozdělilo Konto Bariéry stovky miliónů korun, podpořilo tisíce projektů na zlepšení života lidí s handicapem a jejich plnohodnotné zapojení do společnosti. Z aktuálních potřeb lidí s postižením vychází Konto Bariéry i při formování nových projektů. Doplňuje stát a samosprávu tam, kde jejich pomoc nestačí. Stále více se zaměření jeho pomoci posouvá od standardní pomoci, tedy rehabilitačních a kompenzačních pomůcek, bezbariérových úprav bytů, škol a dalších veřejných budov, k projektům, které souvisejí se vzděláním a pracovním uplatněním lidí s handicapem.Více na www.kontobariery.cz. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Srdce pro děti', 'Život dětem, o.p.s.', 'SRDCEPRODETI', 'Hlavním cílem projektu pomoc vážně nemocným dětem a dospělým v domácí péči svých rodičů. Získání finančních prostředků na podporu rodin s handicapovanými dětmi, na podporu zařízení, která se starají o nemocné děti, na podporu zařízení, která pečují o ohrožená zvířata, na podporu dětských oddělení nemocnic v České republice, na podporu dětských domovů, na podporu vzdělání.', 'Posláním obecně prospěšné společnosti Život dětem je pomoc vážně nemocným dětem i starším, kteří jsou odkázáni na pomoc ostatních, pomoc zařízením, která se starají o nemocné, handicapované a opuštěné děti, podpora vzdělání. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Srdce pro vás', 'Nadační fond J&T', 'SRDCEPROVAS', 'Nadační fond J&T, který v Česku působí už od roku 2004, se spojil s deníkem Blesk. Nadační fond má přesná kritéria výběru organizací, které Blesk podpoří mediálním prostorem, dárci mj. čtenáři pak prostřednictvím Nadačního fondu J&T jednotlivé projekty podpoří i finančně. Základem výběru je odborné zázemí, schopnost využít nabízenou pomoc efektivně či spolehlivost. Pomoc bude směřovat k ohroženým či týraným dětem, rodinám v hmotné či sociální nouzi, matkám samoživitelkám, vážně nemocným dětem a dospělým, handicapovaným, včetně lidí s mentálním postižením či s duševním onemocněním, seniorům v problémech a mnoha dalším potřebným. Čtenáři Blesku budou přispívat na konkrétní příběhy pomocí částek na účet projektu a pomocí dárcovských esemesek. Každý měsíc bude prostřednictvím Nadačního fondu J&T na stránkách Blesku věnován prostor jednomu vybranému tématu. Srdce pro vás přinese vždy minimálně jeden velký příběh měsíčně. Jeho součástí bude i popis toho, co by konkrétním lidem mohlo usnadnit život. Zároveň představuje vybranou neziskovou organizaci, která se tímto tématem zabývá, aktivně pomáhá, stará se o klienty. Přinese tedy zkušenosti lidí z oboru, odborníků. Všechny informace, příběhy, kontakty, možnosti pomoci, či představení nadací budou na webu www.srdceprovas.cz.', 'Posláním Nadačního fondu J&T je podpora aktivit právnických a fyzických osob sledujících obecně prospěšné cíle, zejména se zaměřením na podporu projektů a aktivit sociálního charakteru. Podpora nemocným, fyzicky a mentálně postiženým dětem a dospělým, sociálně slabým rodinám s dětmi, ohroženým dětem za účelem prohloubení všestranné péče o ně. Podpora zvířecích útulků. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Srdcerváči', 'Nadační fond pro podporu zaměstnávání osob se zdravotním postižením', 'SRDCERVACI', 'Výtěžek projektu Srdcerváči je použit na realizaci projektů zaměřených na vznik nových pracovních míst pro zdravotně postižené a na rozvoj jejich podnikání. Cílem projektu je nejen pomoci konkrétním lidem, ale také změnit pohled většinové společnosti na život osob se zdravotním postižením. Většina z nich totiž chce a může pracovat mnohdy lépe než jejich zdraví vrstevníci.', 'NFOZP, neboli Nadační fond pro podporu zaměstnávání osob se zdravotním postižením, vznikl v roce 2007 v reakci na tíživou situaci postižených při vstupu na trh práce. Jeho posláním je snaha tuto situaci pomocí různorodých prostředků neustále zlepšovat.NFOZP je jedinou neziskovou organizací, zaměřenou na zaměstnávání osob se zdravotním postižením (OZP), která chápe komplexitu překážek. Cíleně se tak věnuje nejen aktivnímu vytváření pracovních míst pro OZP, ale i osvětě a vzdělávání personalistů i vedení firem, které postižené zaměstnávají nebo se na to teprve chystají.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Stacionář pro sirotky v Bozoum', 'SIRIRI o.p.s.', 'SIRIRI', 'Mnoho dětí ve středoafrickém městečku Bozoum přichází o rodiče kvůli nedostatečné lékařské péči, v důsledku ozbrojených konfliktů nebo kvůli AIDS, jímž trpí přibližně 6,3 % místních obyvatel. Velké africké rodiny se sirotků naštěstí většinou ujímají, ale bohužel kvůli rostoucí chudobě nemají dost prostředků, aby jim poskytly veškerou péči (často ani jídlo, základní vzdělání...). Projekt Stacionář poskytuje pomoc těmto dětem, aniž by je vytrhl z jejich „náhradních" rodin. Ve Stacionáři se o osiřelé děti stará tým vychovatelů a doplňuje tak péči, na kterou rodiny samy nestačí. Kompletní prezentace projektu pro veřejnost, včetně aktuálního stavu a rozpočtu projektu je na http://www.siriri.org/co-delame/projekty/dlouhodobe-projekty/projekt-stacionar.', 'SIRIRI je česká obecně prospěšná společnost, která se snaží pomáhat Středoafričanům postavit se na vlastní nohy. SIRIRI se zaměřuje na projekty v oblastech školství, zdravotnictví a zemědělství. Hlavním záměrem všech projektů SIRIRI je přispívat k nezávislosti Středoafričanů na pomoci zvenčí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Stavba dětského domova v Keni', 'ONE MORE DAY FOR CHILDREN, nadační fond', 'OMDC', 'Cílem projektu je dokončení projektu "stavba dětského domova" pro 60 dětí - sirotků - ve městě Doldol, oblast Laikipia, stát Keňa, Afrika.Tato stavba nebyla dle projektové dokumentace ještě dokončena a bude pokračovat: - dostavba jídelny, - dostavba kuchyně, - lékařské středisko, - kancelář vedení domova, - vybudování 500 m vodovodního potrubí, které bude napojeno na místní zdroje, - elektrické oplocení proti divoké zvěři - slonům, lvům, apod.Dále nákup vybavení domova - postele, palandy, skříňky, ložní vybavení - matrace, deky, vybavení pro kuchyň a jídelnu, vybavení pro kancelář a lékařské středisko, počítače, telefony, vybavení dětské herny, nákup domáchích zvířat, nákup školních pomůcek pro děti.', 'Posláním organizace je humanitární a charitativní pomoc africkým dětem - sirotkům, umístěným v dětském domově v Doldol a také dalším dětem, žijícím ve slamech, rozvoj jejich osobnosti a vzdělání. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Stavte s námi varhany pro Svatou Horu', 'Matice Svatohorská', 'SVATAHORA', 'Cílem projektu je dokončení stavby velkých varhan pro papežskou baziliku minor na Svaté Hoře u Příbrami, perlu raně barokní architektury, a tak následně přispět k zachování a rozvoji tradice zejména sakrální hudby jak na poutním místě samotném, tak v obecném slova smyslu. Stavba varhan byla zahájena na konci roku 2008. Jedná se o rozsáhlý projekt, jak z pohledu technického a estetického, tak finančního. Prostředky získané prostřednictvím DMS mohou významnou měrou napomoci k dokončení stavby královského nástroje pro Svatou Horu.Matice Svatohorská je spolkem přátel a dobrodinců Svaté Hory, nejvýznamnějšího poutního místa v Čechách spojeného s mariánským kultem. Ve své činnosti se zaměřuje na zcela konkrétní podporu duchovně-kulturního zázemí Svaté Hory. V této souvislosti usiluje o zachování a rozvoj sakrální hudby, k němuž má rozhodujícím způsobem přispět projekt stavby velkých varhan pro Svatou Horu.', 'Sdružuje přátele a dobrodince Svaté Hory. Pečuje o kulturní rozkvět a duchovní a materiální zázemí této národní kulturní památky. Přispívá k vytváření příznivých podmínek pro příchozí turisty a poutníky (opravy, údržba areálu, vzdělávací a kulturní akce). Navazuje na tradici Matice Svatohorské založené v období 1. republiky. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Stezka pro fretky', 'FRETKY BOLESLAV', 'FRETKA', 'STEZKA PRO FRETKY vyjadřuje naši hlavní myšlenku a náš cíl - bezpečnou stezku pro fretky, na kterou u nás v útulku nastoupí, mnohdy vážně nemocné, v každém případě opuštěné nebo odložené, bezpečně touto stezkou projedou (tzn. vyléčí se, socializují atd.), a na konci této stezky vyjedou do nového bezpečného milujícího domova - nové adoptivní rodiny.Jsme útulek specializovaný pro třetí domestikovanou šelmu - fretku. Fretkám pomáháme již pátým rokem a za čtyři předcházející roky činnosti jsme pomohli více než 230 fretkám. Kapacita našeho útulku je 50 fretek. Fretky se k nám často dostanou ve velmi vážném zdravotním stavu, či nesocializované. Projekt DMS nám má pomoci pomáhat právě těmto fretkám.Děkujeme za Vaši pomoc!', 'Hlavním cílem organizace je ochrana nalezených, nechtěných a odložených fretek, poskytnutí pomoci těmto fretkám ve spolupráci s veterinární službou a poskytování azylu na neurčitou dobu těmto fretkám, jejich socializace a vyhledávání nových spolehlivých majitelů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Stipendia', 'Nadační fond manželů Livie a Václava Klausových', 'STIP', 'Stipendium poskytujeme českým studentům tuzemských středních a vysokých škol, kterým jejich sociální situace (někdy i v kombinaci se zdravotním handicapem) ztěžuje studium. Ve všech případech klademe velký důraz na vlastní motivaci studentů. Studenty podporujeme dlouhodobě, aby dosáhli kvalifikace ve zvoleném oboru. Příspěvky vyplácíme v měsíčních splátkách a studenti je mohou použít k platbě školních pomůcek, internátu, koleje, jízdného do školy či obědů. Udržujeme si kontrolu nad účelným využitím prostředků, neboť požadujeme od svých stipendistů zprávy o studijních výsledcích a nepodporujeme zanedbávání docházky a studia. Od roku 2004 získalo stipendium od Nadačního fondu manželů Livie a Václava Klausových téměř 1000 studentů.', 'Prioritou projektů nadačního fondu je podpora různých forem vzdělávání prostřednictvím projektů Kroužky, Stipendium, Senioři komunikují, Řidičské průkazy, Nehoda mi změnila život a Jazykové kurzy v ČR a v zahraničí.Nadační fond pomáhá především jednotlivcům, kteří se z hlediska sociálního nebo zdravotního ocitli ve zvlášť obtížné životní situaci.Finanční prostředky, kterými nadační fond disponuje, jsou zaměřeny na veřejně prospěšné účely a slouží celé společnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Světlo pro svět', 'SVĚTLO PRO SVĚT - Light for the World', 'SVETLO', 'SVĚTLO PRO SVĚT - Light for the World, o. s. je nevládní organizací, která se zaměřuje na prevenci a léčbu slepoty a na systémovou podporu osob s postižením v rozvojových zemích.Díky Vašim darům společně můžeme za:300 Kč - zabránit rozvinutí velmi bolestivého očního onemocnění zvaného trachom u 10 lidí 500 Kč - zajistit preventivní dávku antibiotik Zitromax pro 20 osob 800 Kč - trvale vrátit zrak člověku se šedým zákalem 5000 Kč - pořídíme oftalmoskopFinanční prostředky ze sbírky půjdou na projekty především v: Burkina Faso, Etiopii, Rwanda, dále pak: Jižní Súdán, Uganda, Keňa, DR Kongo, Tanzanie, Mosambik, Bosna a Hercegovina, Afganistán, Pákistán, Indie, Bangladéš, Kambodža, Filipíny, Papua Nová Guinea, Bolívie.', 'Základním posláním SVĚTLA PRO SVĚT je pomoc nevidomým a osobám se zrakovým postižením v rozvojových zemích, zejména Etiopii, Rwandě a Burkině Faso. Projekty se zaměřují na léčbu slepoty prostřednictvím operací šedého zákalu, prevence trachomu a léčení jeho následků, dále také na rehabilitace dětí s postižením a jejich začleňování do společnosti. Snažíme se o rozvoj a dodržování práv osob s postižením a zvyšování povědomí o této problematice. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Světluška', 'Nadační fond Českého rozhlasu', 'SVETLUSKA', 'Světluška je dlouhodobý projekt Nadačního fondu Českého rozhlasu. Výtěžek sbírky je určen zejména na pomoc nevidomým dětem a lidem s těžkým zrakovým a kombinovaným postižením, kteří se dostali do tísnivé životní situace. Pečlivě hlídaný a auditovaný výtěžek přispěje k podpoře životní kvality a samostatnosti konkrétních nevidomých lidí a konkrétních projektů pro nevidomé.', 'Účelem fondu je podpora rozvoje kultury a všech forem umění, zvláště mladé talentované umělce ze sociálně slabých rodin či handicapované, podpora rozvoje a rozšiřování vzdělání, podpora v oblasti sociální a zdravotní, poskytování charitativní a humanitární pomoci. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Šance pro děti', 'Děti patří domů, o.s.', 'SANCEPRODETI', 'Pomáháme dětem z dětských domovů, opuštěným dětem a dětem v nouzi, aby mohly vyrůstat v rodinách a prožít šťastné dětství. U dětí s raným traumatem a deprivací se často objevují projevy, které mohou člověka překvapit. Většina dětí má velké potíže se sebedůvěrou, hodně dětí má odchylky ve vývoji (opožděný psychomotorický vývoj, přeskočení některých fází, problémy s růstem apod.), mnoho dětí má také problémy s pozorností. Našimi klienty jsou pěstounské rodiny z celé ČR, spolupracujeme nyní s 50 pěstounskými rodinami z 10 krajů ČR. V těchto rodinách vyrůstá více než 100 dětí.', 'Posláním Děti patří domů, o. s. je podporovat a rozvíjet náhradní rodinnou péči a hostitelskou péči v České republice s hlavním cílem naplnit právo a potřebu dětí žít v rodině u „svých lidí." Tím se snažíme napomáhat snižování počtu dětí v dětských domovech a kojeneckých ústavech. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Šikmá věž v Ústí nad Labem', 'Ústecká komunitní nadace', 'SIKMAVEZ', 'Šikmá věž kostela Nanebevzetí Panny Marie v Ústí nad Labem se ve svých šedesáti metrech výšky odklání od vertikály o více než dva metry. Je nejšikmější věží v České republice i daleko za hranicemi okolních státu. Ke svému handicapu přišla za druhé světové války, po zásahu leteckou pumou se naklonila a praskla. Tehdy to vypadlo, že brzy spadne. Později ji ale Ústečané podepřeli dřevěnou konstrukcí a nakonec byla složitě zajištěna pomoci betonové injektáže do základů a železobetonového krunýře uvnitř věže.Při bombardování spadla původní schodišťová přístavba, kterou se na věž chodilo, ta nebyla při rekonstrukci obnovena. Dnes je možné se na věž dostat jen krkolomnou cestou před podkroví chrámové lodě. Naším cílem je získat prostředky na obnovení přístupu do věže a umožnit široké veřejnosti navštívit tuto krásku a obdivovat ji jak zevnitř, je zde například byt zvoníka, tak ocenit nádherný výhled z jejího vrcholu.', 'Ústecká komunitní nadace je první českou komunitní nadací, jejíž kořeny sahají do roku 1993 a druhou nejstarší nadací tohoto typu ve střední a východní Evropě. Své sídlo má v Ústí nad Labem.Jsme adresa číslo 1 pro vaše neziskové projekty Každoročně administrujeme desítky žádostí o finanční podporu od neziskových organizací, neformálních skupin i jednotlivců působících či žijících zejména v okresech Děčín, Litoměřice, Teplice a Ústí nad Labem. V roce 2015 jsme vyhověli 123 z nich a rozdělili částku 1 889 088 Kč.Jsme tu pro ty, kteří chtějí strategicky rozdělovat prostředky Ve snaze vyjít maximálně vstříc dobročinným cílům potenciálním dárcům jsme již v roce 2003 zavedli tzv. systém fondů, který představuje transparentní a jednoduchou cestu darování šitou na míru specifickým požadavkům jednotlivých dárců. V roce 2015 jsme získali od našich dárců 3 068 223 korun a využili je k naplnění našeho poslání.Při své práci klademe důraz na maximální transparentnost Každoročně procházíme nezávislým auditem, jsme signatářem Etického kodexu nadací, jsme zakládajícím členem Fóra dárců (asociace českých nadací) a dlouhodobě jsme byli podporováni jednou z nejvýznamnějších nadací z USA Ch.S. Mott Foundation. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Škola hrou ve Středoafrické republice', 'SIRIRI o.p.s.', 'SLABIKAR', 'Středoafrická republika (SAR) je zničena občanskou válkou a chudobou. Úroveň vzdělávacího systému nedává záruku, že by se to v brzké době mělo měnit. Gramotnost obyvatelstva staršího 15 let je v SAR podle statistik 56%. Základním záměrem projektu je zkvalitnění výuky na 1.stupni v základních školách v SAR. Expertní tým složený z českých a středoafrických pedagogů připravil kvalitní a srozumitelné učebnice i metodiku pro učitele. Projekt je připravený na klíč a odpovídá potřebám středoafrických učitelů a vysokému standardu pedagogiky 21.století a je v jazyce, jemuž místní obyvatelé rozumí.', 'SIRIRI je česká obecně prospěšná společnost, která se snaží pomáhat Středoafričanům postavit se na vlastní nohy. SIRIRI se zaměřuje na projekty v oblastech školství, zdravotnictví a zemědělství. Hlavním záměrem všech projektů SIRIRI je přispívat k nezávislosti Středoafričanů na pomoci zvenčí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('The Tap Tap', 'Z.s. TAP', 'TAPTAP', 'Cílem veřejné sbírky je získávat finanční prostředky na zajištění realizovaných projektů a kulturních akcí, na provoz bezbariérových automobilů, na podporu turné černého humoru, grafické práce a úhradu mezd lektorů projektu STUDEO.', 'Posláním z.s. TAP je péče o rozvoj dovedností a schopností osob se zdravotním postižením a podpora jejich přípravy na budoucí uplatnění na trhu práce. Podpora a organizace společenských a kulturních akcí studentů škol Jedličkova ústavu v Praze. Ochrana přírody a krajiny. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Tichý svět', 'Tichý svět, o.p.s.', 'TICHYSVET', 'Tichý svět pomáhá osobám se sluchovým postižením. V České republice má problém se sluchem až půl milionu lidí. Žijí kolem nás, jsou pevnou součástí společnosti. Studují, pracují, vychovávají děti, ale dojít si na úřad, k soudu či k lékaři a tam něco vyřešit, se pro ně stává problémem. Tyto problémy se snažíme odstraňovat prostřednictvím našich služeb, které mají celorepublikovou působnost.Výtěžek ze získaných DMS bude použit na služby, které neslyšícím umožní začlenit se do společnosti a žít kvalitní a smysluplný život.', 'Cílem organizace je poskytovat služby, které neslyšícím umožní začlenit se do společnosti a žít kvalitní a smysluplný život - např. sociální a právní poradenství, pomoc při zprostředkování zaměstnání, online tlumočení. Dále provádět osvětovou a publikační činnost, která přispívá k propojování světa neslyšících a slyšících. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Toulavý autobus', 'Zoologická zahrada hl. m. Prahy', 'TOULAVYAUTOBUS', 'Nedílnou součástí moderní zoo je osvětová činnost v souvislosti se záchranou ohrožených druhů ve volné přírodě Zoo Praha tímto projektem dává najevo, že nemyslí jen na vlastní potřeby a stává se v dobrém slova smyslu reklamní a produkční agenturou pro ohrožené druhy Prostřednictvím veřejné sbírky, kde jsou DMS jedním z možných platebních nástrojů. Návštěvníci Zoo Praha (1,3 mil./rok) i široká veřejnostDlouhodobý projekt, přesahující lhůtu 3 let pro trvání veřejné sbírky. Sbírkou nabyté prostředky hodlá Zoo Praha financovat pokračování projektu Toulavý autobus, zejména provoz autobusu a výrobu a distribuci vzdělávacích materiálů s ním spojených. Projekt jedinečným způsobem vtahuje návštěvníky zoo, zejména děti a mládež, do problematiky ochrany fauny a flóry doma i ve světě. Jednoduchost a rychlost poskytnuté pomoci díky DMS (a dalším souvisejícím platebním metodám) má šanci pochopit 1 300 000 návštěvníků Zoo Praha ročně. V ČR se jedná o nejnavštěvovanější instituci svého druhu.', 'Zoologické zahrady dnes působí ve světě, v němž je stále více ohrožována druhová pestrost přirozených ekosystémů neboli biodiverzita. Jsou to právě tyto instituce, které mají možnost postihnout celý rejstřík ochranářských aktivit, od výzkumu přes ochranu ex-situ (včetně chovu ohrožených druhů a výchovné činnosti) po účast na projektech in-situ přímo v terénu.Výchova a vzdělávání Každým rokem přichází do zoologických zahrad na celém světě více než 600 milionů návštěvníků nejrůznějšího věku i zájmů. V pražské zoo se během posledních tří let počet návštěvníků zdvojnásobil na více než 1,2 milionu osob ročně. Je to velká výzva i závazek. Zoologická zahrada tak ovlivňuje svými výchovnými a vzdělávacími aktivitami širokou veřejnost i zájmové skupiny. Je schopna díky neocenitelnému potenciálu živých zvířat působit u návštěvníků na jejich názory, postoje, vytváření hodnot a chování.Chov zvířat Pražská zoo jako moderní instituce svého druhu vytváří pro chované živočichy co nejoptimálnější podmínky, a to jak koncepcí a ztvárněním výběhů i ubikací, tak sestavováním jedno- či vícedruhových skupin. Návštěvníci si tak mohou vytvořit představu o životě zvířat a jejich vazbách k prostředí, zvířata pak mají příležitost rozvinout co nejširší rejstřík přirozeného chování.Ochrana ex-situ Zoo Praha se dlouhodobě podílí na ochraně živočichů ex-situ. Vytváří pojistné chovné skupiny u druhů, které jsou v přírodě bezprostředně ohrožené, nicméně u kterých již probíhají snahy o zpětný návrat nebo je lze očekávat.Ochrana in-situ Stále výrazněji se pražská zoo podílí na projektech in-situ, jejichž cílem je ochrana živočichů přímo v místě jejich výskytu. Jedná se o zapojení do reintrodukcí ohrožených druhů do rezervací i volné přírody nebo o metodickou a materiální pomoc v těchto oblastech. Programy se týkají mimo jiné koně Převalského, gorily nížinné, adaxe, ibise skalního, zubra, čápa černého, orlosupa bradatého, supa hnědého, užovky podplamaté, sysla obecného, kvakoše nočního, puštíka bělavého, sovy pálené a sýčka obecného. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Trauma - odvrácená strana dětství', 'Dětské krizové centrum, z.ú.', 'TRAUMA', 'Statistiky dětských obětí sexuální kriminality jsou děsivé. S nějakou formou sexuálního násilí se setkalo každé páté dítě, přesněji řečeno každá třetí dívka a každý šestý chlapec ve věku od 3 do 15 let. Čísla bohužel každoročně rostou, je proto nezbytné konečně začít tento problém rychle řešit. Základními prvky ochrany dítěte jsou prevence a osvěta. Neinformované dítě nedokáže rozpoznat podezřelé chování. Situace je o to složitější, že sexuální násilí hrozí nejen od neznámých lidí, ale často i od rodinných příslušníků či známých.Tento projekt je zaměřen jak na děti, tak na rodiče. Jeho součástí budou komiksy, speciální eventy či filmový festival. Trauma po sexuálním zneužití bývá mnohdy až doživotní, nebuďme proto lhostejní a zkusme otevřít oči!', 'působí jako nestátní nezisková organizace zaměřená na problematiku dětí týraných, sexuálně zneužívaných, zanedbávaných (syn CAN = Child Abuse and Neglect), dětí ohrožených na zdravém vývoji rizikovými faktory v prostředí a dále na problematiku dětí nacházejících se v závažných životních situacích či dětí traumatizovaných. Působnost Dětského krizového centra je celorepubliková. Služby Dětského krizového centra jsou uživatelům poskytovány bezplatně.Prvotním cílem DKC, z. ú. je naplňování potřeb, ochrana práv a oprávněných zájmů ohrožených dětí, úprava a stabilizace podmínek, v nichž dítě vyrůstá, a to prostřednictvím přímé odborné práce s dítětem a prostřednictvím odborné práce s rodinným či jiným sociálním systémem. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Tréninkové pracoviště Klubák', 'Klubíčko Beroun, o.p.s.', 'KLUBICKO', 'Klubíčko Beroun, o.p.s. je nestátní nezisková organizace pečující o děti i dospělé převážně s těžkým zdravotním postižením. Více jak 18 let využíváme praktické zkušenosti osob, jejichž život je ovlivněn zdravotním handicapem, a znalosti odborníků ku prospěchu našich klientů se zdravotním postižením. Aktivity Klubíčka vždy vycházely z aktuálních potřeb rodin s postiženými dětmi, mladistvými a dospělými z regionu Berounsko, jež se nachází v obtížné životní situaci. Rodiče dětí/dospělých se zdravotním postižením jsou zastoupeni v orgánech o.p.s. a určují tak činnosti Klubíčka. Jejich praktické zkušenosti, prožitky a znalost problematiky jsou pro fungování Klubíčka neocenitelným přínosem.Fungování Klubíčka je transparentní Klubíčko Beroun o.p.s. je členem Asociace veřejně prospěšných organizací, Na stránkách http://www.avpo.cz/kataloghttp://www.avpo.cz/katalog_detail/165/klubicko-beroun-o-p-s-socialni-firma-prospesna-nejen-lidem-se-zdravotnim-postizenim/ zveřejňujeme výsledky našeho hospodaření včetně součtu tří nejvyšších ročních mezd v organizaci (včetně odvodů). Zřídili jsme transparentní účty za účelem poskytnutí detailních informací o našich transakcích široké veřejnosti. Na https://www.fio.cz/scgi-bin/hermes/dz-transparent.cgi?ID_ucet=2600168295 tak mohou dárci i další veřejnost jednoduše kontrolovat naše nakládání se získanými finančními prostředky.Klubíčko Beroun, o.p.s. funguje jako sociální firma. Přednostně zaměstnáváme lidi se zdravotním postižením, osoby dlouhodobě pečující o blízkého se zdravotním postižením a dlouhodobě nezaměstnané (50+, absolventi bez praxe, rodiče po mateřské dovolené) - 81% z celkového počtu našich pracovníků jsou osoby znevýhodněné na trhu práce a ohrožené sociálním vyloučením. V rámci vytváření pracovních míst pro osoby se zdravotním postižením realizujeme již třetím rokem ve spolupráci s městem Beroun projekt „Beroun - město bez bariér". Týmy vozíčkářů a jejich asistentů vyměřují architektonické bariéry s cílem informovat o nich veřejnost. Na základě těchto výstupů postupně město Beroun odstraňuje drobné bariéry a zpracovává projekty na komplexní řešení mobility v Berouně.Na Vráži u Berouna provozujeme částečně bezbariérový penzion rodinného typu - s nepřetržitým celoročním provozem. Zde poskytujeme registrované Odlehčovací služby a Sociálně aktivizační služby pro osoby se zdravotním postižením. Klientům zde zajišťujeme i terapie (perličková koupel, masáže, canisterapie, muzikoterapie) a nabízíme řadu volnočasových aktivit. Dále poskytujeme terénní sociální služby Osobní asistenci a včetně asistované dopravy osob se zdravotním postižením bezbariérově upraveným automobilem Dacia Dokker. Nabízíme i integrované volnočasové aktivity a zajišťujeme vzdělávání pečujících osob a informační servis z oblasti sociálních služeb a legislativy.Služby soustředěné v Penzionu Klubíčko využívá cca 40 klientů s mentálním a kombinovaným postižením. Dalších cca 20 klientů využívá naše terénní sociální služby. Většina našich klientů je naprosto odkázána na naši péči - podáváme jim jídlo a pití, kompletně zajišťujeme jejich hygienu, polohujeme a snažíme se jim zpříjemnit život pomocí terapií a zapojovat je do různých aktivit.Pro klienty s lehčími stupni postižení postupně budujeme Tréninkové pracoviště Klubák, kde si za pomoci JOB-asistentů osvojují základní pracovní návyky a řadu praktických dovedností - péči o domácnost (příprava jídla a pití, úklidy, praní a žehlení/mandlování, péče o zahradu, ...) péči o drobná domácí zvířata, učí se samostatně cestovat veřejnou dopravou, hospodařit s penězi, pracovat s internetem a obstarávat své osobní záležitosti. Také si zkouší drobné administrativní práce, výrobu drobných upomínkových předmětů, natěračské a zahradnické práce, čištění interiérů aut, úklid sněhu apod. Cílem je posílit samostatnost a soběstačnost klientů a jejich samostatné rozhodování, umožnit jejich seberealizaci a připravit je na budoucí pracovní uplatnění v provozním zázemí Penzionu Klubíčko, na veřejných zakázkách obcí státních i nestátních institucí, zakázkách firem i veřejnosti nebo v podporovaném zaměstnávání mimo naši NNO.', 'Posláním obecně prospěšné společnosti Klubíčko Beroun, o.p.s. je vybudování a provozování sociální firmy, která poskytne lidem se zdravotním postižením pracovní uplatnění, možnost bydlení i komplex sociálních služeb, terapií, vzdělávání a volnočasových aktivit. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Tříkrálová sbírka', 'Charita Česká republika', 'KOLEDA', 'Tříkrálovou sbírku pořádá každoročně Charita Česká republika a jedná se o největší dobrovolnickou akci u nás. Do ulic vychází kolem 15ti tisíc kolednických skupinek - převážně dětí a mladých lidí v přestrojení za tři krále. Vedle příspěvku do kasičky je samozřejmě sbírku možné podpořit i formou DMS nebo finančním příspěvkem na účet.Z výtěžku jsou podpořeny služby a projekty Charit po celé republice, a to zejména na pomoc: - handicapovaným a nemocným - seniorům - matkám s dětmi v tísni - lidem bez domova a v materiální nouzi - lidem v těžké sociální situaci - dětem a mládeži ze sociálně rizikového prostředí - dalším osobám v krizi (například obětem domácího násilí, závislým na návykových látkách apod.) Desetina výnosu jde na humanitární pomoc do zahraničí.', 'Charita Česká republika pomáhá potřebným bez ohledu na jejich vyznání, rasu nebo národnost. Je největším nestátním poskytovatelem zdravotních a sociálních služeb v České republice a věnuje se také humanitární pomoci a rozvojové spolupráci v zahraničí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('TyfloCentrum Olomouc - pomáháme nevidomým', 'TyfloCentrum Olomouc, o.p.s.', 'BILEHULKY', 'Účelem veřejné sbírky „TyfloCentrum Olomouc - pomáháme nevidomým" je informovat veřejnost o potřebách osob s těžkým zrakovým postižením a získat finanční prostředky na činnosti podporující samostatnost, nezávislost, seberealizaci a aktivní zapojení těchto osob do běžného života a pracovního procesu.', 'Posláním TyfloCentra Olomouc, o.p.s. je podporovat osoby se zrakovým a kombinovaným postižením (kdy zrakové je dominantní) v aktivním přístupu k životu, povzbudit jejich vůli k osobnímu růstu a posílit jejich nezávislost. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Udělejme zázrak pro ...', 'Nadační fond Dar sluchu', 'DARSLUCHU', 'Představte si na okamžik, že by ve vašem životě neexistoval zvuk. Nikdy byste neslyšeli, jak zní vaše jméno, hlasy by se zarazily o neviditelnou hladovou zeď, která by všechen zvuk spolkla, zůstalo by jen prázdné ticho. Pomozte s námi těm, kterým byla odepřena možnost radovat se ze zvuků okolního světa. Pojďme společně udělat zázrak pro DAVÍDKA a JANU! Projekt si vzal za cíl oběma pomoci a shromáždit finance potřebné na speciální ušní (takzvané kochleární) implantáty, které Davídkovi i Janě pomohou svět vnímat všemi smysly a naplno!Prostředky budou využity na upgrade řečového procesoru pro Janu a implantaci Davídka Kweku z Ghany, který se narodil jako neslyšící. Vše realizujeme ve spolupráci s FN Motol.', 'Nadační fond Dar sluchu byl založen s cílem podpořit neslyšící s minimální ztrátou sluchu až po úplnou hluchotu. V případně totální ztráty sluchu existuje způsob, jak vrátit neslyšící zpět mezi slyšící.Pokud máte problém se sluchem, nebo víte o někomu, kdo tento problém má, kontaktujte nás! ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('V srdci mám Boží chrám', 'Společnost pro památkovou obnovu hrušovského kostela', 'CHRAMSRDCE', 'Projekt je zaměřen na získávání financí k pokrytí spoluúčasti na dotacích, získávaných na opravy kostela sv. Františka a Viktora v Ostravě-Hrušově. Kostel prošel v posledních letech rozsáhlými opravami, ne-li záchrannými pracemi, které byly nutné pro jeho uchování, a to především po ničivých povodních v roce 1997, kdy voda vystoupala až do výše 3,5 m. Snažíme se navrátit tuto kulturní památku k životu, a to nejen po stránce duchovní, ale také z hlediska rozsáhlých oprav a z hlediska kulturního. Kromě záchranných prací zde probíhají také kulturní akce, především benefičního charakteru.Za jakoukoliv pomoc srdečně děkujeme.', 'Spolek Společnost pro památkovou obnovu hrušovského kostela se zaměřuje na organizování kulturních akcí a získávání finanční podpory pomocí dotací a grantů pro potřeby zajišťování oprav kulturní památky, za kterou byl kostel sv. Františka a Viktora v roce 2010 prohlášen. V posledních letech se nám podařilo kostel ve své podstatě zachránit od postupného celkového úpadku, který nastal po povodních v roce 1997, kdy zde voda vystoupila až do výše 3,5 m. Jsme vděčni za jakoukoliv finanční pomoc. Rádi bychom náš kostel dostali do širšího povědomí lidí, již pro jeho pohnutou minulost i historický význam (stojí na tzv. "hranici světů"). Blíže se o našem kostele i naší organizaci dočtete na webu: beneficehrusov.webnode.cz . V roce 2015 (neúspěšně) usiloval náš kostel o získání označení evropské dědictví, v letošním roce se s jeho fotografiemi můžete setkat v putovní národní výstavě Má vlast cestami proměn. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Varhany pro Královo Pole', 'Varhany pro Královo Pole, z.s.', 'VARHANY', 'Cílem projektu je pořízení nového varhanního nástroje s technickými parametry vyhovujícími pro koncertní prezentaci varhanní hudby do římskokatolického kostela Nejsvětější Trojice v Brně - Králově Poli.', 'Spolek si klade tyto cíle, které představují jeho hlavní činnost:- pořídit nový varhanní nástroj s technickými parametry vyhovujícími pro koncertní prezentaci varhanní hudby do římskokatolického kostela Nejsvětější Trojice v Brně - Králově Poli - po zprovoznění nového varhanního nástroje organizovat veřejnou varhanní koncertní činnost v římskokatolickém kostele Nejsvětější Trojice v Brně - Králově Poli. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Veřejná sbírka Tibet', 'Spolek Lungta', 'TIBET', 'Projekt si klade za cíl zlepšit podmínky vzdělávání mnichů v severoindických tibetských klášterních školách Diskit a Tikse, informovat občany České republiky o dění v Tibetu a tibetském exilu prostřednictvím webového portálu TIBINFO a vydáváním knih, podílet se na zachování tibetské kultury podporou překladu výukových videí Khan Academy do tibetštiny Tibeťanem Tenzinem Jigmem žijícím v ČR. Kniha pro Ladak - podpora klášterní školy Diskit a Tikse v rámci Kniha pro Ladak od léta 2002. Prostředky z Veřejné sbírky Tibet jsou využity podle aktuálních potřeb na financování provozu klášterních škol Diskit a Tikse škol, jejich vybavení, pořizování školních pomůcek, ale i potřeb žáků (zdravotní péče, zdravotně vhodné ubytování aj.). Lungta získané prostředky rozděluje mezi školy podle jejich potřebnosti. Informační činnost Lungty - Lungta poskytuje občanům ČR informace v elektronické a tištěné podobě od svého vzniku v r. 1997. V souladu s posláním založila Lungta webový portál TIBINFO www.tibinfo.cz - unikátní zdroj informací. Zájemcům poskytuje informace o aktuálním dění v Tibetu, tibetském exilu, tibetské kultuře, buddhismu, historii Tibetu. Spolek Lungta informuje o unikátní tibetské kultuře v tištěné podobě prostřednictvím vydávání knih. Připravuje vydání knihy „My Tibeťané".Výuková videa Khan Academy v tibetštině - překlad výukových videí do tibetštiny je jedním ze způsobů zachování tibetské kultury. Otitulkování a dabing výukových videí např. z matematiky, fyziky, dějepisu, zeměpisu, chemie, biologie umožní dětem i dospělým v Tibetu i exilu zprostředkovat šíření tibetštiny jako vzdělávacího jazyka ', 'Občanské sdružení Lungta je nevládní neziskové sdružení občanů, kteří se zajímají o Tibet, tibetskou kulturu nebo tibetský buddhismus. Základním cílem sdružení je získávat a zveřejňovat pravdivé informace o unikátní tibetské kultuře, tibetském buddhismu, situaci v Tibetu, tibetských exilových komunitách občanům České republiky. Sdružení dlouhodobě poskytuje rozvojovou a humanitární pomoc Tibeťanům žijícím v severní Indii. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Vodicí pes', 'Vodicí pes, z.s.', 'VODICIPES', 'Posláním spolku VODICÍ PES, z.s. je podpora nevidomých klientů, kteří využívají vodícího psa jako kompenzační pomůcku nebo se na jeho využívání připravují, popřípadě o tuto pomůcku žádají. Podpora se týká oblasti osobního rozvoje, samostatného pohybu a řešení individuálních problémů při pohybu tak, aby byli klienti úspěšní a spokojení v prostředí, které si sami vybrali k životu.Cíle služby: - podpora samostatného života zrakově postiženého klienta ve společnosti; - podpora samostatného pohybu v jeho přirozeném prostředí; - pomoc při obnově sociálních vazeb; - podpora v samostatnosti a nezávislosti na pomoci jiných lidí; - podpora ve smysluplném trávení volného času; - zajištění výchovných, vzdělávacích a aktivizačních činností.Prioritní aktivitou je pravidelné procvičování práce psů, kteří se tím udržují v dobré pracovní kondici, aby ve své práci během mnohaleté služby byli pro svého pána vždy soustředění a spolehliví. Vodicí pes, z.s. přitom spolupracuje s instruktory brněnské Školy pro výcvik vodicích psů. Spolek Vodicí pes, z.s. uskutečňuje dále rekondiční pobyty spojené zejména s procvičováním práce psů, a klubová setkání, jejichž náplní jsou různé zájmové aktivity, přednášky, exkurze a besedy, společná venčení vodicích psů, apod.Získané finanční prostředky jsou využívány: - ve prospěch zrakově postižených klientů na pořízení vodicích psů a výdaje související s péčí a službou vodicích psů; - na náklady ve prospěch aktivit pro klienty služeb; - na příspěvky pro projekt Patronát štěnětě a Pension pro staré vodicí psy; - na prezentace služby vodicích psů na veřejnosti.', 'Spolek Vodicí pes poskytuje službu sociální rehabilitace. Jejím posláním je podpora klientů, kteří využívají vodícího psa jako kompenzační pomůcku nebo se na jeho využívání připravují, popřípadě o tuto pomůcku žádají. Podpora se týká oblasti osobního rozvoje, samostatného pohybu a řešení individuálních problémů při pohybu tak, aby byli klienti úspěšní a spokojení v prostředí, které si sami vybrali k životu. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Vybudování specializovaného počítačového pracoviště pro duševně handicapované v Nadaci BONA', 'Sdružení pro bezbariérovou kulturu NedomYsleno', 'MEZIPLOTY', 'Sbírka Mezi ploty - pomozte duševně nemocným v návratu do běžného života!Festival Mezi ploty Vám 20 let přináší zábavu a zážitek SVĚTA BEZ BARIÉR. Proto si Vás teď, po dvacetiletých zkušenostech a kontaktu s Psychiatrickou léčebnou Bohnice, dovolujeme poprosit:POMOZTE VYTVOŘIT LIDEM S PSYCHICKÝM HANDICAPEM PROSTŘEDÍ, PRACOVIŠTĚ, KDE UPLATNÍ SVOJE SCHOPNOSTI KREATIVITY, ZÍSKAJÍ ODBORNOST A MOŽNOST NÁSLEDNĚ SE UPLATNIT NA PRACOVNÍM TRHU VĚTŠINOVÉ SPOLEČNOSTI.Ukažme, že mezi námi vládne lidskost, komunikace, tolerance a ochota pomáhat těm, kdo pomoc potřebují. V České republice žijí statisíce lidí trpících úzkostí, depresí, závislostí nebo jinou nemocí lidské duše. Jen se dobře podívejte a uvidíte je ve svém okolí. Máme skvělé obětavé odborníky, kteří dokáží pomáhat a zmírňovat utrpení. Ale psychiatrie je u nás pořád popelkou. Nedostatek financí vede k omezování služeb a zhoršování tolik potřebné péče.Proto pomozte psychicky handicapovaným spoluobčanům, podpořte VEŘEJNOU SBÍRKU A DMS MEZIPLOTY i zdravou duši české společnosti.VYBUDUJME SPOLEČNĚ POČÍTAČOVÉ PRACOVIŠTĚ A DEJME VŠEM STEJNOU ŠANCI!Počítačové pracoviště bude vybavené počítači s nainstalovanými programy GRAFIKA. Osvojením si počítačové grafiky mohou tito klienti využít svých schopností kreativity, invence a fantazie a získat novou odbornost a dostat příležitost pro pracovní uplatnění ve většinové společnosti.Pracoviště bude majetkem Nadace BONA při Psychiatrické nemocnici Bohnice. Pracoviště bude provozovat Nadace BONA v rámci rozvoje komunitní péče jako specializovaný terapeutický program.Nadace Bona již více než 20 let pomáhá svou činností k resocializaci a destigmatizaci duševně nemocných. Je nositelem známky kvality udělované Fórem dárců. Jejím vlastním projektem psychosociální sítě již prošlo více než 2 tisíce klientů, pro které zajišťuje chráněné a podporované bydlení. S podporou EU se Nadaci Bona podařilo realizovat „Rodinný dům chráněného bydlení". Díky jejím aktivitám dokáže zajistit pracovní místa pro desítky klientů ročně.Nadace Bona je plně připravena k odpovědnému vedení projektu specializovaného počítačového pracoviště pro duševně handicapované, čímž se hlásí k dalšímu rozvoji vlastního projektu Psychosociální sítě. ', 'Cílem sdružení NedomYsleno je odstraňovat bariéry mezi lidmi s duševním onemocněním nebo zdravotním postižením a lidmi takzvaně zdravými. Tohoto cíle dosahuje pořádáním kulturních akcí, jako jsou různé výstavy či taneční pro nevidomé. Výjimečným úspěchem sdružení je však především to, že se mu podařilo uspořádat již více než 20 ročníků populárního hudebně-divadelního festivalu Mezi ploty v areálu Psychiatrické nemocnice Bohnice. Tento projekt významně přispívá k odtabuizování psychiatrie a destigmatizaci duševně nemocných a nemocnici pomáhá také finančně. Kromě toho podporuje také integraci zdravotně postižených. Handicapovaní umělci totiž na festivalu vystupují na stejných pódiích a scénách jako špičkové profesionálové. Důležitý je rovněž finanční přínos pro psychiatrickou nemocnici, které pravidelně věnujeme výtěžek ze vstupného. Tento aspekt bude v roce 2014 posílen díky veřejné sbírce na pomoc duševně nemocným. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Výstavba kostela sv. Václava v Sazovicích', 'Spolek pro výstavbu kostela sv. Václava v Sazovicích', 'NOVYKOSTEL', 'Kostel sv.Václava v Sazovicích, zasvěcený patronu obce Sazovice a hlavnímu zemskému patronu, patronu českého národa. Bude nám připomínat dobré činy tohoto knížete.Jeho vlastnosti jako vzdělanost, pokora a zbožnost nám budou trvalým vzorem k následování. Stavba po dokončení bude sloužit k bohoslužbám, ale také k pořádání pravidelných kulturních, společenských a duchovně zaměřených aktivit, např. diskusní večery, koncerty a benefice, skautská, seniorská, mládežnická aj. setkání, besídky místních dětí, setkání s osobnostmi veřejného a kulturního života formou besedy či přednášky apod. V suterénu stavby je k těmto účelům určen sál a klubovna.', 'Spolek vznikl za účelem výstavby kostela sv. Václava v obci Sazovice, ke cti tohoto patrona obce. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Vzděláním za prací', 'Poradna pro integraci, z.ú.', 'POMOCPPI', 'Projekt má za cíl pomoci cizincům v České republice získat zaměstnání a úspěšně se zapojit do života v české společnosti. V projektu si cizinci prohloubí znalosti českého jazyka, získají nové dovednosti a zdokonalí si ty, které již mají. Projekt cizincům nabídne jazykové kurzy, rekvalifikační kurzy a mnoho informací o praktickém životě v ČR, aby jim usnadnil získání zaměstnání a osamostatnění se na sociálním systému ČR.', 'Posláním Poradny pro integraci (PPI) je integrovat cizince do české společnosti, boj proti xenofobii a propagace multikulturní společnosti. Toto poslání naplňujeme již od roku 1997 poskytováním různých služeb cizincům a multikulturními aktivitami pro veřejnost. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('www.signaly.cz', 'signály.cz, z.s.', 'SIGNALY', 'Cílem www.signaly.cz je podpora a ochrana mládeže. Komunitní web nabízí kromě otevřeného a bezpečného prostoru pro mládež vzdělávací články, videa a kurzy. Finančně podporuje malé projekty mladých lidí, které jsou realizovány uživateli ve svých komunitách. Jedná se o projekty z oblasti vzdělávání a výchovy, lidských práv, prevence rizikového chování dětí a mládeže, podpory rodiny a dětí, environmentální, ochrany kulturních a náboženských památek, dobrovolnické činnosti a pomoci seniorům a sociálně vyloučeným osobám.', 'Základním cílem sdružení je podpora a ochrana mládeže. Základní činností sdružení je provozování komunitního webu www.signály.cz. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Za lidská práva', 'Amnesty International Česká republika', 'AMNESTY', 'Přidejte se k obráncům lidských práv a podpořte naše kampaně za nespravedlivě vězněné a utlačované. Naším cílem je vytvářet dopad na mezinárodní stav lidských práv!Podpořte největší členskou lidsko-právní organizaci na světě - Amnesty International.', 'Amnesty International je největší členská lidsko-právní organizace na světě. Zabývá se monitorováním stavu lidských práv ve světě, lobbingem za dodržování těchto práv a vzděláváním k lidským právům. Skrze jednotlivé případy bezpráví přinášíme mediální tlak, který následně nutí daný stát či instituci ke změně přístupu k danému lidskému právu. Činnost AI je financována pouze z členských příspěvků s výjimkou kampaně Vzdělávání k lidským právům, která je financována z grantů nepocházejících z veřejných zdrojů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Za odstranění komunikační bariéry neslyšících v Kišiněvě', 'Neslyšící s nadějí', 'NESLYSICIM', 'Účelem projektu je podpora akademického výzkumu moldavského znakového jazyka. Klade si za účel navzájem spojit fakulty a pobočky sociálních i tlumočnických služeb Asociace neslyšících a nedoslýchavých Moldavska, za cílem odstranění komunikační bariéry hlavně neslyšících uživatelů znakového jazyka. Udržujeme bilaterální styky s místními organiizacemi neslyšících a fakultami v Moldavsku, a proto dobře a do hloubky známe situaci a globální problematiku neslyšících. Prostředky z darů jsou použité na vybavení pro potřeby technických zařízení pro výzkum znakového jazyka: fotoaparát, stojanová svítidla, počítač, kancelářské materiály atd.', 'Spolek Neslyšící s nadějí byl založen v roce 2007 a je jednou z humanitárních organizací v ČR. Hlavním cílem je organizovat rozvojové spolupráce zejména sluchově postižených v rozvojových zemích, lobbing a projekty, které se zaměřují na problematiku neslyšících v oblasti školství, sociální a zdravotnictví, výměnu informací mezi asociacemi neslyšících, šíření informací o rozvojové spolupráci a pořádání seminářů a konferencí. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Začít včas! pomáhá malým dětem s postižením a jejich rodinám', 'I MY, o.p.s.', 'IMY', 'Proč je včasná péče důležitá? Zásadní roli ve vývoji dítěte hrají první roky života. V tomto období jsou kompenzační možnosti mozku obrovské. Umožňují rozvinout náhradní mechanismy i u dětí, které mají v některé z oblastí vývoje vážný handicap. Postižení dítěte zároveň znamená pro celou rodinu náročné období adaptace na novou situaci. Sociální služba raná péče, kterou projekt Začít včas! nabízí, má terénní charakter, zaručuje šetrný způsob spolupráce s dětmi i celou rodinou. Poradkyně dojíždí do rodiny, zapůjčuje pomůcky, motivuje rodiče ke zvládání péče o dítě s postižením, rozvíjí dovednosti dítěte v oblastech komunikace, psychomotoriky atd.', 'Posláním organizace je pomáhat prosazovat zájmy dětí a dospělých s postižením. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Záchrana kláštera Chotěšov', 'Spolek Klášter Chotěšov', 'KLASTERCHOTESOV', 'Klášter Chotěšov je bývalý klášter premonstrátek v Chotěšově v okrese Plzeň-jih, který byl ve 13. století založen na vyvýšeném místě nad řekou Radbuzou. Tento překrásný komplex je jednou z řady dominant plzeňského kraje. Spolek klášter Chotěšov usiluje o záchranu tohoto historického objektu. Pořádáním kulturních akcí, publikováním článků o klášteru, vykonáváním průvodcovské služby a výstavami výtvarných děl se snaží tuto kulturní památku přiblížit širokému okruhu občanů.Cílem projektu je získání finančních prostředků, které společně s dotacemi pomohou opravit klášter Chotěšov. Tomuto významnému duchovnímu místu Plzeňského kraje se snažíme navrátit jeho někdejší význam. Klášter je místem nejen duchovním, ale v současné době hlavně kulturním.', 'Spolek Klášter Chotěšov je dobrovolný svazek, jehož členové usilují o záchranu národní kulturní památky, klášter Chotěšov. Spolek podporuje projekty, které klášter popularizují široké veřejnosti, pořádá v klášteře kulturní akce, provádí průvodcovskou činnost, organizuje výstavy výtvarných děl a koncerty, spolupracuje s médii. Kromě těchto činností spolek vykonává i vydavatelskou činnost, prodává pohlednice, tiskoviny, turistické vizitky a známky, knihy, kalendáře a soustřeďuje historické materiály. Snahou členů Spolku je dostat klášter do širšího povědomí lidí pro jeho historický význam a jeho zachování pro budoucí generace. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zachraňme české filmy!', 'Nadace české bijáky', 'BIJAKY', 'Čistíme svět fantazie Projekt „Čistíme svět fantazie" je společným projektem tří stran České televize, Muzea Karla Zemana a Nadace české bijáky a je založen na myšlence podpořit digitální zrestaurování tří filmů Karla Zemana vybraných ze zlatého fondu české kinematografie. Díky aktivnímu zapojení všech tří subjektů se projekt dále rozvíjí a získává řadu přesahů, například v oblasti vzdělávání či propagace české filmové tvorby v zahraničí. Vybrané filmy k digitálnímu restaurování: Vynález zkázy Baron Prášil Cesta do pravěku Veškeré finanční příspěvky zaslané formou DMS slouží od 20. 4. 2015 výhradně na úhradu nákladů spojených s digitálním restaurováním filmů z projektu „Čistíme svět fantazie". Karlovarský projektV rámci „Karlovarského projektu" bude každý rok digitálně zrestaurován jeden film vybraný ze zlatého fondu české kinematografie. Prostřednictvím digitálního restaurování se podaří uchovat tyto filmy pro budoucí generace v takové kvalitě, aby mohly být opět promítány v kinech na velkých plátnech.Vybrané filmy k digitálnímu restaurování:Obchod na korze Limonádový Joe Spalovač mrtvol Kočár do Vídně Démanty noci Sedmikrásky Daleká cesta Kristián UchoPostřižiny s Jiřím MenzelemProjekt Postřižiny s Jiřím Menzelem navázal na úspěšnou a kvalitní spolupráci mezi Nadací české bijáky a režisérem Jiřím Menzelem při digitálním restaurováním filmu Ostře sledované vlaky v roce 2014. Cílem projektu je digitálně zrestaurovat v té nejvyšší kvalitě film Postřižiny a společně s Jiřím Menzelem a kameramanem Jaromírem Šofrem představit zrestaurovanou verzi široké veřejnosti.Filmy, které byly již digitálně zrestaurovány: Ostře sledované vlaky Všichni dobří rodáci Hoří, má panenko Vynález zkázy Kamenný most Baron Prášil Intimní osvětlení', 'Posláním Nadace české bijáky (NČB) je podpora české kinematografie formou digitální restaurace českých filmových klenotů vybraných ze Zlatého fondu české kinematografie. Touto činností se NČB a její podporovatelé zaslouží o uchování těchto filmových děl v té nejvyšší kvalitě (tedy 4K) pro budoucí generace. NČB úzce spolupracuje s Mezinárodním filmovým festivalem v Karlových Varech, Asociací českých kameramanů, Státním fondem kinematografie, Ministerstvem kultury a s řadou významných filmových tvůrců jako je Miloš Forman, Vojtěch Jasný, Ivan Passer, Jaromír Šofr či Jiří Menzel. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zachraňme stromy!', 'Arnika - Centrum pro podporu občanů', 'ALEJE', 'Pokácet strom motorovou pilou trvá několik minut. Je snadné přitom nepomyslet, jak dlouho trvalo, než vyrostl a jak dlouho bude trvat, než za něj vyroste náhrada. Stromy jsou charakteristickým prvkem našeho životního prostředí a dnes, v době velkého znečištění životního prostředí, jsou stále důležitější.Zachraňme stromy! Aleje utvářejí typický ráz české krajiny; ve městech a obcích jsou stromy při dnešní míře znečištění životního prostředí stále důležitější. Naším cílem je s vaší pomocí chránit zdravé stromy před zbytečným kácením, pečovat o historické aleje i o parky a stromořadí uvnitř měst.', 'Arnika chrání životní prostředí, zapojuje veřejnost do rozhodovacích procesů, informuje prostřednictvím médií a spolupracuje na řešení místních, celostátních i globálních problémů s občanskými sdruženími, odborníky a vědci, politiky a novináři po celém světě. Arnika je výjimečná tím, že jde o českou nevládní organizaci zaměřenou především na pozitivní řešení aktuálních problémů ČR. Některé nedávné úspěchy: dosáhli jsme změny zákona, který lépe chrání stromy. Zastavili jsme plošné kácení historických alejí a řádově stovky stromů v parcích a na veřejných prostranstvích. Ročně poskytneme více než tisíc bezplatných konzultací veřejnosti a pomohli jsme založit desítky lokálních občanských sdružení. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zajištění kvality služeb Charity Ostrava', 'Charita Ostrava', 'CHARITAOSTRAVA', 'Základním posláním Charity Ostrava je pomoc lidem v nouzi bez ohledu na jejich rasu, národnost, postavení či vyznání. Pomoc je realizována prostřednictvím poskytovaných sociálních a sociálně zdravotních služeb na základě křesťanských hodnot. Charita Ostrava je nestátní nezisková organizace působící v oblasti pomoci pro širokou škálu cílových skupin lidí v nepříznivých životních situacích. Zajištění provozu poskytovaných služeb je z části odkázána na finanční pomoc dárců v rámci veřejné sbírky. Účelem je tedy zajištění finančních prostředků na udržení kvality přímé péče v rámci sociálních a zdravotních služeb Charity Ostrava pro seniory, lidi se zdravotním postižením, lidi se specifickými potřebami, lidi bez domova, lidi v obtížné životní situaci, ohrožené děti a mládež, lidi v závěru života.', 'Základním posláním Charity Ostrava je pomoc lidem v nouzi bez ohledu na jejich rasu, národnost, postavení či vyznání. Pomoc je realizována zejména prostřednictvím poskytovaných sociálních a sociálně zdravotních služeb. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zajištění okamžité, rehabilitační a rekonstrukční pomoci při mimořádných událostech a řešení dlouhodobých problémů místních obyvatel s ohledem na ekonomické, sociální, zdravotní a ekologické aspekty života lidí.', 'ADRA o.p.s.', 'ADRA', 'Cílem projektu je získání peněžitých příspěvků na zajištění okamžité, rehabilitační a rekonstrukční pomoci při mimořádných událostech a na řešení dlouhodobých problémů místních obyvatel s ohledem na ekonomické, sociální, zdravotní a ekologické aspekty života lidí.ADRA ČR je součástí mezinárodní sítě ADRA, která působí ve více než 125 zemích světa. Více než 20 let poskytujeme pomoc lidem v nouzi.', 'ADRA, o. p. s. Česká republika organizuje a uskutečňuje pomoc lidem v ohrožení života a majetku, který je způsobeno přírodními katastrofami nebo válečnými konflikty. Svými aktivitami rovněž podporuje rozvoj společnosti v oblasti sociální vzdělávací a osvětové při respektu základních lidských práv a svobod doma i ve světě. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zajištění provozu Hospice sv. Lukáše', 'Charita Ostrava', 'HOSPICLUKAS', 'Charita Ostrava je nestátní nezisková organizace působící v oblasti pomoci pro širokou škálu cílových skupin. Pro seniory, osoby se zdravotním postižením a osoby ohrožené sociálním vyloučením provozujeme registrované sociální služby, zdravotní služby a další služby pomoci zajišťované spolupracujícími dobrovolníky.Hospic sv. Lukáše je specializované zdravotnické zařízení, poskytující hospicové služby včetně léčby bolesti, kterou s sebou postupující nemoc přináší. Hospic je určen pro pacienty nevyléčitelně nemocné v terminálním stádiu života, nejčastěji s onkologickou diagnózou, jejichž obtíže nevyžadují léčbu v nemocnici a není možné je zvládnout ani v domácím prostředí. Než se těžce nemocný člověk dostane do hospice, musí být vyčerpány všechny ostatní možnosti léčby. Pacient by měl být o svém stavu informován a hospic nemocnému garantuje, že nebude trpět nesnesitelnou bolestí, že bude vždy respektována jeho lidská důstojnost, že v posledních chvílích nezůstane osamocen. Cílem práce týmu odborníků hospice je dosažení co nejlepší kvality života nemocného člověka i jeho rodiny.', 'Základním posláním Charity Ostrava je pomoc lidem v nouzi bez ohledu na jejich rasu, národnost, postavení či vyznání. Pomoc je realizována zejména prostřednictvím poskytovaných sociálních a sociálně zdravotních služeb. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zajištění provozu Charitního domu sv. Františka', 'Charita Ostrava', 'AZYLFRANTISEK', 'Zřizovatelem a provozovatelem Charitního domu sv. Františka je Charita Ostrava, nestátní nezisková organizace, která od roku 1991 poskytuje ve svých zařízeních sociální, sociálně zdravotní a humanitární pomoc lidem v nouzi, bez ohledu na jejich vyznání, rasu či národnost. Nabízíme sociální, sociálně zdravotní a zdravotní služby především seniorům, lidem bez domova, matkám s dětmi v tísni ohroženým dětem a mládeži, osobám se zdravotním postižením, osobám s duševním onemocněním, lidem nevyléčitelně nemocným v závěrečném stádiu života a dalším lidem, kteří to potřebují.', 'Základním posláním Charity Ostrava je pomoc lidem v nouzi bez ohledu na jejich rasu, národnost, postavení či vyznání. Pomoc je realizována zejména prostřednictvím poskytovaných sociálních a sociálně zdravotních služeb. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zanechte otisk v osudu dětí!', 'Srdce na dlani, nadační fond', 'SRDCENADLANI', 'Cílem projektu je získání finančních prostředků pro podporu vzdělávání a volnočasových aktivit dětí ve čtyřech dětských domovech - Tisá, Černá Voda, Litoměřice a Krásná Lípa (děti ve věku 6-26 let). Nadační fond Srdce na dlani se zabývá výhradně podporou dětí z DD, a to zejména v oblasti vzdělávání, výchovy a přechodu do reálného života. Bez podpory nadačního fondu nemohou DD nabídnout dětem široké možnosti seberealizace a podporu přechodu do reálného života.', 'Posláním organizace je pomáhat dětem v dětských domovech, a to nejen po materiální stránce. Jedním z hlavních cílů je pomoci dětem snáze se integreovat do společnosti a překonat překážky, které jim často po opuštění domovů v životě vyvstanou. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zasukované tkaničky', 'Spolek Trend vozíčkářů Olomouc', 'TKANICKA', 'Veřejná sbírka Zasukované tkaničky získává finanční prostředky na financování asistenční služby vozíčkářům, na financování dalších aktivit, spojených s usnadněním života lidí na invalidním vozíku.', 'Posláním Spolku Trend vozíčkářů Olomouc je vytvářet podmínky pro prosazování rovnoprávného a rovnocenného postavení člověka s fyzickým a kombinovaným postižením ve společnosti. Hlavní důraz se neklade na sociální zabezpečení a pasivní péči, ale na vytvoření rovnoprávných podmínek (rovnost možností), ve kterých se budou moci i občané se skutečně těžkým zdravotním postižením o sebe postarat především sami - případně se o sebe postarají s pomocí svých osobních asistentů. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zdraví dětí', 'Nadační fond pro zdraví dětí', 'ZDRAVIDETI', 'Nadační fond pro zdraví dětí obecně podporuje zdravotnická zařízení, která zajišťují péči o zdraví matky a jejího dítěte zajišťují. Tímto projektem bude financován nákup přístrojového vybavení souvisejícího s péčí o zdraví matky a jejího dítěte pro Ústav pro péči o matku a dítě. Po ukončení projektu nebo v jeho průběhu bude Ústavem pro péči o matku a dítě rozhodnuto, který konkrétní přístroj je aktuálně nejvíce potřeba.', 'Posláním organizace je podpora výzkumu v oblasti  péče o zdraví matky a jejího dítěte, podporu související publikační činnosti a vzdělávání včetně financování studijních pobytů odborníků a zavádění světových poznatků v této oblasti do praxe a podpora při financování přístrojového vybavení těch zdravotnických zařízení, která péči o zdraví matky jejího dítěte  zajišťují. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zelené Trojmezí - zapojte se!', 'TROJMEZÍ Chodovec, Hostivař, Záběhlice', 'TROJMEZI', 'Občanské sdružení TROJMEZÍ se soustředí na zapojení veřejnosti do záchrany chráněné lokality Trojmezí, která se nachází na hranici tří pražských městských částí 10, 11 a 15 a do níž zasahuje Přírodní park Hostivař - Záběhlice a Přírodní památka Meandry Botiče. Pražané tak mají možnost ovlivnit budoucnost kvality životního prostředí ve svém okolí a aktivně se i na ní sami podílet při provádění praktické ochrany této chráněné lokality.', 'Naším posláním je ochrana lokality Trojmezí, kde se nachází Přírodní park Hostivař – Záběhlice a Přírodní památka Meandry Botiče. Hlavním cílem je přispět k zachování a rozvoji rekreační funkčnosti lokality za podmínek respektování jejího přírodního charakteru. Naše činnost je postavena na třech základních pilířích: •    Praktická ochrana přírody •    Environmentální výchova v přímé vazbě na Trojmezí   •    Právní ochrana Trojmezí ve smyslu veřejného zájmu a prostoru ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zlepšení péče o zdravé, ohrožené a kriticky nemocné novorozence.', 'Nadační fond "N"', 'PLNANARUC', 'Tento projekt je zaměřen na: - zlepšení zdravotní a sociální péče o ohrožené a kriticky nemocné novorozence; - zdokonalení technické vybavenosti pracoviště pro potřeby standardní, specializované a intenzivně-resuscitační péče o novorozence; - snižování novorozenecké úmrtnosti, zejména výrazně nezralých dětí, snižování nezvratných poškození v důsledku závažných onemocnění před, během a po porodu; - zlepšení úrovně pregraduálního vzdělávání studentů a  postgraduálního vzdělávání lékařů a sester v  oboru neonatologie, včetně odborných studijních tuzemských i zahraničních stáží; - zlepšování prostředí pro rodiče hospitalizovaných dětí - podporu projektů spolupráce se zahraničními pracovišti; - rozvíjení moderních metod pro včasné podchycení a léčbu či prevenci neurologického a smyslového postižení u novorozenců; - podporu kvalifikované osvěty o problematice perinatálních onemocnění; - podporu preventivní, léčebné a zdravotně sociální činnosti směřující k všestranné, koordinované a vysoce odborně vedené zdravotní péči odpovídající úrovni běžné ve vyspělých evropských zemích.', 'Posláním organizace je rozvíjení a zlepšování neonatologické péče ve FN Motol, podpora preventivní léčebné a zdravotně sociální péče o novorozené děti, snižování novorozenecké úmrtnosti, zlepšení přístrojové vybavenosti na úroveň špičkových světových pracovišť, růst odborné úrovně podporováním studijních pobytů lékařů i sester. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zlepšení vybavenosti našich Domovů pokojného stáří', 'Oblastní charita Ostrov', 'OCHO', 'Rádi bychom společně s vámi pokročili v naší již několikaetapové/několikaleté snaze Zlepšit zázemí, prostředí pro naše klienty a personál v našich Domovech pokojného stáří, jak v Hroznětíně, tak i v Ostrově. Poskytujeme sociální služby pro seniory s Alzheimerovou demencí a jinými zdravotními postiženími, omezeními. Je tedy nyní před námi dovybavit Domov v Hroznětíně, ale také získat prostředky na již druhou etapu Rozšíření kapacity Domova v Ostrově. Jedno již víme, že bez vaší pomoci to nezvládneme, proto věříme, že nám pomůžete! Potřebujeme prostředky na rozličné vybavení, např. od šatních skříní, stolů, apod.', 'Posláním organizace je poskytování sociálních služeb seniorům a pomoc lidem potřebným. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('ZOO Jihlava', 'Zoologická zahrada Jihlava', 'ZOOJIHLAVA', 'Finanční příspěvky budou použity na výstavbu, zatraktivnění areálu Zoologické zahrady Jihlava a zlepšení výživy a péče o chovaná zvířata.', 'Posláním Zoologické zahrady Jihlava je chov, rozmnožování a prezentace živočichů a rostlin, jež umožňují vytvářet ucelené expozice fauny a flóry v návaznosti na ekologii, uchování a ochranu přírody. Zoologická zahrada Jihlava zajišťuje výkon činností související s péčí o chované živočichy a péčí o přírodní areál, v němž vytváří vhodné prostředí pro odpočinek, kulturní vyžití a vzdělání veřejnosti. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zpátky na nohy', 'CEROS, Centrum komplexní neurorehabilitační péče pro nemocné s roztroušenou sklerózou, o.p.s.', 'ZPATKYNANOHY', 'Projekt ZPÁTKY NA NOHY si klade za cíl zkvalitnění neurorehabilitace poskytované ordinací Neurologické kliniky Fakultní nemocnice Královské Vinohrady v Praze ve spolupráci se 3. Lékařskou Fakultou University Karlovy použitím špičkové technologie', 'Obecně prospěšná společnost CEROS se věnuje vytváření standardů v neurorehabilitaci nejen na úrovni ČR, ale i na mezinárodní úrovni. CEROS o.p.s. se tak snaží zařadit do standardu ČR nejlepší možnou péči v oblasti neurorehabilitace, která bude podložená klinickými studiemi a bude přinášet co možná nejefektivnější výsledky neurologicky nemocným pacientům.Tato práce zahrnuje: - utváření týmu odborníků pro neurorehabilitaci a školení dalších expertů - rozpracovávání teoretických hypotéz a ověřování v klinické praxi s jejich následnou prezentací na národní i mezinárodní úrovni (vytváření standardů v neurorehabilitaci) - příprava argumentů pro jednání se zdravotními pojišťovnami - získávání podpory odborných společností - pořádání projektů, jejichž cílem je informovat lidi s RS, odborníky, i laickou veřejnost o onemocnění RS, možnostech léčby a možnostech/nutnosti aktivního začlenění do společnostiVizí obecně prospěšné společnosti CEROS je vybudovat síť hierarchizovaného systému pracovišť, která by v celém státě zajišťovala včasnou, trvalou, specifickou a komplexní péči v oblasti neurorehabilitace. ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Zvíře v nouzi', 'Český svaz ochránců přírody', 'ZVIREVNOUZI', 'Cílem sbírky je finanční podpora celé Národní sítě záchranných stanic koordinované Českým svazem ochránců přírody. Stanice pokrývají svou působností celé území ČR a ročně jimi projde víc jak deset tisíc potřebných živočichů.Péče je určena pro poraněné volně žijící živočichy s cílem jejich opětovného vypuštění do přírody. Dále pro trvale handicapované volně žijící živočichy, kterým je zajištěno důstojné dožití.Nejčastěji jsou přijímána zvířata poraněná dopravou, popálená elektrickým proudem, poraněná nárazem na skleněnou plochu či postřelená. S pomocí široké veřejnosti se tak snažíme poskytnout první pomoc divokým zvířatům v nesnázi a částečně tak zmírnit negativní dopad lidské činnosti na přírodu . Asi 55 % se každoročně podaří vrátit zpět do přírody. Svým příspěvkem můžete pomoci všem stanicím provozovaným neziskovými organizacemi. Pokud Vám není lhostejný osud poraněného, či jinak potřebného zvířete, přispějte na péči o něj!', 'Český svaz ochránců přírody (ČSOP) je občanské sdružení, jehož členy spojuje aktivní zájem o ochranu přírody a krajiny. Naším posláním je ochrana a obnova přírodního dědictví, ekologická výchova a podpora trvale udržitelného života. V současné době máme cca 7500 členů, mezi nimiž naleznete profesionální odborníky, zkušené dobrovolníky i ty, kteří prostě "jen" mají rádi přírodu. Letos slavíme již 35. výročí vzniku naší organizace.  ', '');
INSERT INTO donations (_project_name, _company_name, _sms_code, _project_desc, _company_desc, _company_img_link) VALUES ('Život dětem', 'Život dětem, o.p.s.', 'ZIVOTDETEM', 'Hlavním cílem projektu je pomoc vážně nemocným dětem a dospělým v domácí péči svých rodičů. Získání finančních prostředků na podporu rodin s handicapovanými dětmi, na podporu zařízení, která se starají o nemocné děti, na podporu dětských oddělení nemocnic v České republice i dětských domovů, na podporu vzdělání.', 'Posláním obecně prospěšné společnosti Život dětem je pomoc vážně nemocným dětem i starším, kteří jsou odkázáni na pomoc ostatních, pomoc zařízením, která se starají o nemocné, handicapované a opuštěné děti, podpora vzdělání. ', '');
